'use client';
import React, { useState } from 'react';
import ViewHeader from '@/components/shared/ViewHeader';
import VerseCard from '@/components/shared/VerseCard';
import SummaryCard from '@/components/shared/SummaryCard';
import { Input } from '@/components/ui/input';

interface SurahAlBaqarahViewProps {
  goBack: () => void;
}

const verses = [
  { number: "2:1", arabic: "الم", lao: "ອາລີຟ, ລາມ, ມີມ.", english: "Alif, Lam, Meem." },
  { number: "2:2", arabic: "ذَٰلِكَ الْكِتَابُ لَا رَيْبَ فِيهِ هُدًى لِّلْمُتَّقِينَ", lao: "ຄໍາພີນີ້ (ກຸຣອານ), ບໍ່ມີຄວາມສົງໄສໃດໆໃນມັນ, ເປັນການຊີ້ແນະແກ່ບັນດາຜູ້ທີ່ມີຄວາມຍຳເກງ.", english: "This is the Book about which there is no doubt, a guidance for those conscious of Allah -" },
  { number: "2:3", arabic: "الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُقِيمُونَ الصَّلَاةَ وَمِمَّا رَزَقْنَاهُمْ يُنفِقُونَ", lao: "ບັນດາຜູ້ທີ່ເຊື່ອໃນສິ່ງທີ່ລັບເລິກ, ແລະດຳລົງການລະໝາດ, ແລະຈາກສິ່ງທີ່ເຮົາໄດ້ປະທານໃຫ້ພວກເຂົາ ພວກເຂົາກໍບໍລິຈາກ.", english: "Who believe in the unseen, establish prayer, and spend out of what We have provided for them," },
  { number: "2:4", arabic: "وَالَّذِينَ يُؤْمِنُونَ بِمَا أُنزِلَ إِلَيْكَ وَمَا أُنزِلَ مِن قَبْلِكَ وَبِالْآخِرَةِ هُمْ يُوقِنُونَ", lao: "ແລະບັນດາຜູ້ທີ່ເຊື່ອໃນສິ່ງທີ່ຖືກເປີດເຜີຍລົງມາຫາເຈົ້າ, ແລະສິ່ງທີ່ຖືກເປີດເຜີຍລົງມາກ່ອນເຈົ້າ, ແລະພວກເຂົາກໍມີຄວາມເຊື່ອໝັ້ນຢ່າງເຕັມທີ່ໃນໂລກຫນ້າ.", english: "And who believe in what has been revealed to you, [O Muhammad], and what was revealed before you, and of the Hereafter they are certain [in faith]." },
  { number: "2:5", arabic: "أُولَٰئِكَ عَلَىٰ هُدًى مِّن رَّبِّهِمْ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ", lao: "ພວກເຂົານັ້ນຢູ່ເທິງເສັ້ນທາງທີ່ຖືກຕ້ອງຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ, ແລະພວກເຂົານັ້ນຄືບັນດາຜູ້ທີ່ໄດ້ຮັບຄວາມສຳເລັດ.", english: "Those are upon [right] guidance from their Lord, and it is those who are the successful." },
  { number: "2:6", arabic: "إِنَّ الَّذِينَ كَفَرُوا سَوَاءٌ عَلَيْهِمْ أَأَنذَرْتَهُمْ أَمْ لَمْ تُنذِرْهُمْ لَا يُؤْمِنُونَ", lao: "ແທ້ຈິງບັນດາຜູ້ທີ່ປະຕິເສດຄວາມເຊື່ອ, ບໍ່ວ່າເຈົ້າຈະຕັກເຕືອນພວກເຂົາຫຼືບໍ່ຕັກເຕືອນພວກເຂົາກໍຕາມ, ພວກເຂົາຈະບໍ່ເຊື່ອ.", english: "Indeed, those who disbelieve - it is all the same for them whether you warn them or do not warn them - they will not believe." },
  { number: "2:7", arabic: "خَتَمَ اللَّهُ عَلَىٰ قُلُوبِهِمْ وَعَلَىٰ سَمْعِهِمْ وَعَلَىٰ أَبْصَarِhِمْ غِشَاوَةٌ وَلَهُمْ عَذَابٌ عَظِيمٌ", lao: "ອັລລໍຮ໌ໄດ້ຊົງປະທັບຕາໄວ້ເທິງຫົວໃຈຂອງພວກເຂົາ ແລະເທິງການໄດ້ຍິນຂອງພວກເຂົາ, ແລະເທິງສາຍຕາຂອງພວກເຂົາມີຜ້າປິດໄວ້; ແລະສຳລັບພວກເຂົາຈະໄດ້ຮັບການທໍລະມານຢ່າງໃຫຍ່ຫຼວງ.", english: "Allah has set a seal upon their hearts and upon their hearing, and over their vision is a veil. And for them is a great punishment." },
  { number: "2:8", arabic: "وَمِنَ النَّاسِ مَن يَقُولُ آمَنَّا بِاللَّهِ وَبِالْيَوْمِ الْآخِرِ وَمَا هُم بِمُؤْمِنِينَ", lao: "ແລະໃນບັນດາມະນຸດ ມີຜູ້ທີ່ກ່າວວ່າ “ພວກເຮົາເຊື່ອໃນອັລລໍຮ໌ ແລະໃນວັນສຸດທ້າຍ” ແຕ່ພວກເຂົາບໍ່ແມ່ນຜູ້ທີ່ເຊື່ອ.", english: "And of the people are some who say, 'We believe in Allah and the Last Day,' but they are not believers." },
  { number: "2:9", arabic: "يُخَادِعُونَ اللَّهَ وَالَّذِينَ آمَنُوا وَمَا يَخْدَعُونَ إِلَّا أَنفُسَهُمْ وَمَا يَشْعُرُونَ", lao: "ພວກເຂົາຫຼອກລວງອັລລໍຮ໌ ແລະບັນດາຜູ້ທີ່ເຊື່ອ, ແຕ່ພວກເຂົາບໍ່ໄດ້ຫຼອກລວງໃຜນອກຈາກຕົວເອງເທົ່ານັ້ນ, ແລະພວກເຂົາກໍບໍ່ຮູ້ສຶກຕົວ.", english: "They [think to] deceive Allah and those who believe, but they deceive not except themselves and perceive [it] not." },
  { number: "2:10", arabic: "فِي قُلُوبِهِم مَّرَضٌ فَزَادَهُمُ اللَّهُ مَرَضًا وَلَهُمْ عَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْذِبُونَ", lao: "ໃນຫົວໃຈຂອງພວກເຂົາມີພະຍາດ, ແລ້ວອັລລໍຮ໌ກໍຊົງເພີ່ມພະຍາດໃຫ້ພວກເຂົາ, ແລະພວກເຂົາຈະໄດ້ຮັບການທໍລະມານຢ່າງເຈັບປວດເນື່ອງຈາກສິ່ງທີ່ພວກເຂົາເຄີຍຕົວະ.", english: "In their hearts is disease, so Allah has increased their disease; and for them is a painful punishment because they [habitually] used to lie." },
  { number: "2:11", arabic: "وَإِذَا قِيلَ لَهُمْ لَا تُفْسِدُوا فِي الْأَرْضِ قَالُوا إِنَّمَا نَحْنُ مُصْلِحُونَ", lao: "ແລະເມື່ອຖືກກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງຢ່າສ້າງຄວາມເສຍຫາຍໃນແຜ່ນດິນ', ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາເປັນພຽງຜູ້ປັບປຸງແກ້ໄຂເທົ່ານັ້ນ'.", english: "And when it is said to them, 'Do not cause corruption on the earth,' they say, 'We are but reformers.'" },
  { number: "2:12", arabic: "أَلَا إِنَّهُمْ هُمُ الْمُفْسِدُونَ وَلَٰكِن لَّا يَشْعُرُونَ", lao: "ຈົ່ງຮູ້ໄວ້ວ່າ, ແທ້ຈິງແລ້ວ ພວກເຂົາຄືຜູ້ທີ່ສ້າງຄວາມເສຍຫາຍ, ແຕ່ພວກເຂົາບໍ່ຮູ້ສຶກຕົວ.", english: "Unquestionably, it is they who are the corrupters, but they perceive [it] not." },
  { number: "2:13", arabic: "وَإِذَا قِيلَ لَهُمْ آمِنُوا كَمَا آمَنَ النَّاسُ قَالُوا أَنُؤْمِنُ كَمَا آمَنَ السُّفَهَاءُ ۗ أَلَا إِنَّهُمْ هُمُ السُّفَهَاءُ وَلَٰكِن لَّا يَعْلَمُونَ", lao: "ແລະເມື່ອຖືກກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງເຊື່ອເໝືອນດັ່ງທີ່ຄົນທັງຫຼາຍໄດ້ເຊື່ອ', ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາຈະເຊື່ອເໝືອນດັ່ງທີ່ຄົນໂງ່ໄດ້ເຊື່ອບໍ?' ຈົ່ງຮູ້ໄວ້ວ່າ, ແທ້ຈິງແລ້ວ ພວກເຂົາຄືຄົນໂງ່, ແຕ່ພວກເຂົາບໍ່ຮູ້.", english: "And when it is said to them, 'Believe as the people have believed,' they say, 'Should we believe as the foolish have believed?' Unquestionably, it is they who are the foolish, but they know it not." },
  { number: "2:14", arabic: "وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَوْا إِلَىٰ شَيَاطِينِهِمْ قَالُوا إِنَّا مَعَكُمْ إِنَّمَا نَحْنُ مُسْتَهْزِئُونَ", lao: "ແລະເມື່ອພວກເຂົາພົບກັບບັນດາຜູ້ທີ່ເຊື່ອ, ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາເຊື່ອແລ້ວ'. ແຕ່ເມື່ອພວກເຂົາຢູ່ກັບພວກຜີມານຂອງພວກເຂົາ, ພວກເຂົາກ່າວວ່າ: 'ແທ້ຈິງ ພວກເຮົາຢູ່ກັບພວກເຈົ້າ, ພວກເຮົາເປັນພຽງຜູ້ທີ່ເຍາະເຍີ້ຍເທົ່ານັ້ນ'.", english: "And when they meet those who believe, they say, 'We believe'; but when they are alone with their evil ones, they say, 'Indeed, we are with you; we were only mockers.'" },
  { number: "2:15", arabic: "اللَّهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ", lao: "ອັລລໍຮ໌ຈະຊົງເຍາະເຍີ້ຍພວກເຂົາ ແລະຈະຊົງປ່ອຍໃຫ້ພວກເຂົາຢູ່ໃນການລະເມີດຂອງພວກເຂົາຢ່າງບໍ່ມີຈຸດໝາຍ.", english: "Allah mocks them and prolongs them in their transgression [while] they wander blindly." },
  { number: "2:16", arabic: "أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدَىٰ فَمَا رَبِحَت تِّجَارَتُهُمْ وَمَا كَانُوا مُهْتَدِينَ", lao: "ພວກເຂົາຄືຜູ້ທີ່ໄດ້ຊື້ການຫຼົງທາງດ້ວຍການຊີ້ແນະ, ດັ່ງນັ້ນການຄ້າຂອງພວກເຂົາຈຶ່ງບໍ່ໄດ້ກຳໄລ ແລະ ພວກເຂົາບໍ່ໄດ້ເປັນຜູ້ທີ່ຖືກຊີ້ແນະ.", english: "Those are the ones who have purchased error [in exchange] for guidance, so their transaction has brought no profit, nor were they guided." },
  { number: "2:17", arabic: "مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ نَارًا فَلَمَّا أَضَاءَتْ مَا حَوْلَهُ ذَهَبَ اللَّهُ بِنُورِهِمْ وَتَرَكَهُمْ فِي ظُلُمَاتٍ لَّا يُبْصِرُونَ", lao: "ຕົວຢ່າງຂອງພວກເຂົາຄືກັບຜູ້ທີ່ໄດ້ຈູດໄຟ, ເມື່ອໄຟໄດ້ສ່ອງແສງສິ່ງທີ່ຢູ່ອ້ອມຮອບລາວ, ອັລລໍຮ໌ກໍໄດ້ເອົາແສງສະຫວ່າງຂອງພວກເຂົາໄປ ແລະ ປ່ອຍພວກເຂົາໄວ້ໃນຄວາມມືດ, ພວກເຂົາບໍ່ສາມາດເຫັນໄດ້.", english: "Their example is that of one who kindled a fire, but when it illuminated what was around him, Allah took away their light and left them in darkness [so] they could not see." },
  { number: "2:18", arabic: "صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُونَ", lao: "ຫູໜວກ, ເປັນໃບ້, ຕາບອດ, ດັ່ງນັ້ນພວກເຂົາຈະບໍ່ກັບມາ.", english: "Deaf, dumb and blind - so they will not return [to the right path]." },
  { number: "2:19", arabic: "أَوْ كَصَيِّبٍ مِّنَ السَّمَاءِ فِيهِ ظُلُمَاتٌ وَرَعْدٌ وَبَرْقٌ يَجْعَلُونَ أَصَابِعَهُمْ فِي آذَانِهِم مِّنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِ ۚ وَاللَّهُ مُحِيطٌ بِالْكَافِرِينَ", lao: "ຫຼືຄືກັບຝົນທີ່ຕົກລົງມາຈາກຟ້າ, ໃນນັ້ນມີຄວາມມືດ, ສຽງຟ້າຮ້ອງ, ແລະຟ້າແມບ. ພວກເຂົາເອົານິ້ວມືອັດຫູຂອງພວກເຂົາຈາກສຽງຟ້າຜ່າ ເພາະຢ້ານຄວາມຕາຍ. ແລະອັລລໍຮ໌ຊົງລ້ອມຜູ້ປະຕິເສດຄວາມເຊື່ອໄວ້.", english: "Or [it is] like a rainstorm from the sky within which is darkness, thunder and lightning. They put their fingers in their ears against the thunderclaps in dread of death. But Allah is encompassing of the disbelievers." },
  { number: "2.20", arabic: "يَكَادُ الْبَرْقُ يَخْطَفُ أَبْصَarَهُمْ ۖ كُلَّمَا أَضَاءَ لَهُم mَّشَوْا فِيهِ وَإِذَا أَظْلَمَ عَلَيْهِمْ قَامُوا ۚ وَلَوْ شَاءَ اللَّهُ لَذَهَبَ بِسَمْعِهِمْ وَأَبْصَarِهِمْ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ຟ້າແມບເກືອບຈະເອົາສາຍຕາຂອງພວກເຂົາໄປ. ທຸກຄັ້ງທີ່ມັນສ່ອງແສງໃຫ້ພວກເຂົາ, ພວກເຂົາກໍຍ່າງໄປໃນແສງນັ້ນ; ແລະເມື່ອມັນມືດລົງ, ພວກເຂົາກໍຢຸດ. ແລະຖ້າອັລລໍຮ໌ຊົງປະສົງ, ພຣະອົງຈະເອົາການໄດ້ຍິນແລະສາຍຕາຂອງພວກເຂົາໄປ. ແທ້ຈິງ, ອັລລໍຮ໌ຊົງມີອຳນາດເໜືອທຸກສິ່ງ.", english: "The lightning almost snatches away their sight. Every time it lights [the way] for them, they walk therein; but when darkness comes over them, they stand [still]. And if Allah had willed, He could have taken away their hearing and their sight. Indeed, Allah is over all things competent." },
  { number: "2:21", arabic: "يَا أَيُّهَا النَّاسُ اعْبُدُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ وَالَّذِينَ مِن قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ", lao: "ໂອ້ ມະນຸດເອີຍ, ຈົ່ງນະມັດສະການພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຈົ້າ, ຜູ້ທີ່ໄດ້ສ້າງພວກເຈົ້າ ແລະຜູ້ທີ່ຢູ່ກ່ອນໜ້າພວກເຈົ້າ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ມີຄວາມຢຳເກງ.", english: "O mankind, worship your Lord, who created you and those before you, that you may become righteous -" },
  { number: "2:22", arabic: "الَّذِي جَعَلَ لَكُمُ الْأَرْضَ فِرَاشًا وَالسَّمَاءَ بِنَاءً وَأَنزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْqًا لَّكُمْ ۖ فَلَا تَجْعَلُوا لِلَّهِ أَندَادًا وَأَنتُمْ تَعْلَمُونَ", lao: "ຜູ້ທີ່ໄດ້ເຮັດໃຫ້ແຜ່ນດິນເປັນທີ່ນອນສຳລັບພວກເຈົ້າ, ແລະຟ້າເປັນຫຼັງຄາ, ແລະໄດ້ສົ່ງນ້ຳລົງມາຈາກຟ້າ, ແລ້ວໄດ້ເອົານ້ຳນັ້ນອອກມາເປັນໝາກໄມ້ເພື່ອເປັນອາຫານສຳລັບພວກເຈົ້າ. ດັ່ງນັ້ນ, ຢ່າຕັ້ງສິ່ງໃດເປັນຄູ່ແຂ່ງກັບອັລລໍຮ໌, ທັງທີ່ພວກເຈົ້າຮູ້.", english: "[He] who made for you the earth a bed [spread out] and the sky a ceiling and sent down from the sky, rain and brought forth thereby fruits as provision for you. So do not attribute to Allah equals while you know [that there is nothing similar to Him]." },
  { number: "2:23", arabic: "وَإِن كُنتُمْ فِي رَيْبٍ مِّمَّا نَزَّلْنَا عَلَىٰ عَبْدِنَا فَأْتُوا بِسُورَةٍ مِّن مِّثْلِهِ وَادْعُوا شُهَدَاءَكُم مِّن دُونِ اللَّهِ إِن كُنتُمْ صَادِقِينَ", lao: "ແລະຖ້າພວກເຈົ້າສົງໄສໃນສິ່ງທີ່ເຮົາໄດ້ເປີດເຜີຍລົງມາໃຫ້ແກ່ຂ້າທາດຂອງເຮົາ, ກໍຈົ່ງນຳມາຊັກບົດໜຶ່ງທີ່ເໝືອນກັບມັນ, ແລະຈົ່ງເອີ້ນພະຍານຂອງພວກເຈົ້ານອກຈາກອັລລໍຮ໌, ຖ້າພວກເຈົ້າເປັນຜູ້ທີ່ສັດຈິງ.", english: "And if you are in doubt about what We have sent down upon Our Servant [Muhammad], then produce a surah the like thereof and call upon your witnesses other than Allah, if you should be truthful." },
  { number: "2.24", arabic: "فَإِن لَّمْ تَفْعَلُوا وَلَن تَفْعَلُوا فَاتَّقُوا النَّarَ الَّتِي وَقُودُهَا النَّاسُ وَالْحِجَarَةُ ۖ أُعِدَّتْ لِلْكَافِرِينَ", lao: "ແຕ່ຖ້າພວກເຈົ້າບໍ່ສາມາດເຮັດໄດ້, ແລະພວກເຈົ້າຈະບໍ່ສາມາດເຮັດໄດ້ຢ່າງແນ່ນອນ, ກໍຈົ່ງຢ້ານກົວໄຟນະລົກ, ຊຶ່ງເຊື້ອໄຟຂອງມັນຄືມະນຸດແລະຫີນ, ທີ່ຖືກກະກຽມໄວ້ສຳລັບຜູ້ປະຕິເສດຄວາມເຊື່ອ.", english: "But if you do not - and you will never be able to - then fear the Fire, whose fuel is men and stones, prepared for the disbelievers." },
  { number: "2.25", arabic: "وَبَشِّرِ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أَنَّ لَهُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَarُ ۖ كُلَّمَا رُزِقُوا مِنْهَا مِن ثَمَرَةٍ رِّزْقًا ۙ قَالُوا هَٰذَا الَّذِي رُزِقْنَا مِن قَبْلُ ۖ وَأُتُوا بِهِ مُتَشَابِهًا ۖ وَلَهُمْ فِيهَا أَزْوَاجٌ مُّطَهَّرَةٌ ۖ وَهُمْ فِيهَا خَالِدُونَ", lao: "ແລະຈົ່ງແຈ້ງຂ່າວດີແກ່ບັນດາຜູ້ທີ່ເຊື່ອແລະເຮັດຄວາມດີວ່າ ສຳລັບພວກເຂົາຈະມີສວນສະຫວັນ, ຊຶ່ງມີແມ່ນ້ຳໄຫຼຜ່ານຢູ່ເບື້ອງລຸ່ມ. ທຸກຄັ້ງທີ່ພວກເຂົາໄດ້ຮັບໝາກໄມ້ຈາກສວນສະຫວັນເປັນອາຫານ, ພວກເຂົາຈະກ່າວວ່າ: 'ນີ້ຄືສິ່ງທີ່ພວກເຮົາເຄີຍໄດ້ຮັບມາກ່ອນ'. ແລະພວກເຂົາຈະໄດ້ຮັບມັນໃນລັກສະນະທີ່ຄ້າຍຄືກັນ. ແລະໃນນັ້ນພວກເຂົາຈະມີຄູ່ຄອງທີ່ບໍລິສຸດ, ແລະພວກເຂົາຈະຢູ່ໃນນັ້ນຕະຫຼອດໄປ.", english: "And give good tidings to those who believe and do righteous deeds that they will have gardens [in Paradise] beneath which rivers flow. Whenever they are provided with a provision of fruit therefrom, they will say, 'This is what we were provided with before.' And it will be given to them in likeness. And they will have therein purified spouses, and they will abide therein eternally." },
  { number: "2:26", arabic: "۞ إِنَّ اللَّهَ لَا يَسْتَحْيِي أَن يَضْرِبَ مَثَلًا مَّا بَعُوضَةً فَمَا فَوْقَهَا ۚ فَأَمَّا الَّذِينَ آمَنُوا فَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِن رَّبِّهِمْ ۖ وَأَمَّا الَّذِينَ كَفَرُوا فَيَقُولُونَ مَاذَا أَرَادَ اللَّهُ بِهَٰذَا مَثَلًا ۘ يُضِلُّ بِهِ كَثِيرًا وَيَهْدِي بِهِ كَثِيرًا ۚ وَمَا يُضِلُّ بِهِ إِلَّا الْفَاسِقِينَ", lao: "ແທ້ຈິງ, ອັລລໍຮ໌ບໍ່ຊົງລະອາຍທີ່ຈະຍົກຕົວຢ່າງໃດໆ, ແມ້ຈະເປັນຍຸງ ຫຼືສິ່ງທີ່ເໜືອມັນ. ສ່ວນຜູ້ທີ່ເຊື່ອ, ພວກເຂົາຮູ້ວ່າມັນເປັນຄວາມຈິງຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ. ແຕ່ສ່ວນຜູ້ທີ່ປະຕິເສດ, ພວກເຂົາກ່າວວ່າ: 'ອັລລໍຮ໌ປະສົງສິ່ງໃດດ້ວຍຕົວຢ່າງນີ້?' ພຣະອົງຊົງເຮັດໃຫ້ຫຼາຍຄົນຫຼົງທາງດ້ວຍມັນ ແລະຊົງຊີ້ແນະຫຼາຍຄົນດ້ວຍມັນ. ແລະພຣະອົງບໍ່ໄດ້ເຮັດໃຫ້ຜູ້ໃດຫຼົງທາງດ້ວຍມັນນອກຈາກຜູ້ທີ່ລະເມີດ.", english: "Indeed, Allah is not timid to present an example - that of a mosquito or what is smaller than it. And those who believe know that it is the truth from their Lord. But as for those who disbelieve, they say, 'What did Allah intend by this as an example?' He misleads many thereby and guides many thereby. And He misleads not thereby except the defiantly disobedient," },
  { number: "2:27", arabic: "الَّذِينَ يَنقُضُونَ عَهْدَ اللَّهِ مِن بَعْدِ مِيثَاقِهِ وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَن يُوصَلَ وَيُفْسِدُونَ فِي الْأَرْضِ ۚ أُولَٰئِكَ هُمُ الْخَاسِرُونَ", lao: "ຜູ້ທີ່ລະເມີດສັນຍາຂອງອັລລໍຮ໌ຫຼັງຈາກທີ່ໄດ້ຢືນຢັນມັນ, ແລະຕັດສິ່ງທີ່ອັລລໍຮ໌ໄດ້ສັ່ງໃຫ້ເຊື່ອມຕໍ່, ແລະສ້າງຄວາມເສຍຫາຍໃນແຜ່ນດິນ. ພວກເຂົາຄືຜູ້ທີ່ຂາດທຶນ.", english: "Who break the covenant of Allah after contracting it and sever that which Allah has ordered to be joined and cause corruption on earth. It is those who are the losers." },
  { number: "2.28", arabic: "كَيْفَ تَكْfُرُونَ بِاللَّهِ وَكُنتُمْ أَمْوَاتًا فَأَحْيَاكُمْ ۖ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ثُمَّ إِلَيْهِ تُرْjَعُونَ", lao: "ພວກເຈົ້າຈະປະຕິເສດອັລລໍຮ໌ໄດ້ແນວໃດ, ທັງທີ່ພວກເຈົ້າເຄີຍຕາຍ ແລ້ວພຣະອົງໄດ້ໃຫ້ພວກເຈົ້າມີຊີວິດ? ຫຼັງຈາກນັ້ນ, ພຣະອົງຈະໃຫ້ພວກເຈົ້າຕາຍ, ແລ້ວຈະໃຫ້ພວກເຈົ້າມີຊີວິດອີກ, ແລ້ວພວກເຈົ້າຈະຖືກນຳກັບໄປຫາພຣະອົງ.", english: "How can you disbelieve in Allah when you were lifeless and He brought you to life; then He will cause you to die, then He will bring you [back] to life, and then to Him you will be returned." },
  { number: "2.29", arabic: "هُوَ الَّذِي خَلَقَ لَكُم مَّا فِي الْأَرْضِ جَمِيعًا ثُمَّ اسْتَوَىٰ إِلَى السَّمَاءِ فَسَوَّاهُنَّ سَبْعَ سَمَاوَاتٍ ۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ", lao: "ພຣະອົງຄືຜູ້ທີ່ໄດ້ສ້າງທຸກສິ່ງໃນແຜ່ນດິນໄວ້ສຳລັບພວກເຈົ້າ, ຫຼັງຈາກນັ້ນພຣະອົງໄດ້ຫັນໄປຫາທ້ອງຟ້າ ແລະໄດ້ເຮັດໃຫ້ມັນສົມບູນເປັນເຈັດຊັ້ນຟ້າ. ແລະພຣະອົງຊົງຮູ້ທຸກສິ່ງ.", english: "It is He who created for you all of that which is on the earth. Then He directed Himself to the heaven, [His being above all creation], and made them seven heavens, and He is Knowing of all things." },
  { number: "2.30", arabic: "وَإِذْ قَالَ رَبُّكَ لِلْمَلَائِكَةِ إِنِّي جَاعِلٌ فِي الْأَرْضِ خَلِيفَةً ۖ قَالُوا أَتَجْعَلُ فِيهَا مَن يُفْسِدُ فِيهَا وَيَسْفِكُ الدِّمَاءَ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ ۖ قَالَ إِنِّي أَعْلَمُ مَا لَا تَعْلَمُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເວລາທີ່ພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໄດ້ກ່າວແກ່ທູດສະຫວັນວ່າ: 'ແທ້ຈິງ, ຂ້ອຍຈະຕັ້ງຜູ້ຕາງໜ້າໄວ້ໃນແຜ່ນດິນ.' ພວກເຂົາກ່າວວ່າ: 'ພຣະອົງຈະຕັ້ງຜູ້ທີ່ຈະສ້າງຄວາມເສຍຫາຍແລະເຮັດໃຫ້ເລືອດໄຫຼໃນນັ້ນບໍ, ທັງທີ່ພວກເຮົາສັນລະເສີນພຣະອົງແລະຍ້ອງຍໍຄວາມບໍລິສຸດຂອງພຣະອົງ?' ພຣະອົງກ່າວວ່າ: 'ແທ້ຈິງ, ຂ້ອຍຮູ້ໃນສິ່ງທີ່ພວກເຈົ້າບໍ່ຮູ້.'", english: "And [mention, O Muhammad], when your Lord said to the angels, 'Indeed, I will make upon the earth a successive authority.' They said, 'Will You place upon it one who causes corruption therein and sheds blood, while we declare Your praise and sanctify You?' Allah said, 'Indeed, I know that which you do not know.'" },
  { number: "2:31", arabic: "وَعَلَّمَ آدَمَ الْأَسْمَاءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلَائِكَةِ فَقَالَ أَنبِئُونِي بِأَسْمَاءِ هَٰؤُلَاءِ إِن كُنتُمْ صَادِقِينَ", lao: "ແລະພຣະອົງໄດ້ສອນຊື່ທັງໝົດໃຫ້ແກ່ອາດັມ, ຫຼັງຈາກນັ້ນພຣະອົງໄດ້ສະແດງພວກເຂົາຕໍ່ໜ້າທູດສະຫວັນ ແລະກ່າວວ່າ: 'ຈົ່ງບອກຊື່ຂອງສິ່ງເຫຼົ່ານີ້ໃຫ້ຂ້ອຍຮູ້, ຖ້າພວກເຈົ້າເປັນຜູ້ທີ່ສັດຈິງ.'", english: "And He taught Adam the names - all of them. Then He showed them to the angels and said, 'Inform Me of the names of these, if you are truthful.'" },
  { number: "2:32", arabic: "قَالُوا سُبْحَانَكَ لَا عِلْمَ لَنَا إِلَّا مَا عَلَّمْتَنَا ۖ إِنَّكَ أَنتَ الْعَلِيمُ الْحَكِيمُ", lao: "ພວກເຂົາກ່າວວ່າ: 'ມະຫາບໍລິສຸດແດ່ພຣະອົງ! ພວກເຮົາບໍ່ມີຄວາມຮູ້ນອກຈາກສິ່ງທີ່ພຣະອົງໄດ້ສອນພວກເຮົາ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງຮອບຮູ້, ຜູ້ຊົງມີປັນຍາ.'", english: "They said, 'Exalted are You; we have no knowledge except what You have taught us. Indeed, it is You who is the Knowing, the Wise.'" },
  { number: "2:33", arabic: "قَالَ يَا آدَمُ أَنبِئْهُم بِأَسْمَائِهِمْ ۖ فَلَمَّا أَنبَأَهُم بِأَسْمَائِهِمْ قَالَ أَلَمْ أَقُل لَّكُمْ إِنِّي أَعْلَمُ غَيْبَ السَّمَاوَاتِ وَالْأَرْضِ وَأَعْلَمُ مَا تُبْدُونَ وَمَا كُنتُمْ تَكْتُمُونَ", lao: "ພຣະອົງກ່າວວ່າ: 'ໂອ້ ອາດັມ, ຈົ່ງບອກຊື່ຂອງພວກເຂົາໃຫ້ພວກເຂົາຮູ້.' ເມື່ອລາວໄດ້ບອກຊື່ຂອງພວກເຂົາ, ພຣະອົງກ່າວວ່າ: 'ຂ້ອຍບໍ່ໄດ້ບອກພວກເຈົ້າບໍວ່າ ແທ້ຈິງຂ້ອຍຮູ້ຄວາມລັບຂອງຊັ້ນຟ້າແລະແຜ່ນດິນ, ແລະຂ້ອຍຮູ້ສິ່ງທີ່ພວກເຈົ້າເປີດເຜີຍແລະສິ່ງທີ່ພວກເຈົ້າປິດບັງ?'", english: "He said, 'O Adam, inform them of their names.' And when he had informed them of their names, He said, 'Did I not tell you that I know the unseen [aspects] of the heavens and the earth? And I know what you reveal and what you have concealed.'" },
  { number: "2:34", arabic: "وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ أَبَىٰ وَاسْتَكْبَرَ وَكَانَ مِنَ الْكَافِرِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ກ່າວແກ່ທູດສະຫວັນວ່າ: 'ຈົ່ງກົ້ມຂາບຕໍ່ອາດັມ.' ພວກເຂົາກໍກົ້ມຂາບຍົກເວັ້ນອິບລີສ. ລາວປະຕິເສດແລະອວດດີ, ແລະລາວຢູ່ໃນກຸ່ມຜູ້ປະຕິເສດ.", english: "And [mention] when We said to the angels, 'Prostrate before Adam'; so they prostrated, except for Iblees. He refused and was arrogant and became of the disbelievers." },
  { number: "2:35", arabic: "وَقُلْنَا يَا آدَمُ اسْكُنْ أَنتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هَٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِمِينَ", lao: "ແລະເຮົາໄດ້ກ່າວວ່າ: 'ໂອ້ ອາດັມ, ຈົ່ງອາໄສຢູ່ໃນສວນສະຫວັນທັງເຈົ້າແລະຄູ່ຄອງຂອງເຈົ້າ, ແລະຈົ່ງກິນຈາກມັນຢ່າງອຸດົມສົມບູນຕາມທີ່ເຈົ້າຕ້ອງການ, ແຕ່ຢ່າເຂົ້າໃກ້ຕົ້ນໄມ້ນີ້, ຖ້າບໍ່ດັ່ງນັ້ນພວກເຈົ້າຈະກາຍເປັນຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.'", english: "And We said, 'O Adam, dwell, you and your wife, in Paradise and eat therefrom in [ease and] abundance from wherever you will. But do not approach this tree, lest you be among the wrongdoers.'" },
  { number: "2:36", arabic: "فَأَزَلَّهُمَا الشَّيْطَانُ عَنْهَا فَأَخْرَجَهُمَا مِمَّا كَانَا فِيهِ ۖ وَقُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۖ وَلَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ إِلَىٰ حِينٍ", lao: "ແຕ່ຊັຍຕອນໄດ້ເຮັດໃຫ້ພວກເຂົາຫຼົງຜິດຈາກມັນ ແລະໄດ້ຂັບໄລ່ພວກເຂົາອອກຈາກສິ່ງທີ່ພວກເຂົາເຄີຍຢູ່. ແລະເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງລົງໄປ, ບາງຄົນໃນພວກເຈົ້າເປັນສັດຕູຕໍ່ກັນ. ແລະສຳລັບພວກເຈົ້າຈະມີທີ່ພັກອາໄສໃນແຜ່ນດິນແລະຄວາມສຸກຊົ່ວຄາວ.'", english: "But Satan caused them to slip out of it and removed them from that [condition] in which they had been. And We said, 'Go down, [all of you], as enemies to one another, and you will have upon the earth a place of settlement and provision for a time.'" },
  { number: "2:37", arabic: "فَتَلَقَّىٰ آدَمُ مِن رَّبِّهِ كَلِمَاتٍ فَتَابَ عَلَيْهِ ۚ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ", lao: "ຫຼັງຈາກນັ້ນ, ອາດັມໄດ້ຮັບຄຳສອນຈາກພຣະຜູ້ເປັນເຈົ້າຂອງລາວ, ແລ້ວພຣະອົງໄດ້ອະໄພໃຫ້ລາວ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງອະໄພ, ຜູ້ຊົງເມດຕາສະເໝີ.", english: "Then Adam received from his Lord [some] words, and He accepted his repentance. Indeed, it is He who is the Accepting of repentance, the Merciful." },
  { number: "2:38", arabic: "قُلْنَا اهْبِطُوا مِنْهَا جَمِيعًا ۖ فَإِمَّا يَأْتِيَنَّكُم مِّنِّي هُدًى فَمَن تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງລົງໄປຈາກທີ່ນີ້ທັງໝົດ. ຖ້າມີການຊີ້ແນະຈາກຂ້ອຍມາຫາພວກເຈົ້າ, ຜູ້ໃດທີ່ຕາມການຊີ້ແນະຂອງຂ້ອຍ, ຈະບໍ່ມີຄວາມຢ້ານກົວໃດໆຕໍ່ພວກເຂົາ ແລະພວກເຂົາຈະບໍ່ໂສກເສົ້າ.'", english: "We said, 'Go down from it, all of you. And when guidance comes to you from Me, whoever follows My guidance - there will be no fear concerning them, nor will they grieve.'" },
  { number: "2:39", arabic: "وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ النَّarِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ແລະຜູ້ທີ່ປະຕິເສດແລະຕົວະຕໍ່ອາຍະຕ່າງໆຂອງເຮົາ, ພວກເຂົາຄືຊາວນາລົກ; ພວກເຂົາຈະຢູ່ໃນນັ້ນຕະຫຼອດໄປ.", english: "And those who disbelieve and deny Our signs - those are the companions of the Fire; they will abide therein eternally." },
  { number: "2:40", arabic: "يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَوْفُوا بِعَهْدِي أُوفِ بِعَهْدِكُمْ وَإِيَّايَ فَarْهَبُونِ", lao: "ໂອ້ ລູກຫຼານຂອງອິດສະຣາເອວ, ຈົ່ງລະນຶກເຖິງຄວາມໂປດປານຂອງຂ້ອຍທີ່ຂ້ອຍໄດ້ໃຫ້ແກ່ພວກເຈົ້າ, ແລະຈົ່ງຮັກສາສັນຍາຂອງຂ້ອຍ, ຂ້ອຍກໍຈະຮັກສາສັນຍາຂອງພວກເຈົ້າ. ແລະຈົ່ງຢຳເກງຂ້ອຍພຽງຜູ້ດຽວ.", english: "O Children of Israel, remember My favor which I have bestowed upon you and fulfill My covenant [upon you] that I will fulfill your covenant [from Me], and be afraid of [only] Me." },
  { number: "2:41", arabic: "وَآمِنُوا بِمَا أَنزَلْتُ مُصَدِّqًا لِّمَا مَعَكُمْ وَلَا تَكُونُوا أَوَّلَ كَافِرٍ بِهِ ۖ وَلَا تَشْتَرُوا بِآيَاتِي ثَمَنًا قَلِيلًا وَإِيَّايَ فَاتَّقُونِ", lao: "ແລະຈົ່ງເຊື່ອໃນສິ່ງທີ່ຂ້ອຍໄດ້ເປີດເຜີຍລົງມາ, ເຊິ່ງຢືນຢັນສິ່ງທີ່ພວກເຈົ້າມີຢູ່, ແລະຢ່າເປັນຜູ້ທຳອິດທີ່ປະຕິເສດມັນ. ແລະຢ່າແລກປ່ຽນອາຍະຕ່າງໆຂອງຂ້ອຍດ້ວຍລາຄາທີ່ຕໍ່າຕ້ອຍ, ແລະຈົ່ງຢຳເກງຂ້ອຍພຽງຜູ້ດຽວ.", english: "And believe in what I have sent down confirming that which is [already] with you, and be not the first to disbelieve in it. And do not exchange My signs for a small price, and fear [only] Me." },
  { number: "2:42", arabic: "وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَأَنتُمْ تَعْلَمُونَ", lao: "ແລະຢ່າປົນຄວາມຈິງກັບຄວາມຕົວະ, ແລະຢ່າປິດບັງຄວາມຈິງທັງທີ່ພວກເຈົ້າຮູ້.", english: "And do not mix the truth with falsehood or conceal the truth while you know [it]." },
  { number: "2:43", arabic: "وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَarْكَعُوا مَعَ الرَّاكِعِينَ", lao: "ແລະຈົ່ງດຳລົງການລະໝາດ, ແລະຈົ່ງຈ່າຍຊາກາດ, ແລະຈົ່ງກົ້ມຂາບພ້ອມກັບຜູ້ທີ່ກົ້ມຂາບ.", english: "And establish prayer and give zakah and bow with those who bow [in worship and obedience]." },
  { number: "2:44", arabic: "أَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنسَوْنَ أَنفُسَكُمْ وَأَنتُمْ تَتْلُونَ الْكِتَابَ ۚ أَفَلَا تَعْقِلُونَ", lao: "ພວກເຈົ້າສັ່ງໃຫ້ຄົນອື່ນເຮັດຄວາມດີແລ້ວລືມຕົວເອງບໍ, ທັງທີ່ພວກເຈົ້າອ່ານຄຳພີ? ພວກເຈົ້າບໍ່ຄິດບໍ?", english: "Do you order righteousness of the people and forget yourselves while you recite the Scripture? Then will you not reason?" },
  { number: "2:45", arabic: "وَاسْتَعِينُوا بِالصَّبْرِ وَالصَّلَاةِ ۚ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى الْخَاشِعِينَ", lao: "ແລະຈົ່ງຂໍຄວາມຊ່ວຍເຫຼືອດ້ວຍຄວາມອົດທົນແລະການລະໝາດ. ແລະແທ້ຈິງ, ມັນເປັນສິ່ງທີ່ຍາກລຳບາກຍົກເວັ້ນສຳລັບผู้ທີ່ຖ່ອມຕົນ.", english: "And seek help through patience and prayer, and indeed, it is difficult except for the humbly submissive [to Allah]" },
  { number: "2:46", arabic: "الَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَاقُو رَبِّهِمْ وَأَنَّهُمْ إِلَيْهِ رَاجِعُونَ", lao: "ຜູ້ທີ່ແນ່ໃຈວ່າພວກເຂົາຈະໄດ້ພົບກັບພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ ແລະພວກເຂົາຈະກັບໄປຫາພຣະອົງ.", english: "Who are certain that they will meet their Lord and that they will return to Him." },
  { number: "2:47", arabic: "يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ", lao: "ໂອ້ ລູກຫຼານຂອງອິດສະຣາເອວ, ຈົ່ງລະນຶກເຖິງຄວາມໂປດປານຂອງຂ້ອຍທີ່ຂ້ອຍໄດ້ໃຫ້ແກ່ພວກເຈົ້າ ແລະວ່າຂ້ອຍໄດ້ຍົກຍ້ອງພວກເຈົ້າເໜືອຄົນທັງຫຼາຍ.", english: "O Children of Israel, remember My favor that I have bestowed upon you and that I preferred you over the worlds." },
  { number: "2:48", arabic: "وَاتَّقُوا يَوْمًا لَّا تَجْزِي نَفْسٌ عَن نَّفْسٍ شَيْئًا وَلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَلَا هُمْ يُنصَرُونَ", lao: "ແລະຈົ່ງຢຳເກງວັນທີ່ບໍ່ມີໃຜສາມາດຊ່ວຍເຫຼືອໃຜໄດ້, ແລະບໍ່ມີການໄກ່ເກ່ຍໃດໆຈະຖືກຮັບຈາກລາວ, ແລະບໍ່ມີຄ່າໄຖ່ໃດໆຈະຖືກເອົາຈາກລາວ, ແລະພວກເຂົາຈະບໍ່ໄດ້ຮັບການຊ່ວຍເຫຼືອ.", english: "And fear a Day when no soul will suffice for another soul at all, nor will intercession be accepted from it, nor will compensation be taken from it, nor will they be aided." },
  { number: "2:49", arabic: "وَإِذْ نَجَّيْنَاكُم مِّنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ sū’َ الْعَذَابِ يُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ ۚ وَفِي ذَٰلِكُم bَلَاءٌ مِّن رَّبِّكُمْ عَظِيمٌ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ຊ່ວຍພວກເຈົ້າໃຫ້ພົ້ນຈາກພັກພວກຂອງຟາໂຣ, ຜູ້ທີ່ໄດ້ທໍລະມານພວກເຈົ້າຢ່າງໂຫດຮ້າຍ, ຂ້າລູກຊາຍຂອງພວກເຈົ້າແລະປະໄວ້ລູກສາວຂອງພວກເຈົ້າ. ແລະໃນນັ້ນມີການທົດສອບອັນໃຫຍ່ຫຼວງຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຈົ້າ.", english: "And [recall] when We saved your forefathers from the people of Pharaoh, who afflicted you with the worst torment, slaughtering your [newborn] sons and keeping your females alive. And in that was a great trial from your Lord." },
  { number: "2:50", arabic: "وَإِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَأَنجَيْنَاكُمْ وَأَغْرَقْنَا آلَ فِرْعَوْنَ وَأَنتُمْ تَنظُرُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ແຍກທະເລໃຫ້ພວກເຈົ້າ, ແລ້ວໄດ້ຊ່ວຍພວກເຈົ້າໃຫ້ພົ້ນແລະໄດ້ຈົມພັກພວກຂອງຟາໂຣໃນຂະນະທີ່ພວກເຈົ້າເບິ່ງຢູ່.", english: "And [recall] when We parted the sea for you and saved you and drowned the people of Pharaoh while you were looking on." },
  { number: "2:51", arabic: "وَإِذْ وَاعَدْنَا مُوسَىٰ أَرْبَعِينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِن بَعْدِهِ وَأَنتُمْ ظَالِمُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ສັນຍາກັບມູຊາເປັນເວລາສີ່ສິບຄືນ, ແລ້ວພວກເຈົ້າໄດ້ເອົາລູກງົວມາເປັນພະເຈົ້າຫຼັງຈາກລາວ, ແລະພວກເຈົ້າກໍເປັນຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.", english: "And [recall] when We made an appointment with Moses for forty nights. Then you took [for worship] the calf after him, while you were wrongdoers." },
  { number: "2:52", arabic: "ثُمَّ عَفَوْنَا عَنكُم مِّن بَعْدِ ذَٰلِكَ لَعَلَّكُمْ تَشْكُرُونَ", lao: "ຫຼັງຈາກນັ້ນ, ເຮົາໄດ້ອະໄພໃຫ້ພວກເຈົ້າ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ຂອບໃຈ.", english: "Then We forgave you after that so perhaps you would be grateful." },
  { number: "2:53", arabic: "وَإِذْ آتَيْنَا مُوسَى الْكِتَابَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ໃຫ້ຄຳພີແລະສິ່ງທີ່ຈຳແນກແກ່ມູຊາ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ຮັບການຊີ້ແນະ.", english: "And [recall] when We gave Moses the Scripture and the criterion that perhaps you would be guided." },
  { number: "2:54", arabic: "وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنفُسَكُم بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوبُوا إِلَىٰ bَarِئِكُمْ فَاقْتُلُوا أَنفُسَكُمْ ذَٰلِكُمْ خَيْرٌ لَّكُمْ عِندَ bَarِئِكُمْ فَتَابَ عَلَيْكُمْ ۚ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອມູຊາໄດ້ກ່າວແກ່ປະຊາຊົນຂອງລາວວ່າ: 'ໂອ້ ປະຊາຊົນຂອງຂ້ອຍ, ແທ້ຈິງພວກເຈົ້າໄດ້ທຳຮ້າຍຕົວເອງໂດຍການເອົາລູກງົວມາເປັນພະເຈົ້າ. ດັ່ງນັ້ນ, ຈົ່ງກັບໃຈຕໍ່ພຣະຜູ້ສ້າງຂອງພວກເຈົ້າແລະຈົ່ງຂ້າຕົວເອງ. ນັ້ນເປັນສິ່ງທີ່ດີກວ່າສຳລັບພວກເຈົ້າຕໍ່ໜ້າພຣະຜູ້ສ້າງຂອງພວກເຈົ້າ.' ແລ້ວພຣະອົງໄດ້ອະໄພໃຫ້ພວກເຈົ້າ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງອະໄພ, ຜູ້ຊົງເມດຕາ.", english: "And [recall] when Moses said to his people, 'O my people, indeed you have wronged yourselves by your taking of the calf [for worship]. So repent to your Creator and kill yourselves. That is best for you in the sight of your Creator.' Then He accepted your repentance; indeed, He is the Accepting of repentance, the Merciful." },
  { number: "2:55", arabic: "وَإِذْ قُلْتُمْ يَا مُوسَىٰ لَن نُّؤْمِنَ لَكَ حَتَّىٰ نَرَى اللَّهَ جَهْرَةً فَأَخَذَتْكُمُ الصَّاعِقَةُ وَأَنتُمْ تَنظُرُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອພວກເຈົ້າໄດ້ກ່າວວ່າ: 'ໂອ້ ມູຊາ, ພວກເຮົາຈະບໍ່ເຊື່ອເຈົ້າຈົນກວ່າພວກເຮົາຈະເຫັນອັລລໍຮ໌ຢ່າງເປີດເຜີຍ.' ແລ້ວສຽງຟ້າຜ່າໄດ້ເອົາພວກເຈົ້າໄປໃນຂະນະທີ່ພວກເຈົ້າເບິ່ງຢູ່.", english: "And [recall] when you said, 'O Moses, we will never believe you until we see Allah outright'; so the thunderbolt took you while you were looking on." },
  { number: "2:56", arabic: "ثُمَّ bَعَثْنَاكُم مِّن بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَ", lao: "ຫຼັງຈາກນັ້ນ, ເຮົາໄດ້ປຸກພວກເຈົ້າຂຶ້ນມາຫຼັງຈາກຄວາມຕາຍຂອງພວກເຈົ້າ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ຂອບໃຈ.", english: "Then We revived you after your death that perhaps you would be grateful." },
  { number: "2:57", arabic: "وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَأَنزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوَىٰ ۖ كُلُوا مِن طَيِّbَاتِ مَا رَزَقْنَاكُمْ ۖ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ", lao: "ແລະເຮົາໄດ້ເຮັດໃຫ້ເມກເປັນຮົ່ມເງົາໃຫ້ພວກເຈົ້າ ແລະໄດ້ສົ່ງມັນນາແລະນົກກະທາລົງມາໃຫ້ພວກເຈົ້າ. 'ຈົ່ງກິນຈາກສິ່ງທີ່ດີທີ່ເຮົາໄດ້ໃຫ້ແກ່ພວກເຈົ້າ.' ແລະພວກເຂົາບໍ່ໄດ້ທຳຮ້າຍເຮົາ, ແຕ່ພວກເຂົາທຳຮ້າຍຕົວເອງ.", english: "And We shaded you with clouds and sent down to you manna and quails, [saying], 'Eat from the good things with which We have provided you.' And they wronged Us not - but they were [only] wronging themselves." },
  { number: "2:58", arabic: "وَإِذْ قُلْنَا ادْخُلُوا هَٰذِهِ الْقَرْيَةَ فَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَادْخُلُوا الْbَابَ سُjَّدًا وَقُولُوا حِطَّةٌ نَّغْفِرْ لَكُمْ خَطَايَاكُمْ ۚ وَسَنَزِيدُ الْمُحْسِنِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງເຂົ້າໄປໃນເມືອງນີ້ແລະກິນຈາກມັນຢ່າງອຸດົມສົມບູນຕາມທີ່ເຈົ້າຕ້ອງການ, ແລະຈົ່ງເຂົ້າປະຕູດ້ວຍການກົ້ມຂາບແລະກ່າວວ່າ 'ຮິດຕໍ', ເຮົາຈະອະໄພໃຫ້ຄວາມຜິດຂອງພວກເຈົ້າ. ແລະເຮົາຈະເພີ່ມໃຫ້ແກ່ผู้ທີ່ເຮັດຄວາມດີ.'", english: "And [recall] when We said, 'Enter this city and eat from it wherever you will in [ease and] abundance, and enter the gate bowing humbly and say, 'Relieve us of our burdens.' We will [then] forgive your sins for you, and We will increase the doers of good.'" },
  { number: "2:59", arabic: "فَبَدَّلَ الَّذِينَ ظَلَمُوا قَوْلًا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَنزَلْنَا عَلَى الَّذِينَ ظَلَمُوا رِjْزًا مِّنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ", lao: "ແຕ່ຜູ້ທີ່ບໍ່ຍຸດຕິທຳໄດ້ປ່ຽນຄຳເວົ້າທີ່ຖືກບອກໃຫ້ພວກເຂົາ. ດັ່ງນັ້ນ, ເຮົາໄດ້ສົ່ງການລົງໂທດຈາກທ້ອງຟ້າລົງມາໃສ່ຜູ້ທີ່ບໍ່ຍຸດຕິທຳເນື່ອງຈາກການລະເມີດຂອງພວກເຂົາ.", english: "But those who wronged changed [the words] to a statement other than that which had been said to them, so We sent down upon those who wronged a punishment from the sky because they were defiantly disobeying." },
  { number: "2:60", arabic: "وَإِذِ اسْتَسْقَىٰ مُوسَىٰ لِقَوْمِهِ فَقُلْنَا اضْرِب بِّعَصَاكَ الْحَjَرَ ۖ فَانfَjَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۖ قَدْ عَلِمَ كُلُّ أُنَاسٍ مَّشْرَbَهُمْ ۖ كُلُوا وَاشْرَبُوا مِن رِّزْقِ اللَّهِ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອມູຊາໄດ້ຂໍນ້ຳໃຫ້ປະຊາຊົນຂອງລາວ, ແລ້ວເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງຕີຫີນດ້ວຍໄມ້ເທົ້າຂອງເຈົ້າ.' ແລ້ວສິບສອງຕານ້ຳໄດ້ໄຫຼອອກມາຈາກມັນ. ແຕ່ລະເຜົ່າໄດ້ຮູ້ບ່ອນດື່ມຂອງຕົນ. 'ຈົ່ງກິນແລະດື່ມຈາກສິ່ງທີ່ອັລລໍຮ໌ໄດ້ໃຫ້, ແລະຢ່າສ້າງຄວາມເສຍຫາຍໃນແຜ່ນດິນ.'", english: "And [recall] when Moses prayed for water for his people, so We said, 'Strike with your staff the stone.' And there gushed forth from it twelve springs, and every people knew its watering place. 'Eat and drink from the provision of Allah, and do not commit abuse on the earth, spreading corruption.'" },
  { number: "2:61", arabic: "وَإِذْ قُلْتُمْ يَا مُوسَىٰ لَن نَّصْبِرَ عَلَىٰ طَعَامٍ وَاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِjْ لَنَا مِمَّا تُنبِتُ الْأَرْضُ مِن بَقْلِهَا وَقِثَّائِهَا وَفُومِهَا وَعَدَسِهَا وَbَصَلِهَا ۖ قَالَ أَتَسْتَبْدِلُونَ الَّذِي هُوَ أَدْنَىٰ بِالَّذِي هُوَ خَيْرٌ ۚ اهْبِطُوا مِصْرًا فَإِنَّ لَكُم مَّا سَأَلْتُمْ ۗ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَbَاءُوا بِغَضَبٍ مِّنَ اللَّهِ ۗ ذَٰلِكَ بِأَنَّهُمْ كَانُوا يَكْfُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّbِيِّينَ بِغَيْرِ الْحَقِّ ۗ ذَٰلِكَ بِمَا عَصَوا وَّكَانُوا يَعْتَدُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອພວກເຈົ້າກ່າວວ່າ: 'ໂອ້ ມູຊາ, ພວກເຮົາບໍ່ສາມາດອົດທົນກັບອາຫານຊະນິດດຽວໄດ້. ດັ່ງນັ້ນ, ຈົ່ງຂໍພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໃຫ້ເອົາສິ່ງທີ່ແຜ່ນດິນປູກອອກມາໃຫ້ພວກເຮົາ, ເຊັ່ນ: ຜັກ, ໝາກແຕງ, ກະທຽມ, ຖົ່ວ, ແລະຫົວຫອມ.' ລາວໄດ້ກ່າວວ່າ: 'ພວກເຈົ້າຈະແລກປ່ຽນສິ່ງທີ່ດີກວ່າດ້ວຍສິ່ງທີ່ຕໍ່າກວ່າບໍ? ຈົ່ງລົງໄປໃນເມືອງໃດກໍໄດ້, ແລະພວກເຈົ້າຈະໄດ້ສິ່ງທີ່ພວກເຈົ້າຂໍ.' ແລະຄວາມອັບອາຍແລະຄວາມທຸກຍາກໄດ້ຖືກກຳນົດໃຫ້ພວກເຂົາ, ແລະພວກເຂົາໄດ້ຮັບຄວາມໂກດແຄ້ນຈາກອັລລໍຮ໌. ນັ້ນເປັນເພາະວ່າພວກເຂົາປະຕິເສດອາຍະຕ່າງໆຂອງອັລລໍຮ໌ແລະຂ້າສາດສະດາໂດຍບໍ່ຍຸດຕິທຳ. ນັ້ນເປັນເພາະວ່າພວກເຂົາບໍ່ເຊື່ອຟັງແລະລະເມີດ.", english: "And [recall] when you said, 'O Moses, we can never endure one [kind of] food. So call upon your Lord to bring forth for us from the earth its green herbs and its cucumbers and its garlic and its lentils and its onions.' [Moses] said, 'Would you exchange what is better for what is less? Go into [any] settlement and indeed, you will have what you have asked.' And they were covered with humiliation and poverty and returned with anger from Allah [upon them]. That was because they [repeatedly] disbelieved in the signs of Allah and killed the prophets without right. That was because they disobeyed and were [habitually] transgressing." },
  { number: "2:62", arabic: "إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالنَّصَarَىٰ وَالصَّابِئِينَ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ أَjْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْfٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ແທ້ຈິງ, ຜູ້ທີ່ເຊື່ອ, ແລະຜູ້ທີ່ເປັນຊາວຢິວ, ແລະຊາວຄຣິດ, ແລະຊາວຊາບີອິນ, ຜູ້ໃດທີ່ເຊື່ອໃນອັລລໍຮ໌ແລະວັນສຸດທ້າຍແລະເຮັດຄວາມດີ, ພວກເຂົາຈະໄດ້ຮັບລາງວັນຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ, ແລະຈະບໍ່ມີຄວາມຢ້ານກົວໃດໆຕໍ່ພວກເຂົາ ແລະພວກເຂົາຈະບໍ່ໂສກເສົ້າ.", english: "Indeed, those who believed and those who were Jews or Christians or Sabeans [before Prophet Muhammad] - those [among them] who believed in Allah and the Last Day and did righteousness - will have their reward with their Lord, and no fear will there be concerning them, nor will they grieve." },
  { number: "2:63", arabic: "وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُم بِقُوَّةٍ وَاذْكُرُوا مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເອົາສັນຍາຈາກພວກເຈົ້າແລະຍົກພູເຂົາຂຶ້ນເໜືອພວກເຈົ້າ, [ໂດຍກ່າວວ່າ]: 'ຈົ່ງຍຶດໝັ້ນໃນສິ່ງທີ່ເຮົາໄດ້ໃຫ້ແກ່ພວກເຈົ້າຢ່າງເຂັ້ມແຂງແລະຈົ່ງລະນຶກເຖິງສິ່ງທີ່ຢູ່ໃນນັ້ນ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ມີຄວາມຢຳເກງ.'", english: "And [recall] when We took your covenant, [O Children of Israel, to abide by the Torah] and We raised over you the mount, [saying], 'Take what We have given you with determination and remember what is in it that perhaps you may become righteous.'" },
  { number: "2:64", arabic: "ثُمَّ تَوَلَّيْتُم مِّن بَعْدِ ذَٰلِكَ ۖ فَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ لَكُنتُم مِّنَ الْخَاسِرِينَ", lao: "ຫຼັງຈາກນັ້ນ, ພວກເຈົ້າໄດ້ຫັນໜີ. ແລະຖ້າບໍ່ແມ່ນຄວາມໂປດປານແລະຄວາມເມດຕາຂອງອັລລໍຮ໌ຕໍ່ພວກເຈົ້າ, ພວກເຈົ້າກໍຈະກາຍເປັນผู้ທີ່ຂາດທຶນ.", english: "Then you turned away after that. And if not for the favor of Allah upon you and His mercy, you would have been among the losers." },
  { number: "2:65", arabic: "وَلَقَدْ عَلِمْتُمُ الَّذِينَ اعْتَدَوْا مِنكُمْ فِي السَّbْتِ فَقُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِئِينَ", lao: "ແລະແນ່ນອນ, ພວກເຈົ້າຮູ້ຈັກຜູ້ທີ່ລະເມີດໃນວັນສະບາໂຕໃນກຸ່ມຂອງພວກເຈົ້າ, ແລ້ວເຮົາໄດ້ກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງເປັນລີງที่ຖືກຂັບໄລ่.'", english: "And you had already known about those who transgressed among you concerning the sabbath, and We said to them, 'Be apes, despised.'" },
  { number: "2:66", arabic: "فَجَعَلْنَاهَا نَكَالًا لِّمَا بَيْنَ يَدَيْهَا وَمَا خَلْfَهَا وَمَوْعِظَةً لِّلْمُتَّقِينَ", lao: "ແລ້ວເຮົາໄດ້ເຮັດໃຫ້มันເປັນຕົວຢ່າງສຳລັບຄົນໃນຍຸກນັ້ນແລະຄົນລຸ້ນຫຼັງ, ແລະເປັນຄຳຕັກເຕືອນສຳລັບผู้ທີ່ຢຳເກງ.", english: "And We made it a deterrent punishment for those who were present and those who succeeded [them] and a lesson for those who fear Allah." },
  { number: "2:67", arabic: "وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ إِنَّ اللَّهَ يَأْمُرُكُمْ أَن تَذْبَحُوا بَقَرَةً ۖ قَالُوا أَتَتَّخِذُنَا هُزُوًا ۖ قَالَ أَعُوذُ بِاللَّهِ أَنْ أَكُونَ مِنَ الْجَاهِلِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອມູຊາໄດ້ກ່າວແກ່ປະຊາຊົນຂອງລາວວ່າ: 'ແທ້ຈິງ, ອັລລໍຮ໌ສັ່ງໃຫ້ພວກເຈົ້າຂ້າງົວແມ່.' ພວກເຂົາກ່າວວ່າ: 'ເຈົ້າຈະເຍາະເຍີ້ຍພວກເຮົາບໍ?' ລາວໄດ້ກ່າວວ່າ: 'ຂ້ອຍຂໍຄວາມຄຸ້ມຄອງຈາກອັລລໍຮ໌ໃຫ້ພົ້ນຈາກການເປັນຄົນໂງ່.'", english: "And [recall] when Moses said to his people, 'Indeed, Allah commands you to slaughter a cow.' They said, 'Do you take us in ridicule?' He said, 'I seek refuge in Allah from being among the ignorant.'" },
  { number: "2:68", arabic: "قَالُوا ادْعُ لَنَا رَبَّكَ يُbَيِّن لَّنَا مَا هِيَ ۚ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لَّا فَarِضٌ وَلَا بِكْرٌ عَوَانٌ بَيْنَ ذَٰلِكَ ۖ فَافْعَلُوا مَا تُؤْمَرُونَ", lao: "ພວກເຂົາກ່າວວ່າ: 'ຈົ່ງຂໍພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໃຫ້ອະທິບາຍໃຫ້ພວກເຮົາຮູ້ວ່າມັນເປັນແນວໃດ.' ລາວໄດ້ກ່າວວ່າ: 'ພຣະອົງກ່າວວ່າມັນເປັນງົວທີ່ບໍ່ແກ່ແລະບໍ່ອ່ອນ, ຢູ່ລະຫວ່າງກາງ. ດັ່ງນັ້ນ, ຈົ່ງເຮັດຕາມທີ່ພວກເຈົ້າຖືກສັ່ງ.'", english: "They said, 'Call upon your Lord to make clear to us what it is.' [Moses] said, '[Allah] says, 'It is a cow which is neither old nor virgin, but median between that,' so do what you are commanded.'" },
  { number: "2:69", arabic: "قَالُوا ادْعُ لَنَا رَبَّكَ يُbَيِّن لَّنَا مَا لَوْنُهَا ۚ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ صَفْرَاءُ فَاقِعٌ لَّوْنُهَا تَسُرُّ النَّاظِرِينَ", lao: "ພວກເຂົາກ່າວວ່າ: 'ຈົ່ງຂໍພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໃຫ້ອະທິບາຍໃຫ້ພວກເຮົາຮູ້ວ່າສີຂອງມັນເປັນແນວໃດ.' ລາວໄດ້ກ່າວວ່າ: 'ພຣະອົງກ່າວວ່າມັນເປັນງົວສີເຫຼືອງສົດໃສ, ສີຂອງມັນເຮັດໃຫ້ผู้ເບິ່ງມີຄວາມສຸກ.'", english: "They said, 'Call upon your Lord to show us what is its color.' He said, 'He says, 'It is a yellow cow, bright in color - pleasing to the observers.''" },
  { number: "2:70", arabic: "قَالُوا ادْعُ لَنَا رَبَّكَ يُbَيِّن لَّنَا مَا هِيَ إِنَّ الْbَقَرَ تَشَابَهَ عَلَيْنَا وَإِنَّا إِن شَاءَ اللَّهُ لَمُهْتَدُونَ", lao: "ພວກເຂົາກ່າວວ່າ: 'ຈົ່ງຂໍພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໃຫ້ອະທິບາຍໃຫ້ພວກເຮົາຮູ້ວ່າມັນເປັນແນວໃດ, ເພາະງົວເບິ່ງຄືກັນສຳລັບພວກເຮົາ. ແລະແທ້ຈິງ, ຖ້າອັລລໍຮ໌ປະສົງ, ພວກເຮົາຈະໄດ້ຮັບການຊີ້ແນະ.'", english: "They said, 'Call upon your Lord to make clear to us what it is. Indeed, [all] cows look alike to us. And indeed we, if Allah wills, will be guided.'" },
  { number: "2:71", arabic: "قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لَّا ذَلُولٌ تُثِيرُ الْأَرْضَ وَلَا تَسْقِي الْحَرْثَ مُسَلَّمَةٌ لَّا شِيَةَ فِيهَا ۚ قَالُوا الْآنَ جِئْتَ بِالْحَقِّ ۚ فَذَبَحُوهَا وَمَا كَادُوا يَفْعَلُونَ", lao: "ລາວໄດ້ກ່າວວ່າ: 'ພຣະອົງກ່າວວ່າມັນເປັນງົວທີ່ບໍ່ໄດ້ຖືກໃຊ້ໄຖນາແລະບໍ່ໄດ້ຖືກໃຊ້ຫົດນ້ຳໄຮ່ນາ, ສົມບູນແບບບໍ່ມີຕຳหนิ.' ພວກເຂົາກ່າວວ່າ: 'ຕອນນີ້ເຈົ້າໄດ້ນຳຄວາມຈິງມາແລ້ວ.' ແລ້ວພວກເຂົາກໍຂ້າມັນ, ແຕ່ເກືອບຈະບໍ່ໄດ້ເຮັດ.", english: "He said, 'He says, 'It is a cow neither trained to plow the earth nor to water the field, sound, lacking any blemish.' ' They said, 'Now you have come with the truth.' So they slaughtered it, but they could hardly do it." },
  { number: "2:72", arabic: "وَإِذْ قَتَلْتُمْ نَفْسًا فَادَّarَأْتُمْ فِيهَا ۖ وَاللَّهُ مُخْرِjٌ مَّا كُنتُمْ تَكْتُمُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອພວກເຈົ້າໄດ້ຂ້າຄົນຜູ້ໜຶ່ງແລ້ວຖຽງກັນກ່ຽວກັບມັນ, ແຕ່ອັລລໍຮ໌ຈະເປີດເຜີຍສິ່ງທີ່ພວກເຈົ້າປິດບັງ.", english: "And [recall] when you slew a man and disputed over it, but Allah was to bring out that which you were concealing." },
  { number: "2:73", arabic: "فَقُلْنَا اضْرِبُوهُ بِbَعْضِهَا ۚ كَذَٰلِكَ يُحْيِي اللَّهُ الْمَوْتَىٰ وَيُرِيكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ", lao: "ແລ້ວເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງຕີລາວດ້ວຍສ່ວນໜຶ່ງຂອງມັນ.' ເຊັ່ນນັ້ນແຫຼະ ອັລລໍຮ໌ຈະເຮັດໃຫ້ຄົນຕາຍມີຊີວິດຂຶ້ນມາແລະສະແດງອາຍະຕ່າງໆຂອງພຣະອົງໃຫ້ພວກເຈົ້າເຫັນ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ຄິດ.", english: "So, We said, 'Strike the slain man with part of it.' Thus does Allah bring the dead to life, and He shows you His signs that you might reason." },
  { number: "2:74", arabic: "ثُمَّ قَسَتْ قُلُوبُكُم مِّن بَعْدِ ذَٰلِكَ فَهِيَ كَالْحِjَarَةِ أَوْ أَشَدُّ قَسْوَةً ۚ وَإِنَّ مِنَ الْحِjَarَةِ لَمَا يَتَفَjَّرُ مِنْهُ الْأَنْهَarُ ۚ وَإِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُjُ مِنْهُ الْمَاءُ ۚ وَإِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللَّهِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", lao: "ຫຼັງຈາກນັ້ນ, ຫົວໃຈຂອງພວກເຈົ້າກໍແຂງກະດ້າງ, ເໝືອນຫີນຫຼືແຂງກວ່າ. ແລະແທ້ຈິງ, ໃນບັນດາຫີນມີບາງກ້ອນທີ່ແມ່ນ້ຳໄຫຼອອກມາ. ແລະມີບາງກ້ອນທີ່ແຕກອອກແລ້ວນ້ຳກໍໄຫຼອອກມາ. ແລະມີບາງກ້ອນທີ່ຕົກລົງມາເພາະຢ້ານອັລລໍຮ໌. ແລະອັລລໍຮ໌ບໍ່ໄດ້ລະເລີຍສິ່ງທີ່ພວກເຈົ້າເຮັດ.", english: "Then your hearts became hardened after that, being like stones or even harder. For indeed, there are stones from which rivers gush forth, and there are some of them that split open and water comes out, and there are some of them that fall down for fear of Allah. And Allah is not unaware of what you do." },
  { number: "2:75", arabic: "أَفَتَطْمَعُونَ أَن يُؤْمِنُوا لَكُمْ وَقَدْ كَانَ فَرِoqٌ mِّنْهُمْ يَسْمَعُونَ كَلَامَ اللَّهِ ثُمَّ يُحَرِّfُونَهُ مِن بَعْدِ مَا عَقَلُوهُ وَهُمْ يَعْلَمُونَ", lao: "ພວກເຈົ້າຫວັງວ່າພວກເຂົາຈະເຊື່ອໃນພວກເຈົ້າບໍ, ທັງທີ່ກຸ່ມໜຶ່ງໃນພວກເຂົາເຄີຍໄດ້ຍິນຄຳເວົ້າຂອງອັລລໍຮ໌ແລ້ວບິດເບືອນມັນຫຼັງຈາກທີ່ໄດ້ເຂົ້າໃຈมัน, ທັງที่ພວກເຂົາຮູ້?", english: "Do you covet [the hope, O believers], that they would believe for you while a party of them used to hear the words of Allah and then distort the Torah after they had understood it while they were knowing?" },
  { number: "2:76", arabic: "وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَا بَعْضُهُمْ إِلَىٰ bَعْضٍ قَالُوا أَتُحَدِّثُونَهُم بِمَا فَتَحَ اللَّهُ عَلَيْكُمْ لِيُحَajُّوكُم بِهِ عِندَ رَبِّكُمْ ۚ أَفَلَا تَعْقِلُونَ", lao: "ແລະເມື່ອພວກເຂົາພົບກັບຜູ້ທີ່ເຊື່ອ, ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາເຊື່ອແລ້ວ.' ແຕ່ເມື່ອພວກເຂົາຢູ່ຕາມລຳພັງ, ພວກເຂົາກ່າວວ່າ: 'ພວກເຈົ້າຈະບອກພວກເຂົາໃນສິ່ງທີ່ອັລລໍຮ໌ໄດ້ເປີດເຜີຍໃຫ້ພວກເຈົ້າຮູ້ບໍ, ເພື່ອພວກເຂົາຈະໄດ້ໃຊ້ເປັນຫຼັກຖານຕໍ່ຕ້ານພວກເຈົ້າຕໍ່ໜ້າພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຈົ້າ? ພວກເຈົ້າບໍ່ຄິດບໍ?'", english: "And when they meet those who believe, they say, 'We have believed'; but when they are alone with one another, they say, 'Do you talk to them about what Allah has revealed to you so they can argue with you about it before your Lord?' Then will you not reason?" },
  { number: "2:77", arabic: "أَوَلَا يَعْلَمُونَ أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ", lao: "ພວກເຂົາບໍ່ຮູ້ບໍວ່າອັລລໍຮ໌ຮູ້ສິ່ງที่พวกเขาປິດບັງແລະສິ່ງที่พวกเขาເປີດເຜີຍ?", english: "But do they not know that Allah knows what they conceal and what they declare?" },
  { number: "2.78", arabic: "وَمِنْهُمْ أُمِّيُّونَ لَا يَعْلَمُونَ الْكِتَابَ إِلَّا أَمَانِيَّ وَإِنْ هُمْ إِلَّا يَظُنُّونَ", lao: "ແລະໃນກຸ່ມຂອງພວກເຂົາມີຜູ້ທີ່ບໍ່ຮູ້ໜັງສື, ພວກເຂົາບໍ່ຮູ້ຄຳພີຍົກເວັ້ນຄວາມປາດຖະຫນາ, ແລະພວກເຂົາເປັນພຽງການຄາດເດົາ.", english: "And among them are unlettered ones who do not know the Scripture except in wishful thinking, but they are only assuming." },
  { number: "2:79", arabic: "فَوَيْلٌ لِّلَّذِينَ يَكْتُbُونَ الْكِتَابَ بِأَيْدِيهِمْ ثُمَّ يَقُولُونَ هَٰذَا مِنْ عِندِ اللَّهِ لِيَشْتَرُوا بِهِ ثَمَنًا قَلِيلًا ۖ فَوَيْلٌ لَّهُم mِّمَّا كَتَبَتْ أَيْدِيهِمْ وَوَيْلٌ لَّهُم mِّمَّا يَكْسِبُونَ", lao: "ດັ່ງນັ້ນ, ຄວາມວິບັດຈະເກີດແກ່ຜູ້ที่ຂຽນຄຳພີດ້ວຍມືຂອງພວກເຂົາເອງແລ້ວກ່າວວ່າ: 'ນີ້ມາຈາກອັລລໍຮ໌' ເພື່ອແລກປ່ຽນมันດ້ວຍລາຄາທີ່ຕໍ່າຕ້ອຍ. ດັ່ງນັ້ນ, ຄວາມວິບັດຈະເກີດແກ່ພວກເຂົາຈາກສິ່ງที่ມືຂອງພວກເຂົາໄດ້ຂຽນ, ແລະຄວາມວິບັດຈະເກີດແກ່ພວກເຂົາຈາກສິ່ງที่พวกເຂົາໄດ້ຮັບ.", english: "So woe to those who write the 'scripture' with their own hands, then say, 'This is from Allah,' in order to exchange it for a small price. Woe to them for what their hands have written and woe to them for what they earn." },
  { number: "2:80", arabic: "وَقَالُوا لَن تَمَسَّنَا النَّarُ إِلَّا أَيَّامًا mَّعْدُودَةً ۚ قُلْ أَتَّخَذْتُمْ عِندَ اللَّهِ عَهْدًا فَلَن يُخْلِfَ اللَّهُ عَهْدَهُ ۖ أَمْ تَقُولُونَ عَلَى اللَّهِ مَا لَا تَعْلَمُونَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ໄຟນະລົກຈະບໍ່ແຕະຕ້ອງພວກເຮົາເວັ້ນແຕ່ສອງສາມວັນ.' ຈົ່ງກ່າວວ່າ: 'ພວກເຈົ້າໄດ້ຮັບສັນຍາຈາກອັລລໍຮ໌ບໍ? ດັ່ງນັ້ນ, ອັລລໍຮ໌ຈະບໍ່ລະເມີດສັນຍາຂອງພຣະອົງ. ຫຼືພວກເຈົ້າເວົ້າກ່ຽວກັບອັລລໍຮ໌ในສິ່ງທີ່ພວກເຈົ້າບໍ່ຮູ້?'", english: "And they say, 'Never will the Fire touch us, except for a few days.' Say, 'Have you taken a covenant from Allah? For Allah will never break His covenant. Or do you say about Allah that which you do not know?'" },
  { number: "2:81", arabic: "بَلَىٰ مَن كَسَبَ سَيِّئَةً وَأَحَاطَتْ بِهِ خَطِيئَتُهُ فَأُولَٰئِكَ أَصْحَابُ النَّarِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ບໍ່ດັ່ງນັ້ນ, ຜູ້ໃດທີ່ກະທຳຄວາມຊົ່ວແລະຄວາມຜິດຂອງລາວໄດ້ລ້ອມຮອບລາວ, ພວກເຂົາຄືຊາວນາລົກ; ພວກເຂົາຈະຢູ່ໃນນັ້ນຕະຫຼອດໄປ.", english: "Yes, whoever earns evil and his sin has encompassed him - those are the companions of the Fire; they will abide therein eternally." },
  { number: "2:82", arabic: "وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ແລະຜູ້ທີ່ເຊື່ອແລະເຮັດຄວາມດີ, ພວກເຂົາຄືຊາວສະຫວັນ; ພວກເຂົາຈະຢູ່ໃນນັ້ນຕະຫຼອດໄປ.", english: "But they who believe and do righteous deeds - those are the companions of Paradise; they will abide therein eternally." },
  { number: "2:83", arabic: "وَإِذْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ لَا تَعْبُdُونَ إِلَّا اللَّهَ وَبِالْوَالِدَيْنِ إِحْسَانًا وَذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَقُولُوا لِلنَّاسِ حُسْنًا وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ ثُمَّ تَوَلَّيْتُمْ إِلَّا قَلِيلًا mِّنكُمْ وَأَنتُم mُّعْرِضُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເອົາສັນຍາຈາກລູກຫຼານຂອງອິດສະຣາເອວວ່າ: 'ຢ່ານະມັດສະການຜູ້ໃດນອກຈາກອັລລໍຮ໌, ແລະຈົ່ງເຮັດດີຕໍ່ພໍ່ແມ່, ຍາດພີ່ນ້ອງ, ເດັກກຳພ້າ, ແລະຄົນຍາກຈົນ. ແລະຈົ່ງເວົ້າດີຕໍ່ຄົນທັງຫຼາຍ, ແລະຈົ່ງດຳລົງການລະໝາດ, ແລະຈົ່ງຈ່າຍຊາກາດ.' ຫຼັງຈາກນັ້ນ, ພວກເຈົ້າໄດ້ຫັນໜີຍົກເວັ້ນຈຳນວນໜ້ອຍໃນກຸ່ມຂອງພວກເຈົ້າ, ແລະພວກເຈົ້າກໍຍັງຜິນຫຼັງ.", english: "And [recall] when We took the covenant from the Children of Israel, [enjoining upon them], 'Do not worship except Allah; and to parents do good and to relatives, orphans, and the needy. And speak to people good [words] and establish prayer and give zakah.' Then you turned away, except a few of you, and you were refusing." },
  { number: "2:84", arabic: "وَإِذْ أَخَذْنَا مِيثَاقَكُمْ لَا تَsْfِكُونَ دِمَاءَكُمْ وَلَا تُخْرِjُونَ أَنفُسَكُم mِّن دِيَarِكُمْ ثُمَّ أَقْرَرْتُمْ وَأَنتُمْ تَشْهَدُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເອົາສັນຍາຈາກພວກເຈົ້າວ່າ: 'ຢ່າເຮັດໃຫ້ເລືອດຂອງພວກເຈົ້າໄຫຼ, ແລະຢ່າຂັບໄລ່ກັນອອກຈາກບ້ານຂອງພວກເຈົ້າ.' ຫຼັງຈາກນັ້ນ, ພວກເຈົ້າໄດ້ຍອມຮັບແລະເປັນພະຍານ.", english: "And [recall] when We took your covenant, [saying], 'Do not shed each other's blood or evict one another from your homes.' Then you acknowledged [this] while you were witnessing." },
  { number: "2:85", arabic: "ثُمَّ أَنتُمْ هَٰؤُلَاءِ تَقْتُلُونَ أَنfُسَكُمْ وَتُخْرِjُونَ فَرِيقًا mِّنكُم mِّن دِيَarِهِمْ تَظَاهَرُونَ عَلَيْهِم بِالْإِثْمِ وَالْعُدْوَانِ وَإِن يَأْتُوكُمْ أُsَarَىٰ تُfَادُوهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ إِخْرَاجُهُمْ ۚ أَfَتُؤْمِنُونَ بِbَعْضِ الْكِتَابِ وَتَكْfُرُونَ بِbَعْضٍ ۚ فَمَا jَزَاءُ مَن يَfْعَلُ ذَٰلِكَ مِنكُمْ إِلَّا خِزْيٌ فِي الْحَيَاةِ الدُّنْيَا ۖ وَيَوْمَ الْقِيَامَةِ يُرَدُّونَ إِلَىٰ أَشَدِّ الْعَذَابِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", lao: "ຫຼັງຈາກນັ້ນ, ພວກເຈົ້າກໍຂ້າກັນເອງແລະຂັບໄລ່ກຸ່ມໜຶ່ງອອກຈາກບ້ານຂອງພວກເຂົາ, ໂດຍຊ່ວຍເຫຼືອເຊິ່ງກັນและກັນໃນການກະທຳບາບແລະການລະເມີດ. ແລະຖ້າພວກເຂົາມາຫາພວກເຈົ້າໃນຖານະຊະເລີຍ, ພວກເຈົ້າກໍໄຖ່ພວກເຂົາ, ທັງທີ່ການຂັບໄລ່ພວກເຂົາເປັນສິ່ງທີ່ຖືກຫ້າມ. ພວກເຈົ້າເຊື່ອໃນບາງສ່ວນຂອງຄຳພີແລະປະຕິເສດບາງສ່ວນບໍ? ດັ່ງນັ້ນ, ການຕອບແທນສຳລັບຜູ້ທີ່ກະທຳเช่นນັ້ນໃນກຸ່ມຂອງພວກເຈົ້າບໍ່ແມ່ນສິ່ງໃດນອກຈາກຄວາມອັບອາຍໃນຊີວິດນີ້. ແລະໃນວັນກິຍາມະ, ພວກເຂົາຈະຖືກນຳໄປສູ່ການລົງໂທດที่รุนแรงที่สุด. ແລະອັລລໍຮ໌ບໍ່ໄດ້ລະເລີຍສິ່ງທີ່ພວກເຈົ້າເຮັດ.", english: "After that you are those who kill one another and evict a party of your people from their homes, cooperating against them in sin and aggression. And if they come to you as captives, you ransom them, although their eviction was forbidden to you. So do you believe in part of the Scripture and disbelieve in part? Then what is the recompense for those who do that among you except disgrace in worldly life; and on the Day of Resurrection they will be sent back to the most severe punishment. And Allah is not unaware of what you do." },
  { number: "2:86", arabic: "أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الْحَيَاةَ الدُّنْيَا بِالْآخِرَةِ ۖ فَلَا يُخَفَّfُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنصَرُونَ", lao: "ພວກເຂົາຄືຜູ້ທີ່ໄດ້ຊື້ຊີວິດນີ້ດ້ວຍໂລກໜ້າ, ດັ່ງນັ້ນການລົງໂທດຈະບໍ່ຖືກຫຼຸດຜ່ອນໃຫ້ພວກເຂົາ, ແລະພວກເຂົາຈະບໍ່ໄດ້ຮັບການຊ່ວຍເຫຼືອ.", english: "Those are the ones who have bought the life of this world [in exchange] for the Hereafter, so the punishment will not be lightened for them, nor will they be aided." },
  { number: "2:87", arabic: "وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَقَفَّيْنَا مِن bَعْدِهِ بِالرُّسُلِ ۖ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْbَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ الْقُدُسِ ۗ أَfَكُلَّمَا jَاءَكُمْ رَسُولٌ بِمَا لَا تَهْوَىٰ أَنfُسُكُمُ اسْتَكْبَرْتُمْ فَfَرِيقًا كَذَّbْتُمْ وَfَرِيقًا tَقْتُلُونَ", lao: "ແລະແນ່ນອນ, ເຮົາໄດ້ໃຫ້ຄຳພີແກ່ມູຊາແລະໄດ້ສົ່ງສາດສະດາตามมาหลังจากเขา. ແລະເຮົາໄດ້ໃຫ້ຫຼັກຖານທີ່ຊັດເຈນແກ່ອີຊາລູກຂອງມາຣີຍຳແລະໄດ້ສະໜັບສະໜູນລາວດ້ວຍວິນຍານບໍລິສຸດ. ທຸກຄັ້ງທີ່ສາດສະດາມາຫາພວກເຈົ້າດ້ວຍສິ່ງທີ່ພວກເຈົ້າບໍ່ມັກ, ພວກເຈົ້າກໍອວດດີ, ໂດຍປະຕິເສດບາງຄົນແລະຂ້າບາງຄົນ.", english: "And We did certainly give Moses the Scripture and followed up after him with messengers. And We gave Jesus, the son of Mary, clear proofs and supported him with the Pure Spirit. But is it [not] that every time a messenger came to you, [O Children of Israel], with what your souls did not desire, you were arrogant? And a party [of messengers] you denied and another party you killed." },
  { number: "2:88", arabic: "وَقَالُوا قُلُوبُنَا غُلْfٌ ۚ بَل لَّعَنَهُمُ اللَّهُ بِكُfْرِهِمْ فَقَلِيلًا mَّا يُؤْمِنُونَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ຫົວໃຈຂອງພວກເຮົາຖືກປິດ.' ບໍ່, ອັລລໍຮ໌ໄດ້ສາບແຊ່ງພວກເຂົາເພາະການປະຕິເສດຂອງພວກເຂົາ, ດັ່ງນັ້ນພວກເຂົາຈຶ່ງເຊື່ອພຽງเล็กน้อย.", english: "And they said, 'Our hearts are wrapped.' But, [in fact], Allah has cursed them for their disbelief, so little is it that they believe." },
  { number: "2:89", arabic: "وَلَمَّا jَاءَهُمْ كِتَابٌ mِّنْ عِندِ اللَّهِ مُصَدِّqٌ lِّ مَا مَعَهُمْ وَكَانُوا مِن قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذِينَ كَفَرُوا فَلَمَّا jَاءَهُم mَّا عَرَfُوا كَفَرُوا بِهِ ۚ فَلَعْنَةُ اللَّهِ عَلَى الْكَافِرِينَ", lao: "ແລະເມື່ອຄຳພີຈາກອັລລໍຮ໌ມາຫາພວກເຂົາ, ຢືນຢັນສິ່ງທີ່ພວກເຂົາມີຢູ່, ທັງທີ່ກ່ອນໜ້ານີ້ພວກເຂົາເຄີຍຂໍໄຊຊະນະເໜືອຜູ້ປະຕິເສດ. ແຕ່ເມື່ອສິ່ງที่พวกເຂົາຮູ້ຈັກມາຫາພວກເຂົາ, ພວກເຂົາກໍປະຕິເສດมัน. ດັ່ງນັ້ນ, ຄຳສາບແຊ່ງຂອງອັລລໍຮ໌จงอยู่กับผู้ปฏิเสธ.", english: "And when there came to them a Book from Allah confirming that which was with them - although before they used to pray for victory against those who disbelieved - but [then] when there came to them that which they recognized, they disbelieved in it; so the curse of Allah will be upon the disbelievers." },
  { number: "2:90", arabic: "بِئْسَمَا اشْتَرَوْا بِهِ أَنfُسَهُمْ أَن يَكْfُرُوا بِمَا أَنزَلَ اللَّهُ بَغْيًا أَن يُنَزِّلَ اللَّهُ مِن فَضْلِهِ عَلَىٰ مَن يَشَاءُ مِنْ عِبَادِهِ ۖ فَبَاءُوا بِغَضَبٍ عَلَىٰ غَضَبٍ ۚ وَلِلْكَافِرِينَ عَذَابٌ mُّهِينٌ", lao: "ຊົ່ວຊ້າທີ່ສຸດຄືສິ່ງที่พวกເຂົາໄດ້ຂາຍຕົວເອງไป, ໂດຍປະຕິເສດສິ່ງທີ່ອັລລໍຮ໌ໄດ້ເປີດເຜີຍລົງມາ, ດ້ວຍຄວາມອິດສາที่ອັລລໍຮ໌ຈະເປີດເຜີຍຄວາມໂປດປານของพระองค์แก่ผู้ที่พระองค์ทรงประสงค์จากบ่าวของพระองค์. ດັ່ງນັ້ນ, ພວກເຂົາจึงกลับมาพร้อมกับความโกรธซ้อนความโกรธ. และสำหรับผู้ปฏิเสธจะได้รับการลงโทษที่น่าอัปยศ.", english: "How wretched is that for which they sold themselves - that they would disbelieve in what Allah has revealed, grudging that Allah should send down of His favor upon whom He wills of His servants. So they have returned with anger upon anger. And for the disbelievers is a humiliating punishment." },
  { number: "2:91", arabic: "وَإِذَا قِيلَ لَهُمْ آمِنُوا بِمَا أَنزَلَ اللَّهُ قَالُوا نُؤْمِنُ بِمَا أُNzِلَ عَلَيْنَا وَيَكْfُرُونَ بِمَا وَرَاءَهُ وَهُوَ الْحَقُّ مُصَدِّqًا lِّ مَا مَعَهُمْ ۗ قُلْ فَلِمَ تَقْتُلُونَ أَنbِيَاءَ اللَّهِ مِن قَبْلُ إِن كُنتُم mُّؤْمِنِينَ", lao: "ແລະເມື່ອຖືກກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງເຊື່ອໃນສິ່ງທີ່ອັລລໍຮ໌ໄດ້ເປີດເຜີຍລົງມາ', ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາເຊື່ອໃນສິ່ງที่ຖືກເປີດເຜີຍລົງມາໃຫ້ພວກເຮົາ.' ແລະພວກເຂົາປະຕິເສດສິ່ງທີ່ຢູ່ເໜືອມັນ, ທັງທີ່ມັນເປັນຄວາມຈິງທີ່ຢືນຢັນສິ່ງທີ່ພວກເຂົາມີຢູ່. ຈົ່ງກ່າວວ່າ: 'ເປັນຫຍັງພວກເຈົ້າຈຶ່ງຂ້າສາດສະດາຂອງອັລລໍຮ໌ໃນອະດີດ, ຖ້າພວກເຈົ້າເປັນຜູ້ທີ່ເຊື່ອ?'", english: "And when it is said to them, 'Believe in what Allah has revealed,' they say, 'We believe [only] in what was revealed to us.' And they disbelieve in what came after it, while it is the truth confirming that which is with them. Say, 'Then why did you kill the prophets of Allah before, if you are [indeed] believers?'" },
  { number: "2:92", arabic: "وَلَقَدْ jَاءَكُم mُّوسَىٰ بِالْbَيِّنَاتِ ثُمَّ اتَّخَذْتُمُ الْعِjْلَ مِن bَعْدِهِ وَأَنتُمْ ظَالِمُونَ", lao: "ແລະແນ່ນອນ, ມູຊາໄດ້ມາຫາພວກເຈົ້າດ້ວຍຫຼັກຖານທີ່ຊັດເຈນ, ແລ້ວພວກເຈົ້າໄດ້ເອົາລູກງົວມາເປັນພະເຈົ້າຫຼັງຈາກລາວ, ແລະພວກເຈົ້າກໍເປັນຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.", english: "And Moses had certainly come to you with clear proofs. Then you took the calf [for worship] after him, while you were wrongdoers." },
  { number: "2:93", arabic: "وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُم بِقُوَّةٍ وَاسْمَعُوا ۖ قَالُوا سَمِعْنَا وَعَصَيْنَا وَأُشْرِبُوا فِي قُلُوبِهِمُ الْعِjْلَ بِكُfْرِهِمْ ۚ قُلْ بِئْسَمَا يَأْمُرُكُم بِهِ إِيمَانُكُمْ إِن كُنتُم mُّؤْمِنِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເອົາສັນຍາຈາກພວກເຈົ້າແລະຍົກພູເຂົາຂຶ້ນເໜືອພວກເຈົ້າ, [ໂດຍກ່າວວ່າ]: 'ຈົ່ງຍຶດໝັ້ນໃນສິ່ງທີ່ເຮົາໄດ້ໃຫ້ແກ່ພວກເຈົ້າຢ່າງເຂັ້ມແຂງແລະຈົ່ງຟັງ.' ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາໄດ້ຍິນແລ້ວແລະພວກເຮົາບໍ່ເຊື່ອຟັງ.' ແລະການນະມັດສະການລູກງົວໄດ້ຖືກດູດซึมเข้าสู่หัวใจของพวกเขาเพราะການປະຕິເສດຂອງພວກເຂົາ. ຈົ່ງກ່າວວ່າ: 'ຊົ່ວຊ້າທີ່ສຸດຄືສິ່ງที่ความเชื่อของพวกเจ้าสั่งให้ทำ, ຖ້າພວກເຈົ້າເປັນຜູ້ທີ່ເຊື່ອ.'", english: "And [recall] when We took your covenant and raised over you the mount, [saying], 'Take what We have given you with determination and listen.' They said, 'We have heard and disobeyed.' And their hearts absorbed [the worship of] the calf because of their disbelief. Say, 'How wretched is that which your faith enjoins upon you, if you should be believers.'" },
  { number: "2:94", arabic: "قُلْ إِن كَانَتْ لَكُمُ الدَّarُ الْآخِرَةُ عِندَ اللَّهِ خَالِصَةً mِّن دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِن كُنتُمْ صَادِقِينَ", lao: "ຈົ່ງກ່າວວ່າ: 'ຖ້າໂລກໜ້າທີ່ອັລລໍຮ໌ເປັນຂອງພວກເຈົ້າໂດຍສະເພາະ, ບໍ່ແມ່ນຂອງຄົນອື່ນ, ກໍຈົ່ງປາດຖະຫນາຄວາມຕາຍ, ຖ້າພວກເຈົ້າເປັນຜູ້ที่สัตย์จริง.'", english: "Say, [O Muhammad], 'If the home of the Hereafter with Allah is for you alone and not the [other] people, then wish for death, if you should be truthful.'" },
  { number: "2:95", arabic: "وَلَن يَتَمَنَّوْهُ أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ", lao: "ແລະພວກເຂົາຈະບໍ່ປາດຖະຫນາມັນເລີຍເພາະສິ່ງທີ່ມືຂອງພວກເຂົາໄດ້ສົ່ງໄປກ່ອນ. ແລະອັລລໍຮ໌ຊົງຮູ້ຈັກผู้ที่ไม่ยุติธรรม.", english: "But they will never wish for it, ever, because of what their hands have put forth. And Allah is Knowing of the wrongdoers." },
  { number: "2:96", arabic: "وَلَتَjِدَنَّهُمْ أَحْرَصَ النَّاسِ عَلَىٰ حَيَاةٍ وَمِنَ الَّذِينَ أَشْرَكُوا ۚ يَوَدُّ أَحَدُهُمْ لَوْ يُعَمَّرُ أَلْfَ سَنَةٍ وَمَا هُوَ بِمُزَحْزِحِهِ مِنَ الْعَذَابِ أَن يُعَمَّرَ ۗ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ", lao: "ແລະແນ່ນອນ, ເຈົ້າຈະພົບວ່າພວກເຂົາເປັນຄົນທີ່ໂລບມາກທີ່ສຸດໃນຊີວິດ, ຍິ່ງກວ່າผู้ที่ตั้งภาคี. ບາງຄົນໃນພວກເຂົາປາດຖະຫນາที่จะมีชีวิตอยู่พันปี, ແຕ່ການมีชีวิตยืนยาวນັ້ນຈະไม่สามารถช่วยให้เขาพ้นจากการลงโทษได้. ແລະອັລລໍຮ໌ຊົງเห็นในสิ่งที่พวกเขาทำ.", english: "And you will surely find them the most greedy of people for life - [even] more than those who associate others with Allah. One of them wishes that he could be granted life a thousand years, but it would not remove him in the least from the punishment that he should be granted life. And Allah is Seeing of what they do." },
  { number: "2:97", arabic: "قُلْ مَن كَانَ عَدُوًّا لِّjِبْرِيلَ فَإِنَّهُ نَزَّلَهُ عَلَىٰ قَلْبِكَ بِإِذْنِ اللَّهِ مُصَدِّqًا lِّ مَا بَيْنَ يَدَيْهِ وَهُدًى وَبُشْرَىٰ لِلْمُؤْمِنِينَ", lao: "ຈົ່ງກ່າວວ່າ: 'ຜູ້ໃດທີ່ເປັນສັດຕູຕໍ່ຍິບຣີລ, ແທ້ຈິງລາວໄດ້ເປີດເຜີຍมันลงมาสู่หัวใจของเจ้าด้วยการอนุมัติของอัลลอฮ์, ยืนยันสิ่งที่อยู่ก่อนหน้ามัน, และเป็นทางนำและข่าวดีสำหรับผู้ศรัทธา.'", english: "Say, 'Whoever is an enemy to Gabriel - it is [none but] he who has brought the Qur'an down upon your heart, [O Muhammad], by permission of Allah, confirming that which was before it and as guidance and good tidings for the believers.'" },
  { number: "2:98", arabic: "مَن كَانَ عَدُوًّا لِّلَّهِ وَمَلَائِكَتِهِ وَرُسُلِهِ وَjِبْرِيلَ وَمِيكَالَ فَإِنَّ اللَّهَ عَدُوٌّ لِّلْكَافِرِينَ", lao: "ຜູ້ໃດທີ່ເປັນສັດຕູຕໍ່ອັລລໍຮ໌, ທູດສະຫວັນຂອງພຣະອົງ, ສາດສະດາຂອງພຣະອົງ, ຍິບຣີລ, ແລະມີກາອີລ, ແທ້ຈິງອັລລໍຮ໌ເປັນສັດຕູຕໍ່ผู้ปฏิเสธ.", english: "Whoever is an enemy to Allah and His angels and His messengers and Gabriel and Michael - then indeed, Allah is an enemy to the disbelievers." },
  { number: "2:99", arabic: "وَلَقَدْ أَنزَلْنَا إِلَيْكَ آيَاتٍ بَيِّنَاتٍ ۖ وَمَا يَكْfُرُ بِهَا إِلَّا الْfَاسِقُونَ", lao: "ແລະແນ່ນອນ, ເຮົາໄດ້ເປີດເຜີຍອາຍະຕ່າງໆที่ชัดเจนลงมาให้เจ้า, ແລະไม่มีใครปฏิเสธมันนอกจากผู้ที่ละเมิด.", english: "And We have certainly revealed to you verses [which are] clear proofs, and no one would disbelieve in them except the defiantly disobedient." },
  { number: "2:100", arabic: "أَوَكُلَّمَا عَاهَدُوا عَهْدًا نَّbَذَهُ فَرِيقٌ mِّنْهُم ۚ بَلْ أَكْثَرُهُمْ لَا يُؤْمِنُونَ", lao: "ທຸກຄັ້ງທີ່ພວກເຂົາເຮັດສັນຍາ, ກຸ່ມໜຶ່ງໃນພວກເຂົາກໍโยนมันทิ้งไปບໍ? ບໍ່, ສ່ວນຫຼາຍຂອງພວກເຂົາບໍ່ເຊື່ອ.", english: "Is it not [the case] that every time they made a covenant, a party of them threw it away? But, [in fact], most of them do not believe." },
  { number: "2:101", arabic: "وَلَمَّا jَاءَهُمْ رَسُولٌ mِّنْ عِندِ اللَّهِ مُصَدِّqٌ lِّ مَا مَعَهُمْ نَبَذَ فَرِيقٌ mِّنَ الَّذِينَ أُوتُوا الْكِتَابَ كِتَابَ اللَّهِ وَرَاءَ ظُهُورِهِمْ كَأَنَّهُمْ لَا يَعْلَمُونَ", lao: "ແລະເມື່ອສາດສະດາຈາກອັລລໍຮ໌ມາຫາພວກເຂົາ, ຢືນຢັນສິ່ງที่พวกเขามีอยู่, ກຸ່ມໜຶ່ງໃນผู้ที่ได้รับคัมภีร์ได้โยนคัมภีร์ของอัลลอฮ์ไปข้างหลัง, ราวกับว่าพวกเขาไม่รู้.", english: "And when a messenger from Allah came to them confirming that which was with them, a party of those who had been given the Scripture threw the Scripture of Allah behind their backs as if they did not know [what it contained]." },
  { number: "2:102", arabic: "وَاتَّbَعُوا مَا تَتْلُو الشَّيَاطِينُ عَلَىٰ مُلْكِ sُلَيْمَانَ ۖ وَمَا كَفَرَ sُلَيْمَانُ وَلَٰكِنَّ الشَّيَاطِينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَ وَمَا أُNzِلَ عَلَى الْمَلَكَيْنِ بِbَابِلَ هَarُوتَ وَمَarُوتَ ۚ وَمَا يُعَلِّمَانِ مِنْ أَحَدٍ حَتَّىٰ يَقُولَا إِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْfُرْ ۖ فَيَتَعَلَّمُونَ مِنْهُمَا مَا يُfَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَزَوْjِهِ ۚ وَمَا هُم بِضَarِّينَ بِهِ مِنْ أَحَدٍ إِلَّا بِإِذْنِ اللَّهِ ۚ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمْ وَلَا يَنfَعُهُمْ ۚ وَلَقَدْ عَلِمُوا لَمَنِ اشْتَرَاهُ مَا لَهُ فِي الْآخِرَةِ مِنْ خَلَاقٍ ۚ وَلَبِئْسَ مَا شَرَوْا بِهِ أَنfُسَهُمْ ۚ لَوْ كَانُوا يَعْلَمُونَ", lao: "ແລະພວກເຂົາໄດ້ຕິດຕາມສິ່ງที่ซัยตอนอ่านในรัชสมัยของสุลัยมาน. ແລະสุลัยมานไม่ได้ปฏิเสธ, แต่ซัยตอนปฏิเสธ, สอนเวทมนตร์แก่ผู้คนและสิ่งที่ถูกเปิดเผยแก่ทูตสวรรค์สององค์ที่บาบิโลน, ฮารุตและมารุต. แต่ทั้งสองไม่ได้สอนใครจนกว่าจะกล่าวว่า: 'เราเป็นเพียงการทดลอง, ดังนั้นอย่าปฏิเสธ.' แล้วพวกเขาเรียนรู้จากทั้งสองสิ่งที่ทำให้เกิดความแตกแยกระหว่างชายและภรรยา. แต่พวกเขาไม่สามารถทำร้ายใครได้ด้วยมันเว้นแต่จะได้รับอนุญาตจากอัลลอฮ์. และพวกเขาเรียนรู้สิ่งที่ทำร้ายพวกเขาและไม่มีประโยชน์ต่อพวกเขา. และแน่นอน, พวกเขารู้ว่าผู้ที่ซื้อมันจะไม่มีส่วนแบ่งในโลกหน้า. และช่างเลวร้ายจริงๆ ที่พวกเขาขายวิญญาณของตัวเองไป, หากพวกเขารู้.", english: "And they followed [instead] what the devils had recited during the reign of Solomon. It was not Solomon who disbelieved, but the devils disbelieved, teaching people magic and that which was revealed to the two angels at Babylon, Harut and Marut. But the two angels do not teach anyone unless they say, 'We are a trial, so do not disbelieve [by practicing magic].' And [yet] they learn from them that by which they cause separation between a man and his wife. But they do not harm anyone with it except by permission of Allah. And they learn what harms them and does not benefit them. And they certainly knew that whoever purchased the magic would not have in the Hereafter any share. And wretched is that for which they sold themselves, if they only knew." },
  { number: "2:103", arabic: "وَلَوْ أَنَّهُمْ آمَنُوا وَاتَّقَوْا لَمَثُوبَةٌ mِّنْ عِندِ اللَّهِ خَيْرٌ ۖ لَّوْ كَانُوا يَعْلَمُونَ", lao: "ແລະຖ້າພວກເຂົາເຊື່ອແລະຢຳເກງ, ລາງວັນຈາກອັລລໍຮ໌จะดีกว่า, ຫາກພວກເຂົາຮູ້.", english: "And if they had believed and feared Allah, then a reward from Allah would have been [far] better, if they only knew." },
  { number: "2:104", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَقُولُوا رَاعِنَا وَقُولُوا انظُرْنَا وَاسْمَعُوا ۗ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຢ່າເວົ້າວ່າ 'รออินา' ແຕ່ຈົ່ງເວົ້າວ່າ 'อันซุรนา' ແລະຈົ່ງຟັງ. และสำหรับผู้ปฏิเสธจะได้รับการลงโทษที่เจ็บปวด.", english: "O you who have believed, do not say, 'Raina,' but say, 'Unthurna,' and listen. And for the disbelievers is a painful punishment." },
  { number: "2:105", arabic: "mَّا يَوَدُّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَلَا الْمُشْرِكِينَ أَن يُنَزَّلَ عَلَيْكُم mِّنْ خَيْرٍ mِّن rَّbِّكُمْ ۗ وَاللَّهُ يَخْتَصُّ بِرَحْمَتِهِ مَن يَشَاءُ ۚ وَاللَّهُ ذُو الْfَضْلِ الْعَظِيمِ", lao: "ຜູ້ที่ปฏิเสธจากชาวคัมภีร์และผู้ตั้งภาคีไม่ต้องการให้ความดีใดๆ ถูกเปิดเผยลงมาให้พวกเจ้าจากพระเจ้าของพวกเจ้า. แต่อัลลอฮ์ทรงเลือกความเมตตาของพระองค์สำหรับผู้ที่พระองค์ทรงประสงค์. และอัลลอฮ์ทรงเป็นเจ้าของความโปรดปรานอันยิ่งใหญ่.", english: "Neither those who disbelieve from the People of the Scripture nor the polytheists wish that any good should be sent down to you from your Lord. But Allah selects for His mercy whom He wills, and Allah is the possessor of great bounty." },
  { number: "2:106", arabic: "مَا نَنسَخْ مِنْ آيَةٍ أَوْ نُنسِهَا نَأْتِ بِخَيْرٍ mِّنْهَا أَوْ مِثْلِهَا ۗ أَلَمْ تَعْلَمْ أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ເຮົາບໍ່ຍົກເລີກອາຍະໃດໆ ຫຼືເຮັດໃຫ້ມັນຖືກລືມ, ເວັ້ນແຕ່ເຮົາຈະນຳສິ່ງທີ່ດີກວ່າຫຼືເໝືອນກັນມາແທນ. ເຈົ້າບໍ່ຮູ້ບໍວ່າອັລລໍຮ໌ມີອຳນາດເໜືອທຸກສິ່ງ?", english: "We do not abrogate a verse or cause it to be forgotten except that We bring forth [one] better than it or similar to it. Do you not know that Allah is over all things competent?" },
  { number: "2:107", arabic: "أَلَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَمَا لَكُم mِّن دُونِ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ", lao: "ເຈົ້າບໍ່ຮູ້ບໍວ່າອັລລໍຮ໌ເປັນເຈົ້າຂອງອຳນາດແຫ່ງຊັ້ນຟ້າແລະແຜ່ນດິນ? ແລະນອກຈາກອັລລໍຮ໌, ພວກເຈົ້າບໍ່ມີຜູ້ປົກປ້ອງຫຼືຜູ້ຊ່ວຍເຫຼືອໃດໆ.", english: "Do you not know that to Allah belongs the dominion of the heavens and the earth and [that] you have not besides Allah any protector or any helper?" },
  { number: "2:108", arabic: "أَمْ تُرِيدُونَ أَن تَsْأَلُوا رَسُولَكُمْ كَمَا sُئِلَ مُوسَىٰ مِن قَبْلُ ۗ وَمَن يَتَبَدَّلِ الْكُfْرَ بِالْإِيمَانِ فَقَدْ ضَلَّ سَوَاءَ السَّbِيلِ", lao: "ຫຼືພວກເຈົ້າຕ້ອງການที่จะถามศาสดาของพวกเจ้าเหมือนกับที่มูซาถูกถามในอดีต? และผู้ใดที่แลกเปลี่ยนการปฏิเสธด้วยความศรัทธา, เขาได้หลงทางจากหนทางที่ถูกต้องแล้ว.", english: "Or do you intend to ask your Messenger as Moses was asked before? And whoever exchanges faith for disbelief has certainly strayed from the soundness of the way." },
  { number: "2:109", arabic: "وَدَّ كَثِيرٌ mِّنْ أَهْلِ الْكِتَابِ لَوْ يَرُدُّونَكُم mِّن bَعْدِ إِيمَانِكُمْ كُfَّarًا حَسَدًا mِّنْ عِندِ أَنfُسِهِم mِّن bَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ ۖ فَاعْfُوا وَاصْfَحُوا حَتَّىٰ يَأْتِيَ اللَّهُ بِأَمْرِهِ ۗ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ชาวคัมภีร์จำนวนมากปรารถนาที่จะทำให้พวกเจ้ากลับไปเป็นผู้ปฏิเสธหลังจากที่พวกเจ้าได้ศรัทธาแล้ว, ด้วยความอิจฉาจากตัวเอง, หลังจากที่ความจริงได้ปรากฏแก่พวกเขาแล้ว. ดังนั้นจงให้อภัยและอดทนจนกว่าอัลลอฮ์จะทรงมีพระบัญชา. แท้จริงอัลลอฮ์ทรงมีอำนาจเหนือทุกสิ่ง.", english: "Many of the People of the Scripture wish they could turn you back to disbelief after you have believed, out of envy from themselves [even] after the truth has become clear to them. So pardon and overlook until Allah brings His command. Indeed, Allah is over all things competent." },
  { number: "2:110", arabic: "وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ ۚ وَمَا تُقَدِّمُوا لِأَنfُسِكُم mِّنْ خَيْرٍ تَjِدُوهُ عِندَ اللَّهِ ۗ إِنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ", lao: "ແລະຈົ່ງດຳລົງການລະໝາດແລະຈົ່ງຈ່າຍຊາກາດ. ແລະຄວາມດີໃດໆที่พวกเจ้าส่งไปล่วงหน้าสำหรับตัวเอง, พวกเจ้าจะพบมันกับอัลลอฮ์. แท้จริงอัลลอฮ์ทรงเห็นในสิ่งที่พวกเจ้าทำ.", english: "And establish prayer and give zakah, and whatever good you put forward for yourselves - you will find it with Allah. Indeed, Allah of what you do, is Seeing." },
  { number: "2:111", arabic: "وَقَالُوا لَن يَدْخُلَ الْجَنَّةَ إِلَّا مَن كَانَ هُودًا أَوْ نَصَarَىٰ ۗ تِلْكَ أَمَانِيُّهُمْ ۗ قُلْ هَاتُوا بُرْهَانَكُمْ إِن كُنتُمْ صَادِقِينَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ไม่มีใครจะเข้าสวรรค์ได้นอกจากผู้ที่เป็นชาวยิวหรือชาวคริสต์.' นั่นคือความปรารถนาของพวกเขา. ຈົ່ງກ່າວວ່າ: 'จงนำหลักฐานของพวกเจ้ามา, ຖ້າພວກເຈົ້າเป็นผู้ที่สัตย์จริง.'", english: "And they say, 'None will enter Paradise except one who is a Jew or a Christian.' That is [merely] their wishful thinking. Say, 'Produce your proof, if you should be truthful.'" },
  { number: "2:112", arabic: "بَلَىٰ مَنْ أَsْلَمَ وَjْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ فَلَهُ أَjْرُهُ عِندَ رَبِّهِ وَلَا خَوْfٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ບໍ່, ຜູ້ໃດที่ยอมจำนนต่ออัลลอฮ์ในขณะที่เขาเป็นผู้ทำความดี, เขาจะได้รับรางวัลจากพระเจ้าของเขา, และจะไม่มีความกลัวใดๆ ต่อพวกเขาและพวกเขาจะไม่โศกเศร้า.", english: "Yes [on the contrary], whoever submits his face in Islam to Allah while being a doer of good will have his reward with his Lord. And no fear will there be concerning them, nor will they grieve." },
  { number: "2:113", arabic: "وَقَالَتِ الْيَهُودُ لَيْسَتِ النَّصَarَىٰ عَلَىٰ شَيْءٍ وَقَالَتِ النَّصَarَىٰ لَيْسَتِ الْيَهُودُ عَلَىٰ شَيْءٍ وَهُمْ يَتْلُونَ الْكِتَابَ ۗ كَذَٰلِكَ قَالَ الَّذِينَ لَا يَعْلَمُونَ مِثْلَ قَوْلِهِمْ ۚ فَاللَّهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِfُونَ", lao: "ແລະຊາວຢິວກ່າວວ່າ: 'ຊາວຄຣິດບໍ່ມີຫຍັງເລີຍ.' ແລະຊາວຄຣິດກ່າວວ່າ: 'ຊາວຢິວບໍ່ມີຫຍັງເລີຍ.' ທັງທີ່ພວກເຂົາອ່ານຄຳພີ. ເຊັ່ນດຽວກັນ, ຜູ້ທີ່ບໍ່ຮູ້ກໍກ່າວເໝືອນກັບພວກເຂົາ. ດັ່ງນັ້ນອັລລໍຮ໌ຈະຊົງຕັດສິນລະຫວ່າງພວກເຂົາໃນວັນກິຍາມະໃນສິ່ງທີ່ພວກເຂົາເຄີຍຂັດແຍ່ງກັນ.", english: "The Jews say 'The Christians have nothing [true] to stand on,' and the Christians say, 'The Jews have nothing to stand on,' although they [both] recite the Scripture. Thus the polytheists speak the same as their words. But Allah will judge between them on the Day of Resurrection concerning that over which they used to differ." },
  { number: "2:114", arabic: "وَمَنْ أَظْلَمُ مِمَّن mَّنَعَ مَسَajِدَ اللَّهِ أَن يُذْكَرَ فِيهَا اسْمُهُ وَسَعَىٰ فِي خَرَابِهَا ۚ أُولَٰئِكَ مَا كَانَ لَهُمْ أَن يَدْخُلُوهَا إِلَّا خَائِfِينَ ۚ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الْآخِرَةِ عَذَابٌ عَظِيمٌ", lao: "ແລະໃຜຈະອະທຳຍິ່ງກວ່າຜູ້ທີ່ຫ້າມມັດສະຍິດຂອງອັລລໍຮ໌ບໍ່ໃຫ້มีการກ່າວເຖິງພຣະນາມຂອງພຣະອົງໃນນັ້ນແລະພະຍາຍາມທຳລາຍມັນ? ພວກເຂົາບໍ່ຄວນເຂົ້າໄປໃນນັ້ນເວັ້ນແຕ່ດ້ວຍຄວາມຢ້ານກົວ. ສຳລັບພວກເຂົາໃນໂລກນີ້ຄືຄວາມອັບອາຍ, ແລະສຳລັບພວກເຂົາໃນໂລກໜ້າຄືການລົງໂທດອັນຍິ່ງໃຫຍ່.", english: "And who are more unjust than those who prevent the name of Allah from being mentioned in His mosques and strive toward their ruin? It is not for them to enter them except in fear. For them in this world is disgrace, and they will have in the Hereafter a great punishment." },
  { number: "2:115", arabic: "وَلِلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ ۚ فَأَيْنَمَا تُوَلُّوا فَثَمَّ وَjْهُ اللَّهِ ۚ إِنَّ اللَّهَ وَاسِعٌ عَلِيمٌ", lao: "ແລະທິດຕາເວັນອອກແລະທິດຕາເວັນຕົກເປັນຂອງອັລລໍຮ໌. ດັ່ງນັ້ນບໍ່ວ່າພວກເຈົ້າຈະຫັນໄປທາງໃດ, ທີ່ນັ້ນຄືພຣະພັກຂອງອັລລໍຮ໌. ແທ້ຈິງອັລລໍຮ໌ຊົງກວ້າງໃຫຍ່, ຊົງຮອບຮູ້.", english: "And to Allah belongs the east and the west. So wherever you [might] turn, there is the Face of Allah. Indeed, Allah is all-Encompassing and Knowing." },
  { number: "2:116", arabic: "وَقَالُوا اتَّخَذَ اللَّهُ وَلَدًا ۗ sُbْحَانَهُ ۖ بَل لَّهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ كُلٌّ لَّهُ قَانِتُونَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ອັລລໍຮ໌ຊົງມີບຸດ.' ພຣະອົງຊົງບໍລິສຸດ! ບໍ່, ສິ່ງທີ່ຢູ່ໃນຊັ້ນຟ້າແລະແຜ່ນດິນເປັນຂອງພຣະອົງ. ທຸກสิ่งຍອມຈຳນົນຕໍ່ພຣະອົງ.", english: "They say, 'Allah has taken a son.' Exalted is He! Rather, to Him belongs whatever is in the heavens and the earth. All are devoutly obedient to Him," },
  { number: "2:117", arabic: "بَدِيعُ السَّمَاوَاتِ وَالْأَرْضِ ۖ وَإِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُن فَيَكُونُ", lao: "ผู้สร้างชั้นฟ้าและแผ่นดิน. และเมื่อพระองค์ทรงกำหนดสิ่งใด, พระองค์เพียงกล่าวแก่มันว่า: 'จงเป็น' และมันก็เป็น.", english: "Originator of the heavens and the earth. When He decrees a matter, He only says to it, 'Be,' and it is." },
  { number: "2:118", arabic: "وَقَالَ الَّذِينَ لَا يَعْلَمُونَ لَوْلَا يُكَلِّمُنَا اللَّهُ أَوْ تَأْتِينَا آيَةٌ ۗ كَذَٰلِكَ قَالَ الَّذِينَ مِن قَبْلِهِم mِّثْلَ قَوْلِهِمْ ۘ تَشَابَهَتْ qُلُوبُهُمْ ۗ قَدْ بَيَّنَّا الْآيَاتِ لِقَوْmٍ يُوقِنُونَ", lao: "ແລະຜູ້ທີ່ບໍ່ຮູ້ກ່າວວ່າ: 'ທຳໄມອັລລໍຮ໌ບໍ່ตรัสກັບเราຫຼືມີอายะມາໃຫ້เรา?' ເຊັ່ນດຽວກັນ, ຜູ້ທີ່ຢູ່ກ່ອນหน้าพวกเขาก็กล่าวเหมือนกับพวกเขา. ຫົວໃຈຂອງພວກເຂົາຄ້າຍກັນ. เราได้ชี้แจงอายะต่างๆ แก่ผู้ที่เชื่อมั่นแล้ว.", english: "Those who do not know say, 'Why does Allah not speak to us or there come to us a sign?' Thus spoke those before them like their words. Their hearts resemble each other. We have shown clearly the signs to a people who are certain [in faith]." },
  { number: "2:119", arabic: "إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا ۖ وَلَا تُsْأَلُ عَنْ أَصْحَابِ الْjَحِيمِ", lao: "แท้จริง, เราได้ส่งเจ้ามาด้วยความจริง, เป็นผู้แจ้งข่าวดีและผู้ตักเตือน. และเจ้าจะไม่ถูกถามเกี่ยวกับชาวนรก.", english: "Indeed, We have sent you, [O Muhammad], with the truth as a bringer of good tidings and a warner, and you will not be asked about the companions of Hellfire." },
  { number: "2:120", arabic: "وَلَن تَرْضَىٰ عَنكَ الْيَهُودُ وَلَا النَّصَarَىٰ حَتَّىٰ تَتَّbِعَ مِلَّتَهُمْ ۗ قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَىٰ ۗ وَلَئِنِ اتَّbَعْتَ أَهْوَاءَهُم bَعْدَ الَّذِي jَاءَكَ مِنَ الْعِلْمِ ۙ مَا لَكَ مِنَ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ", lao: "ແລະຊາວຢິວແລະຊາວຄຣິດຈະບໍ່ມີວັນພໍໃຈเจ้าຈົນກວ່າเจ้าจะปฏิบัติตามศาสนาของพวกเขา. ຈົ່ງກ່າວວ່າ: 'แท้จริง, ทางนำของอัลลอฮ์คือทางนำที่แท้จริง.' และถ้าเจ้าปฏิบัติตามความปรารถนาของพวกเขาหลังจากที่ความรู้ได้มาถึงเจ้าแล้ว, เจ้าจะไม่มีผู้พิทักษ์หรือผู้ช่วยเหลือใดๆ จากอัลลอฮ์.", english: "And never will the Jews or the Christians approve of you until you follow their religion. Say, 'Indeed, the guidance of Allah is the [only] guidance.' If you were to follow their desires after what has come to you of knowledge, you would have against Allah no protector or helper." },
  { number: "2:121", arabic: "الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَتْلُونَهُ حَقَّ تِلَاوَتِهِ أُولَٰئِكَ يُؤْمِنُونَ بِهِ ۗ وَمَن يَكْfُرْ بِهِ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ", lao: "ບັນດາຜູ້ທີ່ເຮົາໄດ້ໃຫ້ຄຳພີແກ່ພວກເຂົາ, ພວກເຂົາອ່ານມັນຢ່າງຖືກຕ້ອງ. ພວກເຂົາຄືຜູ້ທີ່ເຊື່ອໃນມັນ. ແລະຜູ້ໃດທີ່ປະຕິເສດມັນ, ພວກເຂົາຄືຜູ້ທີ່ຂາດທຶນ.", english: "Those to whom We have given the Book recite it with its true recital. They [are the ones who] believe in it. And whoever disbelieves in it - it is they who are the losers." },
  { number: "2:122", arabic: "يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ", lao: "ໂອ້ ລູກຫຼານຂອງອິດສະຣາເອວ, ຈົ່ງລະນຶກເຖິງຄວາມໂປດປານຂອງຂ້ອຍທີ່ຂ້ອຍໄດ້ໃຫ້ແກ່ພວກເຈົ້າ ແລະວ່າຂ້ອຍໄດ້ຍົກຍ້ອງພວກເຈົ້າເໜືອຄົນທັງຫຼາຍ.", english: "O Children of Israel, remember My favor which I have bestowed upon you and that I preferred you over the worlds." },
  { number: "2:123", arabic: "وَاتَّقُوا يَوْمًا لَّا تَjْزِي نَفْسٌ عَن nَّfْسٍ شَيْئًا وَلَا يُقْبَلُ مِنْهَا عَدْلٌ وَلَا تَنfَعُهَا شَفَاعَةٌ وَلَا هُمْ يُنصَرُونَ", lao: "ແລະຈົ່ງຢຳເກງວັນທີ່ບໍ່ມີໃຜສາມາດຊ່ວຍເຫຼືອໃຜໄດ້, ແລະບໍ່ມີຄ່າໄຖ່ໃດໆຈະຖືກຮັບຈາກລາວ, ແລະບໍ່ມີການໄກ່ເກ່ຍໃດໆຈະມີປະໂຫຍດແກ່ລາວ, ແລະພວກເຂົາຈະບໍ່ໄດ້ຮັບການຊ່ວຍເຫຼືອ.", english: "And fear a Day when no soul will suffice for another soul at all, and no compensation will be accepted from it, nor will any intercession benefit it, nor will they be aided." },
  { number: "2:124", arabic: "وَإِذِ ابْتَلَىٰ إِبْرَاهِيمَ رَبُّهُ بِكَلِمَاتٍ فَأَتَمَّهُنَّ ۖ قَالَ إِنِّي jَاعِلُكَ لِلنَّاسِ إِمَامًا ۖ قَالَ وَمِن ذُرِّيَّتِي ۖ قَالَ لَا يَنَالُ عَهْدِي الظَّالِمِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອພຣະຜູ້ເປັນເຈົ້າຂອງອັບຣາຮາມໄດ້ທົດສອບລາວດ້ວຍຄຳສັ່ງຕ່າງໆ, ແລ້ວລາວໄດ້ປະຕິບັດມັນຢ່າງສົມບູນ. ພຣະອົງກ່າວວ່າ: 'ແທ້ຈິງ, ຂ້ອຍຈະເຮັດໃຫ້ເຈົ້າເປັນຜູ້ນຳຂອງມະນຸດ.' ລາວໄດ້ກ່າວວ່າ: 'ແລະຈາກລູກຫຼານຂອງຂ້ອຍເດ?' ພຣະອົງກ່າວວ່າ: 'ສັນຍາຂອງຂ້ອຍຈະບໍ່ລວມເຖິງຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.'", english: "And [mention, O Muhammad], when Abraham was tried by his Lord with commands and he fulfilled them. [Allah] said, 'Indeed, I will make you a leader for the people.' [Abraham] said, 'And of my descendants?' [Allah] said, 'My covenant does not include the wrongdoers.'" },
  { number: "2:125", arabic: "وَإِذْ جَعَلْنَا الْbَيْتَ مَثَابَةً لِّلنَّاسِ وَأَمْنًا وَاتَّخِذُوا مِن mَّقَامِ إِبْرَاهِيمَ مُصَلًّى ۖ وَعَهِدْنَا إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ أَن طَهِّrَا بَيْتِيَ لِلطَّائِfِينَ وَالْعَاكِfِينَ وَالرُّكَّعِ السُّjُودِ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເຮັດໃຫ້ບ້ານ (ກາບະ) ເປັນບ່ອນຊຸມນຸມແລະບ່ອນປອດໄພສຳລັບມະນຸດ. ແລະຈົ່ງເອົາສະຖານທີ່ຂອງອັບຣາຮາມເປັນບ່ອນລະໝາດ. ແລະເຮົາໄດ້ສັນຍາກັບອັບຣາຮາມແລະອິສມາອີລວ່າ: 'ຈົ່ງຊຳລະລ້າງບ້ານຂອງຂ້ອຍໃຫ້ສະອາດສຳລັບຜູ້ທີ່ເດີນອ້ອມ, ຜູ້ທີ່ພັກຢູ່, ແລະຜູ້ທີ່ກົ້ມຂາບແລະຂາບໄຫວ້.'", english: "And [mention] when We made the House a place of return for the people and [a place of] security. And take, [O believers], from the standing place of Abraham a place of prayer. And We charged Abraham and Ishmael, [saying], 'Purify My House for those who perform Tawaf and those who are staying [there] for worship and those who bow and prostrate [in prayer].'" },
  { number: "2:126", arabic: "وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ اجْعَلْ هَٰذَا بَلَدًا آمِنًا وَarْزُقْ أَهْلَهُ مِنَ الثَّمَرَاتِ مَنْ آمَنَ مِنْهُم بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُ قَلِيلًا ثُمَّ أَضْطَرُّهُ إِلَىٰ عَذَابِ النَّarِ ۖ وَبِئْسَ الْمَصِيرُ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອອັບຣາຮາມໄດ້ກ່າວວ່າ: 'ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງຂ້ອຍ, ຂໍໃຫ້ເມືອງນີ້ເປັນເມືອງທີ່ປອດໄພ, ແລະຂໍໃຫ້ປະຊາຊົນຂອງມັນໄດ້ຮັບໝາກໄມ້, ຜູ້ທີ່ເຊື່ອໃນອັລລໍຮ໌ແລະວັນສຸດທ້າຍ.' ພຣະອົງກ່າວວ່າ: 'ແລະຜູ້ທີ່ປະຕິເສດ, ຂ້ອຍຈະໃຫ້ລາວມີຄວາມສຸກຊົ່ວຄາວ, ແລ້ວຂ້ອຍຈະບັງຄັບລາວໄປສູ່ການລົງໂທດຂອງໄຟນະລົກ, ແລະມັນເປັນຈຸດໝາຍປາຍທາງທີ່ຊົ່ວຊ້າ.'", english: "And [mention] when Abraham said, 'My Lord, make this a secure city and provide its people with fruits - whoever of them believes in Allah and the Last Day.' [Allah] said, 'And whoever disbelieves - I will grant him enjoyment for a little; then I will force him to the punishment of the Fire, and wretched is the destination.'" },
  { number: "2:127", arabic: "وَإِذْ يَرْfَعُ إِبْرَاهِيمُ الْقَوَاعِدَ مِنَ الْbَيْتِ وَإِسْمَاعِيلُ رَبَّنَا تَقَبَّلْ مِنَّا ۖ إِنَّكَ أَنتَ السَّمِيعُ الْعَلِيمُ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອອັບຣາຮາມແລະອິສມາອີລໄດ້ຍົກຮາກຖານຂອງບ້ານຂຶ້ນ, [ໂດຍກ່າວວ່າ]: 'ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້ຮັບຈາກພວກເຮົາ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງໄດ້ຍິນ, ຜູ້ຊົງຮອບຮູ້.'", english: "And [mention] when Abraham was raising the foundations of the House and [with him] Ishmael, [saying], 'Our Lord, accept [this] from us. Indeed You are the Hearing, the Knowing.'" },
  { number: "2:128", arabic: "رَبَّنَا وَاجْعَلْنَا مُsْلِمَيْنِ لَكَ وَمِن ذُرِّيَّتِنَا أُمَّةً mُّsْلِمَةً lَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُbْ عَلَيْنَا ۖ إِنَّكَ أَنتَ التَّوَّابُ الرَّحِيمُ", lao: "ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້ພວກເຮົາເປັນຜູ້ທີ່ຍອມຈຳນົນຕໍ່ພຣະອົງ, ແລະຈາກລູກຫຼານຂອງພວກເຮົາ, ຂໍໃຫ້ມີປະຊາຊາດທີ່ຍອມຈຳນົນຕໍ່ພຣະອົງ. ແລະຂໍໃຫ້ສະແດງພິທີກຳຂອງພວກເຮົາໃຫ້ພວກເຮົາເຫັນ, ແລະຂໍໃຫ້ອະໄພໃຫ້ພວກເຮົາ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງອະໄພ, ຜູ້ຊົງເມດຕາ.", english: "Our Lord, and make us Muslims [in submission] to You and from our descendants a Muslim nation [in submission] to You. And show us our rites and accept our repentance. Indeed, You are the Accepting of repentance, the Merciful." },
  { number: "2:129", arabic: "رَبَّنَا وَابْعَثْ فِيهِمْ رَسُولًا mِّنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِكَ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُزَكِّيهِمْ ۚ إِنَّكَ أَنتَ الْعَزِيزُ الْحَكِيمُ", lao: "ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້ສົ່ງສາດສະດາຈາກພວກເຂົາເອງມາໃນກຸ່ມຂອງພວກເຂົາ, ຜູ້ທີ່ຈະອ່ານອາຍະຕ່າງໆຂອງພຣະອົງໃຫ້ພວກເຂົາຟັງ, ແລະສອນຄຳພີແລະປັນຍາໃຫ້ພວກເຂົາ, ແລະຊຳລະລ້າງພວກເຂົາ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງອຳນາດ, ຜູ້ຊົງມີປັນຍາ.", english: "Our Lord, and send among them a messenger from themselves who will recite to them Your verses and teach them the Book and wisdom and purify them. Indeed, You are the Exalted in Might, the Wise." },
  { number: "2:130", arabic: "وَمَن يَرْغَبُ عَن mِّلَّةِ إِبْرَاهِيمَ إِلَّا مَن sَfِهَ نَفْسَهُ ۚ وَلَقَدِ اصْطَفَيْنَاهُ فِي الدُّنْيَا ۖ وَإِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ", lao: "ແລະໃຜຈະຫັນໜີຈາກສາສະໜາຂອງອັບຣາຮາມນອກຈາກຜູ້ທີ່ເຮັດໃຫ້ຕົວເອງໂງ່? ແລະແນ່ນອນ, ເຮົາໄດ້ເລືອກລາວໃນໂລກນີ້, ແລະແທ້ຈິງ, ໃນໂລກໜ້າ, ລາວຈະຢູ່ໃນກຸ່ມຜູ້ທີ່ຊອບທຳ.", english: "And who would be averse to the religion of Abraham except one who makes a fool of himself? And We had chosen him in this world, and indeed he, in the Hereafter, will be among the righteous." },
  { number: "2:131", arabic: "إِذْ قَالَ لَهُ رَبُّهُ أَsْلِمْ ۖ قَالَ أَsْلَمْتُ لِرَبِّ الْعَالَمِينَ", lao: "ເມື່ອພຣະຜູ້ເປັນເຈົ້າຂອງລາວໄດ້ກ່າວແກ່ລາວວ່າ: 'ຈົ່ງຍອມຈຳນົນ.' ລາວໄດ້ກ່າວວ່າ: 'ຂ້ອຍຍອມຈຳນົນຕໍ່ພຣະຜູ້ເປັນເຈົ້າແຫ່ງສາກົນໂລກ.'", english: "When his Lord said to him, 'Submit', he said, 'I have submitted to the Lord of the worlds.'" },
  { number: "2:132", arabic: "وَوَصَّىٰ بِهَا إِبْرَاهِيمُ بَنِيهِ وَيَعْقُوبُ يَا بَنِيَّ إِنَّ اللَّهَ اصْطَفَىٰ لَكُمُ الدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم mُّsْلِمُونَ", lao: "ແລະອັບຣາຮາມໄດ້ສັ່ງເສຍສິ່ງນີ້ແກ່ລູກໆຂອງລາວ, ແລະຢາໂຄບກໍເຊັ່ນກັນ, [โดยกล่าวว่า]: 'ໂອ້ ລູກຂອງຂ້ອຍ, ແທ້ຈິງອັລລໍຮ໌ໄດ້ເລືອກສາສະໜາໃຫ້ພວກເຈົ້າ, ດັ່ງນັ້ນຢ່າຕາຍເວັ້ນແຕ່ໃນສະພາບທີ່ເປັນມຸດສະລິມ.'", english: "And Abraham instructed his sons [to do the same] and [so did] Jacob, saying, 'O my sons, indeed Allah has chosen for you this religion, so do not die except while you are Muslims.'" },
  { number: "2:133", arabic: "أَمْ كُنتُمْ شُهَدَاءَ إِذْ حَضَرَ يَعْقُوبَ الْمَوْتُ إِذْ قَالَ لِبَنِيهِ مَا تَعْبُdُونَ مِن bَعْدِي قَالُوا نَعْبُdُ إِلَٰهَكَ وَإِلَٰهَ آبَائِكَ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ إِلَٰهًا وَاحِدًا وَنَحْنُ لَهُ مُsْلِمُونَ", lao: "ຫຼືພວກເຈົ້າເປັນພະຍານເມື່ອຄວາມຕາຍມາເຖິງຢາໂຄບບໍ? ເມື່ອລາວໄດ້ກ່າວແກ່ລູກໆຂອງລາວວ່າ: 'ພວກເຈົ້າຈະນະມັດສະການຫຍັງຫຼັງຈາກຂ້ອຍ?' ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາຈະນະມັດສະການພຣະເຈົ້າຂອງເຈົ້າແລະພຣະເຈົ້າຂອງບັນພະບຸລຸດຂອງເຈົ້າ, ອັບຣາຮາມ, ອິສມາອີລ, ແລະອີຊາກ - ພຣະເຈົ້າອົງດຽວ. ແລະພວກເຮົາຈະຍອມຈຳນົນຕໍ່ພຣະອົງ.'", english: "Or were you witnesses when death approached Jacob, when he said to his sons, 'What will you worship after me?' They said, 'We will worship your God and the God of your fathers, Abraham and Ishmael and Isaac - one God. And we are Muslims [in submission] to Him.'" },
  { number: "2:134", arabic: "تِلْكَ أُمَّةٌ قَدْ خَلَتْ ۖ لَهَا مَا كَسَبَتْ وَلَكُم mَّا كَسَبْتُمْ ۖ وَلَا تُsْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ", lao: "ນັ້ນຄືປະຊາຊາດທີ່ຜ່ານໄປແລ້ວ. ສຳລັບພວກເຂົາຄືສິ່ງທີ່ພວກເຂົາໄດ້ສະແຫວງຫາ, ແລະສຳລັບພວກເຈົ້າຄືສິ່ງທີ່ພວກເຈົ້າໄດ້ສະແຫວງຫາ. ແລະພວກເຈົ້າຈະບໍ່ຖືກຖາມກ່ຽວກັບສິ່ງທີ່ພວກເຂົາເຄີຍເຮັດ.", english: "That was a nation which has passed on. It will have [the consequence of] what it earned, and you will have what you have earned. And you will not be asked about what they used to do." },
  { number: "2:135", arabic: "وَقَالُوا كُونُوا هُودًا أَوْ نَصَarَىٰ تَهْتَدُوا ۗ قُلْ بَلْ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۖ وَمَا كَانَ مِنَ الْمُشْرِكِينَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ຈົ່ງເປັນຊາວຢິວຫຼືຊາວຄຣິດ, ພວກເຈົ້າຈະໄດ້ຮັບການຊີ້ແນະ.' ຈົ່ງກ່າວວ່າ: 'ບໍ່, [ພວກເຮົາຈະຕາມ] ສາສະໜາຂອງອັບຣາຮາມ, ຜູ້ທີ່ຊື່ກົງ, ແລະລາວບໍ່ໄດ້ຢູ່ໃນກຸ່ມผู้ที่ตั้งภาคี.'", english: "They say, 'Be Jews or Christians [so] you will be guided.' Say, 'Rather, [we follow] the religion of Abraham, inclining toward truth, and he was not of the polytheists.'" },
  { number: "2:136", arabic: "قُولُوا آمَنَّا بِاللَّهِ وَمَا أُNzِلَ إِلَيْنَا وَمَا أُNzِلَ إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَمَا أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَمَا أُوتِيَ النَّbِيُّونَ مِن rَّbِّهِمْ لَا نُfَرِّقُ بَيْنَ أَحَدٍ mِّنْهُمْ وَنَحْنُ لَهُ مُsْلِمُونَ", lao: "ຈົ່ງກ່າວວ່າ: 'ພວກເຮົາເຊື່ອໃນອັລລໍຮ໌, ແລະສິ່ງທີ່ຖືກເປີດເຜີຍລົງມາໃຫ້ພວກເຮົາ, ແລະສິ່ງທີ່ຖືກເປີດເຜີຍລົງມາໃຫ້ອັບຣາຮາມ, ອິສມາອີລ, ອີຊາກ, ຢາໂຄບ, ແລະບັນດາເຜົ່າ, ແລະສິ່ງທີ່ມູຊາແລະອີຊາໄດ້ຮັບ, ແລະສິ່ງທີ່ສາດສະດາທັງຫຼາຍໄດ້ຮັບຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ. ພວກເຮົາບໍ່ແຍກແຍະລະຫວ່າງໃຜໃນພວກເຂົາ, ແລະພວກເຮົາຈະຍອມຈຳນົນຕໍ່ພຣະອົງ.'", english: "Say, [O believers], 'We have believed in Allah and what has been revealed to us and what has been revealed to Abraham and Ishmael and Isaac and Jacob and the Descendants and what was given to Moses and Jesus and what was given to the prophets from their Lord. We make no distinction between any of them, and we are Muslims [in submission] to Him.'" },
  { number: "2:137", arabic: "فَإِنْ آمَنُوا بِمِثْلِ مَا آمَنتُم بِهِ فَقَدِ اهْتَدَوا ۖ وَّإِن تَوَلَّوْا فَإِنَّمَا هُمْ فِي شِقَاقٍ ۖ فَسَيَكْfِيكَهُمُ اللَّهُ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ", lao: "ຖ້າພວກເຂົາເຊື່ອໃນສິ່ງທີ່ພວກເຈົ້າເຊື່ອ, ພວກເຂົາກໍຈະໄດ້ຮັບການຊີ້ແນະ. ແຕ່ຖ້າພວກເຂົາຫັນໜີ, ພວກເຂົາກໍຢູ່ໃນຄວາມແຕກແຍກ. ແລະອັລລໍຮ໌ຈະພຽງພໍສຳລັບເຈົ້າໃນການຕໍ່ຕ້ານພວກເຂົາ. ແລະພຣະອົງຄືຜູ້ຊົງໄດ້ຍິນ, ຜູ້ຊົງຮອບຮູ້.", english: "So if they believe in the same as you believe in, then they have been [rightly] guided; but if they turn away, they are only in dissension, and Allah will be sufficient for you against them. And He is the Hearing, the Knowing." },
  { number: "2:138", arabic: "صِبْغَةَ اللَّهِ ۖ وَمَنْ أَحْسَنُ مِنَ اللَّهِ صِبْغَةً ۖ وَنَحْنُ لَهُ عَابِدُونَ", lao: "ສາສະໜາຂອງອັລລໍຮ໌. ແລະໃຜຈະດີກວ່າອັລລໍຮ໌ໃນການໃຫ້ສາສະໜາ? ແລະພວກເຮົາເປັນผู้นมัสการพระองค์.", english: "[And say, 'Ours is] the religion of Allah. And who is better than Allah in [ordaining] religion? And we are worshippers of Him.'" },
  { number: "2:139", arabic: "قُلْ أَتُحَajُّونَنَا فِي اللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمْ وَلَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ وَنَحْنُ لَهُ مُخْلِصُونَ", lao: "ຈົ່ງກ່າວວ່າ: 'ພວກເຈົ້າຈະຖຽງກັບພວກເຮົາກ່ຽວກັບອັລລໍຮ໌ບໍ, ທັງທີ່ພຣະອົງເປັນພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາและພຣະຜູ້ເປັນເຈົ້າของพวกเจ้า? สำหรับเราคือการกระทำของเรา, และสำหรับพวกเจ้าคือการกระทำของพวกเจ้า. และเราจะอุทิศตนเพื่อพระองค์.'", english: "Say, 'Do you argue with us about Allah while He is our Lord and your Lord? For us are our deeds, and for you are your deeds. And we are sincere [in deed and intention] to Him.'" },
  { number: "2:140", arabic: "أَمْ تَقُولُونَ إِنَّ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطَ كَانُوا هُودًا أَوْ نَصَarَىٰ ۗ qُلْ أَأَنتُمْ أَعْلَمُ أَمِ اللَّهُ ۗ وَمَنْ أَظْلَمُ مِمَّن كَتَمَ شَهَادَةً عِندَهُ مِنَ اللَّهِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", lao: "ຫຼືພວກເຈົ້າເວົ້າວ່າອັບຣາຮາມ, ອິສມາອີລ, ອີຊາກ, ຢາໂຄບ, ແລະບັນດາເຜົ່າເປັນຊາວຢິວຫຼືຊາວຄຣິດ? ຈົ່ງກ່າວວ່າ: 'พวกเจ้ามีความรู้มากกว่าหรืออัลลอฮ์? และใครจะอธรรมยิ่งกว่าผู้ที่ปกปิดคำพยานที่เขามีจากอัลลอฮ์? และอัลลอฮ์ไม่ทรงละเลยในสิ่งที่พวกเจ้าทำ.'", english: "Or do you say that Abraham and Ishmael and Isaac and Jacob and the Descendants were Jews or Christians? Say, 'Are you more knowing or is Allah?' And who is more unjust than one who conceals a testimony he has from Allah? And Allah is not unaware of what you do." },
  { number: "2:141", arabic: "تِلْكَ أُمَّةٌ قَدْ خَلَتْ ۖ لَهَا مَا كَسَبَتْ وَلَكُم mَّا كَسَبْتُمْ ۖ وَلَا تُsْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ", lao: "ນັ້ນຄືປະຊາຊາດທີ່ຜ່ານໄປແລ້ວ. ສຳລັບພວກເຂົາຄືສິ່ງທີ່ພວກເຂົາໄດ້ສະແຫວງຫາ, ແລະສຳລັບພວກເຈົ້າຄືສິ່ງທີ່ພວກເຈົ້າໄດ້ສະແຫວງຫາ. ແລະພວກເຈົ້າຈະບໍ່ຖືກຖາມກ່ຽວກັບສິ່ງທີ່ພວກເຂົາເຄີຍເຮັດ.", english: "That was a nation which has passed on. It will have what it earned, and you will have what you have earned. And you will not be asked about what they used to do." },
  { number: "2:142", arabic: "سَيَقُولُ السُّfَهَاءُ مِنَ النَّاسِ مَا وَلَّاهُمْ عَن قِبْلَتِهِمُ الَّتِي كَانُوا عَلَيْهَا ۚ قُل lِّ للَّهِ الْمَشْرِقُ وَالْمَغْرِبُ ۚ يَهْدِي مَن يَشَاءُ إِلَىٰ صِرَاطٍ mُّsْتَقِيمٍ", lao: "ຄົນໂງ່ໃນບັນດາມະນຸດຈະເວົ້າວ່າ: 'ອັນໃດທີ່ເຮັດໃຫ້ພວກເຂົາຫັນໜີຈາກກິບລາຂອງພວກເຂົາທີ່ພວກເຂົາເຄີຍປະຕິບັດ?' ຈົ່ງກ່າວວ່າ: 'ทิศตะวันออกและทิศตะวันตกเป็นของอัลลอฮ์. พระองค์ทรงชี้นำผู้ที่พระองค์ทรงประสงค์ไปยังหนทางที่เที่ยงตรง.'", english: "The foolish among the people will say, 'What has turned them away from their qiblah, which they used to face?' Say, 'To Allah belongs the east and the west. He guides whom He wills to a straight path.'" },
  { number: "2:143", arabic: "وَكَذَٰلِكَ جَعَلْنَاكُمْ أُمَّةً وَسَطًا لِّتَكُونُوا شُهَدَاءَ عَلَى النَّاسِ وَيَكُونَ الرَّسُولُ عَلَيْكُمْ شَهِيدًا ۗ وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِي كُنتَ عَلَيْهَا إِلَّا لِنَعْلَمَ مَن يَتَّbِعُ الرَّسُولَ مِمَّن يَنقَلِبُ عَلَىٰ عَقِبَيْهِ ۚ وَإِن كَانَتْ لَكَبِيرَةً إِلَّا عَلَى الَّذِينَ هَدَى اللَّهُ ۗ وَمَا كَانَ اللَّهُ لِيُضِيعَ إِيمَانَكُمْ ۚ إِنَّ اللَّهَ بِالنَّاسِ لَرَءُوfٌ rَّحِيمٌ", lao: "ແລະເຊັ່ນນັ້ນແຫຼະ, ເຮົາໄດ້ເຮັດໃຫ້ພວກເຈົ້າເປັນປະຊາຊາດທີ່ເປັນກາງ, ເພື່ອພວກເຈົ້າຈະໄດ້ເປັນພະຍານຕໍ່ມະນຸດ, ແລະສາດສະດາຈະໄດ້ເປັນພະຍານຕໍ່ພວກເຈົ້າ. ແລະເຮົາບໍ່ໄດ້ກຳນົດກິບລາທີ່ເຈົ້າເຄີຍປະຕິບັດນອກຈາກເພື່ອທົດສອບຜູ້ທີ່ຕາມສາດສະດາຈາກຜູ້ທີ່ຫັນຫຼັງກັບ. ແລະແທ້ຈິງ, ມັນເປັນສິ່ງທີ່ຍາກລຳບากຍົກເວັ້ນສຳລັບผู้ທີ່อัลลอฮ์ทรงชี้นำ. และอัลลอฮ์จะไม่ทรงทำให้ความศรัทธาของพวกเจ้าสูญเปล่า. แท้จริงอัลลอฮ์ทรงเมตตาปรานีต่อมนุษย์, ทรงเมตตาเสมอ.", english: "And thus we have made you a just community that you will be witnesses over the people and the Messenger will be a witness over you. And We did not make the qiblah which you used to face except that We might make evident who would follow the Messenger from who would turn back on his heels. And indeed, it is difficult except for those whom Allah has guided. And never would Allah have caused you to lose your faith. Indeed Allah is, to the people, Kind and Merciful." },
  { number: "2:144", arabic: "قَدْ نَرَىٰ تَقَلُّbَ وَjْهِكَ فِي السَّمَاءِ ۖ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضَاهَا ۖ فَوَلِّ وَjْهَكَ شَطْرَ الْمَسْjِدِ الْحَرَامِ ۚ وَحَيْثُ مَا كُنتُمْ فَوَلُّوا وُjُوهَكُمْ شَطْرَهُ ۗ وَإِنَّ الَّذِينَ أُوتُوا الْكِتَابَ لَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِن rَّbِّهِمْ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا يَعْمَلُونَ", lao: "ເຮົາເຫັນເຈົ້າຫັນໜ້າໄປມາເທິງທ້ອງຟ້າ. ດັ່ງນັ້ນ, ເຮົາຈະເຮັດໃຫ້ເຈົ້າຫັນໄປສູ່ກິບລາที่ເຈົ້າพอใจ. ດັ່ງນັ້ນ, ຈົ່ງຫັນໜ້າໄປທາງມັດສະຍິດອັນສັກສິດ. ແລະບໍ່ວ່າພວກເຈົ້າຈະຢູ່ໃສ, ຈົ່ງຫັນໜ້າໄປທາງນັ້ນ. ແລະແທ້ຈິງ, ຜູ້ທີ່ໄດ້ຮັບຄຳພີຮູ້ວ່າມັນເປັນความจริงจากพระเจ้าของพวกเขา. และอัลลอฮ์ไม่ทรงละเลยในสิ่งที่พวกเขาทำ.", english: "We have certainly seen the turning of your face, [O Muhammad], toward the heaven, and We will surely turn you to a qiblah with which you will be pleased. So turn your face toward al-Masjid al-Haram. And wherever you [believers] are, turn your faces toward it [in prayer]. Indeed, those who have been given the Scripture well know that it is the truth from their Lord. And Allah is not unaware of what they do." },
  { number: "2:145", arabic: "وَلَئِنْ أَتَيْتَ الَّذِينَ أُوتُوا الْكِتَابَ بِكُلِّ آيَةٍ mَّا تَبِعُوا قِبْلَتَكَ ۚ وَمَا أَنتَ بِتَابِعٍ قِبْلَتَهُمْ ۚ وَمَا بَعْضُهُم بِتَابِعٍ قِبْلَةَ بَعْضٍ ۚ وَلَئِنِ اتَّbَعْتَ أَهْوَاءَهُم mِّن bَعْدِ مَا jَاءَكَ مِنَ الْعِلْمِ ۙ إِنَّكَ إِذًا لَّمِنَ الظَّالِمِينَ", lao: "และถ้าเจ้าจะนำหลักฐานทุกอย่างมาให้ผู้ที่ได้รับคัมภีร์, พวกเขาก็จะไม่ตามกิบละของเจ้า. และเจ้าก็จะไม่ตามกิบละของพวกเขา. และบางคนในพวกเขาก็จะไม่ตามกิบละของคนอื่น. และถ้าเจ้าจะตามความปรารถนาของพวกเขาหลังจากที่ความรู้ได้มาถึงเจ้าแล้ว, แท้จริงเจ้าก็จะเป็นผู้ที่ไม่ยุติธรรม.", english: "And if you brought to those who were given the Scripture every sign, they would not follow your qiblah. Nor will you be a follower of their qiblah. Nor are some of them followers of the qiblah of others. So if you were to follow their desires after what has come to you of knowledge, indeed, you would then be among the wrongdoers." },
  { number: "2:146", arabic: "الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْرِfُونَهُ كَمَا يَعْرِfُونَ أَبْنَاءَهُمْ ۖ وَإِنَّ فَرِيقًا mِّنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَهُمْ يَعْلَمُونَ", lao: "ຜູ້ທີ່ເຮົາໄດ້ໃຫ້ຄຳພີແກ່ພວກເຂົາ, ພວກເຂົາຮູ້ຈັກລາວເໝືອນດັ່ງที่พวกเขารู้จักลูกชายของตัวเอง. และแท้จริง, กลุ่มหนึ่งในพวกเขากำลังปกปิดความจริงทั้งที่พวกเขารู้.", english: "Those to whom We gave the Scripture know him as they know their own sons. But indeed, a party of them conceal the truth while they know [it]." },
  { number: "2:147", arabic: "الْحَقُّ مِن rَّbِّكَ ۖ فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ", lao: "ความจริงมาจากพระเจ้าของเจ้า, ดังนั้นอย่าเป็นผู้ที่สงสัย.", english: "The truth is from your Lord, so never be among the doubters." },
  { number: "2:148", arabic: "وَلِكُلٍّ وِjْهَةٌ هُوَ مُوَلِّيهَا ۖ فَاسْتَبِقُوا الْخَيْرَاتِ ۚ أَيْنَ مَا تَكُونُوا يَأْتِ بِكُمُ اللَّهُ jَمِيعًا ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ແລະສຳລັບແຕ່ລະຄົນມີທິດທາງที่ລາວຫັນໄປ. ດັ່ງນັ້ນຈົ່ງແຂ່ງຂັນກັນໃນຄວາມດີ. ບໍ່ວ່າພວກເຈົ້າຈະຢູ່ໃສ, ອັລລໍຮ໌ຈະຊົງນຳພວກເຈົ້າທັງໝົດມາ. แท้จริงอัลลอฮ์ทรงมีอำนาจเหนือทุกสิ่ง.", english: "For each [religious following] is a direction toward which it faces. So race to [all that is] good. Wherever you may be, Allah will bring you forth [for judgement] all together. Indeed, Allah is over all things competent." },
  { number: "2:149", arabic: "وَمِنْ حَيْثُ خَرَjْتَ فَوَلِّ وَjْهَكَ شَطْرَ الْمَسْjِدِ الْحَرَامِ ۖ وَإِنَّهُ لَلْحَقُّ مِن rَّbِّكَ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", lao: "และจากที่ใดที่เจ้าออกมา, ຈົ່ງຫັນໜ້າໄປທາງມັດສະຍິດອັນສັກສິດ. และแท้จริง, มันเป็นความจริงจากพระเจ้าของเจ้า. และอัลลอฮ์ไม่ทรงละเลยในสิ่งที่พวกเจ้าทำ.", english: "And from wherever you go out [for prayer, O Muhammad], turn your face toward al-Masjid al-Haram. And indeed, it is the truth from your Lord. And Allah is not unaware of what you do." },
  { number: "2:150", arabic: "وَمِنْ حَيْثُ خَرَjْتَ فَوَلِّ وَjْهَكَ شَطْرَ الْمَسْjِدِ الْحَرَامِ ۚ وَحَيْثُ مَا كُنتُمْ فَوَلُّوا وُjُوهَكُمْ شَطْرَهُ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُjَّةٌ إِلَّا الَّذِينَ ظَلَمُوا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِي وَلِأُتِمَّ نِعْمَتِي عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُونَ", lao: "และจากที่ใดที่เจ้าออกมา, ຈົ່ງຫັນໜ້າໄປທາງມັດສະຍິດອັນສັກສິດ. และไม่ว่าพวกเจ้าจะอยู่ที่ไหน, ຈົ່ງຫันหน้าไปทางนั้น, ເພື່ອไม่ให้ผู้คนมีข้อโต้แย้งกับพวกเจ้า, ยกเว้นผู้ที่ไม่ยุติธรรมในหมู่พวกเขา. ดังนั้นอย่ากลัวพวกเขา, แต่จงกลัวข้า. และเพื่อให้ข้าได้ทำให้ความโปรดปรานของข้าสมบูรณ์แก่พวกเจ้า, และเพื่อให้พวกเจ้าได้รับการชี้นำ.", english: "And from wherever you go out [for prayer], turn your face toward al-Masjid al-Haram. And wherever you [believers] may be, turn your faces toward it in order that the people will not have any argument against you, except for those of them who are wrongdoers, so fear them not but fear Me. And [it is] so I may complete My favor upon you and that you may be guided." },
  { number: "2:151", arabic: "كَمَا أَرْسَلْنَا فِيكُمْ رَسُولًا mِّنكُمْ يَتْلُو عَلَيْكُمْ آيَاتِنَا وَيُزَكِّيكُمْ وَيُعَلِّمُكُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُعَلِّمُكُم mَّا لَمْ تَكُونُوا تَعْلَمُونَ", lao: "เหมือนกับที่เราได้ส่งศาสดาจากพวกเจ้าเองมาในหมู่พวกเจ้า, ผู้อ่านอายะต่างๆ ของเราให้พวกเจ้าฟัง, และชำระล้างพวกเจ้า, และสอนคัมภีร์และปัญญาให้พวกเจ้า, และสอนสิ่งที่พวกเจ้าไม่เคยรู้.", english: "Just as We have sent among you a Messenger from yourselves reciting to you Our verses and purifying you and teaching you the Book and wisdom and teaching you that which you did not know." },
  { number: "2:152", arabic: "فَاذْكُرُونِي أَذْكُرْكُمْ وَاشْكُرُوا لِي وَلَا تَكْfُرُونِ", lao: "ดังนั้นจงระลึกถึงข้า, ข้าจะระลึกถึงพวกเจ้า. และจงขอบคุณข้า, และอย่าปฏิเสธข้า.", english: "So remember Me; I will remember you. And be grateful to Me and do not deny Me." },
  { number: "2:153", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَعِينُوا بِالصَّbْرِ وَالصَّلَاةِ ۚ إِنَّ اللَّهَ مَعَ الصَّابِرِينَ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຈົ່ງຂໍຄວາມຊ່ວຍເຫຼືອດ້ວຍຄວາມອົດທົນແລະການລະໝາດ. แท้จริงอัลลอฮ์อยู่กับผู้ที่อดทน.", english: "O you who have believed, seek help through patience and prayer. Indeed, Allah is with the patient." },
  { number: "2:154", arabic: "وَلَا تَقُولُوا لِمَن يُقْتَلُ فِي سَبِيلِ اللَّهِ أَمْوَاتٌ ۚ بَلْ أَحْيَاءٌ وَلَٰكِن لَّا تَشْعُرُونَ", lao: "และอย่ากล่าวถึงผู้ที่ถูกฆ่าในหนทางของอัลลอฮ์ว่าเป็น 'คนตาย'. ไม่, พวกเขามีชีวิตอยู่, แต่พวกเจ้าไม่รู้สึก.", english: "And do not say about those who are killed in the way of Allah, 'They are dead.' Rather, they are alive, but you perceive [it] not." },
  { number: "2:155", arabic: "وَلَنَبْلُوَنَّكُم بِشَيْءٍ mِّنَ الْخَوْfِ وَالْjُوعِ وَنَقْصٍ mِّنَ الْأَمْوَالِ وَالْأَنfُسِ وَالثَّمَرَاتِ ۗ وَبَشِّرِ الصَّابِرِينَ", lao: "และแน่นอน, เราจะทดสอบพวกเจ้าด้วยความกลัว, ความหิว, การสูญเสียทรัพย์สิน, ชีวิต, และผลไม้. แต่จงแจ้งข่าวดีแก่ผู้ที่อดทน.", english: "And We will surely test you with something of fear and hunger and a loss of wealth and lives and fruits, but give good tidings to the patient," },
  { number: "2:156", arabic: "الَّذِينَ إِذَا أَصَابَتْهُم mُّصِيبَةٌ قَالُوا إِنَّا لِلَّهِ وَإِنَّا إِلَيْهِ رَajِعُونَ", lao: "ผู้ที่เมื่อประสบกับภัยพิบัติ, กล่าวว่า: 'แท้จริงเราเป็นของอัลลอฮ์, และแท้จริงเราจะกลับไปหาพระองค์.'", english: "Who, when disaster strikes them, say, 'Indeed we belong to Allah, and indeed to Him we will return.'" },
  { number: "2:157", arabic: "أُولَٰئِكَ عَلَيْهِمْ صَلَوَاتٌ mِّن rَّbِّهِمْ وَرَحْمَةٌ ۖ وَأُولَٰئِكَ هُمُ الْمُهْتَدُونَ", lao: "พวกเขาคือผู้ที่ได้รับพรจากพระเจ้าของพวกเขาและความเมตตา. และพวกเขาคือผู้ที่ได้รับการชี้นำ.", english: "Those are the ones upon whom are blessings from their Lord and mercy. And it is those who are the [rightly] guided." },
  { number: "2:158", arabic: "إِنَّ الصَّfَا وَالْمَرْوَةَ مِن شَعَائِرِ اللَّهِ ۖ فَمَنْ حَjَّ الْbَيْتَ أَوِ اعْتَمَرَ فَلَا jُنَاحَ عَلَيْهِ أَن يَطَّوَّfَ بِهِمَا ۚ وَمَن تَطَوَّعَ خَيْرًا فَإِنَّ اللَّهَ شَاكِرٌ عَلِيمٌ", lao: "แท้จริง, ซอฟาและมัรวาเป็นสัญลักษณ์ของอัลลอฮ์. ดังนั้นผู้ใดที่ทำฮัจญ์ที่บ้าน (กาบา) หรือทำอุมเราะฮ์, ก็ไม่มีบาปสำหรับเขาที่จะเดินระหว่างทั้งสอง. และผู้ใดที่อาสาทำความดี, แท้จริงอัลลอฮ์ทรงขอบคุณ, ทรงรอบรู้.", english: "Indeed, as-Safa and al-Marwah are among the symbols of Allah. So whoever makes Hajj to the House or performs 'umrah - there is no blame upon him for walking between them. And whoever volunteers good - then indeed, Allah is appreciative and Knowing." },
  { number: "2:159", arabic: "إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنزَلْنَا مِنَ الْbَيِّنَاتِ وَالْهُدَىٰ مِن بَعْدِ مَا بَيَّنَّاهُ لِلنَّاسِ فِي الْكِتَابِ ۙ أُولَٰئِكَ يَلْعَنُهُمُ اللَّهُ وَيَلْعَنُهُمُ اللَّاعِنُونَ", lao: "แท้จริง, ผู้ที่ปกปิดสิ่งที่เราได้เปิดเผยจากหลักฐานที่ชัดเจนและทางนำ, หลังจากที่เราได้ชี้แจงให้แก่ผู้คนในคัมภีร์แล้ว, พวกเขาจะถูกสาปแช่งโดยอัลลอฮ์และผู้ที่สาปแช่ง.", english: "Indeed, those who conceal what We sent down of clear proofs and guidance after We made it clear for the people in the Scripture - those are cursed by Allah and cursed by those who curse," },
  { number: "2:160", arabic: "إِلَّا الَّذِينَ تَابُوا وَأَصْلَحُوا وَبَيَّنُوا فَأُولَٰئِكَ أَتُوبُ عَلَيْهِمْ ۚ وَأَنَا التَّوَّابُ الرَّحِيمُ", lao: "ยกเว้นผู้ที่กลับใจ, แก้ไข, และชี้แจง. พวกเขาคือผู้ที่ข้าจะอภัยให้. และข้าคือผู้ทรงอภัย, ผู้ทรงเมตตา.", english: "Except for those who repent and correct themselves and make clear [what they concealed]. Those - I will accept their repentance, and I am the Accepting of repentance, the Merciful." },
  { number: "2:161", arabic: "إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُfَّarٌ أُولَٰئِكَ عَلَيْهِمْ لَعْنَةُ اللَّهِ وَالْمَلَائِكَةِ وَالنَّاسِ أَjْمَعِينَ", lao: "แท้จริง, ผู้ที่ปฏิเสธและตายในขณะที่ยังเป็นผู้ปฏิเสธ, พวกเขาจะได้รับคำสาปแช่งจากอัลลอฮ์, ทูตสวรรค์, และมนุษย์ทั้งหมด.", english: "Indeed, those who disbelieve and die while they are disbelievers - upon them will be the curse of Allah and of the angels and the people, all together," },
  { number: "2:162", arabic: "خَالِدِينَ فِيهَا ۖ لَا يُخَفَّfُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنظَرُونَ", lao: "พวกเขาจะอยู่ในนั้นตลอดไป. การลงโทษจะไม่ถูกลดหย่อนให้พวกเขา, และพวกเขาจะไม่ถูกรอคอย.", english: "Abiding eternally therein. The punishment will not be lightened for them, nor will they be reprieved." },
  { number: "2:163", arabic: "وَإِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۖ لَّا إِلَٰهَ إِلَّا هُوَ الرَّحْمَٰنُ الرَّحِيمُ", lao: "และพระเจ้าของพวกเจ้าคือพระเจ้าองค์เดียว. ไม่มีพระเจ้าอื่นใดนอกจากพระองค์, ผู้ทรงเมตตา, ผู้ทรงเมตตาเสมอ.", english: "And your god is one God. There is no deity [worthy of worship] except Him, the Entirely Merciful, the Especially Merciful." },
  { number: "2:164", arabic: "إِنَّ فِي خَلْqِ السَّمَاوَاتِ وَالْأَرْضِ وَاخْتِلَافِ اللَّيْلِ وَالنَّهَarِ وَالْfُلْكِ الَّتِي تَjْرِي فِي الْbَحْرِ بِمَا يَنfَعُ النَّاسَ وَمَا أَنزَلَ اللَّهُ مِنَ السَّمَاءِ مِن mَّاءٍ فَأَحْيَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيهَا مِن كُلِّ دَابَّةٍ وَتَصْرِيفِ الرِّيَاحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَاءِ وَالْأَرْضِ لَآيَاتٍ لِّقَوْmٍ يَعْقِلُونَ", lao: "แท้จริง, ในการสร้างชั้นฟ้าและแผ่นดิน, การสลับของกลางคืนและกลางวัน, เรือที่แล่นในทะเลด้วยสิ่งที่ให้ประโยชน์แก่ผู้คน, และสิ่งที่อัลลอฮ์ทรงส่งลงมาจากท้องฟ้าคือน้ำ, แล้วทรงทำให้แผ่นดินมีชีวิตขึ้นมาหลังจากที่มันตายแล้ว, และทรงกระจายสัตว์ทุกชนิดในนั้น, และการเปลี่ยนทิศทางของลมและเมฆที่อยู่ระหว่างท้องฟ้าและแผ่นดิน, ล้วนเป็นสัญญาณสำหรับผู้ที่มีปัญญา.", english: "Indeed, in the creation of the heavens and the earth, and the alternation of the night and the day, and the [great] ships which sail through the sea with that which benefits people, and what Allah has sent down from the heavens of rain, giving life thereby to the earth after its lifelessness and dispersing therein every [kind of] moving creature, and [His] directing of the winds and the clouds controlled between the heaven and the earth are signs for a people who use reason." },
  { number: "2:165", arabic: "وَمِنَ النَّاسِ مَن يَتَّخِذُ مِن دُونِ اللَّهِ أَندَادًا يُحِبُّونَهُمْ كَحُbِّ اللَّهِ ۖ وَالَّذِينَ آمَنُوا أَشَدُّ حُbًّا لِّلَّهِ ۗ وَلَوْ يَرَى الَّذِينَ ظَلَمُوا إِذْ يَرَوْنَ الْعَذَابَ أَنَّ الْقُوَّةَ لِلَّهِ جَمِيعًا وَأَنَّ اللَّهَ شَدِيدُ الْعَذَابِ", lao: "ແລະໃນบรรดาผู้คน, ມີผู้ທີ່เอาสิ่งอื่นนอกจากอัลลอฮ์มาเป็นภาคี, พวกเขารักสิ่งเหล่านั้นเหมือนกับที่รักอัลลอฮ์. แต่ผู้ที่ศรัทธามีความรักต่ออัลลอฮ์มากกว่า. และถ้าผู้ที่ไม่ยุติธรรมจะเห็นเมื่อพวกเขาเห็นการลงโทษว่าอำนาจทั้งหมดเป็นของอัลลอฮ์และอัลลอฮ์ทรงลงโทษอย่างรุนแรง.", english: "And [yet], among the people are those who take other than Allah as equals [to Him]. They love them as they [should] love Allah. But those who believe are stronger in love for Allah. And if only they who have wronged would consider [that] when they see the punishment, [they will be certain] that all power belongs to Allah and that Allah is severe in punishment." },
  { number: "2:166", arabic: "إِذْ تَبَرَّأَ الَّذِينَ اتُّbِعُوا مِنَ الَّذِينَ اتَّbَعُوا وَرَأَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْأَسْبَابُ", lao: "เมื่อผู้ที่ถูกตามได้ปฏิเสธผู้ที่ตาม, และพวกเขาได้เห็นการลงโทษ, และความสัมพันธ์ระหว่างพวกเขาก็ถูกตัดขาด.", english: "[And] when those who were followed disassociate themselves from those who followed [them], and they see the punishment, and cut off from them are the ties [of relationship]," },
  { number: "2:167", arabic: "وَقَالَ الَّذِينَ اتَّbَعُوا لَوْ أَنَّ لَنَا كَرَّةً فَنَتَبَرَّأَ مِنْهُمْ كَمَا تَبَرَّءُوا مِنَّا ۗ كَذَٰلِكَ يُرِيهِمُ اللَّهُ أَعْمَالَهُمْ حَسَرَاتٍ عَلَيْهِمْ ۖ وَمَا هُم بِخَarِjِينَ مِنَ النَّarِ", lao: "และผู้ที่ตามจะกล่าวว่า: 'ถ้าเรามีโอกาสกลับไปอีกครั้ง, เราจะปฏิเสธพวกเขาเหมือนกับที่พวกเขาปฏิเสธเรา.' เช่นนั้นแหละ, อัลลอฮ์จะทรงแสดงให้พวกเขาเห็นการกระทำของพวกเขาเป็นความเสียใจ. และพวกเขาจะไม่สามารถออกจากไฟนรกได้.", english: "And those who followed will say, 'If only we had another turn [at worldly life] so we could disassociate ourselves from them as they have disassociated themselves from us.' Thus will Allah show them their deeds as regrets upon them. And they are not to emerge from the Fire." },
  { number: "2:168", arabic: "يَا أَيُّهَا النَّاسُ كُلُوا مِمَّا فِي الْأَرْضِ حَلَالًا طَيِّbًا وَلَا تَتَّbِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ mُّbِينٌ", lao: "ໂອ້ ມະນຸດເອີຍ, ຈົ່ງກິນຈາກສິ່ງທີ່ຢູ່ໃນແຜ່ນດິນທີ່ເປັນຮາລານและดี, และอย่าตามรอยเท้าของชัยฏอน. แท้จริง, มันเป็นศัตรูที่เปิดเผยสำหรับพวกเจ้า.", english: "O mankind, eat from whatever is on earth [that is] lawful and good and do not follow the footsteps of Satan. Indeed, he is to you a clear enemy." },
  { number: "2:169", arabic: "إِنَّمَا يَأْمُرُكُم بِالسُّوءِ وَالْfَحْشَاءِ وَأَن تَقُولُوا عَلَى اللَّهِ مَا لَا تَعْلَمُونَ", lao: "แท้จริง, มันสั่งให้พวกเจ้าทำความชั่วและความลามก, และให้พวกเจ้ากล่าวถึงอัลลอฮ์ในสิ่งที่พวกเจ้าไม่รู้.", english: "He only orders you to evil and immorality and to say about Allah what you do not know." },
  { number: "2:170", arabic: "وَإِذَا قِيلَ لَهُمُ اتَّbِعُوا مَا أَنزَلَ اللَّهُ قَالُوا بَلْ نَتَّbِعُ مَا أَلْfَيْنَا عَلَيْهِ آبَاءَنَا ۗ أَوَلَوْ كَانَ آبَاؤُهُمْ لَا يَعْقِلُونَ شَيْئًا وَلَا يَهْتَدُونَ", lao: "และเมื่อถูกกล่าวแก่พวกเขาว่า: 'จงปฏิบัติตามสิ่งที่อัลลอฮ์ได้ทรงเปิดเผย', พวกเขากล่าวว่า: 'ไม่, เราจะปฏิบัติตามสิ่งที่เราพบบรรพบุรุษของเราทำ.' แม้ว่าบรรพบุรุษของพวกเขาจะไม่เข้าใจอะไรเลยและไม่ได้รับการชี้นำก็ตามหรือ?", english: "And when it is said to them, 'Follow what Allah has revealed,' they say, 'Rather, we will follow that upon which we found our fathers.' Even if their fathers understood nothing, nor were they guided?" },
  { number: "2:171", arabic: "وَمَثَلُ الَّذِينَ كَفَرُوا كَمَثَلِ الَّذِي يَنْعِقُ بِمَا لَا يَسْمَعُ إِلَّا دُعَاءً وَنِدَاءً ۚ صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُونَ", lao: "ແລະຕົວຢ່າງຂອງຜູ້ທີ່ປະຕິເສດຄືເໝືອນກັບผู้ທີ່ตะโกนใส่สิ่งที่ไม่ได้ยินอะไรเลยนอกจากเสียงเรียกและเสียงร้อง. ຫູໜວກ, ເປັນໃບ້, ຕາບອດ, ດັ່ງນັ້ນພວກເຂົາຈຶ່ງບໍ່ເຂົ້າໃຈ.", english: "The example of those who disbelieve is like that of one who shouts at what hears nothing but calls and cries cattle or sheep - deaf, dumb and blind, so they do not understand." },
  { number: "2:172", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا كُلُوا مِن طَيِّbَاتِ مَا رَزَقْنَاكُمْ وَاشْكُرُوا لِلَّهِ إِن كُنتُمْ إِيَّاهُ تَعْبُdُونَ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຈົ່ງກິນຈາກສິ່ງที่ดีที่เราได้ให้แก่พวกเจ้า, และจงขอบคุณอัลลอฮ์, หากพวกเจ้าบูชาพระองค์.", english: "O you who have believed, eat from the good things which We have provided for you and be grateful to Allah if it is [indeed] Him that you worship." },
  { number: "2:173", arabic: "إِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنزِيرِ وَمَا أُهِلَّ بِهِ لِغَيْرِ اللَّهِ ۖ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَلَا إِثْمَ عَلَيْهِ ۚ إِنَّ اللَّهَ غَفُورٌ rَّحِيمٌ", lao: "แท้จริง, พระองค์ทรงห้ามพวกเจ้าเฉพาะสัตว์ที่ตายเอง, เลือด, เนื้อหมู, และสิ่งที่ถูกเชือดเพื่อสิ่งอื่นนอกจากอัลลอฮ์. แต่ผู้ใดที่ถูกบังคับโดยไม่มีเจตนาที่จะละเมิดหรือล่วงเกิน, ก็ไม่มีบาปสำหรับเขา. แท้จริงอัลลอฮ์ทรงอภัย, ทรงเมตตา.", english: "He has only forbidden to you dead animals, blood, the flesh of swine, and that which has been dedicated to other than Allah. But whoever is forced [by necessity], neither desiring [it] nor transgressing [its limit], there is no sin upon him. Indeed, Allah is Forgiving and Merciful." },
  { number: "2:174", arabic: "إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنزَلَ اللَّهُ مِنَ الْكِتَابِ وَيَشْتَرُونَ بِهِ ثَمَنًا قَلِيلًا ۙ أُولَٰئِكَ مَا يَأْكُلُونَ فِي بُطُونِهِمْ إِلَّا النَّarَ وَلَا يُكَلِّمُهُمُ اللَّهُ يَوْمَ الْقِيَامَةِ وَلَا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ", lao: "แท้จริง, ผู้ที่ปกปิดสิ่งที่อัลลอฮ์ได้ทรงเปิดเผยจากคัมภีร์และขายมันในราคาถูก, พวกเขาไม่ได้กินอะไรเข้าไปในท้องของพวกเขานอกจากไฟ. และอัลลอฮ์จะไม่ทรงตรัสกับพวกเขาในวันกิยามะ, และจะไม่ทรงชำระล้างพวกเขา, และพวกเขาจะได้รับการลงโทษที่เจ็บปวด.", english: "Indeed, they who conceal what Allah has sent down of the Book and exchange it for a small price - those consume not into their bellies except the Fire. And Allah will not speak to them on the Day of Resurrection, nor will He purify them. And they will have a painful punishment." },
  { number: "2:175", arabic: "أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدَىٰ وَالْعَذَابَ بِالْمَغْfِرَةِ ۚ فَمَا أَصْبَرَهُمْ عَلَى النَّarِ", lao: "พวกเขาคือผู้ที่ได้ซื้อความหลงทางด้วยทางนำและการลงโทษด้วยการอภัย. ช่างอดทนต่อไฟนรกเสียนี่กระไร!", english: "Those are the ones who have purchased error for guidance and punishment for forgiveness. So how patient are they in pursuit of the Fire!" },
  { number: "2:176", arabic: "ذَٰلِكَ بِأَنَّ اللَّهَ نَزَّلَ الْكِتَابَ بِالْحَقِّ ۗ وَإِنَّ الَّذِينَ اخْتَلَفُوا فِي الْكِتَابِ لَفِي شِقَاقٍ بَعِيدٍ", lao: "นั่นเป็นเพราะอัลลอฮ์ได้ทรงเปิดเผยคัมภีร์ด้วยความจริง. และแท้จริง, ผู้ที่ขัดแย้งเกี่ยวกับคัมภีร์นั้นอยู่ในความแตกแยกที่ห่างไกล.", english: "That is [deserved by them] because Allah has sent down the Book in truth. And indeed, those who differ over the Book are in extreme dissension." },
  { number: "2:177", arabic: "لَّيْسَ الْbِرَّ أَن تُوَلُّوا وُjُوهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلَٰكِنَّ الْbِرَّ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَالْمَلَائِكَةِ وَالْكِتَابِ وَالنَّbِيِّينَ وَآتَى الْمَالَ عَلَىٰ حُbِّهِ ذَوِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينَ وَابْنَ السَّbِيلِ وَالسَّائِلِينَ وَفِي الرِّقَابِ وَأَقَامَ الصَّلَاةَ وَآتَى الزَّكَاةَ وَالْمُوفُونَ بِعَهْدِهِمْ إِذَا عَاهَدُوا ۖ وَالصَّابِرِينَ فِي الْbَأْسَاءِ وَالضَّرَّاءِ وَحِينَ الْbَأْسِ ۗ أُولَٰئِكَ الَّذِينَ صَدَقُوا ۖ وَأُولَٰئِكَ هُمُ الْمُتَّقُونَ", lao: "ความดีไม่ได้อยู่ที่การหันหน้าไปทางทิศตะวันออกและทิศตะวันตก, แต่ความดีคือผู้ที่ศรัทธาในอัลลอฮ์, วันสุดท้าย, ทูตสวรรค์, คัมภีร์, และศาสดา, และให้ทรัพย์สินที่รักแก่ญาติ, เด็กกำพร้า, คนขัดสน, นักเดินทาง, ผู้ขอ, และเพื่อปลดปล่อยทาส, และดำรงการละหมาด, และจ่ายซะกาต, และผู้ที่ปฏิบัติตามสัญญาเมื่อได้ทำสัญญา, และผู้ที่อดทนในยามทุกข์ยาก, ความเจ็บป่วย, และในยามสงคราม. พวกเขาคือผู้ที่สัตย์จริง, และพวกเขาคือผู้ที่ยำเกรง.", english: "Righteousness is not that you turn your faces toward the east or the west, but [true] righteousness is [in] one who believes in Allah, the Last Day, the angels, the Book, and the prophets and gives wealth, in spite of love for it, to relatives, orphans, the needy, the traveler, those who ask [for help], and for freeing slaves; [and who] establishes prayer and gives zakah; [those who] fulfill their promise when they promise; and [those who] are patient in poverty and hardship and during battle. Those are the ones who have been true, and it is those who are the righteous." },
  { number: "2:178", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِي الْقَتْلَى ۖ الْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْأُنثَىٰ بِالْأُنثَىٰ ۚ فَمَنْ عُfِيَ لَهُ مِنْ أَخِيهِ شَيْءٌ فَاتِّbَاعٌ بِالْمَعْرُوfِ وَأَدَاءٌ إِلَيْهِ بِإِحْسَانٍ ۗ ذَٰلِكَ تَخْfِيفٌ mِّن rَّbِّكُمْ وَرَحْمَةٌ ۗ فَمَنِ اعْتَدَىٰ بَعْدَ ذَٰلِكَ فَلَهُ عَذَابٌ أَلِيمٌ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, การตอบแทนที่เท่าเทียมกันได้ถูกกำหนดไว้สำหรับพวกเจ้าในกรณีของการฆาตกรรม - คนอิสระสำหรับคนอิสระ, ทาสสำหรับทาส, และผู้หญิงสำหรับผู้หญิง. แต่ผู้ใดที่ได้รับการอภัยจากพี่น้องของเขา, ก็ให้ปฏิบัติตามอย่างเหมาะสมและชดใช้ให้เขาด้วยความดี. นั่นคือการผ่อนผันจากพระเจ้าของพวกเจ้าและความเมตตา. แต่ผู้ใดที่ล่วงเกินหลังจากนั้น, เขาจะได้รับการลงโทษที่เจ็บปวด.", english: "O you who have believed, prescribed for you is legal retribution for those murdered - the free for the free, the slave for the slave, and the female for the female. But whoever overlooks from his brother anything, then there should be a suitable follow-up and payment to him with good conduct. This is an alleviation from your Lord and a mercy. But whoever transgresses after that will have a painful punishment." },
  { number: "2:179", arabic: "وَلَكُمْ فِي الْقِصَاصِ حَيَاةٌ يَا أُولِي الْأَلْبَابِ لَعَلَّكُمْ تَتَّقُونَ", lao: "ແລະໃນການຕອບແທນທີ່ເທົ່າທຽມກັນມີຊີວິດສຳລັບພວກເຈົ້າ, ໂອ້ ຜູ້ที่มีปัญญา, ເພື່ອພວກເຈົ້າຈະໄດ້ຢຳເກງ.", english: "And there is for you in legal retribution [saving of] life, O you [people] of understanding, that you may become righteous." },
  { number: "2:180", arabic: "كُتِبَ عَلَيْكُمْ إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ إِن تَرَكَ خَيْرًا الْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْأَقْرَبِينَ بِالْمَعْرُوfِ ۖ حَقًّا عَلَى الْمُتَّقِينَ", lao: "ໄດ້ຖືກກຳນົດໄວ້ສຳລັບພວກເຈົ້າວ່າເມື່ອຄວາມຕາຍມາເຖິງຜູ້ໃດໃນພວກເຈົ້າ, ຖ້າລາວປະໄວ້ຊັບສິນ, ກໍໃຫ້ເຮັດພິນัยກຳໃຫ້ພໍ່ແມ່และญาติสนิทอย่างเหมาะสม. ນີ້ເປັນພັນທະສຳລັບผู้ที่ยำเกรง.", english: "Prescribed for you when death approaches one of you if he leaves wealth [is that he should make] a bequest for the parents and near relatives according to what is acceptable - a duty upon the righteous." },
  { number: "2:181", arabic: "فَمَن بَدَّلَهُ بَعْدَمَا سَمِعَهُ فَإِنَّمَا إِثْمُهُ عَلَى الَّذِينَ يُبَدِّلُونَهُ ۚ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ", lao: "ຜູ້ໃດທີ່ປ່ຽນແປງມັນຫຼັງຈາກທີ່ໄດ້ຍິນມັນແລ້ວ, ບາບຂອງມັນກໍຢູ່ກັບຜູ້ທີ່ປ່ຽນແປງມັນ. ແທ້ຈິງອັລລໍຮ໌ຊົງໄດ້ຍິນ, ຊົງຮອບຮູ້.", english: "Then whoever alters the bequest after he has heard it - the sin is only upon those who alter it. Indeed, Allah is Hearing and Knowing." },
  { number: "2:182", arabic: "فَمَنْ خَافَ مِن مُّوصٍ جَنَفًا أَوْ إِثْمًا فَأَصْلَحَ بَيْنَهُمْ فَلَا إِثْمَ عَلَيْهِ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ", lao: "ແຕ່ຜູ້ໃດທີ່ຢ້ານວ່າຜູ້ເຮັດພິນัยກຳລຳອຽງຫຼືເຮັດຜິດ, ແລ້ວໄກ່ເກ່ຍລະຫວ່າງພວກເຂົາ, ກໍບໍ່ມີບາບໃດໆຕໍ່ລາວ. ແທ້ຈິງອັລລໍຮ໌ຊົງອະໄພ, ຊົງເມດຕາ.", english: "But if one fears from the bequeather some error or sin and corrects that which is between them, there is no sin upon him. Indeed, Allah is Forgiving and Merciful." },
  { number: "2:183", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِينَ مِن قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ການຖືສິນອົດໄດ້ຖືກກຳນົດໄວ້ສຳລັບພວກເຈົ້າເໝືອນດັ່ງທີ່ໄດ້ຖືກກຳນົດໄວ້ສຳລັບผู้ທີ່ຢູ່ກ່ອນหน้าพวกเจ้า, ເພື່ອພວກເຈົ້າຈະໄດ້ຢຳເກງ.", english: "O you who have believed, decreed upon you is fasting as it was decreed upon those before you that you may become righteous -" },
  { number: "2:184", arabic: "أَيَّامًا مَّعْدُودَاتٍ ۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوْ عَلَىٰ سَفَرٍ فَعِدَّةٌ مِّنْ أَيَّامٍ أُخَرَ ۚ وَعَلَى الَّذِينَ يُطِيقُونَهُ فِدْيَةٌ طَعَامُ مِسْكِينٍ ۖ فَمَن تَطَوَّعَ خَيْرًا فَهُوَ خَيْرٌ لَّهُ ۚ وَأَن تَصُومُوا خَيْرٌ لَّكُمْ ۖ إِن كُنتُمْ تَعْلَمُونَ", lao: "ສອງສາມວັນເທົ່ານັ້ນ. ແຕ່ຜູ້ໃດໃນພວກເຈົ້າທີ່ເຈັບປ່ວຍຫຼືກຳລັງເດີນທາງ, ກໍໃຫ້ຊົດເຊີຍໃນວັນອື່ນ. ແລະສຳລັບຜູ້ທີ່ສາມາດຖືສິນອົດໄດ້ດ້ວຍຄວາມລຳບາກ, ກໍໃຫ້ຈ່າຍຄ່າໄຖ່ໂດຍການໃຫ້ອາຫານແກ່ຄົນຍາກຈົນ. ແຕ່ຜູ້ໃດທີ່ອາສາເຮັດຄວາມດີ, ມັນກໍດີກວ່າສຳລັບລາວ. ແລະການທີ່ພວກເຈົ້າຖືສິນອົດນັ້ນດີກວ່າສຳລັບພວກເຈົ້າ, ຫາກພວກເຈົ້າຮູ້.", english: "[Fasting for] a limited number of days. So whoever among you is ill or on a journey [during them] - then an equal number of days [are to be made up]. And upon those who are able [to fast, but with hardship] - a ransom [as a substitute] of feeding a poor person [each day]. And whoever volunteers excess - it is better for him. But to fast is best for you, if you only knew." },
  { number: "2:185", arabic: "شَهْرُ رَمَضَانَ الَّذِي أُنزِلَ فِيهِ الْقُرْآنُ هُدًى لِّلنَّاسِ وَبَيِّنَاتٍ مِّنَ الْهُدَىٰ وَالْفُرْقَانِ ۚ فَمَن شَهِدَ مِنكُمُ الشَّهْرَ فَلْيَصُمْهُ ۖ وَمَن كَانَ مَرِيضًا أَوْ عَلَىٰ سَفَرٍ فَعِدَّةٌ مِّنْ أَيَّامٍ أُخَرَ ۗ يُرِيدُ اللَّهُ بِكُمُ الْيُsْرَ وَلَا يُرِيدُ بِكُمُ الْعُsْرَ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللَّهَ عَلَىٰ مَا هَدَاكُمْ وَلَعَلَّكُمْ تَشْكُرُونَ", lao: "ເດືອນຣໍມະດອນຄືເດືອນທີ່ຄຳພີກຸຣອານໄດ້ຖືກເປີດເຜີຍລົງມາ, ເປັນທາງນຳສຳລັບມະນຸດ, ແລະເປັນຫຼັກຖານທີ່ຊັດເຈນຂອງທາງນຳແລະການຈຳແນກ. ດັ່ງນັ້ນຜູ້ໃດໃນພວກເຈົ້າທີ່ຢູ່ໃນເດືອນນີ້, ກໍໃຫ້ຖືສິນອົດ. ແລະຜູ້ໃດທີ່ເຈັບປ່ວຍຫຼືກຳລັງເດີນທາງ, ກໍໃຫ້ຊົດເຊີຍໃນວັນອື່ນ. ອັລລໍຮ໌ຕ້ອງການຄວາມງ່າຍດາຍສຳລັບພວກເຈົ້າແລະບໍ່ຕ້ອງການຄວາມລຳບາກ. ແລະເພື່ອໃຫ້ພວກເຈົ້າເຮັດໃຫ້ຄົບຈຳນວນ, ແລະເພື່ອໃຫ້ພວກເຈົ້າຍ້ອງຍໍອັລລໍຮ໌ສຳລັບການທີ່ພຣະອົງໄດ້ຊີ້ແນະพวกเจ้า, และเพื่อให้พวกเจ้าขอบคุณ.", english: "The month of Ramadhan [is that] in which was revealed the Qur'an, a guidance for the people and clear proofs of guidance and criterion. So whoever sights [the new moon of] the month, let him fast it; and whoever is ill or on a journey - then an equal number of other days. Allah intends for you ease and does not intend for you hardship and [wants] for you to complete the period and to glorify Allah for that [to] which He has guided you; and perhaps you will be grateful." },
  { number: "2:186", arabic: "وَإِذَا سَأَلَكَ عِبَادِي عَنِّي فَإِنِّي قَرِيبٌ ۖ أُjِيبُ دَعْوَةَ الدَّاعِ إِذَا دَعَانِ ۖ فَلْيَسْتَjِيبُوا لِي وَلْيُؤْمِنُوا بِي لَعَلَّهُمْ يَرْشُdُونَ", lao: "ແລະເມື່ອຂ້າທາດຂອງຂ້ອຍຖາມເຈົ້າກ່ຽວກັບຂ້ອຍ, ແທ້ຈິງຂ້ອຍຢູ່ໃກ້. ຂ້ອຍຕອບຄຳຂໍຂອງຜູ້ທີ່ຂໍເມື່ອລາວຂໍຂ້ອຍ. ດັ່ງນັ້ນໃຫ້ພວກເຂົາຕອບสนองต่อข้าແລະເຊື່ອໃນຂ้า, ເພື່ອພວກເຂົາຈະໄດ້ຮັບການຊີ້ແນະ.", english: "And when My servants ask you, [O Muhammad], concerning Me - indeed I am near. I respond to the invocation of the supplicant when he calls upon Me. So let them respond to Me [by obedience] and believe in Me that they may be [rightly] guided." },
  { number: "2:187", arabic: "أُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّfَثُ إِلَىٰ نِسَائِكُمْ ۚ هُنَّ لِبَاسٌ لَّكُمْ وَأَنتُمْ لِبَاسٌ لَّهُنَّ ۗ عَلِمَ اللَّهُ أَنَّكُمْ كُنتُمْ تَخْتَانُونَ أَنfُsَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنكُمْ ۖ فَالْآنَ بَاشِرُوهُنَّ وَابْتَغُوا مَا كَتَبَ اللَّهُ لَكُمْ ۚ وَكُلُوا وَاشْرَبُوا حَتَّىٰ يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْأَبْيَضُ مِنَ الْخَيْطِ الْأَسْوَدِ مِنَ الْfَjْرِ ۖ ثُمَّ أَتِمُّوا الصِّيَامَ إِلَى اللَّيْلِ ۚ وَلَا تُbَاشِرُوهُنَّ وَأَنتُمْ عَاكِfُونَ فِي الْمَسَajِدِ ۗ تِلْكَ حُdُودُ اللَّهِ فَلَا تَقْرَبُوهَا ۗ كَذَٰلِكَ يُbَيِّنُ اللَّهُ آيَاتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُونَ", lao: "ການມີເພດສຳພັນກັບພັນລະຍາຂອງພວກເຈົ້າໃນຄືນຂອງການຖືສິນອົດໄດ້ຖືກອະນຸຍາດແລ້ວ. ພວກນາງເປັນເຄື່ອງນຸ່ງສຳລັບພວກເຈົ້າ, ແລະພວກເຈົ້າເປັນເຄື່ອງນຸ່ງສຳລັບພວກນາງ. ອັລລໍຮ໌ຮູ້ວ່າພວກເຈົ້າເຄີຍຫຼອກລວງຕົວເອງ, ແລ້ວພຣະອົງໄດ້ອະໄພໃຫ້ພວກເຈົ້າແລະຍົກໂທດໃຫ້. ດັ່ງນັ້ນ, ຕອນນີ້ຈົ່ງມີເພດສຳພັນກັບພວກນາງແລະສະແຫວງຫາສິ່ງທີ່ອັລລໍຮ໌ໄດ້ກຳນົດໄວ້ສຳລັບພວກເຈົ້າ. ແລະຈົ່ງກິນແລະດື່ມຈົນກວ່າເສັ້ນສີຂາວຂອງຕອນເຊົ້າຈະປາກົດຈາກເສັ້ນສີດຳ. ຫຼັງຈາກນັ້ນ, ຈົ່ງຖືສິນອົດຈົນເຖິງກາງຄືນ. ແລະຢ່າມີເພດສຳພັນກັບພວກນາງໃນຂະນະທີ່ພວກເຈົ້າກຳລັງອີຕິກາຟໃນມັດສະຍິດ. ນັ້ນຄືຂອບເຂດຂອງອັລລໍຮ໌, ດັ່ງນັ້ນຢ່າເຂົ້າໃກ້ມັນ. ເຊັ່ນນັ້ນແຫຼະ, ອັລລໍຮ໌ຊົງຊี้แจงอายะต่างๆ ของพระองค์แก่ผู้คน, ເພື່ອພວກເຂົາຈະໄດ້ຢຳເກງ.", english: "It has been made permissible for you the night preceding fasting to go to your wives [for sexual relations]. They are clothing for you and you are clothing for them. Allah knows that you used to deceive yourselves, so He accepted your repentance and forgave you. So now, have relations with them and seek that which Allah has decreed for you. And eat and drink until the white thread of dawn becomes distinct to you from the black thread [of night]. Then complete the fast until the sunset. And do not have relations with them as long as you are staying for worship in the mosques. These are the limits [set by] Allah, so do not approach them. Thus does Allah make clear His ordinances to the people that they may become righteous." },
  { number: "2:188", arabic: "وَلَا تَأْكُلُوا أَمْوَالَكُم بَيْنَكُم بِالْبَاطِلِ وَتُdْلُوا بِهَا إِلَى الْحُكَّامِ لِتَأْكُلُوا فَرِيقًا mِّنْ أَمْوَالِ النَّاسِ بِالْإِثْمِ وَأَنتُمْ تَعْلَمُونَ", lao: "ແລະຢ່າກິນຊັບສິນຂອງກັນແລະກັນໂດຍບໍ່ຖືກຕ້ອງ, ແລະຢ່ານຳມັນໄປໃຫ້ຜູ້ປົກຄອງເພື່ອທີ່ຈະກິນສ່ວນໜຶ່ງຂອງຊັບສິນຂອງຄົນອື່ນໂດຍຜິດກົດໝາຍທັງທີ່ພວກເຈົ້າຮູ້.", english: "And do not consume one another's wealth unjustly or send it [in bribery] to the rulers in order that [they might aid] you [to] consume a portion of the wealth of the people in sin, while you know [it is unlawful]." },
  { number: "2:189", arabic: "يَسْأَلُونَكَ عَنِ الْأَهِلَّةِ ۖ قُلْ هِيَ مَوَاقِيتُ لِلنَّاسِ وَالْحَjِّ ۗ وَلَيْsَ الْbِرُّ بِأَن تَأْتُوا الْbُيُوتَ مِن ظُهُورِهَا وَلَٰكِنَّ الْbِرَّ مَنِ اتَّقَىٰ ۗ وَأْتُوا الْbُيُوتَ مِنْ أَبْوَابِهَا ۚ وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُfْلِحُونَ", lao: "ພວກເຂົາຖາມເຈົ້າກ່ຽວກັບເດືອນໃໝ່. ຈົ່ງກ່າວວ່າ: 'ມັນເປັນເຄື່ອງໝາຍເວລາສຳລັບມະນຸດແລະການເຮັດຮັຈ.' ແລະຄວາມດີບໍ່ໄດ້ຢູ່ທີ່ການເຂົ້າບ້ານທາງຫຼັງ, ແຕ່ຄວາມດີຄືການຢຳເກງ. ແລະຈົ່ງເຂົ້າບ້ານທາງປະຕູ. ແລະຈົ່ງຢຳເກງອັລລໍຮ໌, ເພື່ອພວກເຈົ້າຈະໄດ້ຮັບຄວາມສຳເລັດ.", english: "They ask you, [O Muhammad], about the new moons. Say, 'They are measurements of time for the people and for Hajj.' And it is not righteousness to enter houses from the back, but righteousness is [in] one who fears Allah. And enter houses from their doors. And fear Allah that you may succeed." },
  { number: "2:190", arabic: "وَقَاتِلُوا فِي سَبِيلِ اللَّهِ الَّذِينَ يُقَاتِلُونَكُمْ وَلَا تَعْتَدُوا ۚ إِنَّ اللَّهَ لَا يُحِبُّ الْمُعْتَدِينَ", lao: "ແລະຈົ່ງຕໍ່ສູ້ໃນหนทางของอัลลอฮ์กับผู้ที่ต่อสู้กับพวกเจ้า, ແຕ່ຢ່າລ່ວງເກີນ. แท้จริงอัลลอฮ์ไม่ทรงรักผู้ที่ล่วงเกิน.", english: "Fight in the way of Allah those who fight you but do not transgress. Indeed. Allah does not like transgressors." },
  { number: "2:191", arabic: "وَاقْتُلُوهُمْ حَيْثُ ثَقِfْتُمُوهُمْ وَأَخْرِjُوهُم mِّنْ حَيْثُ أَخْرَjُوكُمْ ۚ وَالْfِتْنَةُ أَشَدُّ مِنَ الْقَتْلِ ۚ وَلَا تُقَاتِلُوهُمْ عِندَ الْمَسْjِدِ الْحَرَامِ حَتَّىٰ يُقَاتِلُوكُمْ فِيهِ ۖ فَإِن قَاتَلُوكُمْ فَاقْتُلُوهُمْ ۗ كَذَٰلِكَ jَزَاءُ الْكَافِرِينَ", lao: "ແລະຈົ່ງຂ້າພວກເຂົາບ່ອນໃດກໍຕາມທີ່ເຈົ້າພົບ, ແລະຈົ່ງຂັບໄລ່ພວກເຂົາອອກຈາກບ່ອນที่พวกเขาขับไล่เจ้า. ແລະການກົດຂີ່ຂົ່ມເຫັງຮ້າຍແຮງກວ່າການຂ້າ. ແລະຢ່າຕໍ່ສູ້ກັບພວກເຂົາທີ່ມັດສະຍິດອັນສັກສິດຈົນກວ່າພວກເຂົາຈະຕໍ່ສູ້กับเจ้าในนั้น. ແຕ່ຖ້າພວກເຂົາຕໍ່สู้กับเจ้า, ກໍຈົ່ງຂ້າພວກເຂົາ. ເຊັ່ນນັ້ນແຫຼະຄືການຕອບແທນຂອງຜູ້ປະຕິເສດ.", english: "And kill them wherever you overtake them and expel them from wherever they have expelled you, and fitnah is worse than killing. And do not fight them at al-Masjid al- Haram until they fight you there. But if they fight you, then kill them. Such is the recompense of the disbelievers." },
  { number: "2:192", arabic: "فَإِنِ انتَهَوْا فَإِنَّ اللَّهَ غَفُورٌ rَّحِيمٌ", lao: "ແຕ່ຖ້າພວກເຂົາຢຸດ, ແທ້ຈິງອັລລໍຮ໌ຊົງອະໄພ, ຊົງເມດຕາ.", english: "And if they cease, then indeed, Allah is Forgiving and Merciful." },
  { number: "2:193", arabic: "وَقَاتِلُوهُمْ حَتَّىٰ لَا تَكُونَ fِتْنَةٌ وَيَكُونَ الدِّينُ لِلَّهِ ۖ فَإِنِ انتَهَوْا فَلَا عُdْوَانَ إِلَّا عَلَى الظَّالِمِينَ", lao: "ແລະຈົ່ງຕໍ່ສູ້กับพวกเขาຈົນກວ່າจะไม่มีการກົດຂີ່ຂົ່ມເຫັງແລະສາສະໜາຈະເປັນຂອງອັລລໍຮ໌. ແຕ່ຖ້າພວກເຂົາຢຸດ, ກໍບໍ່ມີການເປັນສັດຕູນອກຈາກຕໍ່ผู้ที่ไม่ยุติธรรม.", english: "Fight them until there is no [more] fitnah and [until] the religion, all of it, is for Allah. And if they cease - then there is no aggression except against the oppressors." },
  { number: "2:194", arabic: "الشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمَاتُ قِصَاصٌ ۚ فَمَنِ اعْتَدَىٰ عَلَيْكُمْ فَاعْتَدُوا عَلَيْهِ بِمِثْلِ مَا اعْتَدَىٰ عَلَيْكُمْ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ", lao: "ເດືອນທີ່ສັກສິດສຳລັບເດືອນที่ສັກສິດ, ແລະສຳລັບການລ່ວງເກີນໃນສິ່ງທີ່ສັກສິດຄືການຕອບແທນที่เท่าเทียม. ດັ່ງນັ້ນຜູ້ໃດที่ล่วงเกินพวกเจ้า, ກໍຈົ່ງล่วงเกินเขาເໝືອນດັ່ງที่เขาล่วงเกินพวกเจ้า. ແລະຈົ່ງຢຳເກງອັລລໍຮ໌ແລະຮູ້ໄວ້ວ່າອັລລໍຮ໌ຢູ່ກັບผู้ที่ยำเกรง.", english: "The sacred month is for the sacred month, and for [all] violations is legal retribution. So whoever has assaulted you, then assault him in the same way that he has assaulted you. And fear Allah and know that Allah is with those who fear Him." },
  { number: "2:195", arabic: "وَأَنfِقُوا فِي سَبِيلِ اللَّهِ وَلَا تُلْقُوا بِأَيْدِيكُمْ إِلَى التَّهْلُكَةِ ۛ وَأَحْسِنُوا ۛ إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ", lao: "ແລະຈົ່ງບໍລິຈາກໃນหนทางของอัลลอฮ์ແລະຢ່ານຳຕົວເອງไปสู่ความพินาศ. ແລະຈົ່ງເຮັດຄວາມດີ. แท้จริงอัลลอฮ์ทรงรักผู้ที่ทำความดี.", english: "And spend in the way of Allah and do not throw [yourselves] with your [own] hands into destruction [by refraining]. And do good; indeed, Allah loves the doers of good." },
  { number: "2:196", arabic: "وَأَتِمُّوا الْحَjَّ وَالْعُمْرَةَ لِلَّهِ ۚ فَإِنْ أُحْصِرْتُمْ فَمَا اسْتَيْsَرَ مِنَ الْهَدْيِ ۖ وَلَا تَحْلِقُوا رُءُوسَكُمْ حَتَّىٰ يَبْلُغَ الْهَدْيُ مَحِلَّهُ ۚ فَمَن كَانَ مِنكُم mَّرِيضًا أَوْ بِهِ أَذًى mِّن rَّأْسِهِ فَfِدْيَةٌ mِّن صِيَامٍ أَوْ صَدَقَةٍ أَوْ نُsُكٍ ۚ فَإِذَا أَمِنتُمْ فَمَن تَمَتَّعَ بِالْعُمْرَةِ إِلَى الْحَjِّ فَمَا اسْتَيْsَرَ مِنَ الْهَدْيِ ۚ فَمَن لَّمْ يَjِدْ فَصِيَامُ ثَلَاثَةِ أَيَّامٍ فِي الْحَjِّ وَسَبْعَةٍ إِذَا رَjَعْتُمْ ۗ تِلْكَ عَشَرَةٌ كَامِلَةٌ ۗ ذَٰلِكَ لِمَن لَّمْ يَكُنْ أَهْلُهُ حَاضِرِي الْمَسْjِدِ الْحَرَامِ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ", lao: "ແລະຈົ່ງເຮັດຮັຈແລະອຸມเราะฮ์ໃຫ້ສົມບູນເພື່ອອັລລໍຮ໌. ແຕ່ຖ້າພວກເຈົ້າถูกກີດຂວາງ, ກໍໃຫ້ເສຍสละสัตว์ที่หาได้ง่าย. ແລະຢ່າໂກນหัวຈົນກວ່າສັດເສຍสละจะถึงที่ຂອງมัน. ແຕ່ຜູ້ໃດในพวกเจ้าที่เจ็บป่วยหรือมีโรคที่หัว, ກໍให้จ่ายค่าไถ่ด้วยการຖືສິນອົດຫຼືການບໍລິຈາກຫຼືການເສຍสละ. ແລະເມື່ອພວກเจ้าปลอดภัย, ຜູ້ໃດที่ทำอุมเราะฮ์แล้วทำฮัจญ์, ກໍให้ເສຍสละสัตว์ที่หาได้ง่าย. ແຕ່ຜູ້ໃດທີ່หาไม่ได้, ກໍให้ຖືສິນອົດສາມວັນໃນຊ່ວງฮัจญ์ແລະເຈັດວັນເມື່ອກັບມາ. ນັ້ນຄືສິບວັນเต็ม. ນັ້ນສຳລັບผู้ที่ครอบครัวไม่ได้อาศัยอยู่ใกล้มัสยิดอัลฮะรอม. ແລະຈົ່ງຢຳເກງອັລລໍຮ໌ແລະຮູ້ໄວ້ວ່າອັລລໍຮ໌ทรงลงโทษอย่างรุนแรง.", english: "And complete the Hajj and 'umrah for Allah. But if you are prevented, then [offer] what can be obtained with ease of sacrificial animals. And do not shave your heads until the sacrificial animal has reached its place of slaughter. And whoever among you is ill or has an ailment of the head - then a ransom of fasting [three days] or charity or sacrifice. And when you are secure, then whoever performs 'umrah [during the Hajj months] followed by Hajj [offers] what can be obtained with ease of sacrificial animals. And whoever cannot find [or afford such an animal] - then a fast of three days during Hajj and seven when you have returned [home]. Those are ten complete [days]. This is for those whose family is not in the area of al-Masjid al-Haram. And fear Allah and know that Allah is severe in penalty." },
  { number: "2:197", arabic: "الْحَjُّ أَشْهُرٌ mَّعْلُومَاتٌ ۚ فَمَن فَرَضَ فِيهِنَّ الْحَjَّ فَلَا رَفَثَ وَلَا فُsُوقَ وَلَا jِدَالَ فِي الْحَjِّ ۗ وَمَا تَfْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللَّهُ ۗ وَتَزَوَّdُوا فَإِنَّ خَيْرَ الزَّاdِ التَّقْوَىٰ ۚ وَاتَّقُونِ يَا أُولِي الْأَلْبَابِ", lao: "ຮັຈແມ່ນເດືອນที่ຮູ້ຈັກກັນດີ. ດັ່ງນັ້ນຜູ້ໃດที่ຕັ້ງໃຈทำฮัจญ์ในເດືອນເຫຼົ່ານັ້ນ, ກໍບໍ່ມີການມີເພດສຳພັນ, ບໍ່ມີການລະເມີດ, ແລະບໍ່ມີການຖຽງກັນໃນຊ່ວງฮัจญ์. ແລະຄວາມດີໃດໆที่พวกเจ้าทำ, อัลลอฮ์ทรงรู้. ແລະຈົ່ງກະກຽມเสบียง, ແຕ່เสบียงที่ดีที่สุดຄືຄວາມຢຳເກງ. ແລະຈົ່ງຢຳເກງข้า, ໂອ້ ຜູ້ที่มีปัญญา.", english: "Hajj is [during] well-known months, so whoever has made Hajj obligatory upon himself therein [by entering the state of ihram], there is [to be for him] no sexual relations and no disobedience and no disputing during Hajj. And whatever good you do - Allah knows it. And take provisions, but indeed, the best provision is fear of Allah. And fear Me, O you of understanding." },
  { number: "2:198", arabic: "لَيْsَ عَلَيْكُمْ jُنَاحٌ أَن تَبْتَغُوا فَضْلًا mِّن rَّbِّكُمْ ۚ فَإِذَا أَfَضْتُم mِّنْ عَرَfَاتٍ فَاذْكُرُوا اللَّهَ عِندَ الْمَشْعَرِ الْحَرَامِ ۖ وَاذْكُرُوهُ كَمَا هَدَاكُمْ وَإِن كُنتُم mِّن قَبْلِهِ لَمِنَ الضَّالِّينَ", lao: "ບໍ່ມີบาปสำหรับพวกเจ้าที่จะแสวงหาความโปรดปรานจากพระเจ้าของพวกเจ้า. ແຕ່ເມື່ອพวกเจ้าออกจากอารอฟัต, ຈົ່ງລະນຶກເຖິງອັລລໍຮ໌ที่มัสยิดอัลฮะรอม. ແລະຈົ່ງລະນຶກເຖິງພຣະອົງເໝືອນດັ່ງທີ່ພຣະອົງໄດ້ຊี้ແນະพวกเจ้า, ທັງທີ່ກ່ອນหน้านั้นพวกเจ้าเคยอยู่ในກຸ່ມผู้ที่หลงทาง.", english: "There is no blame upon you for seeking bounty from your Lord [during Hajj]. But when you depart from 'Arafat, remember Allah at al- Mash'ar al-Haram. And remember Him, as He has guided you, for indeed, you were before that among those astray." },
  { number: "2:199", arabic: "ثُمَّ أَfِيضُوا مِنْ حَيْثُ أَfَاضَ النَّاسُ وَاسْتَغْfِرُوا اللَّهَ ۚ إِنَّ اللَّهَ غَفُورٌ rَّحِيمٌ", lao: "ຫຼັງຈາກນັ້ນ, ຈົ່ງອອກຈາກບ່ອນที่ผู้คนออก, ແລະຈົ່ງຂໍການອະໄພຈາກອັລລໍຮ໌. ແທ້ຈິງອັລລໍຮ໌ຊົງອະໄພ, ຊົງເມດຕາ.", english: "Then depart from the place from where [all] the people depart and ask forgiveness of Allah. Indeed, Allah is Forgiving and Merciful." },
  { number: "2:200", arabic: "فَإِذَا قَضَيْتُم mَّنَاسِكَكُمْ فَاذْكُرُوا اللَّهَ كَذِكْرِكُمْ آبَاءَكُمْ أَوْ أَشَدَّ ذِكْرًا ۗ فَمِنَ النَّاسِ مَن يَقُولُ رَبَّنَا آتِنَا فِي الدُّنْيَا وَمَا لَهُ فِي الْآخِرَةِ مِنْ خَلَاقٍ", lao: "ແລະເມື່ອพวกเจ้าได้ทำพิธีกรรมของพวกเจ้าเสร็จแล้ว, ຈົ່ງລະນຶກເຖິງອັລລໍຮ໌ເໝືອນດັ່ງທີ່ພວກเจ้าระลึกถึงบรรพบุรุษของพวกเจ้าหรือมากกว่านั้น. และในบรรดาผู้คน, มีผู้ที่กล่าวว่า: 'ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້ພວກເຮົາได้รับໃນໂລກນີ້', ແຕ່เขาจะไม่มีส่วนแบ่งในโลกหน้า.", english: "And when you have completed your rites, remember Allah like your [previous] remembrance of your fathers or with much greater remembrance. And among the people is he who says, 'Our Lord, give us in this world,' and he will have in the Hereafter no share." },
  { number: "2:201", arabic: "وَمِنْهُم mَّن يَقُولُ رَبَّnَا آتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الْآخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّarِ", lao: "ແລະໃນກຸ່ມຂອງພວກເຂົามีผู้ที่กล่าวว่า: 'ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້ພວກເຮົາได้รับความดีในໂລກນີ້ແລະความดีในโลกหน้า, และขอให้ปกป้องเราจากการลงโทษของไฟนรก.'", english: "But among them is he who says, 'Our Lord, give us in this world [that which is] good and in the Hereafter [that which is] good and protect us from the punishment of the Fire.'" },
  { number: "2:202", arabic: "أُولَٰئِكَ لَهُمْ نَصِيبٌ mِّمَّا كَسَبُوا ۚ وَاللَّهُ سَرِيعُ الْحِسَابِ", lao: "พวกเขาคือผู้ที่จะได้รับส่วนแบ่งจากสิ่งที่พวกเขาได้ทำ. ແລະອັລລໍຮ໌ทรงรวดเร็วในการคิดบัญชี.", english: "Those will have a share of what they have earned, and Allah is swift in account." },
  { number: "2:203", arabic: "وَاذْكُرُوا اللَّهَ فِي أَيَّامٍ mَّعْدُودَاتٍ ۚ فَمَن تَعَjَّلَ فِي يَوْمَيْنِ فَلَا إِثْمَ عَلَيْهِ وَمَن تَأَخَّرَ فَلَا إِثْمَ عَلَيْهِ ۚ لِمَنِ اتَّقَىٰ ۗ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّكُمْ إِلَيْهِ تُحْشَرُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງອັລລໍຮ໌ໃນວັນທີ່ກຳນົດໄວ້. ແຕ່ຜູ້ໃດที่รีบร้อนในສອງວັນ, ກໍບໍ່ມີบาปสำหรับเขา. และผู้ใดที่อยู่ต่อ, ກໍບໍ່มีบาปสำหรับเขา - ສຳລັບผู้ที่ยำเกรง. ແລະຈົ່ງຢຳເກງອັລລໍຮ໌และຮູ້ໄວ້ວ່າพวกเจ้าจะถูกรวบรวมไปหาพระองค์.", english: "And remember Allah during [specific] numbered days. Then whoever hastens [his departure] in two days - there is no sin upon him; and whoever delays [until the third] - there is no sin upon him - for him who fears Allah. And fear Allah and know that unto Him you will be gathered." },
  { number: "2:204", arabic: "وَمِنَ النَّاسِ مَن يُعْjِبُكَ قَوْلُهُ فِي الْحَيَاةِ الدُّنْيَا وَيُشْهِدُ اللَّهَ عَلَىٰ مَا فِي قَلْبِهِ وَهُوَ أَلَدُّ الْخِصَامِ", lao: "ແລະໃນบรรดาผู้คน, ມີผู้ที่คำพูดของเขาในຊີວິດນີ້อาจจะทำให้เจ้าพอใจ, ແລະเขาเรียกอัลลอฮ์มาเป็นพยานในสิ่งที่อยู่ในใจของเขา, ທັງທີ່เขาเป็นผู้ที่เถียงเก่งที่สุด.", english: "And of the people is he whose speech pleases you in worldly life, and he calls Allah to witness as to what is in his heart, yet he is the fiercest of opponents." },
  { number: "2:205", arabic: "وَإِذَا تَوَلَّىٰ سَعَىٰ فِي الْأَرْضِ لِيُfْسِدَ فِيهَا وَيُهْلِكَ الْحَرْثَ وَالنَّsْلَ ۗ وَاللَّهُ لَا يُحِبُّ الْfَسَادَ", lao: "ແລະເມື່ອລາວຫັນຫຼັງกลับไป, ລາວພະຍາຍາມສ້າງຄວາມເສຍຫາຍໃນແຜ່ນດິນແລະທຳລາຍພືດພັນและສັດລ້ຽງ. ແລະອັລລໍຮ໌ບໍ່ทรงรักความเสื่อมทราม.", english: "And when he goes away, he strives throughout the land to cause corruption therein and destroy crops and animals. And Allah does not like corruption." },
  { number: "2:206", arabic: "وَإِذَا قِيلَ لَهُ اتَّقِ اللَّهَ أَخَذَتْهُ الْعِزَّةُ بِالْإِثْمِ ۚ فَحَسْبُهُ jَهَنَّمُ ۚ وَلَبِئْسَ الْمِهَادُ", lao: "ແລະເມື່ອຖືກກ່າວແກ່ລາວວ່າ: 'ຈົ່ງຢຳເກງອັລລໍຮ໌', ຄວາມອວດດີก็พาเขาไปสู่บาป. ດັ່ງນັ້ນ, นรกก็เพียงพอสำหรับเขา, ແລະมันเป็นที่พำนักที่เลวร้าย.", english: "And when it is said to him, 'Fear Allah,' pride in the sin takes hold of him. Sufficient for him is Hellfire, and how wretched is the resting place." },
  { number: "2:207", arabic: "وَمِنَ النَّاسِ مَن يَشْرِي نَفْسَهُ ابْتِغَاءَ مَرْضَاتِ اللَّهِ ۗ وَاللَّهُ رَءُوfٌ بِالْعِبَادِ", lao: "ແລະໃນบรรดาผู้คน, ມີผู้ที่ขายตัวเองเพื่อแสวงหาความพอใจของอัลลอฮ์. ແລະອັລລໍຮ໌ทรงเมตตาต่อบ่าว.", english: "And of the people is he who sells himself, seeking means to the approval of Allah. And Allah is kind to [His] servants." },
  { number: "2:208", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا ادْخُلُوا فِي السِّلْمِ كَافَّةً وَلَا تَتَّbِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ mُّbِينٌ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຈົ່ງເຂົ້າມາໃນອິດສະລາມຢ່າງສົມບູນແລະຢ່າຕາມรอยเท้าของชัยฏอน. แท้จริง, มันเป็นศัตรูที่เปิดเผยสำหรับพวกเจ้า.", english: "O you who have believed, enter into Islam completely [and perfectly] and do not follow the footsteps of Satan. Indeed, he is to you a clear enemy." },
  { number: "2:209", arabic: "فَإِن زَلَلْتُم mِّن بَعْدِ مَا jَاءَتْكُمُ الْbَيِّنَاتُ فَاعْلَمُوا أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ", lao: "ແຕ່ຖ້າພວກเจ้าพลาดพลั้งหลังจากที่หลักฐานที่ชัดเจนได้มาถึงพวกเจ้าแล้ว, ກໍຈົ່ງຮູ້ໄວ້ວ່າອັລລໍຮ໌ทรงมีอำนาจ, ทรงมีปัญญา.", english: "But if you slip [i.e., deviate] after clear proofs have come to you, then know that Allah is Exalted in Might and Wise." },
  { number: "2:210", arabic: "هَلْ يَنظُرُونَ إِلَّا أَن يَأْتِيَهُمُ اللَّهُ فِي ظُلَلٍ mِّنَ الْغَمَامِ وَالْمَلَائِكَةُ وَقُضِيَ الْأَمْرُ ۚ وَإِلَى اللَّهِ تُرْjَعُ الْأُمُورُ", lao: "ພວກเขารออะไรอยู่นอกจากอัลลอฮ์จะมาหาพวกเขาในเงาของเมฆพร้อมกับทูตสวรรค์และเรื่องราวจะถูกตัดสิน? ແລະທຸກເລື່ອງຈະຖືກນຳກັບไปหาອັລລໍຮ໌.", english: "Do they await but that Allah should come to them in covers of clouds and the angels [as well] and the matter is [then] decided? And to Allah all matters are returned." },
  { number: "2:211", arabic: "sَلْ بَنِي إِسْرَائِيلَ كَمْ آتَيْنَاهُم mِّنْ آيَةٍ بَيِّنَةٍ ۗ وَمَن يُbَدِّلْ نِعْمَةَ اللَّهِ مِن بَعْدِ مَا jَاءَتْهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ", lao: "ຈົ່ງຖາມລູກຫຼານຂອງອິດສະຣາເອວວ່າເຮົາໄດ້ໃຫ້ຫຼັກຖານທີ່ຊັດເຈນແກ່ພວກເຂົາຈັກອັນ. ແລະຜູ້ໃດที่เปลี่ยนความโปรดปรานของอัลลอฮ์หลังจากที่มันได้มาถึงเขา, แท้จริงอัลลอฮ์ทรงลงโทษอย่างรุนแรง.", english: "Ask the Children of Israel how many a clear sign We have given them. And whoever exchanges the favor of Allah [for disbelief] after it has come to him - then indeed, Allah is severe in penalty." },
  { number: "2:212", arabic: "زُيِّنَ لِلَّذِينَ كَفَرُوا الْحَيَاةُ الدُّنْيَا وَيَسْخَرُونَ مِنَ الَّذِينَ آمَنُوا ۘ وَالَّذِينَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيَامَةِ ۗ وَاللَّهُ يَرْزُقُ مَن يَشَاءُ بِغَيْرِ حِسَابٍ", lao: "ຊີວິດນີ້ໄດ້ຖືກປະດັບປະດາให้สวยงามสำหรับผู้ที่ปฏิเสธ, และพวกเขาเยาะเย้ยผู้ที่ศรัทธา. ແຕ່ผู้ที่ยำเกรงจะอยู่เหนือพวกเขาในວັນກິຍາມະ. ແລະອັລລໍຮ໌ทรงให้ปัจจัยแก่ผู้ที่พระองค์ทรงประสงค์โดยไม่มีการคำนวณ.", english: "Beautified for those who disbelieve is the life of this world, and they ridicule those who believe. But those who fear Allah are above them on the Day of Resurrection. And Allah gives provision to whom He wills without account." },
  { number: "2:213", arabic: "كَانَ النَّاسُ أُمَّةً وَاحِدَةً فَبَعَثَ اللَّهُ النَّbِيِّينَ مُbَشِّرِينَ وَمُنذِرِينَ وَأَنزَلَ مَعَهُمُ الْكِتَابَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيمَا اخْتَلَفُوا فِيهِ ۚ وَمَا اخْتَلَفَ فِيهِ إِلَّا الَّذِينَ أُوتُوهُ مِن بَعْدِ مَا jَاءَتْهُمُ الْbَيِّنَاتُ بَغْيًا بَيْنَهُمْ ۖ فَهَدَى اللَّهُ الَّذِينَ آمَنُوا لِمَا اخْتَلَفُوا فِيهِ مِنَ الْحَقِّ بِإِذْنِهِ ۗ وَاللَّهُ يَهْدِي مَن يَشَاءُ إِلَىٰ صِرَاطٍ mُّsْتَقِيمٍ", lao: "ມະນຸດເຄີຍເປັນປະຊາຊາດດຽວ. ແລ້ວອັລລໍຮ໌ໄດ້ສົ່ງສາດສະດາมาเป็นผู้แจ้งข่าวดีและผู้ตักเตือน, ແລະໄດ້ເປີດເຜີຍຄຳພີພ້ອມກັບພວກເຂົາด้วยความจริง, ເພື່ອຕັດສິນລະຫວ່າງຜູ້ຄົນໃນສິ່ງທີ່ພວກເຂົາຂັດແຍ່ງກັນ. ແລະບໍ່ມີໃຜຂັດແຍ່ງກັນນອກຈາກผู้ที่ได้รับมัน, ຫຼັງຈາກທີ່ຫຼັກຖານທີ່ຊັດเจนได้มาถึงພວກເຂົາแล้ว, ດ້ວຍຄວາມອິດສາซึ่งกันແລະกัน. ແລ້ວອັລລໍຮ໌ໄດ້ຊີ້ແນະຜູ້ທີ່ເຊື່ອໃນສິ່ງທີ່ພວກເຂົາຂັດແຍ່ງກັນດ້ວຍຄວາມຈິງ, ດ້ວຍການອະນຸຍາດຂອງພຣະອົງ. ແລະອັລລໍຮ໌ทรงชี้นำผู้ที่พระองค์ทรงประสงค์ไปยังหนทางที่เที่ยงตรง.", english: "Mankind was [of] one religion [before their deviation]; then Allah sent the prophets as bringers of good tidings and warners and sent down with them the Scripture in truth to judge between the people concerning that in which they differed. And none differed over the Scripture except those who were given it - after the clear proofs came to them - out of jealous animosity among themselves. And Allah guided those who believed to the truth concerning that over which they had differed, by His permission. And Allah guides whom He wills to a straight path." },
  { number: "2:214", arabic: "أَمْ حَسِبْتُمْ أَن تَدْخُلُوا الْjَنَّةَ وَلَمَّا يَأْتِكُم mَّثَلُ الَّذِينَ خَلَوْا مِن قَبْلِكُم ۖ مَّsَّتْهُمُ الْbَأْسَاءُ وَالضَّرَّاءُ وَزُلْزِلُوا حَتَّىٰ يَقُولَ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ مَتَىٰ نَصْرُ اللَّهِ ۗ أَلَا إِنَّ نَصْرَ اللَّهِ قَرِيبٌ", lao: "ຫຼືພວກເຈົ້າຄິດວ່າຈະໄດ້ເຂົ້າสวรรค์ໂດຍທີ່ยังไม่ประสบกับสิ่งທີ່ผู้ที่อยู่ກ່ອນหน้าพวกเจ้าเคยประสบ? ຄວາມທຸກຍາກແລະຄວາມລຳບາກໄດ້ເກີດຂຶ້ນກັບພວກເຂົາ, และพวกเขาถูกเขย่าขวัญจนกระทั่งศาสดาและผู้ที่ศรัทธาพร้อมกับเขากล่าวว่า: 'ການຊ່ວຍເຫຼືອຂອງອັລລໍຮ໌ຈະມາເມື່ອໃດ?' ຈົ່ງຮູ້ໄວ້ວ່າ, ແທ້ຈິງການຊ່ວຍເຫຼືອຂອງອັລລໍຮ໌ໃກ້ເຂົ້າມາແລ້ວ.", english: "Or do you think that you will enter Paradise while such [trial] has not yet come to you as came to those who passed on before you? They were afflicted by poverty and hardship and were shaken until [even their] messenger and those who believed with him said, 'When is the help of Allah?' Unquestionably, the help of Allah is near." },
  { number: "2:215", arabic: "يَسْأَلُونَكَ مَاذَا يُnfِقُونَ ۖ قُلْ مَا أَنfَقْتُم mِّنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْأَقْرَبِينَ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَابْنِ السَّbِيلِ ۗ وَمَا تَfْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ", lao: "ພວກເຂົາຖາມເຈົ້າວ່າພວກເຂົາຄວນບໍລິຈາກຫຍັງ. ຈົ່ງກ່າວວ່າ: 'ความดีใดๆ ที่พวกเจ้าบริจาค, ກໍให้แก่ພໍ່ແມ່, ຍາດພີ່ນ້ອງ, ເດັກກຳພ້າ, ຄົນขัดสน, และนักเดินทาง. ແລະຄວາມດີໃດໆที่พวกเจ้าทำ, ແທ້ຈິງອັລລໍຮ໌ทรงรู้.'", english: "They ask you, [O Muhammad], what they should spend. Say, 'Whatever you spend of good is [to be] for parents and relatives and orphans and the needy and the traveler. And whatever you do of good - indeed, Allah is Knowing of it.'" },
  { number: "2:216", arabic: "كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَّكُمْ ۖ وَعَسَىٰ أَن تَكْرَهُوا شَيْئًا وَهُوَ خَيْرٌ لَّكُمْ ۖ وَعَسَىٰ أَن تُحِبُّوا شَيْئًا وَهُوَ شَرٌّ لَّكُمْ ۗ وَاللَّهُ يَعْلَمُ وَأَنتُمْ لَا تَعْلَمُونَ", lao: "ການຕໍ່ສູ້ໄດ້ຖືກກຳນົດໄວ້ສຳລັບພວກເຈົ້າ, ທັງທີ່ມັນເປັນສິ່ງที่พวกเจ้าไม่ชอบ. ແຕ່ອາດຈະເປັນໄປໄດ້ວ່າພວກເຈົ້າไม่ชอบสิ่งหนึ่ง, ແຕ່ມັນดีสำหรับพวกเจ้า; ແລະອາດຈະເປັນໄປໄດ້ວ່າພວກเจ้ารักสิ่งหนึ่ง, ແຕ່ມັນชั่วสำหรับพวกเจ้า. ແລະອັລລໍຮ໌ทรงรู้, ແຕ່ພວກເຈົ້າບໍ່ຮູ້.", english: "Fighting has been enjoined upon you while it is hateful to you. But perhaps you hate a thing and it is good for you; and perhaps you love a thing and it is bad for you. And Allah Knows, while you know not." },
  { number: "2:217", arabic: "يَسْأَلُونَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ فِيهِ ۖ قُلْ قِتَالٌ فِيهِ كَبِيرٌ ۖ وَصَدٌّ عَن سَبِيلِ اللَّهِ وَكُfْرٌ بِهِ وَالْمَسْjِدِ الْحَرَامِ وَإِخْرَajُ أَهْلِهِ مِنْهُ أَكْبَرُ عِندَ اللَّهِ ۚ وَالْfِتْنَةُ أَكْبَرُ مِنَ الْقَتْلِ ۗ وَلَا يَزَالُونَ يُقَاتِلُونَكُمْ حَتَّىٰ يَرُdُّوكُمْ عَن دِينِكُمْ إِنِ اسْتَطَاعُوا ۚ وَمَن يَرْتَدِدْ مِنكُمْ عَن دِينِهِ فَيَمُتْ وَهُوَ كَافِرٌ فَأُولَٰئِكَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالْآخِرَةِ ۖ وَأُولَٰئِكَ أَصْحَابُ النَّarِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ພວກເຂົາຖາມເຈົ້າກ່ຽວກັບເດືອນທີ່ສັກສິດ - ການຕໍ່ສູ້ໃນນັ້ນ. ຈົ່ງກ່າວວ່າ: 'ການຕໍ່ສູ້ໃນນັ້ນເປັນบาปใหญ่. ແຕ່การกีดกันจากหนทางของอัลลอฮ์, การปฏิเสธพระองค์, การกีดกันจากมัสยิดอัลฮะรอม, ແລະການຂັບໄລ່ຜູ້ຄົນອອກຈາກນັ້ນ, ຍິ່ງใหญ่ກວ່າในสายตาของอัลลอฮ์. ແລະการกดขี่ข่มเหงຮ້າຍແຮງກວ່າการຂ້າ.' ແລະພວກເຂົາຈະບໍ່ຢຸດຕໍ່ສູ້กับพวกเจ้าຈົນກວ່າจะทำให้พวกเจ้าละทิ้งศาสนาของพวกเจ้า, ຫາກພວກເຂົາສາມາດ. ແລະຜູ້ໃດໃນພວກເຈົ້າທີ່ละทิ้งศาสนาของเขาแล้วตายในสภาพที่ยังเป็นผู้ปฏิเสธ, ການກະທຳຂອງພວກເຂົາຈະสูญเปล่าในโลกนี้และโลกหน้า. ແລະພວກເຂົາຄືชาวนรก; ພວກເຂົາຈະอยู่ในນັ້ນຕະຫຼອດໄປ.", english: "They ask you about the sacred month - about fighting therein. Say, 'Fighting therein is great [sin], but averting [people] from the way of Allah and disbelief in Him and [preventing access to] al-Masjid al-Haram and the expulsion of its people therefrom are greater [evil] in the sight of Allah. And fitnah is greater than killing.' And they will continue to fight you until they turn you back from your religion if they are able. And whoever of you reverts from his religion [to disbelief] and dies while he is a disbeliever - for those, their deeds have become worthless in this world and the Hereafter, and those are the companions of the Fire, they will abide therein eternally." },
  { number: "2:218", arabic: "إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَajَرُوا وَjَاهَدُوا فِي سَبِيلِ اللَّهِ أُولَٰئِكَ يَرْjُونَ رَحْمَتَ اللَّهِ ۚ وَاللَّهُ غَفُورٌ rَّحِيمٌ", lao: "ແທ້ຈິງ, ຜູ້ທີ່ເຊື່ອ, ຜູ້ທີ່ອົບພະຍົບ, ແລະຜູ້ที่ต่อสู้ในหนทางของอัลลอฮ์, พวกเขาหวังในความเมตตาของอัลลอฮ์. ແລະອັລລໍຮ໌ทรงอภัย, ทรงเมตตา.", english: "Indeed, those who have believed and those who have emigrated and fought in the cause of Allah - those expect the mercy of Allah. And Allah is Forgiving and Merciful." },
  { number: "2:219", arabic: "يَسْأَلُونَكَ عَنِ الْخَمْرِ وَالْمَيْsِرِ ۖ قُلْ فِيهِمَا إِثْمٌ كَبِيرٌ وَمَنَافِعُ لِلنَّاسِ وَإِثْمُهُمَا أَكْبَرُ مِن nَّfْعِهِمَا ۗ وَيَسْأَلُونَكَ مَاذَا يُnfِقُونَ قُلِ الْعَفْوَ ۗ كَذَٰلِكَ يُbَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ", lao: "ພວກເຂົາຖາມເຈົ້າກ່ຽວກັບເຫຼົ້າและการพนัน. ຈົ່ງກ່າວວ່າ: 'ໃນທັງສອງມີบาปใหญ่ແລະຜົນປະໂຫຍດບາງຢ່າງสำหรับผู้คน, ແຕ່บาปของมันใหญ่ກວ່າຜົນປະໂຫຍດ.' ແລະພວກເຂົາຖາມເຈົ້າວ່າພວກເຂົາຄວນບໍລິຈາກຫຍັງ. ຈົ່ງກ່າວວ່າ: 'ສ່ວນที่เหลือ.' ເຊັ່ນນັ້ນແຫຼະ, ອັລລໍຮ໌ทรงຊี้ແຈງອາຍະຕ່າງໆໃຫ້พวกเจ้า, ເພື່ອพวกเจ้าจะได้ไตร่ตรอง.", english: "They ask you about wine and gambling. Say, 'In them is great sin and [yet, some] benefit for people. But their sin is greater than their benefit.' And they ask you what they should spend. Say, 'The excess [beyond needs].' Thus Allah makes clear to you the verses [of revelation] that you might give thought." },
  { number: "2:220", arabic: "فِي الدُّنْيَا وَالْآخِرَةِ ۗ وَيَسْأَلُونَكَ عَنِ الْيَتَامَىٰ ۖ قُلْ إِصْلَاحٌ لَّهُمْ خَيْرٌ ۖ وَإِن تُخَالِطُوهُمْ فَإِخْوَانُكُمْ ۚ وَاللَّهُ يَعْلَمُ الْمُfْسِدَ مِنَ الْمُصْلِحِ ۚ وَلَوْ شَاءَ اللَّهُ لَأَعْنَتَكُمْ ۚ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ", lao: "ກ່ຽວກັບໂລກນີ້ແລະໂລກໜ້າ. ແລະພວກເຂົາຖາມເຈົ້າກ່ຽວກັບເດັກກຳພ້າ. ຈົ່ງກ່າວວ່າ: 'ການປັບປຸງສະພາບຂອງພວກເຂົາເປັນສິ່ງທີ່ດີ. ແລະຖ້າພວກເຈົ້າຢູ່ຮ່ວມກັບພວກເຂົາ, ພວກເຂົາກໍຄືອ້າຍນ້ອງຂອງພວກເຈົ້າ.' ແລະອັລລໍຮ໌ຮູ້ຈັກຜູ້ທີ່ສ້າງຄວາມເສຍຫາຍຈາກຜູ້ທີ່ປັບປຸງ. ແລະຖ້າອັລລໍຮ໌ປະສົງ, ພຣະອົງຈະເຮັດໃຫ້ພວກເຈົ້າລຳບາກ. ແທ້ຈິງອັລລໍຮ໌ทรงมีอำนาจ, ทรงมีปัญญา.", english: "To this world and the Hereafter. And they ask you about orphans. Say, 'Improvement for them is best. And if you mix your affairs with theirs - they are your brothers.' And Allah knows the corrupter from the amender. And if Allah had willed, He could have put you in difficulty. Indeed, Allah is Exalted in Might and Wise." },
  { number: "2:221", arabic: "وَلَا تَنكِحُوا الْمُشْرِكَاتِ حَتَّىٰ يُؤْمِنَّ ۚ وَلَأَمَةٌ mُّؤْمِنَةٌ خَيْرٌ mِّن mُّشْرِكَةٍ وَلَوْ أَعْjَبَتْكُمْ ۗ وَلَا تُنكِحُوا الْمُشْرِكِينَ حَتَّىٰ يُؤْمِنُوا ۚ وَلَعَبْدٌ mُّؤْمِنٌ خَيْرٌ mِّن mُّشْرِكٍ وَلَوْ أَعْjَبَكُمْ ۗ أُولَٰئِكَ يَدْعُونَ إِلَى النَّarِ ۖ وَاللَّهُ يَدْعُو إِلَى الْjَنَّةِ وَالْمَغْfِرَةِ بِإِذْنِهِ ۖ وَيُbَيِّنُ آيَاتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ", lao: "ແລະຢ່າແຕ່ງງານກັບຍິງທີ່ຕັ້ງພາກີຈົນກວ່າພວກນາງຈະເຊື່ອ. ແລະທາດສາວທີ່ເຊື່ອດີກວ່າຍິງທີ່ຕັ້ງພາກີ, ເຖິງແມ່ນວ່ານາງຈະເຮັດໃຫ້ເຈົ້າພໍໃຈ. ແລະຢ່າໃຫ້ຍິງທີ່ເຊື່ອແຕ່ງງານກັບຊາຍທີ່ຕັ້ງພາກີຈົນກວ່າພວກເຂົາຈະເຊື່ອ. ແລະທາດຊາຍທີ່ເຊື່ອດີກວ່າຊາຍທີ່ຕັ້ງພາກີ, ເຖິງແມ່ນວ່າລາວຈະເຮັດໃຫ້ເຈົ້າພໍໃຈ. ພວກເຂົາເຫຼົ່ານັ້ນເຊີນຊວນไปสู่นรก, ແຕ່ອັລລໍຮ໌ເຊີນຊວນไปสู่สวรรค์และการอภัยด้วยการอนุมัติของพระองค์. ແລະພຣະອົງຊົງຊี้ແຈງອາຍະຕ່າງໆຂອງພຣະອົງแก่ผู้คน, ເພື່ອພວກເຂົາຈະໄດ້ລະນຶກ.", english: "And do not marry polytheistic women until they believe. And a believing slave woman is better than a polytheist, even though she might please you. And do not marry polytheistic men [to your women] until they believe. And a believing slave is better than a polytheist, even though he might please you. Those invite [you] to the Fire, but Allah invites to Paradise and to forgiveness, by His permission. And He makes clear His verses to the people that perhaps they may remember." },
  { number: "2:222", arabic: "وَيَسْأَلُونَكَ عَنِ الْمَحِيضِ ۖ قُلْ هُوَ أَذًى فَاعْتَزِلُوا النِّsَاءَ فِي الْمَحِيضِ ۖ وَلَا تَقْرَبُوهُنَّ حَتَّىٰ يَطْهُرْنَ ۖ فَإِذَا تَطَهَّrْنَ فَأْتُوهُنَّ مِنْ حَيْثُ أَمَرَكُمُ اللَّهُ ۚ إِنَّ اللَّهَ يُحِبُّ التَّوَّابِينَ وَيُحِبُّ الْمُتَطَهِّرِينَ", lao: "ແລະພວກເຂົາຖາມເຈົ້າກ່ຽວກັບປະຈຳເດືອນ. ຈົ່ງກ່າວວ່າ: 'ມັນເປັນສິ່ງທີ່ບໍ່ສະອາດ.' ດັ່ງນັ້ນຈົ່ງຢູ່ห่างจากผู้หญิงໃນຊ່ວງປະຈຳເດືອນ. ແລະຢ່າເຂົ້າໃກ້ພວກນາງຈົນກວ່າພວກນາງຈະສະອາດ. ແລະເມື່ອພວກນາງສະອາດແລ້ວ, ຈົ່ງເຂົ້າຫາພວກນາງຈາກບ່ອນທີ່ອັລລໍຮ໌ໄດ້ສັ່ງ. ແທ້ຈິງອັລລໍຮ໌ทรงรักผู้ที่กลับใจและทรงรักผู้ที่ชำระล้างตัวเอง.", english: "And they ask you about menstruation. Say, 'It is harm, so keep away from wives during menstruation. And do not approach them until they are pure. And when they have purified themselves, then come to them from where Allah has ordained for you. Indeed, Allah loves those who are constantly repentant and loves those who purify themselves.'" },
  { number: "2:223", arabic: "نِسَاؤُكُمْ حَرْثٌ لَّكُمْ فَأْتُوا حَرْثَكُمْ أَنَّىٰ شِئْتُمْ ۖ وَقَدِّمُوا لِأَنfُsِكُمْ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّكُم mُّلَاقُوهُ ۗ وَبَشِّرِ الْمُؤْمِنِينَ", lao: "ພັນລະຍາຂອງພວກເຈົ້າຄືໄຮ່ນາຂອງພວກເຈົ້າ, ດັ່ງນັ້ນຈົ່ງເຂົ້າຫາໄຮ່ນາຂອງພວກເຈົ້າຕາມที่พวกเจ้าต้องการ. ແລະຈົ່ງສົ່ງຄວາມດີໄປລ່ວງຫນ້າສຳລັບຕົວເອງ. ແລະຈົ່ງຢຳເກງອັລລໍຮ໌ແລະຮູ້ໄວ້ວ່າພວກเจ้าจะพบพระองค์. และຈົ່ງแจ้งข่าวดีแก่ผู้ศรัทธา.", english: "Your wives are a place of sowing of seed for you, so come to your place of cultivation however you wish. And put forth [righteousness] for yourselves. And fear Allah and know that you will meet Him. And give good tidings to the believers." },
  { number: "2:224", arabic: "وَلَا تَjْعَلُوا اللَّهَ عُرْضَةً لِّأَيْمَانِكُمْ أَن تَبَرُّوا وَتَتَّقُوا وَتُصْلِحُوا بَيْنَ النَّاسِ ۚ وَاللَّهُ سَمِيعٌ عَلِيمٌ", lao: "ແລະຢ່າໃຊ້ອັລລໍຮ໌ເປັນຂໍ້ອ້າງໃນການສາບານຂອງພວກເຈົ້າເພື່ອที่จะບໍ່ทำความดี, ບໍ່ຢຳເກງ, ແລະບໍ່ไกล่เกลี่ยລະຫວ່າງຜູ້ຄົນ. ແລະອັລລໍຮ໌ทรงได้ยิน, ทรงรอบรู้.", english: "And do not make [your oath by] Allah an excuse against being righteous and fearing Allah and making peace among people. And Allah is Hearing and Knowing." },
  { number: "2:225", arabic: "لَّا يُؤَاخِذُكُمُ اللَّهُ بِاللَّغْوِ فِي أَيْمَانِكُمْ وَلَٰكِن يُؤَاخِذُكُم بِمَا كَسَبَتْ قُلُوبُكُمْ ۗ وَاللَّهُ غَفُورٌ حَلِيمٌ", lao: "ອັລລໍຮ໌ຈະບໍ່ລົງໂທດພວກເຈົ້າສຳລັບຄຳສາບານທີ່ບໍ່ຕັ້ງໃຈ, ແຕ່ຈະລົງໂທດພວກເຈົ້າສຳລັບສິ່ງທີ່ຫົວໃຈຂອງພວກເຈົ້າໄດ້ຕັ້ງໃຈ. ແລະອັລລໍຮ໌ทรงอภัย, ทรงอดทน.", english: "Allah does not impose blame upon you for what is unintentional in your oaths, but He imposes blame upon you for what your hearts have earned. And Allah is Forgiving and Forbearing." },
  { number: "2:226", arabic: "لِّلَّذِينَ يُؤْلُونَ مِن nِّsَائِهِمْ تَرَبُّصُ أَرْبَعَةِ أَشْهُرٍ ۖ فَإِن فَاءُوا فَإِنَّ اللَّهَ غَفُورٌ rَّحِيمٌ", lao: "ສຳລັບผู้ที่ສາບານว่าจะບໍ່มีເພດສຳພັນກັບພັນລະຍາຂອງພວກເຂົາ, ໃຫ້รอສີ່ເດືອນ. ແຕ່ຖ້າພວກເຂົາກັບຄືນມາ, ແທ້ຈິງອັລລໍຮ໌ทรงอภัย, ทรงเมตตา.", english: "For those who swear not to have sexual relations with their wives is a waiting time of four months, but if they return [to normal relations] - then indeed, Allah is Forgiving and Merciful." },
  { number: "2:227", arabic: "وَإِنْ عَزَمُوا الطَّلَاقَ فَإِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ", lao: "ແລະຖ້າພວກເຂົາຕັດສິນໃຈหย่า, ແທ້ຈິງອັລລໍຮ໌ทรงได้ยิน, ทรงรอบรู้.", english: "And if they decide on divorce - then indeed, Allah is Hearing and Knowing." },
  { number: "2:228", arabic: "وَالْمُطَلَّقَاتُ يَتَرَبَّصْنَ بِأَنfُsِهِنَّ ثَلَاثَةَ قُرُوءٍ ۚ وَلَا يَحِلُّ لَهُنَّ أَن يَكْتُمْنَ مَا خَلَقَ اللَّهُ فِي أَرْحَامِهِنَّ إِن كُنَّ يُؤْمِنَّ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۚ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذَٰلِكَ إِنْ أَرَادُوا إِصْلَاحًا ۚ وَلَهُنَّ مِثْلُ الَّذِي عَلَيْهِنَّ بِالْمَعْرُوfِ ۚ وَلِلرِّjَالِ عَلَيْهِنَّ دَرَjَةٌ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ", lao: "ຍິງທີ່ຖືກຢ່າร้างໃຫ້รอສາມຮອບປະຈຳເດືອນ. ແລະບໍ່ອະນຸຍາດໃຫ້ພວກນາງปกปิดສິ່ງที่อัลลอฮ์ทรงสร้างในมดลูกของพวกนาง, ຫາກພວກນາງເຊື່ອในอัลลอฮ์และวันสุดท้าย. ແລະສາມີຂອງພວກນາງມີສິດທິ์ที่จะรับพวกนางกลับຄືນໃນຊ່ວງນັ້ນ, ຫາກພວກເຂົາต้องการปรองดอง. ແລະພວກນາງມີສິດທິ์ເໝືອນດັ່ງຫນ້າที่ที่พวกนางມີอย่างเหมาะสม. ແຕ່ຜູ້ຊາຍມີລະດັບເໜືອພວກນາງ. ແລະອັລລໍຮ໌ทรงมีอำนาจ, ทรงมีปัญญา.", english: "Divorced women remain in waiting for three periods, and it is not lawful for them to conceal what Allah has created in their wombs if they believe in Allah and the Last Day. And their husbands have more right to take them back in this [period] if they want reconciliation. And due to the wives is similar to what is expected of them, according to what is reasonable. But the men have a degree over them [in responsibility and authority]. And Allah is Exalted in Might and Wise." },
  { number: "2:229", arabic: "الطَّلَاقُ مَرَّتَانِ ۖ فَإِمْسَاكٌ بِمَعْرُوfٍ أَوْ تَsْرِيحٌ بِإِحْسَانٍ ۗ وَلَا يَحِلُّ لَكُمْ أَن تَأْخُذُوا مِمَّا آتَيْتُمُوهُنَّ شَيْئًا إِلَّا أَن يَخَافَا أَلَّا يُقِيمَا حُdُودَ اللَّهِ ۖ فَإِنْ خِfْتُمْ أَلَّا يُقِيمَا حُdُودَ اللَّهِ فَلَا jُنَاحَ عَلَيْهِمَا فِيمَا افْتَدَتْ بِهِ ۗ تِلْكَ حُdُودُ اللَّهِ فَلَا تَعْتَدُوهَا ۚ وَمَن يَتَعَدَّ حُdُودَ اللَّهِ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ", lao: "ການຢ່າร้างສາມາດເຮັດໄດ້ສອງຄັ້ງ. ຫຼັງຈາກນັ້ນ, ໃຫ້ຮັກສາไว้ດ້ວຍຄວາມดีຫຼືປ່ອຍไปด้วยความดี. ແລະບໍ່ອະນຸຍາດໃຫ້พวกเจ้าเอาสิ่งໃດกลับคืนจากสิ่งที่พวกเจ้าได้ให้แก่นาง, ยกเว้นกรณีที่ทั้งสองฝ่ายกลัวว่าจะไม่สามารถรักษาขอบเขตของอัลลอฮ์ได้. ແຕ່ຖ້າພວກເຈົ້າກລົວວ່າພວກເຂົາจะไม่ສາມາດรักษาຂອບເຂດຂອງອັລລໍຮ໌ได้, ກໍບໍ່มีบาปสำหรับทั้งສອງฝ่ายในสิ่งที่นางใช้ไถ่ตัวเอง. ນັ້ນຄືຂອບເຂດຂອງອັລລໍຮ໌, ດັ່ງນັ້ນຢ່າລ່ວງເກີນมัน. ແລະຜູ້ໃດที่ล่วงเกินຂອບເຂດຂອງອັລລໍຮ໌, ພວກເຂົາຄືຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.", english: "Divorce is twice. Then, either keep [her] in an acceptable manner or release [her] with good treatment. And it is not lawful for you to take back anything of what you have given them unless both fear that they will not be able to keep [within] the limits of Allah. But if you fear that they will not keep [within] the limits of Allah, then there is no blame upon either of them concerning that by which she ransoms herself. These are the limits of Allah, so do not transgress them. And whoever transgresses the limits of Allah - it is those who are the wrongdoers." },
  { number: "2:230", arabic: "فَإِن طَلَّقَهَا فَلَا تَحِلُّ لَهُ مِن بَعْدُ حَتَّىٰ تَنكِحَ زَوْjًا غَيْرَهُ ۗ فَإِن طَلَّقَهَا فَلَا jُنَاحَ عَلَيْهِمَا أَن يَتَرَajَعَا إِن ظَنَّا أَن يُقِيمَا حُdُودَ اللَّهِ ۗ وَتِلْكَ حُdُودُ اللَّهِ يُbَيِّنُهَا لِقَوْmٍ يَعْلَمُونَ", lao: "ແລະຖ້າລາວຢ່ານາງ (ຄັ້ງທີສາມ), ນາງຈະບໍ່ຖືກອະນຸຍາດໃຫ້ລາວຫຼັງຈາກນັ້ນຈົນກວ່ານາງຈະແຕ່ງງານກັບສາມີຄົນອື່ນ. ແລະຖ້າຄົນหลังຢ່ານາງ, ກໍບໍ່มีบาปสำหรับทั้งສອງฝ่ายที่จะกลับมาຄືນດີກັນ, ຫາກພວກເຂົາຄິດວ່າจะສາມາດຮັກສາຂອບເຂດຂອງອັລລໍຮ໌ได้. ແລະນັ້ນຄືຂອບເຂດຂອງອັລລໍຮ໌, ພຣະອົງຊົງຊີ້ແຈງมันแก่ผู้คนที่รู้.", english: "And if he has divorced her [for the third time], then she is not lawful to him afterward until [after] she marries a husband other than him. And if the latter husband divorces her [or dies], there is no blame upon the woman and her former husband for returning to each other if they think that they can keep [within] the limits of Allah. These are the limits of Allah, which He makes clear to a people who know." },
  { number: "2:231", arabic: "وَإِذَا طَلَّقْتُمُ النِّsَاءَ فَبَلَغْنَ أَjَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوfٍ أَوْ سَرِّحُوهُنَّ بِمَعْرُوfٍ ۚ وَلَا تُمْsِكُوهُنَّ ضِرَarًا لِّتَعْتَدُوا ۚ وَمَن يَfْعَلْ ذَٰلِكَ فَقَدْ ظَلَمَ نَفْسَهُ ۚ وَلَا تَتَّخِذُوا آيَاتِ اللَّهِ هُزُوًا ۚ وَاذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ وَمَا أَنزَلَ عَلَيْكُم mِّنَ الْكِتَابِ وَالْحِكْمَةِ يَعِظُكُم بِهِ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ", lao: "ແລະເມື່ອພວກเจ้าหย่าผู้หญิงແລະพวกนางໃກ້ຈະຄົບກຳນົດ, ກໍໃຫ້ຮັກສາพวกนางไว้ด้วยความดีหรือปล่อยไปด้วยความดี. ແລະຢ່າຮັກສາพวกนางไว้เพื่อทำร้าย, ເພື່ອล่วงเกิน. ແລະຜູ້ໃດທີ່ทำเช่นນັ້ນ, ລາວກໍໄດ້ທຳຮ້າຍຕົວເອງ. ແລະຢ່າเอาอายะของอัลลอฮ์มาเป็นเรื่องล้อเล่น. ແລະຈົ່ງລະນຶກເຖິງຄວາມโปรดปรานของอัลลอฮ์ที่มีต่อพวกเจ้าແລະສິ່ງທີ່ພຣະອົງໄດ້ເປີດເຜີຍລົງມາให้ພວກเจ้าจากคัมภีร์และปัญญา, ເພື່ອเตือนสติพวกเจ้า. ແລະຈົ່ງຢຳເກງອັລລໍຮ໌ແລະຮູ້ໄວ້ວ່າອັລລໍຮ໌ทรงรอบรู้ทุกสิ่ง.", english: "And when you divorce women and they have [nearly] fulfilled their term, either retain them according to acceptable terms or release them according to acceptable terms, and do not retain them to harm [them] or transgress. And whoever does that has certainly wronged himself. And do not take the verses of Allah in jest. And remember the favor of Allah upon you and what has been revealed to you of the Book and wisdom by which He instructs you. And fear Allah and know that Allah is Knowing of all things." },
  { number: "2:232", arabic: "وَإِذَا طَلَّقْتُمُ النِّsَاءَ فَبَلَغْنَ أَjَلَهُنَّ فَلَا تَعْضُلُوهُنَّ أَن يَنكِحْنَ أَزْوَajَهُنَّ إِذَا تَرَاضَوْا بَيْنَهُم بِالْمَعْرُوfِ ۗ ذَٰلِكَ يُوعَظُ بِهِ مَن كَانَ مِنكُمْ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۗ ذَٰلِكُمْ أَزْكَىٰ لَكُمْ وَأَطْهَرُ ۚ وَاللَّهُ يَعْلَمُ وَأَنتُمْ لَا تَعْلَمُونَ", lao: "ແລະເມື່ອພວກเจ้าหย่าผู้หญิงແລະพวกนางได้ครบกำหนดแล้ว, ຢ່າຂັດຂວາງພວກນາງຈາກການແຕ່ງງານກັບສາມີຂອງພວກນາງ, ເມື່ອພວກເຂົາตกลงกันอย่างเหมาะสม. ນັ້ນເປັນຄຳຕັກເຕືອນสำหรับผู้ที่เชื่อในอัลลอฮ์และวันสุดท้ายໃນກຸ່ມของพวกเจ้า. ນັ້ນสะอาดກວ່າและบริสุทธิ์ກວ່າสำหรับพวกเจ้า. ແລະອັລລໍຮ໌ทรงรู้, ແຕ່พวกเจ้าບໍ່ຮູ້.", english: "And when you divorce women and they have fulfilled their term, do not prevent them from remarrying their [former] husbands if they agree among themselves on an acceptable basis. That is instructed to whoever of you believes in Allah and the Last Day. That is better for you and purer, and Allah knows and you know not." },
  { number: "2:233", arabic: "وَالْوَالِدَاتُ يُرْضِعْنَ أَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ ۖ لِمَنْ أَرَادَ أَن يُتِمَّ الرَّضَاعَةَ ۚ وَعَلَى الْمَوْلُوdِ لَهُ رِزْقُهُنَّ وَكِsْوَتُهُنَّ بِالْمَعْرُوfِ ۚ لَا تُكَلَّfُ نَفْسٌ إِلَّا وُsْعَهَا ۚ لَا تُضَarَّ وَالِدَةٌ بِوَلَدِهَا وَلَا مَوْلُوdٌ لَّهُ بِوَلَدِهِ ۚ وَعَلَى الْوَarِثِ مِثْلُ ذَٰلِكَ ۗ فَإِنْ أَرَادَا فِصَالًا عَن تَرَاضٍ mِّنْهُمَا وَتَشَاوُرٍ فَلَا jُنَاحَ عَلَيْهِمَا ۗ وَإِنْ أَرَدتُّمْ أَن تَsْتَرْضِعُوا أَوْلَادَكُمْ فَلَا jُنَاحَ عَلَيْكُمْ إِذَا سَلَّمْتُم mَّا آتَيْتُم بِالْمَعْرُوfِ ۗ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ", lao: "ແມ່ໃຫ້ລູກກິນນົມເປັນເວລາສອງປີເຕັມ, ສຳລັບผู้ที่ต้องการจะให้นมครบกำหนด. ແລະໜ້າທີ່ຂອງພໍ່ຄືການໃຫ້ອາຫານและເຄື່ອງນຸ່ງແກ່พวกนางอย่างเหมาะสม. ບໍ່ມີໃຜຈະຖືກບັງຄັບເກີນຄວາມສາມາດ. ແມ່ບໍ່ຄວນຖືກທຳຮ້າຍຍ້ອນລູກຂອງນາງ, และพ่อก็ไม่ควรถูกทำร้ายຍ້ອນລູກຂອງเขา. ແລະໜ້າທີ່ເໝືອນກັນນັ້ນຢູ່ກັບຜູ້ສືບທອດ. ແຕ່ຖ້າທັງສອງฝ่ายต้องการหย่านมด้วยความยินยอมและปรึกษาหารือ, ກໍບໍ່ມີบาปสำหรับพวกเขา. ແລະຖ້າພວກเจ้าต้องการหาแม่นมให้ลูก, ກໍບໍ່มีบาปสำหรับพวกเจ้า, ຫາກพวกเจ้าจ่ายในสิ่งที่พวกเจ้าได้สัญญาไว้อย่างเหมาะสม. ແລະຈົ່ງຢຳເກງອັລລໍຮ໌ແລະຮູ້ໄວ້ວ່າອັລລໍຮ໌ทรงเห็นในสิ่งที่พวกเจ้าทำ.", english: "Mothers may breastfeed their children two complete years for whoever wishes to complete the nursing [period]. Upon the father is the mothers' provision and their clothing according to what is acceptable. No person is charged with more than his capacity. No mother should be harmed through her child, and no father through his child. And upon the [father's] heir is [a duty] like that [of the father]. And if they both desire weaning through mutual consent from both of them and consultation, there is no blame upon either of them. And if you wish to have your children nursed by a substitute, there is no blame upon you as long as you give payment according to what is acceptable. And fear Allah and know that Allah is Seeing of what you do." },
  { number: "2:234", arabic: "وَالَّذِينَ يُتَوَfَّوْنَ مِنكُمْ وَيَذَرُونَ أَزْوَajًا يَتَرَبَّصْنَ بِأَنfُsِهِنَّ أَرْبَعَةَ أَشْهُرٍ وَعَشْرًا ۖ فَإِذَا بَلَغْنَ أَjَلَهُنَّ فَلَا jُنَاحَ عَلَيْكُمْ فِيمَا فَعَلْنَ فِي أَنfُsِهِنَّ بِالْمَعْرُوfِ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ", lao: "ແລະຜູ້ທີ່ຕາຍໃນກຸ່ມຂອງພວກเจ้าและทิ้งภรรยาไว้, ພວກນາງໃຫ້รอສີ່ເດືອນກັບສິບວັນ. ແລະເມື່ອพวกนางครบกำหนดแล้ว, ກໍບໍ່มีบาปสำหรับพวกเจ้าในสิ่งที่พวกนางทำกับตัวเองอย่างเหมาะสม. ແລະອັລລໍຮ໌ทรงรอบรู้ในสิ่งที่พวกเจ้าทำ.", english: "And those who are taken in death among you and leave wives behind - they, [the wives, shall] wait four months and ten [days]. And when they have fulfilled their term, then there is no blame upon you for what they do with themselves in an acceptable manner. And Allah is [fully] Acquainted with what you do." },
  { number: "2:235", arabic: "وَلَا jُنَاحَ عَلَيْكُمْ فِيمَا عَرَّضْتُم بِهِ مِنْ خِطْبَةِ النِّsَاءِ أَوْ أَكْنَنتُمْ فِي أَنfُsِكُمْ ۚ عَلِمَ اللَّهُ أَنَّكُمْ سَتَذْكُرُونَهُنَّ وَلَٰكِن لَّا تُوَاعِدُوهُنَّ سِرًّا إِلَّا أَن تَقُولُوا قَوْلًا mَّعْرُوفًا ۚ وَلَا تَعْزِمُوا عُقْدَةَ النِّكَاحِ حَتَّىٰ يَبْلُغَ الْكِتَابُ أَjَلَهُ ۚ وَاعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي أَنfُsِكُمْ فَاحْذَرُوهُ ۚ وَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ حَلِيمٌ", lao: "ແລະບໍ່ມີบาปสำหรับพวกเจ้าในสิ่งที่พวกเจ้าบอกใบ้ในการขอແຕ່ງງານກັບผู้หญิงหรือเก็บไว้ในใจ. ອັລລໍຮ໌ຮູ້ວ່າพวกเจ้าจะระลึกถึงพวกนาง. ແຕ່ຢ່າສັນຍາลับໆກັບพวกนาง, ยกเว้นจะกล่าวคำพูดที่เหมาะสม. และอย่าตัดสินใจແຕ່ງງານຈົນກວ່າກຳหนดเวลาจะสิ้นสุด. ແລະຮູ້ໄວ້ວ່າອັລລໍຮ໌รู้สิ่งที่อยู่ในใจของพวกเจ้า, ດັ່ງນັ້ນຈົ່ງระวังพระองค์. ແລະຮູ້ໄວ້ວ່າອັລລໍຮ໌ทรงอภัย, ทรงอดทน.", english: "There is no blame upon you for that to which you [indirectly] allude concerning a proposal to women or for what you conceal within yourselves. Allah knows that you will have them in mind. But do not promise them secretly except for saying a proper saying. And do not determine to undertake a marriage contract until the decreed period reaches its end. And know that Allah knows what is within yourselves, so beware of Him. And know that Allah is Forgiving and Forbearing." },
  { number: "2:236", arabic: "لَّا jُنَاحَ عَلَيْكُمْ إِن طَلَّقْتُمُ النِّsَاءَ مَا لَمْ تَمَسُّوهُنَّ أَوْ تَfْرِضُوا لَهُنَّ فَرِيضَةً ۚ وَمَتِّعُوهُنَّ عَلَى الْمُوسِعِ قَدَرُهُ وَعَلَى الْمُقْتِرِ قَدَرُهُ ۖ مَتَاعًا بِالْمَعْرُوfِ ۖ حَقًّا عَلَى الْمُحْسِنِينَ", lao: "ບໍ່ມີบาปสำหรับพวกเจ้าຖ້າພວກเจ้าหย่าผู้หญิงກ່ອນที่จะมีເພດສຳພັນກັບพวกนางหรือກຳหนดสินสอดให้ພວກนาง. ແຕ່ຈົ່ງให้ของขวัญແກ່ພວກนาง - ຄົນรวยตามความสามารถ, ແລະຄົນจนตามความสามารถ - ຂອງຂວັນທີ່ເໝາະສົມ. ນີ້ເປັນພັນທະສຳລັບผู้ที่ทำความดี.", english: "There is no blame upon you if you divorce women you have not touched nor specified for them an obligation. But give them a gift of compensation - the wealthy according to his means and the poor according to his means - a gift of compensation according to what is acceptable, a duty upon the doers of good." },
  { number: "2:237", arabic: "وَإِن طَلَّقْتُمُوهُنَّ مِن قَبْلِ أَن تَمَسُّوهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيضَةً فَنِصْfُ مَا فَرَضْتُمْ إِلَّا أَن يَعْfُونَ أَوْ يَعْfُوَ الَّذِي بِيَدِهِ عُقْدَةُ النِّكَاحِ ۚ وَأَن تَعْfُوا أَقْرَبُ لِلتَّقْوَىٰ ۚ وَلَا تَnsَوُا الْfَضْلَ بَيْنَكُمْ ۚ إِنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ", lao: "ແລະຖ້າພວກเจ้าหย่าພວກนางກ່ອນที่จะมีເພດສຳພັນກັບພວກนาง, ແຕ່ได้กำหนดสินสอดให้ພວກนางแล้ว, ກໍให้จ่ายครึ่งหนึ่งของสิ่งที่ได้กำหนดไว้, ยกเว้นພວກນາງຈະຍົກເວັ້ນหรือຜູ້ທີ່ຖືສັນຍາແຕ່ງງານຈະຍົກເວັ້ນ. ແລະການຍົກເວັ້ນໃກ້ຄຽງກັບຄວາມຢຳເກງ. ແລະຢ່າลืมความดีต่อกัน. ແທ້ຈິງອັລລໍຮ໌ทรงเห็นในสิ่งที่พวกเจ้าทำ.", english: "And if you divorce them before you have touched them and you have already specified for them an obligation, then [give] half of what you specified - unless they forego the right or the one in whose hand is the marriage contract foregoes it. And to forego it is nearer to righteousness. And do not forget graciousness between you. Indeed Allah, of what you do, is Seeing." },
  { number: "2:238", arabic: "حَافِظُوا عَلَى الصَّلَوَاتِ وَالصَّلَاةِ الْوُsْطَىٰ وَقُومُوا لِلَّهِ قَانِتِينَ", lao: "ຈົ່ງຮັກສາການລະໝາດ, ໂດຍສະເພາະການລະໝາດກາງ, ແລະຈົ່ງຢືນຕໍ່ຫນ້າອັລລໍຮ໌ດ້ວຍຄວາມຍອມຈຳນົນ.", english: "Maintain with care the [obligatory] prayers and [in particular] the middle prayer and stand before Allah, devoutly obedient." },
  { number: "2:239", arabic: "فَإِنْ خِfْتُمْ فَرِjَالًا أَوْ رُكْبَانًا ۖ فَإِذَا أَمِنتُمْ فَاذْkُرُوا اللَّهَ كَمَا عَلَّمَكُم mَّا لَمْ تَكُونُوا تَعْلَمُونَ", lao: "ແລະຖ້າພວກเจ้ากลัว, ກໍລະໝາດในຂະນະທີ່ຍ່າງຫຼືຂີ່ມ້າ. ແຕ່ເມື່ອພວກเจ้าปลอดภัย, ຈົ່ງລະນຶກເຖິງອັລລໍຮ໌ເໝືອນດັ່ງທີ່ພຣະອົງໄດ້ສອນพวกเจ้าในสิ่งที่พวกเจ้าไม่เคยรู้.", english: "And if you fear [an enemy, then pray] on foot or riding. But when you are secure, then remember Allah [in prayer], as He has taught you that which you did not [previously] know." },
  { number: "2:240", arabic: "وَالَّذِينَ يُتَوَfَّوْنَ مِنكُمْ وَيَذَرُونَ أَزْوَajًا وَصِيَّةً لِّأَزْوَajِهِم mَّتَاعًا إِلَى الْحَوْلِ غَيْرَ إِخْرَajٍ ۚ فَإِنْ خَرَjْنَ فَلَا jُنَاحَ عَلَيْكُمْ فِي مَا فَعَلْنَ فِي أَنfُsِهِنَّ مِن mَّعْرُوفٍ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ", lao: "ແລະຜູ້ທີ່ຕາຍในກຸ່ມຂອງพวกเจ้าและทิ้งภรรยาไว้, ໃຫ້ເຮັດພິນัยກຳให้ภรรยาຂອງพวกเขา - ຄ່າລ້ຽງດູໜຶ່ງປີโดยไม่ถูกขับไล่. ແຕ່ຖ້າพวกนางອອກไป, ກໍບໍ່ມີบาปสำหรับพวกเจ้าในสิ่งที่พวกนางทำกับตัวเองอย่างเหมาะสม. ແລະອັລລໍຮ໌ทรงมีอำนาจ, ทรงมีปัญญา.", english: "And those who are taken in death among you and leave wives behind - for their wives is a bequest: maintenance for one year without turning [them] out. But if they leave [of their own accord], then there is no blame upon you for what they do with themselves in an acceptable way. And Allah is Exalted in Might and Wise." },
  { number: "2:241", arabic: "وَلِلْمُطَلَّقَاتِ مَتَاعٌ بِالْمَعْرُوفِ ۖ حَقًّا عَلَى الْمُتَّقِينَ", lao: "ແລະສຳລັບຍິງທີ່ຖືກຢ່າร้าง, ໃຫ້มีค่าเลี้ยงดูอย่างเหมาะสม. ນີ້ເປັນພັນທະສຳລັບผู้ที่ยำเกรง.", english: "And for divorced women is a provision according to what is acceptable - a duty upon the righteous." },
  { number: "2:242", arabic: "كَذَٰلِكَ يُbَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ", lao: "ເຊັ່ນນັ້ນແຫຼະ, ອັລລໍຮ໌ຊົງຊີ້ແຈງອາຍະຕ່າງໆของพระองค์แก่พวกเจ้า, ເພື່ອพวกเจ้าจะได้ຄິດ.", english: "Thus does Allah make clear to you His verses that you might use reason." },
  { number: "2:243", arabic: "أَلَمْ تَرَ إِلَى الَّذِينَ خَرَjُوا مِن دِيَarِهِمْ وَهُمْ أُلُوfٌ حَذَرَ الْمَوْتِ فَقَالَ لَهُمُ اللَّهُ مُوتُوا ثُمَّ أَحْيَاهُمْ ۚ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَشْكُرُونَ", lao: "ເຈົ້າບໍ່ເຫັນບໍຜູ້ที่หนีออกจากบ้านຂອງพวกเขา - ພວກເຂົາມີຈຳນວນหลายพันຄົນ - ຍ້ອນຢ້ານຄວາມຕາຍ? ແລ້ວອັລລໍຮ໌ໄດ້ກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງຕາຍ.' ຫຼັງຈາກນັ້ນ, ພຣະອົງໄດ້ເຮັດໃຫ້ພວກເຂົາມີຊີວິດຄືນມາ. ແທ້ຈິງອັລລໍຮ໌ทรงเป็นเจ้าของความโปรดปรานต่อผู้คน, ແຕ່ສ່ວນຫຼາຍของผู้คนບໍ່ຂອບໃຈ.", english: "Have you not considered those who left their homes in many thousands, fearing death? Allah said to them, 'Die'; then He restored them to life. And Allah is full of bounty to the people, but most of the people do not show gratitude." },
  { number: "2:244", arabic: "وَقَاتِلُوا فِي سَبِيلِ اللَّهِ وَاعْلَمُوا أَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ", lao: "ແລະຈົ່ງຕໍ່ສູ້ในหนทางของอัลลอฮ์และຮູ້ໄວ້ວ່າອັລລໍຮ໌ทรงได้ยิน, ทรงรอบรู้.", english: "And fight in the cause of Allah and know that Allah is Hearing and Knowing." },
  { number: "2:245", arabic: "mَّن ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا فَيُضَاعِfَهُ لَهُ أَضْعَافًا كَثِيرَةً ۚ وَاللَّهُ يَقْبِضُ وَيَبْsُطُ وَإِلَيْهِ تُرْjَعُونَ", lao: "ໃຜຈະໃຫ້ອັລລໍຮ໌ກູ້ຢືມທີ່ດີ, ແລ້ວພຣະອົງຈະເພີ່ມມັນให้เขาหลายเท่า? ແລະອັລລໍຮ໌ทรงควบคุมและทรงขยาย, ແລະພວກเจ้าจะถูกนำกลับไปหาพระองค์.", english: "Who is it that would loan Allah a goodly loan so He may multiply it for him many times over? And it is Allah who withholds and grants abundance, and to Him you will be returned." },
  { number: "2:246", arabic: "أَلَمْ تَرَ إِلَى الْمَلَإِ مِن بَنِي إِسْرَائِيلَ مِن بَعْدِ مُوسَىٰ إِذْ قَالُوا لِنَبِيٍّ لَّهُمُ ابْعَثْ لَنَا مَلِكًا نُّقَاتِلْ فِي سَبِilِ اللَّهِ ۖ قَالَ هَلْ عَسَيْتُمْ إِن كُتِبَ عَلَيْكُمُ الْقِتَالُ أَلَّا تُقَاتِلُوا ۖ قَالُوا وَمَا لَنَا أَلَّا نُقَاتِلَ فِي سَبِilِ اللَّهِ وَقَدْ أُخْرِjْنَا مِن دِيَarِنَا وَأَبْنَائِنَا ۖ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا إِلَّا قَلِيلًا mِّنْهُمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ", lao: "ເຈົ້າບໍ່ເຫັນບໍຜู้นำของລູກຫຼານຂອງອິດສະຣາເອວຫຼັງຈາກມູຊາ? ເມື່ອพวกเขากล่าวกับศาสดาของพวกเขาว่า: 'ຈົ່ງແຕ່ງตั้งກະສັດให้ພວກເຮົາ, เราจะต่อสู้ในหนทางของอัลลอฮ์.' ລາວໄດ້ກ່າວວ່າ: 'ຖ້າການຕໍ່ສູ້ຖືກກຳນົດໃຫ້ພວກเจ้า, ພວກเจ้าจะไม่ต่อสู้ບໍ?' ພວກເຂົາກ່າວວ່າ: 'ເປັນຫຍັງພວກເຮົາจะไม่ต่อสู้ในหนทางของอัลลอฮ์, ທັງທີ່ພວກເຮົາถูกຂັບໄລ່ອອກจากบ้านและลูกหลานของเรา?' ແຕ່ເມື່ອການຕໍ່ສູ້ຖືກກຳນົດให้พวกเขา, ພວກເຂົາກໍຫັນຫຼັງกลับยกเว้นຈຳນວນໜ້ອຍ. ແລະອັລລໍຮ໌ทรงຮູ້ຈັກผู้ที่ไม่ยุติธรรม.", english: "Have you not considered the assembly of the Children of Israel after [the time of] Moses when they said to a prophet of theirs, 'Send to us a king, and we will fight in the way of Allah'? He said, 'Would you perhaps not fight if fighting was prescribed for you?' They said, 'And why should we not fight in the cause of Allah when we have been driven out from our homes and from our children?' But when fighting was prescribed for them, they turned away, except for a few of them. And Allah is Knowing of the wrongdoers." },
  { number: "2:247", arabic: "وَقَالَ لَهُمْ نَبِيُّهُمْ إِنَّ اللَّهَ قَدْ بَعَثَ لَكُمْ طَالُوتَ مَلِكًا ۚ قَالُوا أَنَّىٰ يَكُونُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ أَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً mِّنَ الْمَالِ ۚ قَالَ إِنَّ اللَّهَ اصْطَفَاهُ عَلَيْكُمْ وَزَادَهُ بَسْطَةً فِي الْعِلْمِ وَالْjِسْمِ ۖ وَاللَّهُ يُؤْتِي مُلْكَهُ مَن يَشَاءُ ۚ وَاللَّهُ وَاسِعٌ عَلِيمٌ", lao: "ແລະສາດສະດາຂອງພວກເຂົາໄດ້ກ່າວແກ່ພວກເຂົາວ່າ: 'ແທ້ຈິງອັລລໍຮ໌ໄດ້ແຕ່ງตั้งຕໍລູຕເປັນກະສັດໃຫ້ພວກเจ้า.' ພວກເຂົາກ່າວວ່າ: 'ລາວຈະມີອຳນາດເໜືອພວກເຮົາໄດ້ແນວໃດ, ທັງທີ່ພວກເຮົາມີສິດທິ์ໃນອຳນາດมากกว่าລາວ, ແລະລາວບໍ່ได้รับทรัพย์สินมากมาย?' ລາວໄດ້ກ່າວວ່າ: 'ແທ້ຈິງອັລລໍຮ໌ໄດ້ເລືອກລາວເໜືອพวกเจ้าແລະໄດ້ເພີ່ມความກວ້າງໃຫຍ່ໃນຄວາມຮູ້และຮ່າງກາຍให้ລາว. ແລະອັລລໍຮ໌ทรงให้ອຳນາດຂອງพระองค์แก่ผู้ที่พระองค์ทรงประสงค์. ແລະອັລລໍຮ໌ทรงກວ້າງໃຫຍ່, ทรงรอบรู้.'", english: "And their prophet said to them, 'Indeed, Allah has sent to you Saul as a king.' They said, 'How can he have kingship over us while we are more worthy of kingship than him and he has not been given any measure of wealth?' He said, 'Indeed, Allah has chosen him over you and has increased him abundantly in knowledge and stature. And Allah gives His sovereignty to whom He wills. And Allah is all-Encompassing [in favor] and Knowing.'" },
  { number: "2:248", arabic: "وَقَالَ لَهُمْ نَبِيُّهُمْ إِنَّ آيَةَ مُلْكِهِ أَن يَأْتِيَكُمُ التَّابُوتُ فِيهِ سَكِينَةٌ mِّن rَّbِّكُمْ وَبَقِيَّةٌ mِّمَّا تَرَكَ آلُ مُوسَىٰ وَآلُ هَarُونَ تَحْمِلُهُ الْمَلَائِكَةُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لَّكُمْ إِن كُنتُم mُّؤْمِنِينَ", lao: "ແລະສາດສະດາຂອງພວກເຂົາໄດ້ກ່າວແກ່ພວກເຂົາວ່າ: 'ແທ້ຈິງ, ສັນຍານຂອງອຳນາດຂອງລາວຄືຫີບສັນຍາຈະມາກັບพวกเจ้า, ໃນນັ້ນມີຄວາມสงบจากพระเจ้าของพวกเจ้าແລະສິ່ງທີ່เหลือจากຄອບຄົວຂອງມູซາແລະຄອບຄົວຂອງຮາຣູນ, ໂດຍມີທູດสวรรค์แบกมา. ແທ້ຈິງໃນນັ້ນມີສັນຍານສຳລັບพวกเจ้า, ຫາກພວກเจ้าເປັນຜູ້ศรัทธา.'", english: "And their prophet said to them, 'Indeed, a sign of his kingship is that the Ark of the Covenant will come to you in which is assurance from your Lord and a remnant of what the family of Moses and the family of Aaron had left, carried by the angels. Indeed in that is a sign for you, if you are believers.'" },
  { number: "2:249", arabic: "فَلَمَّا فَصَلَ طَالُوتُ بِالْjُنُوdِ قَالَ إِنَّ اللَّهَ مُbْتَلِيكُم بِنَهَرٍ فَمَن شَرِبَ مِنْهُ فَلَيْsَ مِنِّي وَمَن لَّمْ يَطْعَمْهُ فَإِنَّهُ مِنِّي إِلَّا مَنِ اغْتَرَfَ غُرْfَةً بِيَدِهِ ۚ فَشَرِبُوا مِنْهُ إِلَّا قَلِيلًا mِّنْهُمْ ۚ فَلَمَّا jَاوَزَهُ هُوَ وَالَّذِينَ آمَنُوا مَعَهُ قَالُوا لَا طَاقَةَ لَنَا الْيَوْمَ بِjَالُوتَ وَjُنُوdِهِ ۚ قَالَ الَّذِينَ يَظُنُّونَ أَنَّهُم mُّلَاقُو اللَّهِ كَم mِّن فِئَةٍ قَلِيلَةٍ غَلَبَتْ فِئَةً كَثِيرَةً بِإِذْنِ اللَّهِ ۗ وَاللَّهُ مَعَ الصَّابِرِينَ", lao: "ແລະເມື່ອຕໍລູຕອອກເດີນທາງພ້ອມກັບທະຫານ, ລາວໄດ້ກ່າວວ່າ: 'ແທ້ຈິງອັລລໍຮ໌ຈະທົດສອບพวกเจ้าด้วยแม่น้ำ. ຜູ້ໃດที่ดื่มจากมัน, ກໍບໍ່ແມ່ນพวกข้า. ແລະຜູ້ໃດທີ່ไม่ชิมมัน, ກໍเป็นพวกข้า, ยกเว้นผู้ที่ตักดื่มด้วยมือ.' ແຕ່ພວກເຂົາກໍดื่มจากมันยกเว้นຈຳນວນໜ້ອຍ. ແລະເມື່ອລາວແລະຜູ້ที่ศรัทธาพร้อมกับเขาข้ามไป, ພວກເຂົາກ່າວວ່າ: 'ມື້ນີ້ພວກເຮົາບໍ່ມີກຳລັງຕໍ່ສູ້กับโกลิอัทและທະຫານของเขา.' ແຕ່ผู้ที่ແນ่ใจว่าจะพบอัลลอฮ์ກ່າວວ່າ: 'ກຸ່ມນ້ອຍຈັກກຸ່ມທີ່เอาชนะກຸ່ມໃຫຍ່ດ້ວຍການອະນຸຍາດຂອງອັລລໍຮ໌?' ແລະອັລລໍຮ໌ຢູ່ກັບผู้ที่อดทน.", english: "And when Saul went forth with the soldiers, he said, 'Indeed, Allah will be testing you with a river. So whoever drinks from it is not of me, and whoever does not taste it is indeed of me, excepting one who takes [from it] in the hollow of his hand.' But they drank from it, except a few of them. Then when he had crossed it along with those who had believed with him, they said, 'There is no power for us today against Goliath and his soldiers.' But those who were certain that they would meet Allah said, 'How many a small company has overcome a large company by permission of Allah. And Allah is with the patient.'" },
  { number: "2:250", arabic: "وَلَمَّا بَرَزُوا لِjَالُوتَ وَjُنُوdِهِ قَالُوا رَبَّnَا أَfْرِغْ عَلَيْنَا صَبْرًا وَثَبِّتْ أَقْدَامَنَا وَانصُرْنَا عَلَى الْقَوْmِ الْكَافِرِينَ", lao: "ແລະເມື່ອພວກເຂົາเผชิญหน้ากับโกลิอัทและทหารของเขา, ພວກເຂົາກ່າວວ່າ: 'ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້เทความอดทนลงมาให้ພວກເຮົາ, ແລະເຮັດໃຫ້เท้าของพวกเรามั่นคง, ແລະຊ່ວຍให้พวกเราเอาชนะກຸ່ມผู้ปฏิเสธ.'", english: "And when they went forth to [face] Goliath and his soldiers, they said, 'Our Lord, pour upon us patience and make firm our feet and give us victory over the disbelieving people.'" },
  { number: "2:251", arabic: "فَهَزَمُوهُم بِإِذْنِ اللَّهِ وَقَتَلَ دَاوُوdُ jَالُوتَ وَآتَاهُ اللَّهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهُ مِمَّا يَشَاءُ ۗ وَلَوْلَا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُم بِbَعْضٍ لَّfَسَدَتِ الْأَرْضُ وَلَٰكِنَّ اللَّهَ ذُو فَضْلٍ عَلَى الْعَالَمِينَ", lao: "ດັ່ງນັ້ນພວກເຂົາຈຶ່ງเอาชนะพวกเขาด้วยการอนุมัติของอัลลอฮ์. ແລະດາວິດໄດ້ຂ້າโกลิอัท, ແລະອັລລໍຮ໌ໄດ້ໃຫ້ອຳນາດແລະປັນຍາແກ່ລາວແລະສອນລາວในสิ่งที่พระองค์ทรงประสงค์. ແລະຖ້າບໍ່ແມ່ນการที่อัลลอฮ์ทรงขับไล่ผู้คนบางคนด้วยคนอื่น, แผ่นดินກໍจะเสื่อมทราม. ແຕ່ອັລລໍຮ໌ทรงเป็นเจ้าของความโปรดปรานต่อโลก.", english: "So they defeated them by permission of Allah, and David killed Goliath, and Allah gave him kingship and wisdom and taught him from that which He willed. And if it were not for Allah checking [some] people by means of others, the earth would have been corrupted, but Allah is full of bounty to the worlds." },
  { number: "2:252", arabic: "تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ ۚ وَإِنَّكَ لَمِنَ الْمُرْسَلِينَ", lao: "ນັ້ນຄືອາຍະຕ່າງໆຂອງອັລລໍຮ໌, เราอ่านมันให้เจ้าฟังด้วยความจริง. และแท้จริง, เจ้าเป็นหนึ่งในศาสดา.", english: "These are the verses of Allah which We recite to you, [O Muhammad], in truth. And indeed, you are from among the messengers." },
  { number: "2:253", arabic: "تِلْكَ الرُّsُلُ فَضَّلْنَا بَعْضَهُمْ عَلَىٰ بَعْضٍ ۘ مِّنْهُم mَّن كَلَّمَ اللَّهُ ۖ وَرَفَعَ بَعْضَهُمْ دَرَjَاتٍ ۚ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْbَيِّنَاتِ وَأَيَّdْنَاهُ بِرُوحِ الْقُدُسِ ۗ وَلَوْ شَاءَ اللَّهُ مَا اقْتَتَلَ الَّذِينَ مِن بَعْدِهِم mِّن بَعْدِ مَا jَاءَتْهُمُ الْbَيِّنَاتُ وَلَٰكِنِ اخْتَلَفُوا فَمِنْهُم mَّنْ آمَنَ وَمِنْهُم mَّن كَفَرَ ۚ وَلَوْ شَاءَ اللَّهُ مَا اقْتَتَلُوا وَلَٰكِنَّ اللَّهَ يَfْعَلُ مَا يُرِيدُ", lao: "ເຫຼົ່ານັ້ນຄືສາດສະດາ, ພວກເຮົາໄດ້ຍົກຍ້ອງບາງຄົນເໜືອຄົນອື່ນ. ໃນກຸ່ມຂອງພວກເຂົາມີผู้ที่อัลลอฮ์ทรงตรัสด้วย, ແລະພຣະອົງໄດ້ຍົກບາງຄົນຂຶ້ນหลายระดับ. ແລະເຮົາໄດ້ໃຫ້ຫຼັກຖານທີ່ຊັດເຈນແກ່ອີຊາລູກຂອງມາຣີຍຳແລະສະໜັບສະໜູນລາວด้วยวิญญาณบริสุทธิ์. ແລະຖ້າອັລລໍຮ໌ປະສົງ, ຜູ້ທີ່ຢູ່ຫຼັງຈາກພວກເຂົາจะไม่ต่อสู้กันหลังจากที่หลักฐานที่ชัดเจนได้มาถึงพวกเขาแล้ว. ແຕ່ພວກເຂົາຂັດແຍ່ງກັນ, ແລະໃນກຸ່ມຂອງພວກເຂົາມີผู้ที่ศรัทธาແລະຜູ້ที่ปฏิเสธ. ແລະຖ້າອັລລໍຮ໌ປະສົງ, ພວກເຂົາจะไม่ต่อสู้กัน, ແຕ່ອັລລໍຮ໌ทรงทำในสิ่งที่พระองค์ทรงประสงค์.", english: "Those messengers - some of them We caused to exceed others. Among them were those to whom Allah spoke, and He raised some of them in degree. And We gave Jesus, the Son of Mary, clear proofs, and We supported him with the Pure Spirit. If Allah had willed, those [generations] who came after them would not have fought each other after the clear proofs had come to them. But they differed, and some of them believed and some of them disbelieved. And if Allah had willed, they would not have fought each other, but Allah does what He intends." },
  { number: "2:254", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا أَنfِقُوا مِمَّا رَزَقْنَاكُم mِّن قَبْلِ أَن يَأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيهِ وَلَا خُلَّةٌ وَلَا شَفَاعَةٌ ۗ وَالْكَافِرُونَ هُمُ الظَّالِمُونَ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຈົ່ງບໍລິຈາກຈາກສິ່ງທີ່ເຮົາໄດ້ໃຫ້ແກ່พวกเจ้าກ່ອນທີ່ວັນໜຶ່ງจะมาถึง, ວັນທີ່ບໍ່ມີການຊື້ຂາຍ, ບໍ່ມີมิตรภาพ, ແລະບໍ່ມີการไกล่เกลี่ย. ແລະຜູ້ປະຕິເສດຄືຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.", english: "O you who have believed, spend from that which We have provided for you before there comes a Day in which there is no exchange and no friendship and no intercession. And the disbelievers - they are the wrongdoers." },
  { number: "2:255", arabic: "اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ الْحَيُّ الْقَيُّومُ ۚ لَا تَأْخُذُهُ سِنَةٌ وَلَا نَوْمٌ ۚ لَّهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ مَن ذَا الَّذِي يَشْfَعُ عِندَهُ إِلَّا بِإِذْنِهِ ۚ يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْfَهُمْ ۖ وَلَا يُحِيطُونَ بِشَيْءٍ mِّنْ عِلْمِهِ إِلَّا بِمَا شَاءَ ۚ وَsِعَ كُرْسِيُّهُ السَّمَاوَاتِ وَالْأَرْضَ ۖ وَلَا يَئُودُهُ حِfْظُهُمَا ۚ وَهُوَ الْعَلِيُّ الْعَظِيمُ", lao: "ອັລລໍຮ໌ - ບໍ່ມີພະເຈົ້າອື່ນໃດນອກຈາກພຣະອົງ, ຜູ້ทรงມີຊີວິດ, ຜູ້ทรงຄົງຢູ່. ຄວາມເຫງົານອນແລະການນອນບໍ່สามารถครอบงำพระองค์ได้. ສິ່ງທີ່ຢູ່ໃນຊັ້ນຟ້າและสิ่งที่อยู่ในแผ่นดินเป็นของพระองค์. ໃຜຈະສາມາດໄກ່ເກ່ຍຕໍ່ຫນ້າพระองค์ได้ยกเว้นด้วยการอนุมัติของพระองค์? ພຣະອົງทรงรู้สิ่งที่อยู่ข้างหน้าพวกเขาແລະสิ่งที่อยู่ข้างหลังพวกเขา, และพวกเขาไม่สามารถเข้าใจสิ่งใดจากความรู้ของพระองค์ได้ยกเว้นสิ่งที่พระองค์ทรงประสงค์. บัลลังก์ของพระองค์ครอบคลุมชั้นฟ้าและแผ่นดิน, และการดูแลรักษาพวกมันไม่เป็นภาระแก่พระองค์. และพระองค์ทรงสูงสุด, ทรงยิ่งใหญ่.", english: "Allah - there is no deity except Him, the Ever-Living, the Sustainer of [all] existence. Neither drowsiness overtakes Him nor sleep. To Him belongs whatever is in the heavens and whatever is on the earth. Who is it that can intercede with Him except by His permission? He knows what is [presently] before them and what will be after them, and they encompass not a thing of His knowledge except for what He wills. His Kursi extends over the heavens and the earth, and their preservation tires Him not. And He is the Most High, the Most Great." },
  { number: "2:256", arabic: "لَا إِكْرَاهَ فِي الدِّينِ ۖ قَد تَّbَيَّnَ الرُّشْدُ مِنَ الْغَيِّ ۚ فَمَن يَكْfُرْ بِالطَّاغُوتِ وَيُؤْمِن بِاللَّهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقَىٰ لَا انفِصَامَ لَهَا ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ", lao: "ບໍ່ມີການບັງຄັບในศาสนา. ທາງທີ່ຖືກต้องໄດ້ແຈ່ມແຈ້ງຈາກທາງที่ผิดแล้ว. ດັ່ງນັ້ນຜູ້ໃດที่ปฏิเสธຕາฆูຕและเชื่อในอัลลอฮ์, ລາວກໍได้ยึดมั่นในห่วงที่แข็งแรงที่สุด, ທີ່ບໍ່ມີວັນຂาด. ແລະອັລລໍຮ໌ทรงได้ยิน, ทรงรอบรู้.", english: "There shall be no compulsion in [acceptance of] the religion. The right course has become clear from the wrong. So whoever disbelieves in Taghut and believes in Allah has grasped the most trustworthy handhold with no break in it. And Allah is Hearing and Knowing." },
  { number: "2:257", arabic: "اللَّهُ وَلِيُّ الَّذِينَ آمَنُوا يُخْرِjُهُم mِّنَ الظُّلُمَاتِ إِلَى النُّورِ ۖ وَالَّذِينَ كَفَرُوا أَوْلِيَاؤُهُمُ الطَّاغُوتُ يُخْرِjُونَهُم mِّنَ النُّورِ إِلَى الظُّلُمَاتِ ۗ أُولَٰئِكَ أَصْحَابُ النَّarِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ອັລລໍຮ໌ເປັນຜູ້ປົກປ້ອງของผู้ที่ศรัทธา. ພຣະອົງทรงนำพวกเขาออกจากความมืดไปสู่แสงสว่าง. ແຕ່ຜູ້ที่ปฏิเสธ, ຜູ້ປົກປ້ອງຂອງพวกเขาคือຕາฆูຕ. ພວກเขานำพวกเขาออกจากแสงสว่างไปสู่ความมืด. ພວກເຂົາຄືชาวนรก; ພວກເຂົາจะอยู่ในນັ້ນຕະຫຼອດໄປ.", english: "Allah is the ally of those who believe. He brings them out from darknesses into the light. And those who disbelieve - their allies are Taghut. They take them out of the light into darknesses. Those are the companions of the Fire; they will abide eternally therein." },
  { number: "2:258", arabic: "أَلَمْ تَرَ إِلَى الَّذِي حَajَّ إِبْرَاهِيمَ فِي رَبِّهِ أَنْ آتَاهُ اللَّهُ الْمُلْكَ إِذْ قَالَ إِبْرَاهِيمُ رَبِّيَ الَّذِي يُحْيِي وَيُمِيتُ قَالَ أَنَا أُحْيِي وَأُمِيتُ ۖ قَالَ إِبْرَاهِيمُ فَإِنَّ اللَّهَ يَأْتِي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِي كَفَرَ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْmَ الظَّالِمِينَ", lao: "ເຈົ້າບໍ່ເຫັນບໍผู้ที่เถียงกับอับราฮัมเกี่ยวกับพระเจ้าของเขาเพราะอัลลอฮ์ได้ให้อำนาจแก่เขา? ເມື່ອอับราฮัมกล่าวว่า: 'พระเจ้าของข้าคือผู้ที่ให้ชีวิตและให้ความตาย', ລາວກ່າວວ່າ: 'ຂ้าก็ให้ຊີວິດແລະให้ความตาย.' ອັບຣາຮາມກ່າວວ່າ: 'ແທ້ຈິງອັລລໍຮ໌ทรงนำดวงอาทิตย์มาจากทิศตะวันออก, ດັ່ງນັ້ນຈົ່ງนำมันมาจากทิศตะวันตก.' ດັ່ງນັ້ນผู้ที่ปฏิเสธก็งงงวย. ແລະອັລລໍຮ໌ไม่ทรงชี้นำผู้ที่ไม่ยุติธรรม.", english: "Have you not considered the one who argued with Abraham about his Lord [merely] because Allah had given him kingship? When Abraham said, 'My Lord is the one who gives life and causes death,' he said, 'I give life and cause death.' Abraham said, 'Indeed, Allah brings up the sun from the east, so bring it up from the west.' So the disbeliever was overwhelmed [by astonishment], and Allah does not guide the wrongdoing people." },
  { number: "2:259", arabic: "أَوْ كَالَّذِي مَرَّ عَلَىٰ قَرْيَةٍ وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا قَالَ أَنَّىٰ يُحْيِي هَٰذِهِ اللَّهُ بَعْدَ مَوْتِهَا ۖ فَأَمَاتَهُ اللَّهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهُ ۖ قَالَ كَمْ لَبِثْتَ ۖ قَالَ لَبِثْتُ يَوْmًا أَوْ بَعْضَ يَوْmٍ ۖ قَالَ بَل لَّبِثْتَ مِائَةَ عَامٍ فَانظُرْ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْ ۖ وَانظُرْ إِلَىٰ حِمَarِكَ وَلِنَjْعَلَكَ آيَةً لِّلنَّاسِ ۖ وَانظُرْ إِلَى الْعِظَامِ كَيْfَ نُنْشِزُهَا ثُمَّ نَكْسُوهَا لَحْمًا ۚ فَلَمَّا تَبَيَّnَ لَهُ قَالَ أَعْلَمُ أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ຫຼືເໝືອນກັບผู้ที่ผ่านหมู่บ้านหนึ่งที่พังทลายลงมา. ລາວກ່າວວ່າ: 'ອັລລໍຮ໌ຈະເຮັດໃຫ້สิ่งนี้มีຊີວິດຄືນມາໄດ້ແນວໃດຫຼັງຈາກที่มันตายแล้ว?' ດັ່ງນັ້ນອັລລໍຮ໌ໄດ້ເຮັດให้ລາวຕາຍเป็นเวลาໜຶ່ງร้อยປີ, ຫຼັງຈາກນັ້ນກໍປຸກລາວຂຶ້ນມາ. ພຣະອົງກ່າວວ່າ: 'ເຈົ້າຢູ່ທີ່ນີ້ດົນປານໃດ?' ລາວກ່າວວ່າ: 'ຂ้าຢູ່ທີ່ນີ້ມື້ໜຶ່ງຫຼືບາງສ່ວນของມື້.' ພຣະອົງກ່າວວ່າ: 'ບໍ່, ເຈົ້າຢູ່ທີ່ນີ້ໜຶ່ງร้อยປີ. ດັ່ງນັ້ນຈົ່ງເບິ່ງอาหารແລະເຄື່ອງດື່ມຂອງเจ้า; ມັນບໍ່ໄດ້เน่าเปื่อย. ແລະເບິ່ງລໍຂອງເຈົ້າ; ແລະເພື່ອເຮັດໃຫ້เจ้าเป็นสัญลักษณ์สำหรับผู้คน. ແລະເບິ່ງກະດູກ, เราจะประกอบมันขึ้นมาอย่างไรแล้วหุ้มมันด้วยเนื้อ.' ແລະເມື່ອมันชัดเจนสำหรับเขา, ລາວກ່າວວ່າ: 'ຂ้ารู้ວ່າອັລລໍຮ໌ทรงมีอำนาจเหนือทุกสิ่ง.'", english: "Or [consider such an example] as the one who passed by a township which had fallen into ruin. He said, 'How will Allah bring this to life after its death?' So Allah caused him to die for a hundred years; then He revived him. He said, 'How long have you remained?' The man said, 'I have remained a day or part of a day.' He said, 'Rather, you have remained one hundred years. Look at your food and your drink; it has not changed with time. And look at your donkey; and We will make you a sign for the people. And look at the bones - how We raise them and then We cover them with flesh.' And when it became clear to him, he said, 'I know that Allah is over all things competent.'" },
  { number: "2:260", arabic: "وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ أَرِنِي كَيْfَ تُحْيِي الْمَوْتَىٰ ۖ قَالَ أَوَلَمْ تُؤْمِن ۖ قَالَ بَلَىٰ وَلَٰكِن لِّيَطْمَئِنَّ قَلْبِي ۖ قَالَ فَخُذْ أَرْبَعَةً mِّنَ الطَّيْرِ فَصُرْهُنَّ إِلَيْكَ ثُمَّ اجْعَلْ عَلَىٰ كُلِّ jَبَلٍ mِّنْهُنَّ jُزْءًا ثُمَّ ادْعُهُنَّ يَأْتِينَكَ سَعْيًا ۚ وَاعْلَمْ أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ", lao: "ແລະເມື່ອอับราฮัมກ່າວວ່າ: 'ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງຂ้า, ຂໍให้ข้าเห็นว่าพระองค์ทรงทำให้ຄົນຕາຍมีຊີວິດຄືນມາอย่างไร.' ພຣະອົງກ່າວວ່າ: 'เจ้าບໍ່ເຊື່ອບໍ?' ລາວກ່າວວ່າ: 'ເຊື່ອ, ແຕ່ເພື່ອให้ຫົວໃຈของข้าสงบ.' ພຣະອົງກ່າວວ່າ: 'ຈົ່ງເອົານົກສີ່ໂຕມາแล้วฝึกให้ມັນເຊື່ອຟັງเจ้า. ຫຼັງຈາກນັ້ນຈົ່ງວາງສ່ວນໜຶ່ງของพวกมันไว้ເທິງພູແຕ່ລະลูก. ຫຼັງຈາກນັ້ນຈົ່ງເອີ້ນพวกมัน, ພວກมันจะมาหาเจ้าอย่างรวดเร็ว. ແລະຮູ້ໄວ້ວ່າອັລລໍຮ໌ทรงมีอำนาจ, ทรงมีปัญญา.'", english: "And [mention] when Abraham said, 'My Lord, show me how You give life to the dead.' [Allah] said, 'Have you not believed?' He said, 'Yes, but [I ask] only that my heart may be satisfied.' [Allah] said, 'Take four birds and commit them to yourself. Then [after slaughtering them] put on each hill a portion of them; then call them - they will come [flying] to you in haste. And know that Allah is Exalted in Might and Wise.'" },
  { number: "2:261", arabic: "mَّثَلُ الَّذِينَ يُnfِقُونَ أَمْوَالَهُمْ فِي سَبِilِ اللَّهِ كَمَثَلِ حَبَّةٍ أَنbَتَتْ سَبْعَ سَنَابِلَ فِي كُلِّ sُnbُلَةٍ mِّائَةُ حَبَّةٍ ۗ وَاللَّهُ يُضَاعِfُ لِمَن يَشَاءُ ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ", lao: "ຕົວຢ່າງຂອງຜູ້ที่บริจาคทรัพย์สินของพวกเขาໃນหนทางของอัลลอฮ์ຄືກັບเมล็ดພືດที่งอกເຈັດຮວງ, ในແຕ່ละຮວງมีໜຶ່ງร้อยเมล็ด. ແລະອັລລໍຮ໌ทรงเพิ่มให้ผู้ที่พระองค์ทรงประสงค์. ແລະອັລລໍຮ໌ทรงກວ້າງໃຫຍ່, ทรงรอบรู้.", english: "The example of those who spend their wealth in the way of Allah is like a seed [of grain] which grows seven spikes; in each spike is a hundred grains. And Allah multiplies [His reward] for whom He wills. And Allah is all-Encompassing and Knowing." },
  { number: "2:262", arabic: "الَّذِينَ يُnfِقُونَ أَمْوَالَهُمْ فِي سَبِilِ اللَّهِ ثُمَّ لَا يُتْبِعُونَ مَا أَنfَقُوا مَنًّا وَلَا أَذًى ۙ لَّهُمْ أَjْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْfٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ຜູ້ທີ່บริจาคทรัพย์สินของพวกเขาໃນหนทางของอัลลอฮ์ແລ້ວບໍ່ตามด้วยການทวงບຸນคุณหรือການทำร้าย, ພວກເຂົາຈະໄດ້รับรางวัลจากພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ, ແລະຈະບໍ່มีຄວາມกลัวใดๆ ต่อพวกเขาແລະພວກເຂົາຈະບໍ່ໂສກເສົ້າ.", english: "Those who spend their wealth in the way of Allah and then do not follow up what they have spent with reminders [of it] or [other] injury will have their reward with their Lord, and there will be no fear concerning them, nor will they grieve." },
  { number: "2:263", arabic: "قَوْلٌ mَّعْرُوفٌ وَمَغْfِرَةٌ خَيْرٌ mِّن صَدَقَةٍ يَتْبَعُهَا أَذًى ۗ وَاللَّهُ غَنِيٌّ حَلِيمٌ", lao: "ຄຳເວົ້າที่ດີແລະການໃຫ້ອະໄພดีກວ່າການบริจาคที่ตามด้วยการทำร้าย. ແລະອັລລໍຮ໌ทรงร่ำรวย, ทรงอดทน.", english: "Kind speech and forgiveness are better than charity followed by injury. And Allah is Free of need and Forbearing." },
  { number: "2:264", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُbْطِلُوا صَدَقَاتِكُم بِالْمَنِّ وَالْأَذَىٰ كَالَّذِي يُnfِقُ مَالَهُ رِئَاءَ النَّاسِ وَلَا يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ فَمَثَلُهُ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَأَصَابَهُ وَابِلٌ فَتَرَكَهُ صَلْدًا ۖ لَّا يَقْدِرُونَ عَلَىٰ شَيْءٍ mِّمَّا كَسَبُوا ۗ وَاللَّهُ لَا يَهْدِي الْقَوْmَ الْكَافِرِينَ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຢ່າทำลายการบริจาคของพวกเจ้าด้วยການทวงບຸນคุณหรือການทำร้าย, ເໝືອນກັບผู้ที่บริจาคทรัพย์สินของเขาเพื่อโอ้อวดต่อผู้คนและไม่เชื่อในอัลลอฮ์และวันสุดท้าย. ຕົວຢ່າງຂອງລາວຄືກັບຫินກ້ຽງที่มีดินຢູ່ເທິງ, ແລ້ວຝົນຕົກหนักก็พัดมันไป, ເຮັດໃຫ້ມັນກ້ຽງ. ພວກເຂົາບໍ່สามารถควบคุมสิ่งใดจากสิ่งที่พวกเขาได้ทำ. ແລະອັລລໍຮ໌ບໍ່ทรงชี้นำผู้ที่ไม่เชื่อ.", english: "O you who have believed, do not invalidate your charities with reminders [of it] or injury as does one who spends his wealth only to be seen by the people and does not believe in Allah and the Last Day. His example is like that of a smooth rock upon which is dust and is hit by a downpour that leaves it bare. They are unable [to keep] anything of what they have earned. And Allah does not guide the disbelieving people." },
  { number: "2:265", arabic: "وَمَثَلُ الَّذِينَ يُnfِقُونَ أَمْوَالَهُمُ ابْتِغَاءَ مَرْضَاتِ اللَّهِ وَتَثْبِيتًا mِّنْ أَنfُsِهِمْ كَمَثَلِ jَنَّةٍ بِرَبْوَةٍ أَصَابَهَا وَابِلٌ فَآتَتْ أُكُلَهَا ضِعْfَيْنِ فَإِن لَّمْ يُصِبْهَا وَابِلٌ فَطَلٌّ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ", lao: "ແລະຕົວຢ່າງຂອງຜູ້ที่บริจาคทรัพย์สินของพวกเขาเพื่อแสวงหาความพอใจของอัลลอฮ์ແລະເພື່ອเสริมสร้างความมั่นคงในตัวเองຄືກັບสวนบนเนินสูงที่โดนฝนตกหนัก, ແລ້ວมันก็ให้ผลผลิตเป็นສອງเท่า. ແຕ່ຖ້າไม่โดนฝนตกหนัก, ກໍມີฝนปรอย. ແລະອັລລໍຮ໌ทรงเห็นในสิ่งที่พวกเจ้าทำ.", english: "And the example of those who spend their wealth seeking means to the approval of Allah and assuring [reward for] themselves is like a garden on high ground which is hit by a downpour - so it yields its fruit in double. And if it is not hit by a downpour, then a drizzle [is sufficient]. And Allah, of what you do, is Seeing." },
  { number: "2:266", arabic: "أَيَوَدُّ أَحَدُكُمْ أَن تَكُونَ لَهُ jَنَّةٌ mِّن nَّخِيلٍ وَأَعْنَابٍ تَjْرِي مِن تَحْتِهَا الْأَنْهَarُ لَهُ فِيهَا مِن كُلِّ الثَّمَرَاتِ وَأَصَابَهُ الْكِبَرُ وَلَهُ ذُرِّيَّةٌ ضُعَفَاءُ فَأَصَابَهَا إِعْصَarٌ فِيهِ نَarٌ فَاحْتَرَقَتْ ۗ كَذَٰلِكَ يُbَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ", lao: "ຜູ້ໃດໃນພວກเจ้าຢາກມີสวนอินทผลัมและองุ่น, ที่มีแม่น้ำไหลผ่าน, ລາວมีผลไม้ทุกชนิดในนั้น, ແລ້ວความแก่ชราก็มาถึงລາวในขณะที่ລາวมีลูกหลานที่อ่อนแอ, ແລ້ວพายุที่เต็มไปด้วยไฟก็พัดมาทำลายมัน? ເຊັ່ນນັ້ນແຫຼະ, ອັລລໍຮ໌ทรงຊี้ແຈງອາຍະຕ່າງໆให้พวกเจ้า, ເພື່ອพวกเจ้าจะได้ไตร่ตรอง.", english: "Would one of you like to have a garden of palm trees and grapevines underneath which rivers flow in which he has from every fruit? But he is afflicted with old age and has weak offspring, and it is hit by a whirlwind containing fire and is burned. Thus does Allah make clear to you [His] verses that you might give thought." },
  { number: "2:267", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا أَنfِقُوا مِن طَيِّbَاتِ مَا كَسَبْتُمْ وَمِمَّا أَخْرَjْنَا لَكُم mِّنَ الْأَرْضِ ۖ وَلَا تَيَمَّمُوا الْخَبِيثَ مِنْهُ تُnfِقُونَ وَلَسْتُم بِآخِذِيهِ إِلَّا أَن تُغْمِضُوا فِيهِ ۚ وَاعْلَمُوا أَنَّ اللَّهَ غَنِيٌّ حَمِيدٌ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຈົ່ງบริจาคຈາກสิ่งที่ดีที่พวกเจ้าได้หามาและจากสิ่งที่เราได้นำออกมาให้พวกเจ้าจากแผ่นดิน. ແລະຢ่าตั้งใจเอาสิ่งที่ไม่ดีมาบริจาค, ທັງที่พวกเจ้าเองก็ไม่รับมันยกเว้นจะหลับตา. ແລະຮູ້ໄວ້ວ່າອັລລໍຮ໌ทรงร่ำรวย, ทรงได้รับการสรรเสริญ.", english: "O you who have believed, spend from the good things which you have earned and from that which We have produced for you from the earth. And do not aim toward the defective therefrom, spending [from it] while you would not take it [yourself] except with closed eyes. And know that Allah is Free of need and Praiseworthy." },
  { number: "2:268", arabic: "الشَّيْطَانُ يَعِدُكُمُ الْfَقْرَ وَيَأْمُرُكُم بِالْfَحْشَاءِ ۖ وَاللَّهُ يَعِدُكُم mَّغْfِرَةً mِّنْهُ وَفَضْلًا ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ", lao: "ชัยฏอนสัญญาว่าจะทำให้พวกเจ้ายากจนและสั่งให้พวกเจ้าทำความลามก. ແຕ່ອັລລໍຮ໌ทรงสัญญาว่าจะให้การอภัยและความโปรดปราน. ແລະອັລລໍຮ໌ทรงກວ້າງໃຫຍ່, ทรงรอบรู้.", english: "Satan threatens you with poverty and orders you to immorality, while Allah promises you forgiveness from Him and bounty. And Allah is all-Encompassing and Knowing." },
  { number: "2:269", arabic: "يُؤْتِي الْحِكْمَةَ مَن يَشَاءُ ۚ وَمَن يُؤْتَ الْحِكْمَةَ فَقَدْ أُوتِيَ خَيْرًا كَثِيرًا ۗ وَمَا يَذَّكَّرُ إِلَّا أُولُو الْأَلْبَابِ", lao: "ພຣະອົງทรงให้ปัญญาแก่ผู้ที่พระองค์ทรงประสงค์. ແລະຜູ້ໃດที่ได้รับปัญญา, ລາວກໍໄດ້รับความดีมากมาย. ແລະບໍ່ມີໃຜระลึกได้ยกเว้นผู้ที่มีปัญญา.", english: "He gives wisdom to whom He wills, and whoever has been given wisdom has certainly been given much good. And none will remember except those of understanding." },
  { number: "2:270", arabic: "وَمَا أَنfَقْتُم mِّن نَّfَقَةٍ أَوْ نَذَرْتُم mِّن نَّذْرٍ فَإِنَّ اللَّهَ يَعْلَمُهُ ۗ وَمَا لِلظَّالِمِينَ مِنْ أَنصَarٍ", lao: "ແລະການบริจาคใดๆ ที่พวกเจ้าทำหรือคำสาบานใดๆ ที่พวกเจ้าทำ, ແທ້ຈິງອັລລໍຮ໌ทรงรู้. ແລະສຳລັບຜູ້ທີ່ບໍ່ຍຸດຕິທຳ, ບໍ່ມີຜູ້ຊ່ວຍເຫຼືອ.", english: "And whatever you spend of expenditures or make of vows - indeed, Allah knows of it. And for the wrongdoers there are no helpers." },
  { number: "2:271", arabic: "إِن تُbْدُوا الصَّدَقَاتِ فَنِعِمَّا هِيَ ۖ وَإِن تُخْfُوهَا وَتُؤْتُوهَا الْfُقَرَاءَ فَهُوَ خَيْرٌ لَّكُمْ ۚ وَيُكَfِّrُ عَنكُم mِّن سَيِّئَاتِكُمْ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ", lao: "ຖ້າพวกเจ้าເປີດເຜີຍการบริจาค, ມັນກໍດີ. ແຕ່ຖ້າพวกเจ้าปกปิดมันและให้แก่คนจน, ມັນກໍດີກວ່າสำหรับพวกเจ้า. ແລະພຣະອົງຈະລົບລ້າງบาปของพวกเจ้า. ແລະອັລລໍຮ໌ทรงรอบรู้ในสิ่งที่พวกเจ้าทำ.", english: "If you disclose your charitable expenditures, they are good; but if you conceal them and give them to the poor, it is better for you, and He will remove from you some of your misdeeds. And Allah, with what you do, is [fully] Acquainted." },
  { number: "2:272", arabic: "لَّيْسَ عَلَيْكَ هُدَاهُمْ وَلَٰكِنَّ اللَّهَ يَهْدِي مَن يَشَاءُ ۗ وَمَا تُnfِقُوا مِنْ خَيْرٍ فَلِأَنfُsِكُمْ ۚ وَمَا تُnfِقُونَ إِلَّا ابْتِغَاءَ وَjْهِ اللَّهِ ۚ وَمَا تُnfِقُوا مِنْ خَيْرٍ يُوَfَّ إِلَيْكُمْ وَأَنتُمْ لَا تُظْلَمُونَ", lao: "ການຊີ້ແນະພວກເຂົາບໍ່ແມ່ນຫນ້າທີ່ຂອງเจ้า, ແຕ່ອັລລໍຮ໌ทรงชี้นำผู้ที่พระองค์ทรงประสงค์. และความดีใดๆ ที่พวกเจ้าบริจาค, ກໍเพื่อตัวเอง. ແລະພວກเจ้าບໍ່บริจาคยกเว้นເພື່ອแสวงหาพระพักตร์ของอัลลอฮ์. ແລະความดีใดๆ ที่พวกเจ้าบริจาค, ຈະถูกตอบแทนอย่างเต็มที่, ແລະພວກเจ้าจะไม่ถูกทำร้าย.", english: "Not upon you, [O Muhammad], is their guidance, but Allah guides whom He wills. And whatever good you [believers] spend is for yourselves, and you do not spend except seeking the countenance of Allah. And whatever you spend of good - it will be fully repaid to you, and you will not be wronged." },
  { number: "2:273", arabic: "لِلْfُقَرَاءِ الَّذِينَ أُحْصِرُوا فِي سَبِilِ اللَّهِ لَا يَsْتَطِيعُونَ ضَرْbًا فِي الْأَرْضِ يَحْسَبُهُمُ الْjَاهِلُ أَغْنِيَاءَ مِنَ التَّعَفُّfِ تَعْرِfُهُم بِsِيمَاهُمْ لَا يَsْأَلُونَ النَّاسَ إِلْحَافًا ۗ وَمَا تُnfِقُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ", lao: "ສຳລັບคนจนที่ถูกจำกัดในหนทางของอัลลอฮ์, ບໍ່สามารถเดินทางในแผ่นดินได้. ຄົນที่ไม่รู้คิดว่าพวกเขาร่ำรวยเพราะຄວາມ自制. ເຈົ້າจะรู้จักพวกเขาจากลักษณะของพวกเขา. ພວກເຂົາບໍ່ขอทานอย่างหน้าด้าน. ແລະความดีໃດໆທີ່ພວກเจ้าบริจาค, ແທ້ຈິງອັລລໍຮ໌ทรงรู้.", english: "[Charity is] for the poor who have been restricted for the cause of Allah, unable to move about in the land. An ignorant [person] would think them wealthy because of their restraint. You will know them by their sign: they do not ask people persistently [or at all]. And whatever you spend of good - indeed, Allah is Knowing of it." },
  { number: "2:274", arabic: "الَّذِينَ يُnfِقُونَ أَمْوَالَهُم بِاللَّيْلِ وَالنَّهَarِ سِرًّا وَعَلَانِيَةً فَلَهُمْ أَjْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْfٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ຜູ້ທີ່บริจาคทรัพย์สินของพวกเขาໃນຕອນກາງຄືນและกลางวัน, ຢ່າງລັບໆและเปิดเผย, ພວກເຂົາຈະໄດ້รับรางวัลจากພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ, ແລະຈະບໍ່ມີຄວາມกลัวใดๆ ต่อพวกเขาແລະພວກເຂົາຈະບໍ່ໂສກເສົ້າ.", english: "Those who spend their wealth by night and by day, secretly and publicly - they will have their reward with their Lord. And no fear will there be concerning them, nor will they grieve." },
  { number: "2:275", arabic: "الَّذِينَ يَأْكُلُونَ الرِّbَا لَا يَقُومُونَ إِلَّا كَمَا يَقُومُ الَّذِي يَتَخَبَّطُهُ الشَّيْطَانُ مِنَ الْمَسِّ ۚ ذَٰلِكَ بِأَنَّهُمْ قَالُوا إِنَّمَا الْbَيْعُ مِثْلُ الرِّbَا ۗ وَأَحَلَّ اللَّهُ الْbَيْعَ وَحَرَّمَ الرِّbَا ۚ فَمَن jَاءَهُ مَوْعِظَةٌ mِّن rَّbِّهِ فَانتَهَىٰ فَلَهُ مَا سَلَfَ وَأَمْرُهُ إِلَى اللَّهِ ۖ وَمَنْ عَادَ فَأُولَٰئِكَ أَصْحَابُ النَّarِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ຜູ້ທີ່ກິນດອກเบี้ยບໍ່ສາມາດຢືນໄດ້ຍົກเว้นເໝືອນກັບຜູ້ທີ່ถูกชัยฏอนสิง. ນັ້ນเป็นเพราะพวกเขากล่าวว่า: 'ການຄ້າຄືກັບດອກเบี้ย.' ແຕ່ອັລລໍຮ໌ได้ອະນຸຍາດການຄ້າແລະຫ้ามດອກเบี้ย. ດັ່ງນັ້ນຜູ້ໃດที่ได้รับຄຳຕັກเตือนจากพระเจ้าของเขาแล้วหยุด, ສິ່ງที่ຜ່ານมาແລ້ວກໍເປັນຂອງລາว, ແລະเรื่องของเขาอยู่กับอัลลอฮ์. ແຕ່ຜູ້ໃດที่กลับไป, ພວກເຂົາຄືชาวนรก; ພວກເຂົາจะอยู่ในນັ້ນຕະຫຼອດໄປ.", english: "Those who consume interest cannot stand [on the Day of Resurrection] except as one stands who is being beaten by Satan into insanity. That is because they say, 'Trade is [just] like interest.' But Allah has permitted trade and has forbidden interest. So whoever has received an admonition from his Lord and desists may have what is past, and his affair is for Allah. But whoever returns [to dealing in interest] - those are the companions of the Fire; they will abide eternally therein." },
  { number: "2:276", arabic: "يَمْحَقُ اللَّهُ الرِّbَا وَيُرْبِي الصَّدَقَاتِ ۗ وَاللَّهُ لَا يُحِبُّ كُلَّ كَفَّarٍ أَثِيمٍ", lao: "ອັລລໍຮ໌ทรงทำลายດອກเบี้ยແລະทรงเพิ่มການบริจาค. ແລະອັລລໍຮ໌ບໍ່ทรงรักผู้ที่ไม่เชื่อ, ผู้ที่ทำบาป.", english: "Allah destroys interest and gives increase for charities. And Allah does not like every sinning disbeliever." },
  { number: "2:277", arabic: "إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ لَهُمْ أَjْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْfٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ແທ້ຈິງ, ຜູ້ທີ່ເຊື່ອແລະເຮັດຄວາມດີ, ແລະดำรงการละหมาด, และจ่ายซะกาต, ພວກເຂົາຈະໄດ້รับรางวัลจากພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ, ແລະຈະບໍ່ມີຄວາມกลัวใดๆ ต่อพวกเขาແລະພວກເຂົາຈະບໍ່ໂສກເສົ້າ.", english: "Indeed, those who believe and do righteous deeds and establish prayer and give zakah will have their reward with their Lord, and there will be no fear concerning them, nor will they grieve." },
  { number: "2:278", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَذَرُوا مَا بَقِيَ مِنَ الرِّbَا إِن كُنتُم mُّؤْمِنِينَ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຈົ່ງຢຳເກງອັລລໍຮ໌และละทิ้งสิ่งที่เหลืออยู่ของดอกเบี้ย, ຫາກพวกเจ้าເປັນผู้ศรัทธา.", english: "O you who have believed, fear Allah and give up what remains [due to you] of interest, if you should be believers." },
  { number: "2:279", arabic: "فَإِن لَّمْ تَfْعَلُوا فَأْذَنُوا بِحَرْbٍ mِّنَ اللَّهِ وَرَسُولِهِ ۖ وَإِن تُbْتُمْ فَلَكُمْ رُءُوسُ أَمْوَالِكُمْ لَا تَظْلِمُونَ وَلَا تُظْلَمُونَ", lao: "ແລະຖ້າพวกเจ้าไม่ทำ, ກໍจงรับรู้ถึงสงครามจากอัลลอฮ์และศาสดาของพระองค์. ແຕ່ຖ้าพวกเจ้ากลับใจ, ພວກเจ้าจะได้เงินต้นคืน. ພວກเจ้าจะไม่ทำผิดແລະจะไม่ถูกทำผิด.", english: "And if you do not, then be informed of a war [against you] from Allah and His Messenger. But if you repent, you may have your principal - [thus] you do no wrong, nor are you wronged." },
  { number: "2:280", arabic: "وَإِن كَانَ ذُو عُsْرَةٍ فَنَظِرَةٌ إِلَىٰ مَيْsَرَةٍ ۚ وَأَن تَصَدَّقُوا خَيْرٌ لَّكُمْ ۖ إِن كُنتُمْ تَعْلَمُونَ", lao: "ແລະຖ້າลูกหนี้อยู่ในความลำบาก, ກໍให้รอจนกว่าจะมีความสามารถ. ແຕ່ถ้าพวกเจ้าให้เป็นทาน, มันก็ดีกว่าสำหรับพวกเจ้า, ຫາກพวกเจ้ารู้.", english: "And if someone is in hardship, then [let there be] postponement until a time of ease. But if you give [from your right as] charity, then it is better for you, if you only knew." },
  { number: "2:281", arabic: "وَاتَّقُوا يَوْmًا تُرْjَعُونَ فِيهِ إِلَى اللَّهِ ۖ ثُمَّ تُوَfَّىٰ كُلُّ نَفْسٍ mَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ", lao: "ແລະຈົ່ງຢຳເກງວັນที่พวกเจ้าจะถูกนำกลับไปหาอัลลอฮ์. ຫຼັງจากนั้น, ທຸກຄົນจะได้รับการตอบแทนอย่างเต็มที่สำหรับสิ่งที่ได้ทำ, ແລະພວກເຂົາจะไม่ถูกทำร้าย.", english: "And fear a Day when you will be returned to Allah. Then every soul will be compensated for what it earned, and they will not be treated unjustly." },
  { number: "2:282", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا تَدَايَنتُم بِdَيْنٍ إِلَىٰ أَjَلٍ mُّsَمًّى فَاكْتُbُوهُ ۚ وَلْيَكْتُb بَّيْنَكُمْ كَاتِبٌ بِالْعَدْلِ ۚ وَلَا يَأْبَ كَاتِبٌ أَن يَكْتُbَ كَمَا عَلَّمَهُ اللَّهُ ۚ فَلْيَكْتُbْ وَلْيُمْلِلِ الَّذِي عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللَّهَ رَبَّهُ وَلَا يَبْخَسْ مِنْهُ شَيْئًا ۚ فَإِن كَانَ الَّذِي عَلَيْهِ الْحَقُّ سَفِيهًا أَوْ ضَعِيفًا أَوْ لَا يَsْتَطِيعُ أَن يُمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُ بِالْعَدْلِ ۚ وَاسْتَشْهِdُوا شَهِيدَيْنِ مِن rِّjَالِكُمْ ۖ فَإِن لَّمْ يَكُونَا رَjُلَيْنِ فَرَjُلٌ وَامْرَأَتَانِ مِمَّn تَرْضَوْنَ مِنَ الشُّهَدَاءِ أَن تَضِلَّ إِحْدَاهُمَا فَتُذَكِّrَ إِحْدَاهُمَا الْأُخْرَىٰ ۚ وَلَا يَأْبَ الشُّهَدَاءُ إِذَا مَا دُعُوا ۚ وَلَا تَsْأَمُوا أَن تَكْتُbُوهُ صَغِيرًا أَوْ كَبِيرًا إِلَىٰ أَjَلِهِ ۚ ذَٰلِكُمْ أَقْسَطُ عِندَ اللَّهِ وَأَقْوَمُ لِلشَّهَادَةِ وَأَدْنَىٰ أَلَّا تَرْتَابُوا ۖ إِلَّا أَن تَكُونَ تِjَarَةً حَاضِرَةً تُdِيرُونَهَا بَيْنَكُمْ فَلَيْsَ عَلَيْكُمْ jُنَاحٌ أَلَّا تَكْتُbُوهَا ۗ وَأَشْهِdُوا إِذَا تَبَايَعْتُمْ ۚ وَلَا يُضَarَّ كَاتِبٌ وَلَا شَهِيدٌ ۚ وَإِن تَfْعَلُوا فَإِنَّهُ fُsُوقٌ بِكُمْ ۗ وَاتَّقُوا اللَّهَ ۖ وَيُعَلِّمُكُمُ اللَّهُ ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ເມື່ອພວກเจ้าทำสัญญาหนี้สินสำหรับระยะเวลาที่กำหนด, ຈົ່ງຂຽນมันไว้. ແລະໃຫ້มีคนเขียนในหมู่พวกเจ้าเขียนด้วยความยุติธรรม. ແລະอย่าให้คนเขียนปฏิเสธที่จะเขียนເໝືອນດັ່ງທີ່ອັລລໍຮ໌ໄດ້ສອນລາว. ດັ່ງນັ້ນໃຫ້ລາวเขียนແລະໃຫ້ຜູ້ที่เป็นหนี้บอก. ແລະໃຫ້ລາวຢຳເກງອັລລໍຮ໌พระเจ้าของเขาແລະບໍ່ลดทอนสิ่งใดจากมัน. ແຕ່ຖ້າຜູ້ที่เป็นหนี้เป็นคนโง่หรืออ่อนแอหรือບໍ່ສາມາດบอกได้, ກໍให้ผู้ปกครองของเขาบอกด้วยความยุติธรรม. ແລະເອົາພະຍານສອງຄົນจากชายของพวกเจ้า. ແຕ່ຖ້າไม่มียชายสองຄົນ, ກໍให้เป็นชายหนึ่งຄົນและหญิงສອງຄົນจากผู้ที่พวกเจ้ายอมรับเป็นพยาน, ເພື່ອຖ້າຄົນໜຶ່ງໃນພວກນາງລືມ, ອີກຄົນໜຶ່ງຈະເຕືອນ. ແລະอย่าให้พยานปฏิเสธເມື່ອຖືກເອີ້ນ. ແລະอย่าเบื่อที่จะเขียนมัน, ບໍ່ວ່າจะນ້ອຍหรือໃຫຍ່, ພ້ອມກັບກຳหนดເວລາ. ນັ້ນยุติธรรมກວ່າในสายตาของอัลลอฮ์, และมั่นคงກວ່າสำหรับพยาน, ແລະเหมาะสมກວ່າที่จะป้องกันความสงสัย. ยกเว้นกรณีที่เป็นการค้าขายทันทีที่พวกเจ้าทำกัน, ກໍບໍ່มีบาปสำหรับพวกเจ้าที่จะไม่เขียนมัน. ແລະເອົາພະຍານເມື່ອพวกเจ้าทำสัญญาซื้อขาย. ແລະอย่าให้คนเขียนหรือพยานถูกทำร้าย. ແລະຖ້າพวกเจ้าทำ, ແທ້ຈິງມັນເປັນການละเมิด. ແລະຈົ່ງຢຳເກງອັລລໍຮ໌. ແລະອັລລໍຮ໌ทรงสอนพวกเจ้า. และอัลลอฮ์ทรงรอบรู้ทุกสิ่ง.", english: "O you who have believed, when you contract a debt for a specified term, write it down. And let a scribe write [it] between you in justice. Let no scribe refuse to write as Allah has taught him. So let him write and let the one who has the obligation dictate. And let him fear Allah, his Lord, and not leave anything out of it. But if the one who has the obligation is of limited understanding or weak or unable to dictate himself, then let his guardian dictate in justice. And bring to witness two witnesses from among your men. And if there are not two men [available], then a man and two women from those whom you accept as witnesses - so that if one of the women errs, then the other can remind her. And let not the witnesses refuse when they are called upon. And do not be [too] weary to write it, whether it is small or large, for its specified term. That is more just in the sight of Allah and stronger as evidence and more likely to prevent doubt between you, except when it is an immediate transaction which you conduct among yourselves. For [then] there is no blame upon you if you do not write it. And take witnesses when you conclude a contract. Let no scribe be harmed or any witness. For if you do so, indeed, it is [grave] disobedience in you. And fear Allah. And Allah teaches you. And Allah is Knowing of all things." },
  { number: "2:283", arabic: "وَإِن كُنتُمْ عَلَىٰ سَفَرٍ وَلَمْ تَjِدُوا كَاتِبًا فَرِهَانٌ mَّقْبُوضَةٌ ۖ فَإِنْ أَمِنَ بَعْضُكُم بَعْضًا فَلْيُؤَدِّ الَّذِي اؤْتُمِنَ أَمَانَتَهُ وَلْيَتَّقِ اللَّهَ رَبَّهُ ۗ وَلَا تَكْتُمُوا الشَّهَادَةَ ۚ وَمَن يَكْتُمْهَا فَإِنَّهُ آثِمٌ قَلْبُهُ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ عَلِيمٌ", lao: "ແລະຖ້າພວກเจ้าກຳລັງເດີນທາງແລະບໍ່ພົບຄົນຂຽນ, ກໍให้มีหลักประกัน. ແຕ່ຖ້າบางคนໃນพวกเจ้าไว้ใจกัน, ກໍให้ຜູ້ที่ได้รับความไว้ใจคืนความไว้ใจของเขาແລະໃຫ້ລາวຢຳເກງອັລລໍຮ໌พระเจ้าของเขา. ແລະຢ່າปกปิดคำพยาน. ແລະຜູ້ໃດที่ปกปิดมัน, ແທ້ຈິງຫົວໃຈຂອງລາวก็บาป. ແລະອັລລໍຮ໌ทรงรอบรู้ในสิ่งที่พวกเจ้าทำ.", english: "And if you are on a journey and cannot find a scribe, then a security deposit [should be] taken. And if one of you entrusts another, then let him who is entrusted discharge his trust [faithfully] and let him fear Allah, his Lord. And do not conceal testimony, for whoever conceals it - his heart is indeed sinful, and Allah is Knowing of what you do." },
  { number: "2:284", arabic: "لِّلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَإِن تُbْدُوا مَا فِي أَنfُsِكُمْ أَوْ تُخْfُوهُ يُحَاسِبْكُم بِهِ اللَّهُ ۖ فَيَغْfِرُ لِمَن يَشَاءُ وَيُعَذِّbُ مَن يَشَاءُ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ສິ່ງທີ່ຢູ່ໃນຊັ້ນຟ້າແລະสิ่งที่อยู่ในแผ่นดินເປັນຂອງອັລລໍຮ໌. ແລະຖ້າพวกเจ้าເປີດເຜີຍສິ່ງທີ່อยู่ในใจหรือปกปิดมัน, ອັລລໍຮ໌จะคิดบัญชีกับพวกเจ้า. ແລ້ວພຣະອົງจะอภัยให้ผู้ที่พระองค์ทรงประสงค์ແລະลงโทษผู้ที่พระองค์ทรงประสงค์. ແລະອັລລໍຮ໌ทรงมีอำนาจเหนือทุกสิ่ง.", english: "To Allah belongs whatever is in the heavens and whatever is in the earth. Whether you show what is within yourselves or conceal it, Allah will bring you to account for it. Then He will forgive whom He wills and punish whom He wills, and Allah is over all things competent." },
  { number: "2:285", arabic: "آمَنَ الرَّsُولُ بِمَا أُNzِلَ إِلَيْهِ مِن rَّbِّهِ وَالْمُؤْمِنُونَ ۚ كُلٌّ آمَنَ بِاللَّهِ وَمَلَائِكَتِهِ وَكُتُbِهِ وَرُsُلِهِ لَا نُfَرِّقُ بَيْنَ أَحَدٍ mِّن rُّsُلِهِ ۚ وَقَالُوا سَمِعْنَا وَأَطَعْنَا ۖ غُfْرَانَكَ رَبَّnَا وَإِلَيْكَ الْمَصِيرُ", lao: "ศาสดาໄດ້ເຊື່ອในสิ่งที่ຖືກເປີດເຜີຍລົງມາให้เขาจากพระเจ้าของเขา, ແລະผู้ศรัทธาก็เช่นกัน. ທຸກຄົນເຊື່ອในอัลลอฮ์, ทูตสวรรค์ของพระองค์, คัมภีร์ของพระองค์, และศาสดาของพระองค์, [โดยกล่าวว่า]: 'เราไม่ແຍກແຍะລະຫວ່າງໃຜในศาสดาของพระองค์.' ແລະພວກเขากล่าวว่า: 'เราได้ยินและเราເຊື່ອຟັງ. [เราขอ] ການອະໄພຈາກພຣະອົງ, ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, และการกลับไปอยู่กับพระองค์.'", english: "The Messenger has believed in what has been revealed to him from his Lord, and [so have] the believers. All of them have believed in Allah and His angels and His books and His messengers, [saying], 'We make no distinction between any of His messengers.' And they say, 'We hear and we obey. [We seek] Your forgiveness, our Lord, and to You is the [final] destination.'" },
  { number: "2:286", arabic: "لَا يُكَلِّfُ اللَّهُ نَفْسًا إِلَّا وُsْعَهَا ۚ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْ ۗ رَبَّnَا لَا تُؤَاخِذْنَا إِن nَّsِينَا أَوْ أَخْطَأْنَا ۚ رَبَّnَا وَلَا تَحْمِلْ عَلَيْنَا إِصْرًا كَمَا حَمَلْتَهُ عَلَى الَّذِينَ مِن قَبْلِنَا ۚ رَبَّnَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِهِ ۖ وَاعْfُ عَنَّا وَاغْfِرْ لَنَا وَarْحَمْنَا ۚ أَنتَ مَوْلَانَا فَانصُرْنَا عَلَى الْقَوْmِ الْكَافِرِينَ", lao: "ອັລລໍຮ໌ບໍ່ทรงບັງຄັບໃຜເກີນຄວາມສາມາດ. ລາວຈະได้รับสิ่งที่ລາวทำ, ແລະລາวจะรับผิดชอบสิ่งที่ລາวทำ. ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຢ່າລົງໂທດພວກເຮົາຖ້າພວກເຮົາລືມຫຼືເຮັດຜິດ. ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ແລະຢ່າວາງภาระหนักໃສ່ພວກເຮົາເໝືອນດັ່ງທີ່ພຣະອົງໄດ້ວາງໃສ່ผู้ທີ່อยู่ກ່ອນหน้าเรา. ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, และอย่าให้เราแบกรับสิ่งที่ไม่มีกำลังสำหรับเรา. และขอให้ยกโทษให้เรา, และขอให้อภัยให้เรา, และขอให้เมตตาเรา. พระองค์คือผู้พิทักษ์ของเรา, ดังนั้นขอให้ช่วยเราเอาชนะผู้ที่ไม่เชื่อ.", english: "Allah does not charge a soul except [with that within] its capacity. It will have [the consequence of] what [good] it has gained, and it will bear [the consequence of] what [evil] it has earned. 'Our Lord, do not impose blame upon us if we have forgotten or erred. Our Lord, and lay not upon us a burden like that which You laid upon those before us. Our Lord, and burden us not with that which we have no ability to bear. And pardon us; and forgive us; and have mercy upon us. You are our protector, so give us victory over the disbelieving people.'" }
];

export default function SurahAlBaqarahView({ goBack }: SurahAlBaqarahViewProps) {
  const [searchQuery, setSearchQuery] = useState('');

  const filteredVerses = verses.filter(verse =>
    verse.number.includes(searchQuery) ||
    verse.lao.toLowerCase().includes(searchQuery.toLowerCase()) ||
    verse.english.toLowerCase().includes(searchQuery.toLowerCase()) ||
    verse.arabic.includes(searchQuery)
  );
  
  return (
    <div className="flex flex-col">
      <ViewHeader title="ສູຣໍ ອັລບາກໍຣໍ" onBack={goBack} />
      <div className="p-4 sticky top-0 bg-background z-10">
        <Input
          type="text"
          placeholder="ຄົ້ນຫາອາຍັດ..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-full"
        />
      </div>
      <main className="p-4 pt-0">
        {filteredVerses.length > 0 ? (
          filteredVerses.map(v => <VerseCard key={v.number} {...v} />)
        ) : (
          <p className="text-center text-muted-foreground">ບໍ່ພົບອາຍັດທີ່ທ່ານຄົ້ນຫາ.</p>
        )}
        <SummaryCard title="ສະຫຼຸບ">
          ອາຍັດເຫຼົ່ານີ້ອະທິບາຍເຖິງກຸ່ມຄົນຕ່າງໆ: ຜູ້ສັດທາ, ຜູ້ປະຕິເສດ, ແລະ ຜູ້ໜ້າຊື່ໃຈຄົດ. ມັນໄດ້ທ້າທາຍຜູ້ທີ່ສົງໄສໃນຄຳພີກຸຣອານ, ໄດ້ສັນຍາລາງວັນແກ່ผู้ທີ່ເຊື່ອ, ແລະໄດ້ຢືນຢັນເຖິງອຳນາດຂອງອັນລໍຫ໌ໃນການສ້າງຈັກກະວານ ແລະ ມະນຸດ.
        </SummaryCard>
      </main>
    </div>
  );
}
