'use client';
import React, { useState } from 'react';
import ViewHeader from '@/components/shared/ViewHeader';
import VerseCard from '@/components/shared/VerseCard';
import SummaryCard from '@/components/shared/SummaryCard';
import { Input } from '@/components/ui/input';

interface SurahAlBaqarahViewProps {
  goBack: () => void;
}

const verses = [
  { number: "2:1", arabic: "الم", lao: "ອາລີຟ, ລາມ, ມີມ.", english: "Alif, Lam, Meem." },
  { number: "2:2", arabic: "ذَٰلِكَ الْكِتَابُ لَا رَيْبَ فِيهِ هُدًى لِّلْمُتَّقِينَ", lao: "ຄໍາພີນີ້ (ກຸຣອານ), ບໍ່ມີຄວາມສົງໄສໃດໆໃນມັນ, ເປັນການຊີ້ແນະແກ່ບັນດາຜູ້ທີ່ມີຄວາມຍຳເກງ.", english: "This is the Book about which there is no doubt, a guidance for those conscious of Allah -" },
  { number: "2:3", arabic: "الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُقِيمُونَ الصَّلَاةَ وَمِمَّا رَزَقْنَاهُمْ يُنفِقُونَ", lao: "ບັນດາຜູ້ທີ່ເຊື່ອໃນສິ່ງທີ່ລັບເລິກ, ແລະດຳລົງການລະໝາດ, ແລະຈາກສິ່ງທີ່ເຮົາໄດ້ປະທານໃຫ້ພວກເຂົາ ພວກເຂົາກໍບໍລິຈາກ.", english: "Who believe in the unseen, establish prayer, and spend out of what We have provided for them," },
  { number: "2:4", arabic: "وَالَّذِينَ يُؤْمِنُونَ بِمَا أُنزِلَ إِلَيْكَ وَمَا أُنزِلَ مِن قَبْلِكَ وَبِالْآخِرَةِ هُمْ يُوقِنُونَ", lao: "ແລະບັນດາຜູ້ທີ່ເຊື່ອໃນສິ່ງທີ່ຖືກເປີດເຜີຍລົງມາຫາເຈົ້າ, ແລະສິ່ງທີ່ຖືກເປີດເຜີຍລົງມາກ່ອນເຈົ້າ, ແລະພວກເຂົາກໍມີຄວາມເຊື່ອໝັ້ນຢ່າງເຕັມທີ່ໃນໂລກຫນ້າ.", english: "And who believe in what has been revealed to you, [O Muhammad], and what was revealed before you, and of the Hereafter they are certain [in faith]." },
  { number: "2:5", arabic: "أُولَٰئِكَ عَلَىٰ هُدًى مِّن رَّبِّهِمْ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ", lao: "ພວກເຂົານັ້ນຢູ່ເທິງເສັ້ນທາງທີ່ຖືກຕ້ອງຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ, ແລະພວກເຂົານັ້ນຄືບັນດາຜູ້ທີ່ໄດ້ຮັບຄວາມສຳເລັດ.", english: "Those are upon [right] guidance from their Lord, and it is those who are the successful." },
  { number: "2:6", arabic: "إِنَّ الَّذِينَ كَفَرُوا سَوَاءٌ عَلَيْهِمْ أَأَنذَرْتَهُمْ أَمْ لَمْ تُنذِرْهُمْ لَا يُؤْمِنُونَ", lao: "ແທ້ຈິງບັນດາຜູ້ທີ່ປະຕິເສດຄວາມເຊື່ອ, ບໍ່ວ່າເຈົ້າຈະຕັກເຕືອນພວກເຂົາຫຼືບໍ່ຕັກເຕືອນພວກເຂົາກໍຕາມ, ພວກເຂົາຈະບໍ່ເຊື່ອ.", english: "Indeed, those who disbelieve - it is all the same for them whether you warn them or do not warn them - they will not believe." },
  { number: "2:7", arabic: "خَتَمَ اللَّهُ عَلَىٰ قُلُوبِهِمْ وَعَلَىٰ سَمْعِهِمْ وَعَلَىٰ أَبْصَarِhِمْ غِشَاوَةٌ وَلَهُمْ عَذَابٌ عَظِيمٌ", lao: "ອັລລໍຮ໌ໄດ້ຊົງປະທັບຕາໄວ້ເທິງຫົວໃຈຂອງພວກເຂົາ ແລະເທິງການໄດ້ຍິນຂອງພວກເຂົາ, ແລະເທິງສາຍຕາຂອງພວກເຂົາມີຜ້າປິດໄວ້; ແລະສຳລັບພວກເຂົາຈະໄດ້ຮັບການທໍລະມານຢ່າງໃຫຍ່ຫຼວງ.", english: "Allah has set a seal upon their hearts and upon their hearing, and over their vision is a veil. And for them is a great punishment." },
  { number: "2:8", arabic: "وَمِنَ النَّاسِ مَن يَقُولُ آمَنَّا بِاللَّهِ وَبِالْيَوْمِ الْآخِرِ وَمَا هُم بِمُؤْمِنِينَ", lao: "ແລະໃນບັນດາມະນຸດ ມີຜູ້ທີ່ກ່າວວ່າ “ພວກເຮົາເຊື່ອໃນອັລລໍຮ໌ ແລະໃນວັນສຸດທ້າຍ” ແຕ່ພວກເຂົາບໍ່ແມ່ນຜູ້ທີ່ເຊື່ອ.", english: "And of the people are some who say, 'We believe in Allah and the Last Day,' but they are not believers." },
  { number: "2:9", arabic: "يُخَادِعُونَ اللَّهَ وَالَّذِينَ آمَنُوا وَمَا يَخْدَعُونَ إِلَّا أَنفُسَهُمْ وَمَا يَشْعُرُونَ", lao: "ພວກເຂົາຫຼອກລວງອັລລໍຮ໌ ແລະບັນດາຜູ້ທີ່ເຊື່ອ, ແຕ່ພວກເຂົາບໍ່ໄດ້ຫຼອກລວງໃຜນອກຈາກຕົວເອງເທົ່ານັ້ນ, ແລະພວກເຂົາກໍບໍ່ຮູ້ສຶກຕົວ.", english: "They [think to] deceive Allah and those who believe, but they deceive not except themselves and perceive [it] not." },
  { number: "2:10", arabic: "فِي قُلُوبِهِم مَّرَضٌ فَزَادَهُمُ اللَّهُ مَرَضًا وَلَهُمْ عَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْذِبُونَ", lao: "ໃນຫົວໃຈຂອງພວກເຂົາມີພະຍາດ, ແລ້ວອັລລໍຮ໌ກໍຊົງເພີ່ມພະຍາດໃຫ້ພວກເຂົາ, ແລະພວກເຂົາຈະໄດ້ຮັບການທໍລະມານຢ່າງເຈັບປວດເນື່ອງຈາກສິ່ງທີ່ພວກເຂົາເຄີຍຕົວະ.", english: "In their hearts is disease, so Allah has increased their disease; and for them is a painful punishment because they [habitually] used to lie." },
  { number: "2:11", arabic: "وَإِذَا قِيلَ لَهُمْ لَا تُفْسِدُوا فِي الْأَرْضِ قَالُوا إِنَّمَا نَحْنُ مُصْلِحُونَ", lao: "ແລະເມື່ອຖືກກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງຢ່າສ້າງຄວາມເສຍຫາຍໃນແຜ່ນດິນ', ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາເປັນພຽງຜູ້ປັບປຸງແກ້ໄຂເທົ່ານັ້ນ'.", english: "And when it is said to them, 'Do not cause corruption on the earth,' they say, 'We are but reformers.'" },
  { number: "2:12", arabic: "أَلَا إِنَّهُمْ هُمُ الْمُفْسِدُونَ وَلَٰكِن لَّا يَشْعُرُونَ", lao: "ຈົ່ງຮູ້ໄວ້ວ່າ, ແທ້ຈິງແລ້ວ ພວກເຂົາຄືຜູ້ທີ່ສ້າງຄວາມເສຍຫາຍ, ແຕ່ພວກເຂົາບໍ່ຮູ້ສຶກຕົວ.", english: "Unquestionably, it is they who are the corrupters, but they perceive [it] not." },
  { number: "2:13", arabic: "وَإِذَا قِيلَ لَهُمْ آمِنُوا كَمَا آمَنَ النَّاسُ قَالُوا أَنُؤْمِنُ كَمَا آمَنَ السُّفَهَاءُ ۗ أَلَا إِنَّهُمْ هُمُ السُّفَهَاءُ وَلَٰكِن لَّا يَعْلَمُونَ", lao: "ແລະເມື່ອຖືກກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງເຊື່ອເໝືອນດັ່ງທີ່ຄົນທັງຫຼາຍໄດ້ເຊື່ອ', ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາຈະເຊື່ອເໝືອນດັ່ງທີ່ຄົນໂງ່ໄດ້ເຊື່ອບໍ?' ຈົ່ງຮູ້ໄວ້ວ່າ, ແທ້ຈິງແລ້ວ ພວກເຂົາຄືຄົນໂງ່, ແຕ່ພວກເຂົາບໍ່ຮູ້.", english: "And when it is said to them, 'Believe as the people have believed,' they say, 'Should we believe as the foolish have believed?' Unquestionably, it is they who are the foolish, but they know it not." },
  { number: "2:14", arabic: "وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَوْا إِلَىٰ شَيَاطِينِهِمْ قَالُوا إِنَّا مَعَكُمْ إِنَّمَا نَحْنُ مُسْتَهْزِئُونَ", lao: "ແລະເມື່ອພວກເຂົາພົບກັບບັນດາຜູ້ທີ່ເຊື່ອ, ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາເຊື່ອແລ້ວ'. ແຕ່ເມື່ອພວກເຂົາຢູ່ກັບພວກຜີມານຂອງພວກເຂົາ, ພວກເຂົາກ່າວວ່າ: 'ແທ້ຈິງ ພວກເຮົາຢູ່ກັບພວກເຈົ້າ, ພວກເຮົາເປັນພຽງຜູ້ທີ່ເຍາະເຍີ້ຍເທົ່ານັ້ນ'.", english: "And when they meet those who believe, they say, 'We believe'; but when they are alone with their evil ones, they say, 'Indeed, we are with you; we were only mockers.'" },
  { number: "2:15", arabic: "اللَّهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ", lao: "ອັລລໍຮ໌ຈະຊົງເຍາະເຍີ້ຍພວກເຂົາ ແລະຈະຊົງປ່ອຍໃຫ້ພວກເຂົາຢູ່ໃນການລະເມີດຂອງພວກເຂົາຢ່າງບໍ່ມີຈຸດໝາຍ.", english: "Allah mocks them and prolongs them in their transgression [while] they wander blindly." },
  { number: "2:16", arabic: "أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدَىٰ فَمَا رَبِحَت تِّجَارَتُهُمْ وَمَا كَانُوا مُهْتَدِينَ", lao: "ພວກເຂົາຄືຜູ້ທີ່ໄດ້ຊື້ການຫຼົງທາງດ້ວຍການຊີ້ແນະ, ດັ່ງນັ້ນການຄ້າຂອງພວກເຂົາຈຶ່ງບໍ່ໄດ້ກຳໄລ ແລະ ພວກເຂົາບໍ່ໄດ້ເປັນຜູ້ທີ່ຖືກຊີ້ແນະ.", english: "Those are the ones who have purchased error [in exchange] for guidance, so their transaction has brought no profit, nor were they guided." },
  { number: "2:17", arabic: "مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ نَارًا فَلَمَّا أَضَاءَتْ مَا حَوْلَهُ ذَهَبَ اللَّهُ بِنُورِهِمْ وَتَرَكَهُمْ فِي ظُلُمَاتٍ لَّا يُبْصِرُونَ", lao: "ຕົວຢ່າງຂອງພວກເຂົາຄືກັບຜູ້ທີ່ໄດ້ຈູດໄຟ, ເມື່ອໄຟໄດ້ສ່ອງແສງສິ່ງທີ່ຢູ່ອ້ອມຮອບລາວ, ອັລລໍຮ໌ກໍໄດ້ເອົາແສງສະຫວ່າງຂອງພວກເຂົາໄປ ແລະ ປ່ອຍພວກເຂົາໄວ້ໃນຄວາມມືດ, ພວກເຂົາບໍ່ສາມາດເຫັນໄດ້.", english: "Their example is that of one who kindled a fire, but when it illuminated what was around him, Allah took away their light and left them in darkness [so] they could not see." },
  { number: "2:18", arabic: "صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُونَ", lao: "ຫູໜວກ, ເປັນໃບ້, ຕາບອດ, ດັ່ງນັ້ນພວກເຂົາຈະບໍ່ກັບມາ.", english: "Deaf, dumb and blind - so they will not return [to the right path]." },
  { number: "2:19", arabic: "أَوْ كَصَيِّبٍ مِّنَ السَّمَاءِ فِيهِ ظُلُمَاتٌ وَرَعْدٌ وَبَرْقٌ يَجْعَلُونَ أَصَابِعَهُمْ فِي آذَانِهِم مِّنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِ ۚ وَاللَّهُ مُحِيطٌ بِالْكَافِرِينَ", lao: "ຫຼືຄືກັບຝົນທີ່ຕົກລົງມາຈາກຟ້າ, ໃນນັ້ນມີຄວາມມືດ, ສຽງຟ້າຮ້ອງ, ແລະຟ້າແມບ. ພວກເຂົາເອົານິ້ວມືອັດຫູຂອງພວກເຂົາຈາກສຽງຟ້າຜ່າ ເພາະຢ້ານຄວາມຕາຍ. ແລະອັລລໍຮ໌ຊົງລ້ອມຜູ້ປະຕິເສດຄວາມເຊື່ອໄວ້.", english: "Or [it is] like a rainstorm from the sky within which is darkness, thunder and lightning. They put their fingers in their ears against the thunderclaps in dread of death. But Allah is encompassing of the disbelievers." },
  { number: "2.20", arabic: "يَكَادُ الْبَرْقُ يَخْطَفُ أَبْصَarَهُمْ ۖ كُلَّمَا أَضَاءَ لَهُم mَّشَوْا فِيهِ وَإِذَا أَظْلَمَ عَلَيْهِمْ قَامُوا ۚ وَلَوْ شَاءَ اللَّهُ لَذَهَبَ بِسَمْعِهِمْ وَأَبْصَarِهِمْ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ຟ້າແມບເກືອບຈະເອົາສາຍຕາຂອງພວກເຂົາໄປ. ທຸກຄັ້ງທີ່ມັນສ່ອງແສງໃຫ້ພວກເຂົາ, ພວກເຂົາກໍຍ່າງໄປໃນແສງນັ້ນ; ແລະເມື່ອມັນມືດລົງ, ພວກເຂົາກໍຢຸດ. ແລະຖ້າອັລລໍຮ໌ຊົງປະສົງ, ພຣະອົງຈະເອົາການໄດ້ຍິນແລະສາຍຕາຂອງພວກເຂົາໄປ. ແທ້ຈິງ, ອັລລໍຮ໌ຊົງມີອຳນາດເໜືອທຸກສິ່ງ.", english: "The lightning almost snatches away their sight. Every time it lights [the way] for them, they walk therein; but when darkness comes over them, they stand [still]. And if Allah had willed, He could have taken away their hearing and their sight. Indeed, Allah is over all things competent." },
  { number: "2:21", arabic: "يَا أَيُّهَا النَّاسُ اعْبُدُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ وَالَّذِينَ مِن قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ", lao: "ໂອ້ ມະນຸດເອີຍ, ຈົ່ງນະມັດສະການພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຈົ້າ, ຜູ້ທີ່ໄດ້ສ້າງພວກເຈົ້າ ແລະຜູ້ທີ່ຢູ່ກ່ອນໜ້າພວກເຈົ້າ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ມີຄວາມຢຳເກງ.", english: "O mankind, worship your Lord, who created you and those before you, that you may become righteous -" },
  { number: "2:22", arabic: "الَّذِي جَعَلَ لَكُمُ الْأَرْضَ فِرَاشًا وَالسَّمَاءَ بِنَاءً وَأَنزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْقًا لَّكُمْ ۖ فَلَا تَجْعَلُوا لِلَّهِ أَندَادًا وَأَنتُمْ تَعْلَمُونَ", lao: "ຜູ້ທີ່ໄດ້ເຮັດໃຫ້ແຜ່ນດິນເປັນທີ່ນອນສຳລັບພວກເຈົ້າ, ແລະຟ້າເປັນຫຼັງຄາ, ແລະໄດ້ສົ່ງນ້ຳລົງມາຈາກຟ້າ, ແລ້ວໄດ້ເອົານ້ຳນັ້ນອອກມາເປັນໝາກໄມ້ເພື່ອເປັນອາຫານສຳລັບພວກເຈົ້າ. ດັ່ງນັ້ນ, ຢ່າຕັ້ງສິ່ງໃດເປັນຄູ່ແຂ່ງກັບອັລລໍຮ໌, ທັງທີ່ພວກເຈົ້າຮູ້.", english: "[He] who made for you the earth a bed [spread out] and the sky a ceiling and sent down from the sky, rain and brought forth thereby fruits as provision for you. So do not attribute to Allah equals while you know [that there is nothing similar to Him]." },
  { number: "2:23", arabic: "وَإِن كُنتُمْ فِي رَيْبٍ مِّمَّا نَزَّلْنَا عَلَىٰ عَبْدِنَا فَأْتُوا بِسُورَةٍ مِّن مِّثْلِهِ وَادْعُوا شُهَدَاءَكُم مِّن دُونِ اللَّهِ إِن كُنتُمْ صَادِقِينَ", lao: "ແລະຖ້າພວກເຈົ້າສົງໄສໃນສິ່ງທີ່ເຮົາໄດ້ເປີດເຜີຍລົງມາໃຫ້ແກ່ຂ້າທາດຂອງເຮົາ, ກໍຈົ່ງນຳມາຊັກບົດໜຶ່ງທີ່ເໝືອນກັບມັນ, ແລະຈົ່ງເອີ້ນພະຍານຂອງພວກເຈົ້ານອກຈາກອັລລໍຮ໌, ຖ້າພວກເຈົ້າເປັນຜູ້ທີ່ສັດຈິງ.", english: "And if you are in doubt about what We have sent down upon Our Servant [Muhammad], then produce a surah the like thereof and call upon your witnesses other than Allah, if you should be truthful." },
  { number: "2.24", arabic: "فَإِن لَّمْ تَفْعَلُوا وَلَن تَفْعَلُوا فَاتَّقُوا النَّarَ الَّتِي وَقُودُهَا النَّاسُ وَالْحِجَarَةُ ۖ أُعِدَّتْ لِلْكَافِرِينَ", lao: "ແຕ່ຖ້າພວກເຈົ້າບໍ່ສາມາດເຮັດໄດ້, ແລະພວກເຈົ້າຈະບໍ່ສາມາດເຮັດໄດ້ຢ່າງແນ່ນອນ, ກໍຈົ່ງຢ້ານກົວໄຟນະລົກ, ຊຶ່ງເຊື້ອໄຟຂອງມັນຄືມະນຸດແລະຫີນ, ທີ່ຖືກກະກຽມໄວ້ສຳລັບຜູ້ປະຕິເສດຄວາມເຊື່ອ.", english: "But if you do not - and you will never be able to - then fear the Fire, whose fuel is men and stones, prepared for the disbelievers." },
  { number: "2.25", arabic: "وَبَشِّرِ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أَنَّ لَهُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَarُ ۖ كُلَّمَا رُزِقُوا مِنْهَا مِن ثَمَرَةٍ رِّزْقًا ۙ قَالُوا هَٰذَا الَّذِي رُزِقْنَا مِن قَبْلُ ۖ وَأُتُوا بِهِ مُتَشَابِهًا ۖ وَلَهُمْ فِيهَا أَزْوَاجٌ مُّطَهَّرَةٌ ۖ وَهُمْ فِيهَا خَالِدُونَ", lao: "ແລະຈົ່ງແຈ້ງຂ່າວດີແກ່ບັນດາຜູ້ທີ່ເຊື່ອແລະເຮັດຄວາມດີວ່າ ສຳລັບພວກເຂົາຈະມີສວນສະຫວັນ, ຊຶ່ງມີແມ່ນ້ຳໄຫຼຜ່ານຢູ່ເບື້ອງລຸ່ມ. ທຸກຄັ້ງທີ່ພວກເຂົາໄດ້ຮັບໝາກໄມ້ຈາກສວນສະຫວັນເປັນອາຫານ, ພວກເຂົາຈະກ່າວວ່າ: 'ນີ້ຄືສິ່ງທີ່ພວກເຮົາເຄີຍໄດ້ຮັບມາກ່ອນ'. ແລະພວກເຂົາຈະໄດ້ຮັບມັນໃນລັກສະນະທີ່ຄ້າຍຄືກັນ. ແລະໃນນັ້ນພວກເຂົາຈະມີຄູ່ຄອງທີ່ບໍລິສຸດ, ແລະພວກເຂົາຈະຢູ່ໃນນັ້ນຕະຫຼອດໄປ.", english: "And give good tidings to those who believe and do righteous deeds that they will have gardens [in Paradise] beneath which rivers flow. Whenever they are provided with a provision of fruit therefrom, they will say, 'This is what we were provided with before.' And it will be given to them in likeness. And they will have therein purified spouses, and they will abide therein eternally." },
  { number: "2:26", arabic: "۞ إِنَّ اللَّهَ لَا يَسْتَحْيِي أَن يَضْرِبَ مَثَلًا مَّا بَعُوضَةً فَمَا فَوْقَهَا ۚ فَأَمَّا الَّذِينَ آمَنُوا فَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِن رَّبِّهِمْ ۖ وَأَمَّا الَّذِينَ كَفَرُوا فَيَقُولُونَ مَاذَا أَرَادَ اللَّهُ بِهَٰذَا مَثَلًا ۘ يُضِلُّ بِهِ كَثِيرًا وَيَهْدِي بِهِ كَثِيرًا ۚ وَمَا يُضِلُّ بِهِ إِلَّا الْفَاسِقِينَ", lao: "ແທ້ຈິງ, ອັລລໍຮ໌ບໍ່ຊົງລະອາຍທີ່ຈະຍົກຕົວຢ່າງໃດໆ, ແມ້ຈະເປັນຍຸງ ຫຼືສິ່ງທີ່ເໜືອມັນ. ສ່ວນຜູ້ທີ່ເຊື່ອ, ພວກເຂົາຮູ້ວ່າມັນເປັນຄວາມຈິງຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ. ແຕ່ສ່ວນຜູ້ທີ່ປະຕິເສດ, ພວກເຂົາກ່າວວ່າ: 'ອັລລໍຮ໌ປະສົງສິ່ງໃດດ້ວຍຕົວຢ່າງນີ້?' ພຣະອົງຊົງເຮັດໃຫ້ຫຼາຍຄົນຫຼົງທາງດ້ວຍມັນ ແລະຊົງຊີ້ແນະຫຼາຍຄົນດ້ວຍມັນ. ແລະພຣະອົງບໍ່ໄດ້ເຮັດໃຫ້ຜູ້ໃດຫຼົງທາງດ້ວຍມັນນອກຈາກຜູ້ທີ່ລະເມີດ.", english: "Indeed, Allah is not timid to present an example - that of a mosquito or what is smaller than it. And those who believe know that it is the truth from their Lord. But as for those who disbelieve, they say, 'What did Allah intend by this as an example?' He misleads many thereby and guides many thereby. And He misleads not thereby except the defiantly disobedient," },
  { number: "2:27", arabic: "الَّذِينَ يَنقُضُونَ عَهْدَ اللَّهِ مِن بَعْدِ مِيثَاقِهِ وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَن يُوصَلَ وَيُفْسِدُونَ فِي الْأَرْضِ ۚ أُولَٰئِكَ هُمُ الْخَاسِرُونَ", lao: "ຜູ້ທີ່ລະເມີດສັນຍາຂອງອັລລໍຮ໌ຫຼັງຈາກທີ່ໄດ້ຢືນຢັນມັນ, ແລະຕັດສິ່ງທີ່ອັລລໍຮ໌ໄດ້ສັ່ງໃຫ້ເຊື່ອມຕໍ່, ແລະສ້າງຄວາມເສຍຫາຍໃນແຜ່ນດິນ. ພວກເຂົາຄືຜູ້ທີ່ຂາດທຶນ.", english: "Who break the covenant of Allah after contracting it and sever that which Allah has ordered to be joined and cause corruption on earth. It is those who are the losers." },
  { number: "2.28", arabic: "كَيْفَ تَكْfُرُونَ بِاللَّهِ وَكُنتُمْ أَمْوَاتًا فَأَحْيَاكُمْ ۖ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ثُمَّ إِلَيْهِ تُرْjَعُونَ", lao: "ພວກເຈົ້າຈະປະຕິເສດອັລລໍຮ໌ໄດ້ແນວໃດ, ທັງທີ່ພວກເຈົ້າເຄີຍຕາຍ ແລ້ວພຣະອົງໄດ້ໃຫ້ພວກເຈົ້າມີຊີວິດ? ຫຼັງຈາກນັ້ນ, ພຣະອົງຈະໃຫ້ພວກເຈົ້າຕາຍ, ແລ້ວຈະໃຫ້ພວກເຈົ້າມີຊີວິດອີກ, ແລ້ວພວກເຈົ້າຈະຖືກນຳກັບໄປຫາພຣະອົງ.", english: "How can you disbelieve in Allah when you were lifeless and He brought you to life; then He will cause you to die, then He will bring you [back] to life, and then to Him you will be returned." },
  { number: "2.29", arabic: "هُوَ الَّذِي خَلَقَ لَكُم مَّا فِي الْأَرْضِ جَمِيعًا ثُمَّ اسْتَوَىٰ إِلَى السَّمَاءِ فَسَوَّاهُنَّ سَبْعَ سَمَاوَاتٍ ۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ", lao: "ພຣະອົງຄືຜູ້ທີ່ໄດ້ສ້າງທຸກສິ່ງໃນແຜ່ນດິນໄວ້ສຳລັບພວກເຈົ້າ, ຫຼັງຈາກນັ້ນພຣະອົງໄດ້ຫັນໄປຫາທ້ອງຟ້າ ແລະໄດ້ເຮັດໃຫ້ມັນສົມບູນເປັນເຈັດຊັ້ນຟ້າ. ແລະພຣະອົງຊົງຮູ້ທຸກສິ່ງ.", english: "It is He who created for you all of that which is on the earth. Then He directed Himself to the heaven, [His being above all creation], and made them seven heavens, and He is Knowing of all things." },
  { number: "2.30", arabic: "وَإِذْ قَالَ رَبُّكَ لِلْمَلَائِكَةِ إِنِّي جَاعِلٌ فِي الْأَرْضِ خَلِيفَةً ۖ قَالُوا أَتَجْعَلُ فِيهَا مَن يُفْسِدُ فِيهَا وَيَسْفِكُ الدِّمَاءَ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ ۖ قَالَ إِنِّي أَعْلَمُ مَا لَا تَعْلَمُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເວລາທີ່ພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໄດ້ກ່າວແກ່ທູດສະຫວັນວ່າ: 'ແທ້ຈິງ, ຂ້ອຍຈະຕັ້ງຜູ້ຕາງໜ້າໄວ້ໃນແຜ່ນດິນ.' ພວກເຂົາກ່າວວ່າ: 'ພຣະອົງຈະຕັ້ງຜູ້ທີ່ຈະສ້າງຄວາມເສຍຫາຍແລະເຮັດໃຫ້ເລືອດໄຫຼໃນນັ້ນບໍ, ທັງທີ່ພວກເຮົາສັນລະເສີນພຣະອົງແລະຍ້ອງຍໍຄວາມບໍລິສຸດຂອງພຣະອົງ?' ພຣະອົງກ່າວວ່າ: 'ແທ້ຈິງ, ຂ້ອຍຮູ້ໃນສິ່ງທີ່ພວກເຈົ້າບໍ່ຮູ້.'", english: "And [mention, O Muhammad], when your Lord said to the angels, 'Indeed, I will make upon the earth a successive authority.' They said, 'Will You place upon it one who causes corruption therein and sheds blood, while we declare Your praise and sanctify You?' Allah said, 'Indeed, I know that which you do not know.'" },
  { number: "2:31", arabic: "وَعَلَّمَ آدَمَ الْأَسْمَاءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلَائِكَةِ فَقَالَ أَنبِئُونِي بِأَسْمَاءِ هَٰؤُلَاءِ إِن كُنتُمْ صَادِقِينَ", lao: "ແລະພຣະອົງໄດ້ສອນຊື່ທັງໝົດໃຫ້ແກ່ອາດັມ, ຫຼັງຈາກນັ້ນພຣະອົງໄດ້ສະແດງພວກເຂົາຕໍ່ໜ້າທູດສະຫວັນ ແລະກ່າວວ່າ: 'ຈົ່ງບອກຊື່ຂອງສິ່ງເຫຼົ່ານີ້ໃຫ້ຂ້ອຍຮູ້, ຖ້າພວກເຈົ້າເປັນຜູ້ທີ່ສັດຈິງ.'", english: "And He taught Adam the names - all of them. Then He showed them to the angels and said, 'Inform Me of the names of these, if you are truthful.'" },
  { number: "2:32", arabic: "قَالُوا سُبْحَانَكَ لَا عِلْمَ لَنَا إِلَّا مَا عَلَّمْتَنَا ۖ إِنَّكَ أَنتَ الْعَلِيمُ الْحَكِيمُ", lao: "ພວກເຂົາກ່າວວ່າ: 'ມະຫາບໍລິສຸດແດ່ພຣະອົງ! ພວກເຮົາບໍ່ມີຄວາມຮູ້ນອກຈາກສິ່ງທີ່ພຣະອົງໄດ້ສອນພວກເຮົາ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງຮອບຮູ້, ຜູ້ຊົງມີປັນຍາ.'", english: "They said, 'Exalted are You; we have no knowledge except what You have taught us. Indeed, it is You who is the Knowing, the Wise.'" },
  { number: "2:33", arabic: "قَالَ يَا آدَمُ أَنبِئْهُم بِأَسْمَائِهِمْ ۖ فَلَمَّا أَنبَأَهُم بِأَسْمَائِهِمْ قَالَ أَلَمْ أَقُل لَّكُمْ إِنِّي أَعْلَمُ غَيْبَ السَّمَاوَاتِ وَالْأَرْضِ وَأَعْلَمُ مَا تُبْدُونَ وَمَا كُنتُمْ تَكْتُمُونَ", lao: "ພຣະອົງກ່າວວ່າ: 'ໂອ້ ອາດັມ, ຈົ່ງບອກຊື່ຂອງພວກເຂົາໃຫ້ພວກເຂົາຮູ້.' ເມື່ອລາວໄດ້ບອກຊື່ຂອງພວກເຂົາ, ພຣະອົງກ່າວວ່າ: 'ຂ້ອຍບໍ່ໄດ້ບອກພວກເຈົ້າບໍວ່າ ແທ້ຈິງຂ້ອຍຮູ້ຄວາມລັບຂອງຊັ້ນຟ້າແລະແຜ່ນດິນ, ແລະຂ້ອຍຮູ້ສິ່ງທີ່ພວກເຈົ້າເປີດເຜີຍແລະສິ່ງທີ່ພວກເຈົ້າປິດບັງ?'", english: "He said, 'O Adam, inform them of their names.' And when he had informed them of their names, He said, 'Did I not tell you that I know the unseen [aspects] of the heavens and the earth? And I know what you reveal and what you have concealed.'" },
  { number: "2:34", arabic: "وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ أَبَىٰ وَاسْتَكْبَرَ وَكَانَ مِنَ الْكَافِرِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ກ່າວແກ່ທູດສະຫວັນວ່າ: 'ຈົ່ງກົ້ມຂາບຕໍ່ອາດັມ.' ພວກເຂົາກໍກົ້ມຂາບຍົກເວັ້ນອິບລີສ. ລາວປະຕິເສດແລະອວດດີ, ແລະລາວຢູ່ໃນກຸ່ມຜູ້ປະຕິເສດ.", english: "And [mention] when We said to the angels, 'Prostrate before Adam'; so they prostrated, except for Iblees. He refused and was arrogant and became of the disbelievers." },
  { number: "2:35", arabic: "وَقُلْنَا يَا آدَمُ اسْكُنْ أَنتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هَٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِمِينَ", lao: "ແລະເຮົາໄດ້ກ່າວວ່າ: 'ໂອ້ ອາດັມ, ຈົ່ງອາໄສຢູ່ໃນສວນສະຫວັນທັງເຈົ້າແລະຄູ່ຄອງຂອງເຈົ້າ, ແລະຈົ່ງກິນຈາກມັນຢ່າງອຸດົມສົມບູນຕາມທີ່ເຈົ້າຕ້ອງການ, ແຕ່ຢ່າເຂົ້າໃກ້ຕົ້ນໄມ້ນີ້, ຖ້າບໍ່ດັ່ງນັ້ນພວກເຈົ້າຈະກາຍເປັນຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.'", english: "And We said, 'O Adam, dwell, you and your wife, in Paradise and eat therefrom in [ease and] abundance from wherever you will. But do not approach this tree, lest you be among the wrongdoers.'" },
  { number: "2:36", arabic: "فَأَزَلَّهُمَا الشَّيْطَانُ عَنْهَا فَأَخْرَجَهُمَا مِمَّا كَانَا فِيهِ ۖ وَقُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۖ وَلَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ إِلَىٰ حِينٍ", lao: "ແຕ່ຊັຍຕອນໄດ້ເຮັດໃຫ້ພວກເຂົາຫຼົງຜິດຈາກມັນ ແລະໄດ້ຂັບໄລ່ພວກເຂົາອອກຈາກສິ່ງທີ່ພວກເຂົາເຄີຍຢູ່. ແລະເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງລົງໄປ, ບາງຄົນໃນພວກເຈົ້າເປັນສັດຕູຕໍ່ກັນ. ແລະສຳລັບພວກເຈົ້າຈະມີທີ່ພັກອາໄສໃນແຜ່ນດິນແລະຄວາມສຸກຊົ່ວຄາວ.'", english: "But Satan caused them to slip out of it and removed them from that [condition] in which they had been. And We said, 'Go down, [all of you], as enemies to one another, and you will have upon the earth a place of settlement and provision for a time.'" },
  { number: "2:37", arabic: "فَتَلَقَّىٰ آدَمُ مِن رَّبِّهِ كَلِمَاتٍ فَتَابَ عَلَيْهِ ۚ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ", lao: "ຫຼັງຈາກນັ້ນ, ອາດັມໄດ້ຮັບຄຳສອນຈາກພຣະຜູ້ເປັນເຈົ້າຂອງລາວ, ແລ້ວພຣະອົງໄດ້ອະໄພໃຫ້ລາວ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງອະໄພ, ຜູ້ຊົງເມດຕາສະເໝີ.", english: "Then Adam received from his Lord [some] words, and He accepted his repentance. Indeed, it is He who is the Accepting of repentance, the Merciful." },
  { number: "2:38", arabic: "قُلْنَا اهْبِطُوا مِنْهَا جَمِيعًا ۖ فَإِمَّا يَأْتِيَنَّكُم مِّنِّي هُدًى فَمَن تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງລົງໄປຈາກທີ່ນີ້ທັງໝົດ. ຖ້າມີການຊີ້ແນະຈາກຂ້ອຍມາຫາພວກເຈົ້າ, ຜູ້ໃດທີ່ຕາມການຊີ້ແນະຂອງຂ້ອຍ, ຈະບໍ່ມີຄວາມຢ້ານກົວໃດໆຕໍ່ພວກເຂົາ ແລະພວກເຂົາຈະບໍ່ໂສກເສົ້າ.'", english: "We said, 'Go down from it, all of you. And when guidance comes to you from Me, whoever follows My guidance - there will be no fear concerning them, nor will they grieve.'" },
  { number: "2:39", arabic: "وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ النَّarِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ແລະຜູ້ທີ່ປະຕິເສດແລະຕົວະຕໍ່ອາຍະຕ່າງໆຂອງເຮົາ, ພວກເຂົາຄືຊາວນາລົກ; ພວກເຂົາຈະຢູ່ໃນນັ້ນຕະຫຼອດໄປ.", english: "And those who disbelieve and deny Our signs - those are the companions of the Fire; they will abide therein eternally." },
  { number: "2:40", arabic: "يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَوْفُوا بِعَهْدِي أُوفِ بِعَهْدِكُمْ وَإِيَّايَ فَarْهَبُونِ", lao: "ໂອ້ ລູກຫຼານຂອງອິດສະຣາເອວ, ຈົ່ງລະນຶກເຖິງຄວາມໂປດປານຂອງຂ້ອຍທີ່ຂ້ອຍໄດ້ໃຫ້ແກ່ພວກເຈົ້າ, ແລະຈົ່ງຮັກສາສັນຍາຂອງຂ້ອຍ, ຂ້ອຍກໍຈະຮັກສາສັນຍາຂອງພວກເຈົ້າ. ແລະຈົ່ງຢຳເກງຂ້ອຍພຽງຜູ້ດຽວ.", english: "O Children of Israel, remember My favor which I have bestowed upon you and fulfill My covenant [upon you] that I will fulfill your covenant [from Me], and be afraid of [only] Me." },
  { number: "2:41", arabic: "وَآمِنُوا بِمَا أَنزَلْتُ مُصَدِّقًا لِّمَا مَعَكُمْ وَلَا تَكُونُوا أَوَّلَ كَافِرٍ بِهِ ۖ وَلَا تَشْتَرُوا بِآيَاتِي ثَمَنًا قَلِيلًا وَإِيَّايَ فَاتَّقُونِ", lao: "ແລະຈົ່ງເຊື່ອໃນສິ່ງທີ່ຂ້ອຍໄດ້ເປີດເຜີຍລົງມາ, ເຊິ່ງຢືນຢັນສິ່ງທີ່ພວກເຈົ້າມີຢູ່, ແລະຢ່າເປັນຜູ້ທຳອິດທີ່ປະຕິເສດມັນ. ແລະຢ່າແລກປ່ຽນອາຍະຕ່າງໆຂອງຂ້ອຍດ້ວຍລາຄາທີ່ຕໍ່າຕ້ອຍ, ແລະຈົ່ງຢຳເກງຂ້ອຍພຽງຜູ້ດຽວ.", english: "And believe in what I have sent down confirming that which is [already] with you, and be not the first to disbelieve in it. And do not exchange My signs for a small price, and fear [only] Me." },
  { number: "2:42", arabic: "وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَأَنتُمْ تَعْلَمُونَ", lao: "ແລະຢ່າປົນຄວາມຈິງກັບຄວາມຕົວະ, ແລະຢ່າປິດບັງຄວາມຈິງທັງທີ່ພວກເຈົ້າຮູ້.", english: "And do not mix the truth with falsehood or conceal the truth while you know [it]." },
  { number: "2:43", arabic: "وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَarْكَعُوا مَعَ الرَّاكِعِينَ", lao: "ແລະຈົ່ງດຳລົງການລະໝາດ, ແລະຈົ່ງຈ່າຍຊາກາດ, ແລະຈົ່ງກົ້ມຂາບພ້ອມກັບຜູ້ທີ່ກົ້ມຂາບ.", english: "And establish prayer and give zakah and bow with those who bow [in worship and obedience]." },
  { number: "2:44", arabic: "أَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنسَوْنَ أَنفُسَكُمْ وَأَنتُمْ تَتْلُونَ الْكِتَابَ ۚ أَفَلَا تَعْقِلُونَ", lao: "ພວກເຈົ້າສັ່ງໃຫ້ຄົນອື່ນເຮັດຄວາມດີແລ້ວລືມຕົວເອງບໍ, ທັງທີ່ພວກເຈົ້າອ່ານຄຳພີ? ພວກເຈົ້າບໍ່ຄິດບໍ?", english: "Do you order righteousness of the people and forget yourselves while you recite the Scripture? Then will you not reason?" },
  { number: "2:45", arabic: "وَاسْتَعِينُوا بِالصَّبْرِ وَالصَّلَاةِ ۚ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى الْخَاشِعِينَ", lao: "ແລະຈົ່ງຂໍຄວາມຊ່ວຍເຫຼືອດ້ວຍຄວາມອົດທົນແລະການລະໝາດ. ແລະແທ້ຈິງ, ມັນເປັນສິ່ງທີ່ຍາກລຳບາກຍົກເວັ້ນສຳລັບผู้ທີ່ຖ່ອມຕົນ.", english: "And seek help through patience and prayer, and indeed, it is difficult except for the humbly submissive [to Allah]" },
  { number: "2:46", arabic: "الَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَاقُو رَبِّهِمْ وَأَنَّهُمْ إِلَيْهِ رَاجِعُونَ", lao: "ຜູ້ທີ່ແນ່ໃຈວ່າພວກເຂົາຈະໄດ້ພົບກັບພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ ແລະພວກເຂົາຈະກັບໄປຫາພຣະອົງ.", english: "Who are certain that they will meet their Lord and that they will return to Him." },
  { number: "2:47", arabic: "يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ", lao: "ໂອ້ ລູກຫຼານຂອງອິດສະຣາເອວ, ຈົ່ງລະນຶກເຖິງຄວາມໂປດປານຂອງຂ້ອຍທີ່ຂ້ອຍໄດ້ໃຫ້ແກ່ພວກເຈົ້າ ແລະວ່າຂ້ອຍໄດ້ຍົກຍ້ອງພວກເຈົ້າເໜືອຄົນທັງຫຼາຍ.", english: "O Children of Israel, remember My favor that I have bestowed upon you and that I preferred you over the worlds." },
  { number: "2:48", arabic: "وَاتَّقُوا يَوْمًا لَّا تَجْزِي نَفْسٌ عَن نَّفْسٍ شَيْئًا وَلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَلَا هُمْ يُنصَرُونَ", lao: "ແລະຈົ່ງຢຳເກງວັນທີ່ບໍ່ມີໃຜສາມາດຊ່ວຍເຫຼືອໃຜໄດ້, ແລະບໍ່ມີການໄກ່ເກ່ຍໃດໆຈະຖືກຮັບຈາກລາວ, ແລະບໍ່ມີຄ່າໄຖ່ໃດໆຈະຖືກເອົາຈາກລາວ, ແລະພວກເຂົາຈະບໍ່ໄດ້ຮັບການຊ່ວຍເຫຼືອ.", english: "And fear a Day when no soul will suffice for another soul at all, nor will intercession be accepted from it, nor will compensation be taken from it, nor will they be aided." },
  { number: "2:49", arabic: "وَإِذْ نَجَّيْنَاكُم مِّنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ يُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ ۚ وَفِي ذَٰلِكُم bَلَاءٌ مِّن رَّبِّكُمْ عَظِيمٌ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ຊ່ວຍພວກເຈົ້າໃຫ້ພົ້ນຈາກພັກພວກຂອງຟາໂຣ, ຜູ້ທີ່ໄດ້ທໍລະມານພວກເຈົ້າຢ່າງໂຫດຮ້າຍ, ຂ້າລູກຊາຍຂອງພວກເຈົ້າແລະປະໄວ້ລູກສາວຂອງພວກເຈົ້າ. ແລະໃນນັ້ນມີການທົດສອບອັນໃຫຍ່ຫຼວງຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຈົ້າ.", english: "And [recall] when We saved your forefathers from the people of Pharaoh, who afflicted you with the worst torment, slaughtering your [newborn] sons and keeping your females alive. And in that was a great trial from your Lord." },
  { number: "2:50", arabic: "وَإِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَأَنجَيْنَاكُمْ وَأَغْرَقْنَا آلَ فِرْعَوْنَ وَأَنتُمْ تَنظُرُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ແຍກທະເລໃຫ້ພວກເຈົ້າ, ແລ້ວໄດ້ຊ່ວຍພວກເຈົ້າໃຫ້ພົ້ນແລະໄດ້ຈົມພັກພວກຂອງຟາໂຣໃນຂະນະທີ່ພວກເຈົ້າເບິ່ງຢູ່.", english: "And [recall] when We parted the sea for you and saved you and drowned the people of Pharaoh while you were looking on." },
  { number: "2:51", arabic: "وَإِذْ وَاعَدْنَا مُوسَىٰ أَرْبَعِينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِن بَعْدِهِ وَأَنتُمْ ظَالِمُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ສັນຍາກັບມູຊາເປັນເວລາສີ່ສິບຄືນ, ແລ້ວພວກເຈົ້າໄດ້ເອົາລູກງົວມາເປັນພະເຈົ້າຫຼັງຈາກລາວ, ແລະພວກເຈົ້າກໍເປັນຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.", english: "And [recall] when We made an appointment with Moses for forty nights. Then you took [for worship] the calf after him, while you were wrongdoers." },
  { number: "2:52", arabic: "ثُمَّ عَفَوْنَا عَنكُم مِّن بَعْدِ ذَٰلِكَ لَعَلَّكُمْ تَشْكُرُونَ", lao: "ຫຼັງຈາກນັ້ນ, ເຮົາໄດ້ອະໄພໃຫ້ພວກເຈົ້າ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ຂອບໃຈ.", english: "Then We forgave you after that so perhaps you would be grateful." },
  { number: "2:53", arabic: "وَإِذْ آتَيْنَا مُوسَى الْكِتَابَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ໃຫ້ຄຳພີແລະສິ່ງທີ່ຈຳແນກແກ່ມູຊາ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ຮັບການຊີ້ແນະ.", english: "And [recall] when We gave Moses the Scripture and the criterion that perhaps you would be guided." },
  { number: "2:54", arabic: "وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنفُسَكُم بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوبُوا إِلَىٰ bَarِئِكُمْ فَاقْتُلُوا أَنفُسَكُمْ ذَٰلِكُمْ خَيْرٌ لَّكُمْ عِندَ bَarِئِكُمْ فَتَابَ عَلَيْكُمْ ۚ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອມູຊາໄດ້ກ່າວແກ່ປະຊາຊົນຂອງລາວວ່າ: 'ໂອ້ ປະຊາຊົນຂອງຂ້ອຍ, ແທ້ຈິງພວກເຈົ້າໄດ້ທຳຮ້າຍຕົວເອງໂດຍການເອົາລູກງົວມາເປັນພະເຈົ້າ. ດັ່ງນັ້ນ, ຈົ່ງກັບໃຈຕໍ່ພຣະຜູ້ສ້າງຂອງພວກເຈົ້າແລະຈົ່ງຂ້າຕົວເອງ. ນັ້ນເປັນສິ່ງທີ່ດີກວ່າສຳລັບພວກເຈົ້າຕໍ່ໜ້າພຣະຜູ້ສ້າງຂອງພວກເຈົ້າ.' ແລ້ວພຣະອົງໄດ້ອະໄພໃຫ້ພວກເຈົ້າ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງອະໄພ, ຜູ້ຊົງເມດຕາ.", english: "And [recall] when Moses said to his people, 'O my people, indeed you have wronged yourselves by your taking of the calf [for worship]. So repent to your Creator and kill yourselves. That is best for you in the sight of your Creator.' Then He accepted your repentance; indeed, He is the Accepting of repentance, the Merciful." },
  { number: "2:55", arabic: "وَإِذْ قُلْتُمْ يَا مُوسَىٰ لَن نُّؤْمِنَ لَكَ حَتَّىٰ نَرَى اللَّهَ جَهْرَةً فَأَخَذَتْكُمُ الصَّاعِقَةُ وَأَنتُمْ تَنظُرُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອພວກເຈົ້າໄດ້ກ່າວວ່າ: 'ໂອ້ ມູຊາ, ພວກເຮົາຈະບໍ່ເຊື່ອເຈົ້າຈົນກວ່າພວກເຮົາຈະເຫັນອັລລໍຮ໌ຢ່າງເປີດເຜີຍ.' ແລ້ວສຽງຟ້າຜ່າໄດ້ເອົາພວກເຈົ້າໄປໃນຂະນະທີ່ພວກເຈົ້າເບິ່ງຢູ່.", english: "And [recall] when you said, 'O Moses, we will never believe you until we see Allah outright'; so the thunderbolt took you while you were looking on." },
  { number: "2:56", arabic: "ثُمَّ bَعَثْنَاكُم مِّن بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَ", lao: "ຫຼັງຈາກນັ້ນ, ເຮົາໄດ້ປຸກພວກເຈົ້າຂຶ້ນມາຫຼັງຈາກຄວາມຕາຍຂອງພວກເຈົ້າ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ຂອບໃຈ.", english: "Then We revived you after your death that perhaps you would be grateful." },
  { number: "2:57", arabic: "وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَأَنزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوَىٰ ۖ كُلُوا مِن طَيِّbَاتِ مَا رَزَقْنَاكُمْ ۖ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ", lao: "ແລະເຮົາໄດ້ເຮັດໃຫ້ເມກເປັນຮົ່ມເງົາໃຫ້ພວກເຈົ້າ ແລະໄດ້ສົ່ງມັນນາແລະນົກກະທາລົງມາໃຫ້ພວກເຈົ້າ. 'ຈົ່ງກິນຈາກສິ່ງທີ່ດີທີ່ເຮົາໄດ້ໃຫ້ແກ່ພວກເຈົ້າ.' ແລະພວກເຂົາບໍ່ໄດ້ທຳຮ້າຍເຮົາ, ແຕ່ພວກເຂົາທຳຮ້າຍຕົວເອງ.", english: "And We shaded you with clouds and sent down to you manna and quails, [saying], 'Eat from the good things with which We have provided you.' And they wronged Us not - but they were [only] wronging themselves." },
  { number: "2:58", arabic: "وَإِذْ قُلْنَا ادْخُلُوا هَٰذِهِ الْقَرْيَةَ فَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَادْخُلُوا الْbَابَ سُjَّدًا وَقُولُوا حِطَّةٌ نَّغْفِرْ لَكُمْ خَطَايَاكُمْ ۚ وَسَنَزِيدُ الْمُحْسِنِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງເຂົ້າໄປໃນເມືອງນີ້ແລະກິນຈາກມັນຢ່າງອຸດົມສົມບູນຕາມທີ່ເຈົ້າຕ້ອງການ, ແລະຈົ່ງເຂົ້າປະຕູດ້ວຍການກົ້ມຂາບແລະກ່າວວ່າ 'ຮິດຕໍ', ເຮົາຈະອະໄພໃຫ້ຄວາມຜິດຂອງພວກເຈົ້າ. ແລະເຮົາຈະເພີ່ມໃຫ້ແກ່ผู้ທີ່ເຮັດຄວາມດີ.'", english: "And [recall] when We said, 'Enter this city and eat from it wherever you will in [ease and] abundance, and enter the gate bowing humbly and say, 'Relieve us of our burdens.' We will [then] forgive your sins for you, and We will increase the doers of good.'" },
  { number: "2:59", arabic: "فَبَدَّلَ الَّذِينَ ظَلَمُوا قَوْلًا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَنزَلْنَا عَلَى الَّذِينَ ظَلَمُوا رِjْزًا مِّنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ", lao: "ແຕ່ຜູ້ທີ່ບໍ່ຍຸດຕິທຳໄດ້ປ່ຽນຄຳເວົ້າທີ່ຖືກບອກໃຫ້ພວກເຂົາ. ດັ່ງນັ້ນ, ເຮົາໄດ້ສົ່ງການລົງໂທດຈາກທ້ອງຟ້າລົງມາໃສ່ຜູ້ທີ່ບໍ່ຍຸດຕິທຳເນື່ອງຈາກການລະເມີດຂອງພວກເຂົາ.", english: "But those who wronged changed [the words] to a statement other than that which had been said to them, so We sent down upon those who wronged a punishment from the sky because they were defiantly disobeying." },
  { number: "2:60", arabic: "وَإِذِ اسْتَسْقَىٰ مُوسَىٰ لِقَوْمِهِ فَقُلْنَا اضْرِب بِّعَصَاكَ الْحَjَرَ ۖ فَانفَjَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۖ قَدْ عَلِمَ كُلُّ أُنَاسٍ مَّشْرَbَهُمْ ۖ كُلُوا وَاشْرَبُوا مِن رِّزْقِ اللَّهِ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອມູຊາໄດ້ຂໍນ້ຳໃຫ້ປະຊາຊົນຂອງລາວ, ແລ້ວເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງຕີຫີນດ້ວຍໄມ້ເທົ້າຂອງເຈົ້າ.' ແລ້ວສິບສອງຕານ້ຳໄດ້ໄຫຼອອກມາຈາກມັນ. ແຕ່ລະເຜົ່າໄດ້ຮູ້ບ່ອນດື່ມຂອງຕົນ. 'ຈົ່ງກິນແລະດື່ມຈາກສິ່ງທີ່ອັລລໍຮ໌ໄດ້ໃຫ້, ແລະຢ່າສ້າງຄວາມເສຍຫາຍໃນແຜ່ນດິນ.'", english: "And [recall] when Moses prayed for water for his people, so We said, 'Strike with your staff the stone.' And there gushed forth from it twelve springs, and every people knew its watering place. 'Eat and drink from the provision of Allah, and do not commit abuse on the earth, spreading corruption.'" },
  { number: "2:61", arabic: "وَإِذْ قُلْتُمْ يَا مُوسَىٰ لَن نَّصْبِرَ عَلَىٰ طَعَامٍ وَاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِjْ لَنَا مِمَّا تُنبِتُ الْأَرْضُ مِن بَقْلِهَا وَقِثَّائِهَا وَفُومِهَا وَعَدَسِهَا وَbَصَلِهَا ۖ قَالَ أَتَسْتَبْدِلُونَ الَّذِي هُوَ أَدْنَىٰ بِالَّذِي هُوَ خَيْرٌ ۚ اهْبِطُوا مِصْرًا فَإِنَّ لَكُم مَّا سَأَلْتُمْ ۗ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَbَاءُوا بِغَضَبٍ مِّنَ اللَّهِ ۗ ذَٰلِكَ بِأَنَّهُمْ كَانُوا يَكْfُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّbِيِّينَ بِغَيْرِ الْحَقِّ ۗ ذَٰلِكَ بِمَا عَصَوا وَّكَانُوا يَعْتَدُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອພວກເຈົ້າກ່າວວ່າ: 'ໂອ້ ມູຊາ, ພວກເຮົາບໍ່ສາມາດອົດທົນກັບອາຫານຊະນິດດຽວໄດ້. ດັ່ງນັ້ນ, ຈົ່ງຂໍພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໃຫ້ເອົາສິ່ງທີ່ແຜ່ນດິນປູກອອກມາໃຫ້ພວກເຮົາ, ເຊັ່ນ: ຜັກ, ໝາກແຕງ, ກະທຽມ, ຖົ່ວ, ແລະຫົວຫອມ.' ລາວໄດ້ກ່າວວ່າ: 'ພວກເຈົ້າຈະແລກປ່ຽນສິ່ງທີ່ດີກວ່າດ້ວຍສິ່ງທີ່ຕໍ່າກວ່າບໍ? ຈົ່ງລົງໄປໃນເມືອງໃດກໍໄດ້, ແລະພວກເຈົ້າຈະໄດ້ສິ່ງທີ່ພວກເຈົ້າຂໍ.' ແລະຄວາມອັບອາຍແລະຄວາມທຸກຍາກໄດ້ຖືກກຳນົດໃຫ້ພວກເຂົາ, ແລະພວກເຂົາໄດ້ຮັບຄວາມໂກດແຄ້ນຈາກອັລລໍຮ໌. ນັ້ນເປັນເພາະວ່າພວກເຂົາປະຕິເສດອາຍະຕ່າງໆຂອງອັລລໍຮ໌ແລະຂ້າສາດສະດາโดยບໍ່ຍຸດຕິທຳ. ນັ້ນເປັນເພາະວ່າພວກເຂົາບໍ່ເຊື່ອຟັງແລະລະເມີດ.", english: "And [recall] when you said, 'O Moses, we can never endure one [kind of] food. So call upon your Lord to bring forth for us from the earth its green herbs and its cucumbers and its garlic and its lentils and its onions.' [Moses] said, 'Would you exchange what is better for what is less? Go into [any] settlement and indeed, you will have what you have asked.' And they were covered with humiliation and poverty and returned with anger from Allah [upon them]. That was because they [repeatedly] disbelieved in the signs of Allah and killed the prophets without right. That was because they disobeyed and were [habitually] transgressing." },
  { number: "2:62", arabic: "إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالنَّصَarَىٰ وَالصَّابِئِينَ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ أَjْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ແທ້ຈິງ, ຜູ້ທີ່ເຊື່ອ, ແລະຜູ້ທີ່ເປັນຊາວຢິວ, ແລະຊາວຄຣິດ, ແລະຊາວຊາບີອິນ, ຜູ້ໃດທີ່ເຊື່ອໃນອັລລໍຮ໌ແລະວັນສຸດທ້າຍແລະເຮັດຄວາມດີ, ພວກເຂົາຈະໄດ້ຮັບລາງວັນຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ, ແລະຈະບໍ່ມີຄວາມຢ້ານກົວໃດໆຕໍ່ພວກເຂົາ ແລະພວກເຂົາຈະບໍ່ໂສກເສົ້າ.", english: "Indeed, those who believed and those who were Jews or Christians or Sabeans [before Prophet Muhammad] - those [among them] who believed in Allah and the Last Day and did righteousness - will have their reward with their Lord, and no fear will there be concerning them, nor will they grieve." },
  { number: "2:63", arabic: "وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُم بِقُوَّةٍ وَاذْكُرُوا مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເອົາສັນຍາຈາກພວກເຈົ້າແລະຍົກພູເຂົາຂຶ້ນເໜືອພວກເຈົ້າ, [ໂດຍກ່າວວ່າ]: 'ຈົ່ງຍຶດໝັ້ນໃນສິ່ງທີ່ເຮົາໄດ້ໃຫ້ແກ່ພວກເຈົ້າຢ່າງເຂັ້ມແຂງແລະຈົ່ງລະນຶກເຖິງສິ່ງທີ່ຢູ່ໃນນັ້ນ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ມີຄວາມຢຳເກງ.'", english: "And [recall] when We took your covenant, [O Children of Israel, to abide by the Torah] and We raised over you the mount, [saying], 'Take what We have given you with determination and remember what is in it that perhaps you may become righteous.'" },
  { number: "2:64", arabic: "ثُمَّ تَوَلَّيْتُم مِّن بَعْدِ ذَٰلِكَ ۖ فَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ لَكُنتُم مِّنَ الْخَاسِرِينَ", lao: "ຫຼັງຈາກນັ້ນ, ພວກເຈົ້າໄດ້ຫັນໜີ. ແລະຖ້າບໍ່ແມ່ນຄວາມໂປດປານແລະຄວາມເມດຕາຂອງອັລລໍຮ໌ຕໍ່ພວກເຈົ້າ, ພວກເຈົ້າກໍຈະກາຍເປັນผู้ທີ່ຂາດທຶນ.", english: "Then you turned away after that. And if not for the favor of Allah upon you and His mercy, you would have been among the losers." },
  { number: "2:65", arabic: "وَلَقَدْ عَلِمْتُمُ الَّذِينَ اعْتَدَوْا مِنكُمْ فِي السَّbْتِ فَقُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِئِينَ", lao: "ແລະແນ່ນອນ, ພວກເຈົ້າຮູ້ຈັກຜູ້ທີ່ລະເມີດໃນວັນສະບາໂຕໃນກຸ່ມຂອງພວກເຈົ້າ, ແລ້ວເຮົາໄດ້ກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງເປັນລີງที่ຖືກຂັບໄລ่.'", english: "And you had already known about those who transgressed among you concerning the sabbath, and We said to them, 'Be apes, despised.'" },
  { number: "2:66", arabic: "فَجَعَلْنَاهَا نَكَالًا لِّمَا بَيْنَ يَدَيْهَا وَمَا خَلْfَهَا وَمَوْعِظَةً لِّلْمُتَّقِينَ", lao: "ແລ້ວເຮົາໄດ້ເຮັດໃຫ້มันເປັນຕົວຢ່າງສຳລັບຄົນໃນຍຸກນັ້ນແລະຄົນລຸ້ນຫຼັງ, ແລະເປັນຄຳຕັກເຕືອນສຳລັບผู้ທີ່ຢຳເກງ.", english: "And We made it a deterrent punishment for those who were present and those who succeeded [them] and a lesson for those who fear Allah." },
  { number: "2:67", arabic: "وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ إِنَّ اللَّهَ يَأْمُرُكُمْ أَن تَذْبَحُوا بَقَرَةً ۖ قَالُوا أَتَتَّخِذُنَا هُزُوًا ۖ قَالَ أَعُوذُ بِاللَّهِ أَنْ أَكُونَ مِنَ الْجَاهِلِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອມູຊາໄດ້ກ່າວແກ່ປະຊາຊົນຂອງລາວວ່າ: 'ແທ້ຈິງ, ອັລລໍຮ໌ສັ່ງໃຫ້ພວກເຈົ້າຂ້າງົວແມ່.' ພວກເຂົາກ່າວວ່າ: 'ເຈົ້າຈະເຍາະເຍີ້ຍພວກເຮົາບໍ?' ລາວໄດ້ກ່າວວ່າ: 'ຂ້ອຍຂໍຄວາມຄຸ້ມຄອງຈາກອັລລໍຮ໌ໃຫ້ພົ້ນຈາກການເປັນຄົນໂງ່.'", english: "And [recall] when Moses said to his people, 'Indeed, Allah commands you to slaughter a cow.' They said, 'Do you take us in ridicule?' He said, 'I seek refuge in Allah from being among the ignorant.'" },
  { number: "2:68", arabic: "قَالُوا ادْعُ لَنَا رَبَّكَ يُbَيِّن لَّنَا مَا هِيَ ۚ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لَّا فَarِضٌ وَلَا بِكْرٌ عَوَانٌ بَيْنَ ذَٰلِكَ ۖ فَافْعَلُوا مَا تُؤْمَرُونَ", lao: "ພວກເຂົາກ່າວວ່າ: 'ຈົ່ງຂໍພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໃຫ້ອະທິບາຍໃຫ້ພວກເຮົາຮູ້ວ່າມັນເປັນແນວໃດ.' ລາວໄດ້ກ່າວວ່າ: 'ພຣະອົງກ່າວວ່າມັນເປັນງົວທີ່ບໍ່ແກ່ແລະບໍ່ອ່ອນ, ຢູ່ລະຫວ່າງກາງ. ດັ່ງນັ້ນ, ຈົ່ງເຮັດຕາມທີ່ພວກເຈົ້າຖືກສັ່ງ.'", english: "They said, 'Call upon your Lord to make clear to us what it is.' [Moses] said, '[Allah] says, 'It is a cow which is neither old nor virgin, but median between that,' so do what you are commanded.'" },
  { number: "2:69", arabic: "قَالُوا ادْعُ لَنَا رَبَّكَ يُbَيِّن لَّنَا مَا لَوْنُهَا ۚ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ صَفْرَاءُ فَاقِعٌ لَّوْنُهَا تَسُرُّ النَّاظِرِينَ", lao: "ພວກເຂົາກ່າວວ່າ: 'ຈົ່ງຂໍພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໃຫ້ອະທິບາຍໃຫ້ພວກເຮົາຮູ້ວ່າສີຂອງມັນເປັນແນວໃດ.' ລາວໄດ້ກ່າວວ່າ: 'ພຣະອົງກ່າວວ່າມັນເປັນງົວສີເຫຼືອງສົດໃສ, ສີຂອງມັນເຮັດໃຫ້ผู้ເບິ່ງມີຄວາມສຸກ.'", english: "They said, 'Call upon your Lord to show us what is its color.' He said, 'He says, 'It is a yellow cow, bright in color - pleasing to the observers.''" },
  { number: "2:70", arabic: "قَالُوا ادْعُ لَنَا رَبَّكَ يُbَيِّن لَّنَا مَا هِيَ إِنَّ الْbَقَرَ تَشَابَهَ عَلَيْنَا وَإِنَّا إِن شَاءَ اللَّهُ لَمُهْتَدُونَ", lao: "ພວກເຂົາກ່າວວ່າ: 'ຈົ່ງຂໍພຣະຜູ້ເປັນເຈົ້າຂອງເຈົ້າໃຫ້ອະທິບາຍໃຫ້ພວກເຮົາຮູ້ວ່າມັນເປັນແນວໃດ, ເພາະງົວເບິ່ງຄືກັນສຳລັບພວກເຮົາ. ແລະແທ້ຈິງ, ຖ້າອັລລໍຮ໌ປະສົງ, ພວກເຮົາຈະໄດ້ຮັບການຊີ້ແນະ.'", english: "They said, 'Call upon your Lord to make clear to us what it is. Indeed, [all] cows look alike to us. And indeed we, if Allah wills, will be guided.'" },
  { number: "2:71", arabic: "قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لَّا ذَلُولٌ تُثِيرُ الْأَرْضَ وَلَا تَسْقِي الْحَرْثَ مُسَلَّمَةٌ لَّا شِيَةَ فِيهَا ۚ قَالُوا الْآنَ جِئْتَ بِالْحَقِّ ۚ فَذَبَحُوهَا وَمَا كَادُوا يَفْعَلُونَ", lao: "ລາວໄດ້ກ່າວວ່າ: 'ພຣະອົງກ່າວວ່າມັນເປັນງົວທີ່ບໍ່ໄດ້ຖືກໃຊ້ໄຖນາແລະບໍ່ໄດ້ຖືກໃຊ້ຫົດນ້ຳໄຮ່ນາ, ສົມບູນແບບບໍ່ມີຕຳหนิ.' ພວກເຂົາກ່າວວ່າ: 'ຕອນນີ້ເຈົ້າໄດ້ນຳຄວາມຈິງມາແລ້ວ.' ແລ້ວພວກເຂົາກໍຂ້າມັນ, ແຕ່ເກືອບຈະບໍ່ໄດ້ເຮັດ.", english: "He said, 'He says, 'It is a cow neither trained to plow the earth nor to water the field, sound, lacking any blemish.' ' They said, 'Now you have come with the truth.' So they slaughtered it, but they could hardly do it." },
  { number: "2:72", arabic: "وَإِذْ قَتَلْتُمْ نَفْسًا فَادَّarَأْتُمْ فِيهَا ۖ وَاللَّهُ مُخْرِjٌ مَّا كُنتُمْ تَكْتُمُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອພວກເຈົ້າໄດ້ຂ້າຄົນຜູ້ໜຶ່ງແລ້ວຖຽງກັນກ່ຽວກັບມັນ, ແຕ່ອັລລໍຮ໌ຈະເປີດເຜີຍສິ່ງທີ່ພວກເຈົ້າປິດບັງ.", english: "And [recall] when you slew a man and disputed over it, but Allah was to bring out that which you were concealing." },
  { number: "2:73", arabic: "فَقُلْنَا اضْرِبُوهُ بِbَعْضِهَا ۚ كَذَٰلِكَ يُحْيِي اللَّهُ الْمَوْتَىٰ وَيُرِيكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ", lao: "ແລ້ວເຮົາໄດ້ກ່າວວ່າ: 'ຈົ່ງຕີລາວດ້ວຍສ່ວນໜຶ່ງຂອງມັນ.' ເຊັ່ນນັ້ນແຫຼະ ອັລລໍຮ໌ຈະເຮັດໃຫ້ຄົນຕາຍມີຊີວິດຂຶ້ນມາແລະສະແດງອາຍະຕ່າງໆຂອງພຣະອົງໃຫ້ພວກເຈົ້າເຫັນ, ເພື່ອວ່າພວກເຈົ້າຈະໄດ້ຄິດ.", english: "So, We said, 'Strike the slain man with part of it.' Thus does Allah bring the dead to life, and He shows you His signs that you might reason." },
  { number: "2:74", arabic: "ثُمَّ قَسَتْ قُلُوبُكُم مِّن بَعْدِ ذَٰلِكَ فَهِيَ كَالْحِjَarَةِ أَوْ أَشَدُّ قَسْوَةً ۚ وَإِنَّ مِنَ الْحِjَarَةِ لَمَا يَتَفَjَّرُ مِنْهُ الْأَنْهَarُ ۚ وَإِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُjُ مِنْهُ الْمَاءُ ۚ وَإِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللَّهِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", lao: "ຫຼັງຈາກນັ້ນ, ຫົວໃຈຂອງພວກເຈົ້າກໍແຂງກະດ້າງ, ເໝືອນຫີນຫຼືແຂງກວ່າ. ແລະແທ້ຈິງ, ໃນບັນດາຫີນມີບາງກ້ອນທີ່ແມ່ນ້ຳໄຫຼອອກມາ. ແລະມີບາງກ້ອນທີ່ແຕກອອກແລ້ວນ້ຳກໍໄຫຼອອກມາ. ແລະມີບາງກ້ອນທີ່ຕົກລົງມາເພາະຢ້ານອັລລໍຮ໌. ແລະອັລລໍຮ໌ບໍ່ໄດ້ລະເລີຍສິ່ງທີ່ພວກເຈົ້າເຮັດ.", english: "Then your hearts became hardened after that, being like stones or even harder. For indeed, there are stones from which rivers gush forth, and there are some of them that split open and water comes out, and there are some of them that fall down for fear of Allah. And Allah is not unaware of what you do." },
  { number: "2:75", arabic: "أَفَتَطْمَعُونَ أَن يُؤْمِنُوا لَكُمْ وَقَدْ كَانَ فَرِوقٌ مِّنْهُمْ يَسْمَعُونَ كَلَامَ اللَّهِ ثُمَّ يُحَرِّfُونَهُ مِن بَعْدِ مَا عَقَلُوهُ وَهُمْ يَعْلَمُونَ", lao: "ພວກເຈົ້າຫວັງວ່າພວກເຂົາຈະເຊື່ອໃນພວກເຈົ້າບໍ, ທັງທີ່ກຸ່ມໜຶ່ງໃນພວກເຂົາເຄີຍໄດ້ຍິນຄຳເວົ້າຂອງອັລລໍຮ໌ແລ້ວບິດເບືອນມັນຫຼັງຈາກທີ່ໄດ້ເຂົ້າໃຈมัน, ທັງที่ພວກເຂົາຮູ້?", english: "Do you covet [the hope, O believers], that they would believe for you while a party of them used to hear the words of Allah and then distort the Torah after they had understood it while they were knowing?" },
  { number: "2:76", arabic: "وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَا بَعْضُهُمْ إِلَىٰ bَعْضٍ قَالُوا أَتُحَدِّثُونَهُم بِمَا فَتَحَ اللَّهُ عَلَيْكُمْ لِيُحَajُّوكُم بِهِ عِندَ رَبِّكُمْ ۚ أَفَلَا تَعْقِلُونَ", lao: "ແລະເມື່ອພວກເຂົາພົບກັບຜູ້ທີ່ເຊື່ອ, ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາເຊື່ອແລ້ວ.' ແຕ່ເມື່ອພວກເຂົາຢູ່ຕາມລຳພັງ, ພວກເຂົາກ່າວວ່າ: 'ພວກເຈົ້າຈະບອກພວກເຂົາໃນສິ່ງທີ່ອັລລໍຮ໌ໄດ້ເປີດເຜີຍໃຫ້ພວກເຈົ້າຮູ້ບໍ, ເພື່ອພວກເຂົາຈະໄດ້ໃຊ້ເປັນຫຼັກຖານຕໍ່ຕ້ານພວກເຈົ້າຕໍ່ໜ້າພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຈົ້າ? ພວກເຈົ້າບໍ່ຄິດບໍ?'", english: "And when they meet those who believe, they say, 'We have believed'; but when they are alone with one another, they say, 'Do you talk to them about what Allah has revealed to you so they can argue with you about it before your Lord?' Then will you not reason?" },
  { number: "2:77", arabic: "أَوَلَا يَعْلَمُونَ أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ", lao: "ພວກເຂົາບໍ່ຮູ້ບໍວ່າອັລລໍຮ໌ຮູ້ສິ່ງที่พวกเขาປິດບັງແລະສິ່ງที่พวกเขาເປີດເຜີຍ?", english: "But do they not know that Allah knows what they conceal and what they declare?" },
  { number: "2.78", arabic: "وَمِنْهُمْ أُمِّيُّونَ لَا يَعْلَمُونَ الْكِتَابَ إِلَّا أَمَانِيَّ وَإِنْ هُمْ إِلَّا يَظُنُّونَ", lao: "ແລະໃນກຸ່ມຂອງພວກເຂົາມີຜູ້ທີ່ບໍ່ຮູ້ໜັງສື, ພວກເຂົາບໍ່ຮູ້ຄຳພີຍົກເວັ້ນຄວາມປາດຖະຫນາ, ແລະພວກເຂົາເປັນພຽງການຄາດເດົາ.", english: "And among them are unlettered ones who do not know the Scripture except in wishful thinking, but they are only assuming." },
  { number: "2:79", arabic: "فَوَيْلٌ لِّلَّذِينَ يَكْتُbُونَ الْكِتَابَ بِأَيْدِيهِمْ ثُمَّ يَقُولُونَ هَٰذَا مِنْ عِندِ اللَّهِ لِيَشْتَرُوا بِهِ ثَمَنًا قَلِيلًا ۖ فَوَيْلٌ لَّهُم مِّمَّا كَتَبَتْ أَيْدِيهِمْ وَوَيْلٌ لَّهُم mِّمَّا يَكْسِبُونَ", lao: "ດັ່ງນັ້ນ, ຄວາມວິບັດຈະເກີດແກ່ຜູ້ที่ຂຽນຄຳພີດ້ວຍມືຂອງພວກເຂົາເອງແລ້ວກ່າວວ່າ: 'ນີ້ມາຈາກອັລລໍຮ໌' ເພື່ອແລກປ່ຽນมันດ້ວຍລາຄາທີ່ຕໍ່າຕ້ອຍ. ດັ່ງນັ້ນ, ຄວາມວິບັດຈະເກີດແກ່ພວກເຂົາຈາກສິ່ງที่ມືຂອງພວກເຂົາໄດ້ຂຽນ, ແລະຄວາມວິບັດຈະເກີດແກ່ພວກເຂົາຈາກສິ່ງที่พวกເຂົາໄດ້ຮັບ.", english: "So woe to those who write the 'scripture' with their own hands, then say, 'This is from Allah,' in order to exchange it for a small price. Woe to them for what their hands have written and woe to them for what they earn." },
  { number: "2:80", arabic: "وَقَالُوا لَن تَمَسَّنَا النَّarُ إِلَّا أَيَّامًا مَّعْدُودَةً ۚ قُلْ أَتَّخَذْتُمْ عِندَ اللَّهِ عَهْدًا فَلَن يُخْلِfَ اللَّهُ عَهْدَهُ ۖ أَمْ تَقُولُونَ عَلَى اللَّهِ مَا لَا تَعْلَمُونَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ໄຟນະລົກຈະບໍ່ແຕະຕ້ອງພວກເຮົາເວັ້ນແຕ່ສອງສາມວັນ.' ຈົ່ງກ່າວວ່າ: 'ພວກເຈົ້າໄດ້ຮັບສັນຍາຈາກອັລລໍຮ໌ບໍ? ດັ່ງນັ້ນ, ອັລລໍຮ໌ຈະບໍ່ລະເມີດສັນຍາຂອງພຣະອົງ. ຫຼືພວກເຈົ້າເວົ້າກ່ຽວກັບອັລລໍຮ໌ในສິ່ງທີ່ພວກເຈົ້າບໍ່ຮູ້?'", english: "And they say, 'Never will the Fire touch us, except for a few days.' Say, 'Have you taken a covenant from Allah? For Allah will never break His covenant. Or do you say about Allah that which you do not know?'" },
  { number: "2:81", arabic: "بَلَىٰ مَن كَسَبَ سَيِّئَةً وَأَحَاطَتْ بِهِ خَطِيئَتُهُ فَأُولَٰئِكَ أَصْحَابُ النَّarِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ບໍ່ດັ່ງນັ້ນ, ຜູ້ໃດທີ່ກະທຳຄວາມຊົ່ວແລະຄວາມຜິດຂອງລາວໄດ້ລ້ອມຮອບລາວ, ພວກເຂົາຄືຊາວນາລົກ; ພວກເຂົາຈະຢູ່ໃນນັ້ນຕະຫຼອດໄປ.", english: "Yes, whoever earns evil and his sin has encompassed him - those are the companions of the Fire; they will abide therein eternally." },
  { number: "2:82", arabic: "وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ", lao: "ແລະຜູ້ທີ່ເຊື່ອແລະເຮັດຄວາມດີ, ພວກເຂົາຄືຊາວສະຫວັນ; ພວກເຂົາຈະຢູ່ໃນນັ້ນຕະຫຼອດໄປ.", english: "But they who believe and do righteous deeds - those are the companions of Paradise; they will abide therein eternally." },
  { number: "2:83", arabic: "وَإِذْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ لَا تَعْبُdُونَ إِلَّا اللَّهَ وَبِالْوَالِدَيْنِ إِحْسَانًا وَذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَقُولُوا لِلنَّاسِ حُسْنًا وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ ثُمَّ تَوَلَّيْتُمْ إِلَّا قَلِيلًا مِّنكُمْ وَأَنتُم مُّعْرِضُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເອົາສັນຍາຈາກລູກຫຼານຂອງອິດສະຣາເອວວ່າ: 'ຢ່ານະມັດສະການຜູ້ໃດນອກຈາກອັລລໍຮ໌, ແລະຈົ່ງເຮັດດີຕໍ່ພໍ່ແມ່, ຍາດພີ່ນ້ອງ, ເດັກກຳພ້າ, ແລະຄົນຍາກຈົນ. ແລະຈົ່ງເວົ້າດີຕໍ່ຄົນທັງຫຼາຍ, ແລະຈົ່ງດຳລົງການລະໝາດ, ແລະຈົ່ງຈ່າຍຊາກາດ.' ຫຼັງຈາກນັ້ນ, ພວກເຈົ້າໄດ້ຫັນໜີຍົກເວັ້ນຈຳນວນໜ້ອຍໃນກຸ່ມຂອງພວກເຈົ້າ, ແລະພວກເຈົ້າກໍຍັງຜິນຫຼັງ.", english: "And [recall] when We took the covenant from the Children of Israel, [enjoining upon them], 'Do not worship except Allah; and to parents do good and to relatives, orphans, and the needy. And speak to people good [words] and establish prayer and give zakah.' Then you turned away, except a few of you, and you were refusing." },
  { number: "2:84", arabic: "وَإِذْ أَخَذْنَا مِيثَاقَكُمْ لَا تَسْfِكُونَ دِمَاءَكُمْ وَلَا تُخْرِjُونَ أَنفُسَكُم مِّن دِيَarِكُمْ ثُمَّ أَقْرَرْتُمْ وَأَنتُمْ تَشْهَدُونَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເອົາສັນຍາຈາກພວກເຈົ້າວ່າ: 'ຢ່າເຮັດໃຫ້ເລືອດຂອງພວກເຈົ້າໄຫຼ, ແລະຢ່າຂັບໄລ່ກັນອອກຈາກບ້ານຂອງພວກເຈົ້າ.' ຫຼັງຈາກນັ້ນ, ພວກເຈົ້າໄດ້ຍອມຮັບແລະເປັນພະຍານ.", english: "And [recall] when We took your covenant, [saying], 'Do not shed each other's blood or evict one another from your homes.' Then you acknowledged [this] while you were witnessing." },
  { number: "2:85", arabic: "ثُمَّ أَنتُمْ هَٰؤُلَاءِ تَقْتُلُونَ أَنفُسَكُمْ وَتُخْرِjُونَ فَرِيقًا مِّنكُم مِّن دِيَarِهِمْ تَظَاهَرُونَ عَلَيْهِم بِالْإِثْمِ وَالْعُدْوَانِ وَإِن يَأْتُوكُمْ أُسَarَىٰ تُfَادُوهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ إِخْرَاجُهُمْ ۚ أَفَتُؤْمِنُونَ بِbَعْضِ الْكِتَابِ وَتَكْfُرُونَ بِbَعْضٍ ۚ فَمَا jَزَاءُ مَن يَfْعَلُ ذَٰلِكَ مِنكُمْ إِلَّا خِزْيٌ فِي الْحَيَاةِ الدُّنْيَا ۖ وَيَوْمَ الْقِيَامَةِ يُرَدُّونَ إِلَىٰ أَشَدِّ الْعَذَابِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", lao: "ຫຼັງຈາກນັ້ນ, ພວກເຈົ້າກໍຂ້າກັນເອງແລະຂັບໄລ່ກຸ່ມໜຶ່ງອອກຈາກບ້ານຂອງພວກເຂົາ, ໂດຍຊ່ວຍເຫຼືອເຊິ່ງກັນและກັນໃນການກະທຳບາບແລະການລະເມີດ. ແລະຖ້າພວກເຂົາມາຫາພວກເຈົ້າໃນຖານະຊະເລີຍ, ພວກເຈົ້າກໍໄຖ່ພວກເຂົາ, ທັງທີ່ການຂັບໄລ່ພວກເຂົາເປັນສິ່ງທີ່ຖືກຫ້າມ. ພວກເຈົ້າເຊື່ອໃນບາງສ່ວນຂອງຄຳພີແລະປະຕິເສດບາງສ່ວນບໍ? ດັ່ງນັ້ນ, ການຕອບແທນສຳລັບຜູ້ທີ່ກະທຳเช่นນັ້ນໃນກຸ່ມຂອງພວກເຈົ້າບໍ່ແມ່ນສິ່ງໃດນອກຈາກຄວາມອັບອາຍໃນຊີວິດນີ້. ແລະໃນວັນກິຍາມະ, ພວກເຂົາຈະຖືກນຳໄປສູ່ການລົງໂທດที่รุนแรงที่สุด. ແລະອັລລໍຮ໌ບໍ່ໄດ້ລະເລີຍສິ່ງທີ່ພວກເຈົ້າເຮັດ.", english: "After that you are those who kill one another and evict a party of your people from their homes, cooperating against them in sin and aggression. And if they come to you as captives, you ransom them, although their eviction was forbidden to you. So do you believe in part of the Scripture and disbelieve in part? Then what is the recompense for those who do that among you except disgrace in worldly life; and on the Day of Resurrection they will be sent back to the most severe punishment. And Allah is not unaware of what you do." },
  { number: "2:86", arabic: "أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الْحَيَاةَ الدُّنْيَا بِالْآخِرَةِ ۖ فَلَا يُخَفَّfُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنصَرُونَ", lao: "ພວກເຂົາຄືຜູ້ທີ່ໄດ້ຊື້ຊີວິດນີ້ດ້ວຍໂລກໜ້າ, ດັ່ງນັ້ນການລົງໂທດຈະບໍ່ຖືກຫຼຸດຜ່ອນໃຫ້ພວກເຂົາ, ແລະພວກເຂົາຈະບໍ່ໄດ້ຮັບການຊ່ວຍເຫຼືອ.", english: "Those are the ones who have bought the life of this world [in exchange] for the Hereafter, so the punishment will not be lightened for them, nor will they be aided." },
  { number: "2:87", arabic: "وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَقَفَّيْنَا مِن بَعْدِهِ بِالرُّسُلِ ۖ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْbَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ الْقُدُسِ ۗ أَفَكُلَّمَا jَاءَكُمْ رَسُولٌ بِمَا لَا تَهْوَىٰ أَنفُسُكُمُ اسْتَكْبَرْتُمْ فَfَرِيقًا كَذَّbْتُمْ وَfَرِيقًا تَقْتُلُونَ", lao: "ແລະແນ່ນອນ, ເຮົາໄດ້ໃຫ້ຄຳພີແກ່ມູຊາແລະໄດ້ສົ່ງສາດສະດາตามมาหลังจากเขา. ແລະເຮົາໄດ້ໃຫ້ຫຼັກຖານທີ່ຊັດເຈນແກ່ອີຊາລູກຂອງມາຣີຍຳແລະໄດ້ສະໜັບສະໜູນລາວດ້ວຍວິນຍານບໍລິສຸດ. ທຸກຄັ້ງທີ່ສາດສະດາມາຫາພວກເຈົ້າດ້ວຍສິ່ງທີ່ພວກເຈົ້າບໍ່ມັກ, ພວກເຈົ້າກໍອວດດີ, ໂດຍປະຕິເສດບາງຄົນແລະຂ້າບາງຄົນ.", english: "And We did certainly give Moses the Scripture and followed up after him with messengers. And We gave Jesus, the son of Mary, clear proofs and supported him with the Pure Spirit. But is it [not] that every time a messenger came to you, [O Children of Israel], with what your souls did not desire, you were arrogant? And a party [of messengers] you denied and another party you killed." },
  { number: "2:88", arabic: "وَقَالُوا قُلُوبُنَا غُلْfٌ ۚ بَل لَّعَنَهُمُ اللَّهُ بِكُfْرِهِمْ فَقَلِيلًا مَّا يُؤْمِنُونَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ຫົວໃຈຂອງພວກເຮົາຖືກປິດ.' ບໍ່, ອັລລໍຮ໌ໄດ້ສາບແຊ່ງພວກເຂົາເພາະການປະຕິເສດຂອງພວກເຂົາ, ດັ່ງນັ້ນພວກເຂົາຈຶ່ງເຊື່ອພຽງเล็กน้อย.", english: "And they said, 'Our hearts are wrapped.' But, [in fact], Allah has cursed them for their disbelief, so little is it that they believe." },
  { number: "2:89", arabic: "وَلَمَّا jَاءَهُمْ كِتَابٌ مِّنْ عِندِ اللَّهِ مُصَدِّقٌ لِّمَا مَعَهُمْ وَكَانُوا مِن قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذِينَ كَفَرُوا فَلَمَّا jَاءَهُم مَّا عَرَfُوا كَفَرُوا بِهِ ۚ فَلَعْنَةُ اللَّهِ عَلَى الْكَافِرِينَ", lao: "ແລະເມື່ອຄຳພີຈາກອັລລໍຮ໌ມາຫາພວກເຂົາ, ຢືນຢັນສິ່ງທີ່ພວກເຂົາມີຢູ່, ທັງທີ່ກ່ອນໜ້ານີ້ພວກເຂົາເຄີຍຂໍໄຊຊະນະເໜືອຜູ້ປະຕິເສດ. ແຕ່ເມື່ອສິ່ງที่พวกເຂົາຮູ້ຈັກມາຫາພວກເຂົາ, ພວກເຂົາກໍປະຕິເສດมัน. ດັ່ງນັ້ນ, ຄຳສາບແຊ່ງຂອງອັລລໍຮ໌จงอยู่กับผู้ปฏิเสธ.", english: "And when there came to them a Book from Allah confirming that which was with them - although before they used to pray for victory against those who disbelieved - but [then] when there came to them that which they recognized, they disbelieved in it; so the curse of Allah will be upon the disbelievers." },
  { number: "2:90", arabic: "بِئْسَمَا اشْتَرَوْا بِهِ أَنfُسَهُمْ أَن يَكْfُرُوا بِمَا أَنزَلَ اللَّهُ بَغْيًا أَن يُنَزِّلَ اللَّهُ مِن فَضْلِهِ عَلَىٰ مَن يَشَاءُ مِنْ عِبَادِهِ ۖ فَبَاءُوا بِغَضَبٍ عَلَىٰ غَضَبٍ ۚ وَلِلْكَافِرِينَ عَذَابٌ مُّهِينٌ", lao: "ຊົ່ວຊ້າທີ່ສຸດຄືສິ່ງที่พวกເຂົາໄດ້ຂາຍຕົວເອງไป, ໂດຍປະຕິເສດສິ່ງທີ່ອັລລໍຮ໌ໄດ້ເປີດເຜີຍລົງມາ, ດ້ວຍຄວາມອິດສາที่ອັລລໍຮ໌ຈະເປີດເຜີຍຄວາມໂປດປານของพระองค์แก่ผู้ที่พระองค์ทรงประสงค์จากบ่าวของพระองค์. ດັ່ງນັ້ນ, ພວກເຂົາจึงกลับมาพร้อมกับความโกรธซ้อนความโกรธ. และสำหรับผู้ปฏิเสธจะได้รับการลงโทษที่น่าอัปยศ.", english: "How wretched is that for which they sold themselves - that they would disbelieve in what Allah has revealed, grudging that Allah should send down of His favor upon whom He wills of His servants. So they have returned with anger upon anger. And for the disbelievers is a humiliating punishment." },
  { number: "2:91", arabic: "وَإِذَا قِيلَ لَهُمْ آمِنُوا بِمَا أَنزَلَ اللَّهُ قَالُوا نُؤْمِنُ بِمَا أُنزِلَ عَلَيْنَا وَيَكْfُرُونَ بِمَا وَرَاءَهُ وَهُوَ الْحَقُّ مُصَدِّقًا لِّمَا مَعَهُمْ ۗ قُلْ فَلِمَ تَقْتُلُونَ أَنbِيَاءَ اللَّهِ مِن قَبْلُ إِن كُنتُم مُّؤْمِنِينَ", lao: "ແລະເມື່ອຖືກກ່າວແກ່ພວກເຂົາວ່າ: 'ຈົ່ງເຊື່ອໃນສິ່ງທີ່ອັລລໍຮ໌ໄດ້ເປີດເຜີຍລົງມາ', ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາເຊື່ອໃນສິ່ງที่ຖືກເປີດເຜີຍລົງມາໃຫ້ພວກເຮົາ.' ແລະພວກເຂົາປະຕິເສດສິ່ງທີ່ຢູ່ເໜືອມັນ, ທັງທີ່ມັນເປັນຄວາມຈິງທີ່ຢືນຢັນສິ່ງທີ່ພວກເຂົາມີຢູ່. ຈົ່ງກ່າວວ່າ: 'ເປັນຫຍັງພວກເຈົ້າຈຶ່ງຂ້າສາດສະດາຂອງອັລລໍຮ໌ໃນອະດີດ, ຖ້າພວກເຈົ້າເປັນຜູ້ທີ່ເຊື່ອ?'", english: "And when it is said to them, 'Believe in what Allah has revealed,' they say, 'We believe [only] in what was revealed to us.' And they disbelieve in what came after it, while it is the truth confirming that which is with them. Say, 'Then why did you kill the prophets of Allah before, if you are [indeed] believers?'" },
  { number: "2:92", arabic: "وَلَقَدْ jَاءَكُم مُّوسَىٰ بِالْbَيِّنَاتِ ثُمَّ اتَّخَذْتُمُ الْعِjْلَ مِن بَعْدِهِ وَأَنتُمْ ظَالِمُونَ", lao: "ແລະແນ່ນອນ, ມູຊາໄດ້ມາຫາພວກເຈົ້າດ້ວຍຫຼັກຖານທີ່ຊັດເຈນ, ແລ້ວພວກເຈົ້າໄດ້ເອົາລູກງົວມາເປັນພະເຈົ້າຫຼັງຈາກລາວ, ແລະພວກເຈົ້າກໍເປັນຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.", english: "And Moses had certainly come to you with clear proofs. Then you took the calf [for worship] after him, while you were wrongdoers." },
  { number: "2:93", arabic: "وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُم بِقُوَّةٍ وَاسْمَعُوا ۖ قَالُوا سَمِعْنَا وَعَصَيْنَا وَأُشْرِبُوا فِي قُلُوبِهِمُ الْعِjْلَ بِكُfْرِهِمْ ۚ قُلْ بِئْسَمَا يَأْمُرُكُم بِهِ إِيمَانُكُمْ إِن كُنتُم مُّؤْمِنِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເອົາສັນຍາຈາກພວກເຈົ້າແລະຍົກພູເຂົາຂຶ້ນເໜືອພວກເຈົ້າ, [ໂດຍກ່າວວ່າ]: 'ຈົ່ງຍຶດໝັ້ນໃນສິ່ງທີ່ເຮົາໄດ້ໃຫ້ແກ່ພວກເຈົ້າຢ່າງເຂັ້ມແຂງແລະຈົ່ງຟັງ.' ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາໄດ້ຍິນແລ້ວແລະພວກເຮົາບໍ່ເຊື່ອຟັງ.' ແລະການນະມັດສະການລູກງົວໄດ້ຖືກດູດซึมเข้าสู่หัวใจของพวกเขาเพราะການປະຕິເສດຂອງພວກເຂົາ. ຈົ່ງກ່າວວ່າ: 'ຊົ່ວຊ້າທີ່ສຸດຄືສິ່ງที่ความเชื่อของพวกเจ้าสั่งให้ทำ, ຖ້າພວກເຈົ້າເປັນຜູ້ທີ່ເຊື່ອ.'", english: "And [recall] when We took your covenant and raised over you the mount, [saying], 'Take what We have given you with determination and listen.' They said, 'We have heard and disobeyed.' And their hearts absorbed [the worship of] the calf because of their disbelief. Say, 'How wretched is that which your faith enjoins upon you, if you should be believers.'" },
  { number: "2:94", arabic: "قُلْ إِن كَانَتْ لَكُمُ الدَّarُ الْآخِرَةُ عِندَ اللَّهِ خَالِصَةً مِّن دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِن كُنتُمْ صَادِقِينَ", lao: "ຈົ່ງກ່າວວ່າ: 'ຖ້າໂລກໜ້າທີ່ອັລລໍຮ໌ເປັນຂອງພວກເຈົ້າໂດຍສະເພາະ, ບໍ່ແມ່ນຂອງຄົນອື່ນ, ກໍຈົ່ງປາດຖະຫນາຄວາມຕາຍ, ຖ້າພວກເຈົ້າເປັນຜູ້ที่สัตย์จริง.'", english: "Say, [O Muhammad], 'If the home of the Hereafter with Allah is for you alone and not the [other] people, then wish for death, if you should be truthful.'" },
  { number: "2:95", arabic: "وَلَن يَتَمَنَّوْهُ أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ", lao: "ແລະພວກເຂົາຈະບໍ່ປາດຖະຫນາມັນເລີຍເພາະສິ່ງທີ່ມືຂອງພວກເຂົາໄດ້ສົ່ງໄປກ່ອນ. ແລະອັລລໍຮ໌ຊົງຮູ້ຈັກผู้ที่ไม่ยุติธรรม.", english: "But they will never wish for it, ever, because of what their hands have put forth. And Allah is Knowing of the wrongdoers." },
  { number: "2:96", arabic: "وَلَتَjِدَنَّهُمْ أَحْرَصَ النَّاسِ عَلَىٰ حَيَاةٍ وَمِنَ الَّذِينَ أَشْرَكُوا ۚ يَوَدُّ أَحَدُهُمْ لَوْ يُعَمَّرُ أَلْfَ سَنَةٍ وَمَا هُوَ بِمُزَحْزِحِهِ مِنَ الْعَذَابِ أَن يُعَمَّرَ ۗ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ", lao: "ແລະແນ່ນອນ, ເຈົ້າຈະພົບວ່າພວກເຂົາເປັນຄົນທີ່ໂລບມາກທີ່ສຸດໃນຊີວິດ, ຍິ່ງກວ່າผู้ที่ตั้งภาคี. ບາງຄົນໃນພວກເຂົາປາດຖະຫນາที่จะมีชีวิตอยู่พันปี, ແຕ່ການมีชีวิตยืนยาวນັ້ນຈະไม่สามารถช่วยให้เขาพ้นจากการลงโทษได้. ແລະອັລລໍຮ໌ຊົງเห็นในสิ่งที่พวกเขาทำ.", english: "And you will surely find them the most greedy of people for life - [even] more than those who associate others with Allah. One of them wishes that he could be granted life a thousand years, but it would not remove him in the least from the punishment that he should be granted life. And Allah is Seeing of what they do." },
  { number: "2:97", arabic: "قُلْ مَن كَانَ عَدُوًّا لِّjِبْرِيلَ فَإِنَّهُ نَزَّلَهُ عَلَىٰ قَلْبِكَ بِإِذْنِ اللَّهِ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَهُدًى وَبُشْرَىٰ لِلْمُؤْمِنِينَ", lao: "ຈົ່ງກ່າວວ່າ: 'ຜູ້ໃດທີ່ເປັນສັດຕູຕໍ່ຍິບຣີລ, ແທ້ຈິງລາວໄດ້ເປີດເຜີຍมันลงมาสู่หัวใจของเจ้าด้วยการอนุมัติของอัลลอฮ์, ยืนยันสิ่งที่อยู่ก่อนหน้ามัน, และเป็นทางนำและข่าวดีสำหรับผู้ศรัทธา.'", english: "Say, 'Whoever is an enemy to Gabriel - it is [none but] he who has brought the Qur'an down upon your heart, [O Muhammad], by permission of Allah, confirming that which was before it and as guidance and good tidings for the believers.'" },
  { number: "2:98", arabic: "مَن كَانَ عَدُوًّا لِّلَّهِ وَمَلَائِكَتِهِ وَرُسُلِهِ وَjِبْرِيلَ وَمِيكَالَ فَإِنَّ اللَّهَ عَدُوٌّ لِّلْكَافِرِينَ", lao: "ຜູ້ໃດທີ່ເປັນສັດຕູຕໍ່ອັລລໍຮ໌, ທູດສະຫວັນຂອງພຣະອົງ, ສາດສະດາຂອງພຣະອົງ, ຍິບຣີລ, ແລະມີກາອີລ, ແທ້ຈິງອັລລໍຮ໌ເປັນສັດຕູຕໍ່ผู้ปฏิเสธ.", english: "Whoever is an enemy to Allah and His angels and His messengers and Gabriel and Michael - then indeed, Allah is an enemy to the disbelievers." },
  { number: "2:99", arabic: "وَلَقَدْ أَنزَلْنَا إِلَيْكَ آيَاتٍ بَيِّنَاتٍ ۖ وَمَا يَكْfُرُ بِهَا إِلَّا الْfَاسِقُونَ", lao: "ແລະແນ່ນອນ, ເຮົາໄດ້ເປີດເຜີຍອາຍະຕ່າງໆที่ชัดเจนลงมาให้เจ้า, ແລະไม่มีใครปฏิเสธมันนอกจากผู้ที่ละเมิด.", english: "And We have certainly revealed to you verses [which are] clear proofs, and no one would disbelieve in them except the defiantly disobedient." },
  { number: "2:100", arabic: "أَوَكُلَّمَا عَاهَدُوا عَهْدًا نَّbَذَهُ فَرِيقٌ مِّنْهُم ۚ بَلْ أَكْثَرُهُمْ لَا يُؤْمِنُونَ", lao: "ທຸກຄັ້ງທີ່ພວກເຂົາເຮັດສັນຍາ, ກຸ່ມໜຶ່ງໃນພວກເຂົາກໍโยนมันทิ้งไปບໍ? ບໍ່, ສ່ວນຫຼາຍຂອງພວກເຂົາບໍ່ເຊື່ອ.", english: "Is it not [the case] that every time they made a covenant, a party of them threw it away? But, [in fact], most of them do not believe." },
  { number: "2:101", arabic: "وَلَمَّا jَاءَهُمْ رَسُولٌ مِّنْ عِندِ اللَّهِ مُصَدِّقٌ لِّمَا مَعَهُمْ نَبَذَ فَرِيقٌ مِّنَ الَّذِينَ أُوتُوا الْكِتَابَ كِتَابَ اللَّهِ وَرَاءَ ظُهُورِهِمْ كَأَنَّهُمْ لَا يَعْلَمُونَ", lao: "ແລະເມື່ອສາດສະດາຈາກອັລລໍຮ໌ມາຫາພວກເຂົາ, ຢືນຢັນສິ່ງที่พวกเขามีอยู่, ກຸ່ມໜຶ່ງໃນผู้ที่ได้รับคัมภีร์ได้โยนคัมภีร์ของอัลลอฮ์ไปข้างหลัง, ราวกับว่าพวกเขาไม่รู้.", english: "And when a messenger from Allah came to them confirming that which was with them, a party of those who had been given the Scripture threw the Scripture of Allah behind their backs as if they did not know [what it contained]." },
  { number: "2:102", arabic: "وَاتَّbَعُوا مَا تَتْلُو الشَّيَاطِينُ عَلَىٰ مُلْكِ سُلَيْمَانَ ۖ وَمَا كَفَرَ سُلَيْمَانُ وَلَٰكِنَّ الشَّيَاطِينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَ وَمَا أُنزِلَ عَلَى الْمَلَكَيْنِ بِbَابِلَ هَarُوتَ وَمَarُوتَ ۚ وَمَا يُعَلِّمَانِ مِنْ أَحَدٍ حَتَّىٰ يَقُولَا إِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْfُرْ ۖ فَيَتَعَلَّمُونَ مِنْهُمَا مَا يُfَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَزَوْjِهِ ۚ وَمَا هُم بِضَarِّينَ بِهِ مِنْ أَحَدٍ إِلَّا بِإِذْنِ اللَّهِ ۚ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمْ وَلَا يَنfَعُهُمْ ۚ وَلَقَدْ عَلِمُوا لَمَنِ اشْتَرَاهُ مَا لَهُ فِي الْآخِرَةِ مِنْ خَلَاقٍ ۚ وَلَبِئْسَ مَا شَرَوْا بِهِ أَنfُسَهُمْ ۚ لَوْ كَانُوا يَعْلَمُونَ", lao: "ແລະພວກເຂົາໄດ້ຕິດຕາມສິ່ງที่ซัยตอนอ่านในรัชสมัยของสุลัยมาน. และสุลัยมานไม่ได้ปฏิเสธ, แต่ซัยตอนปฏิเสธ, สอนเวทมนตร์แก่ผู้คนและสิ่งที่ถูกเปิดเผยแก่ทูตสวรรค์สององค์ที่บาบิโลน, ฮารุตและมารุต. แต่ทั้งสองไม่ได้สอนใครจนกว่าจะกล่าวว่า: 'เราเป็นเพียงการทดลอง, ดังนั้นอย่าปฏิเสธ.' แล้วพวกเขาเรียนรู้จากทั้งสองสิ่งที่ทำให้เกิดความแตกแยกระหว่างชายและภรรยา. แต่พวกเขาไม่สามารถทำร้ายใครได้ด้วยมันเว้นแต่จะได้รับอนุญาตจากอัลลอฮ์. และพวกเขาเรียนรู้สิ่งที่ทำร้ายพวกเขาและไม่มีประโยชน์ต่อพวกเขา. และแน่นอน, พวกเขารู้ว่าผู้ที่ซื้อมันจะไม่มีส่วนแบ่งในโลกหน้า. และช่างเลวร้ายจริงๆ ที่พวกเขาขายวิญญาณของตัวเองไป, หากพวกเขารู้.", english: "And they followed [instead] what the devils had recited during the reign of Solomon. It was not Solomon who disbelieved, but the devils disbelieved, teaching people magic and that which was revealed to the two angels at Babylon, Harut and Marut. But the two angels do not teach anyone unless they say, 'We are a trial, so do not disbelieve [by practicing magic].' And [yet] they learn from them that by which they cause separation between a man and his wife. But they do not harm anyone with it except by permission of Allah. And they learn what harms them and does not benefit them. And they certainly knew that whoever purchased the magic would not have in the Hereafter any share. And wretched is that for which they sold themselves, if they only knew." },
  { number: "2:103", arabic: "وَلَوْ أَنَّهُمْ آمَنُوا وَاتَّقَوْا لَمَثُوبَةٌ مِّنْ عِندِ اللَّهِ خَيْرٌ ۖ لَّوْ كَانُوا يَعْلَمُونَ", lao: "ແລະຖ້າພວກເຂົາເຊື່ອແລະຢຳເກງ, ລາງວັນຈາກອັລລໍຮ໌จะดีกว่า, ຫາກພວກເຂົາຮູ້.", english: "And if they had believed and feared Allah, then a reward from Allah would have been [far] better, if they only knew." },
  { number: "2:104", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَقُولُوا رَاعِنَا وَقُولُوا انظُرْنَا وَاسْمَعُوا ۗ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຢ່າເວົ້າວ່າ 'รออินา' ແຕ່ຈົ່ງເວົ້າວ່າ 'อันซุรนา' ແລະຈົ່ງຟັງ. ແລະສຳລັບผู้ปฏิเสธจะได้รับการลงโทษที่เจ็บปวด.", english: "O you who have believed, do not say, 'Raina,' but say, 'Unthurna,' and listen. And for the disbelievers is a painful punishment." },
  { number: "2:105", arabic: "مَّا يَوَدُّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَلَا الْمُشْرِكِينَ أَن يُنَزَّلَ عَلَيْكُم مِّنْ خَيْرٍ مِّن رَّbِّكُمْ ۗ وَاللَّهُ يَخْتَصُّ بِرَحْمَتِهِ مَن يَشَاءُ ۚ وَاللَّهُ ذُو الْfَضْلِ الْعَظِيمِ", lao: "ຜູ້ที่ปฏิเสธจากชาวคัมภีร์และผู้ตั้งภาคีไม่ต้องการให้ความดีใดๆ ถูกเปิดเผยลงมาให้พวกเจ้าจากพระเจ้าของพวกเจ้า. แต่อัลลอฮ์ทรงเลือกความเมตตาของพระองค์สำหรับผู้ที่พระองค์ทรงประสงค์. และอัลลอฮ์ทรงเป็นเจ้าของความโปรดปรานอันยิ่งใหญ่.", english: "Neither those who disbelieve from the People of the Scripture nor the polytheists wish that any good should be sent down to you from your Lord. But Allah selects for His mercy whom He wills, and Allah is the possessor of great bounty." },
  { number: "2:106", arabic: "مَا نَنسَخْ مِنْ آيَةٍ أَوْ نُنسِهَا نَأْتِ بِخَيْرٍ مِّنْهَا أَوْ مِثْلِهَا ۗ أَلَمْ تَعْلَمْ أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ເຮົາບໍ່ຍົກເລີກອາຍະໃດໆ ຫຼືເຮັດໃຫ້ມັນຖືກລືມ, ເວັ້ນແຕ່ເຮົາຈະນຳສິ່ງທີ່ດີກວ່າຫຼືເໝືອນກັນມາແທນ. ເຈົ້າບໍ່ຮູ້ບໍວ່າອັລລໍຮ໌ມີອຳນາດເໜືອທຸກສິ່ງ?", english: "We do not abrogate a verse or cause it to be forgotten except that We bring forth [one] better than it or similar to it. Do you not know that Allah is over all things competent?" },
  { number: "2:107", arabic: "أَلَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَمَا لَكُم مِّن دُونِ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ", lao: "ເຈົ້າບໍ່ຮູ້ບໍວ່າອັລລໍຮ໌ເປັນເຈົ້າຂອງອຳນາດແຫ່ງຊັ້ນຟ້າແລະແຜ່ນດິນ? ແລະນອກຈາກອັລລໍຮ໌, ພວກເຈົ້າບໍ່ມີຜູ້ປົກປ້ອງຫຼືຜູ້ຊ່ວຍເຫຼືອໃດໆ.", english: "Do you not know that to Allah belongs the dominion of the heavens and the earth and [that] you have not besides Allah any protector or any helper?" },
  { number: "2:108", arabic: "أَمْ تُرِيدُونَ أَن تَسْأَلُوا رَسُولَكُمْ كَمَا سُئِلَ مُوسَىٰ مِن قَبْلُ ۗ وَمَن يَتَبَدَّلِ الْكُfْرَ بِالْإِيمَانِ فَقَدْ ضَلَّ سَوَاءَ السَّbِيلِ", lao: "ຫຼືພວກເຈົ້າຕ້ອງການที่จะถามศาสดาของพวกเจ้าเหมือนกับที่มูซาถูกถามในอดีต? และผู้ใดที่แลกเปลี่ยนการปฏิเสธด้วยความศรัทธา, เขาได้หลงทางจากหนทางที่ถูกต้องแล้ว.", english: "Or do you intend to ask your Messenger as Moses was asked before? And whoever exchanges faith for disbelief has certainly strayed from the soundness of the way." },
  { number: "2:109", arabic: "وَدَّ كَثِيرٌ مِّنْ أَهْلِ الْكِتَابِ لَوْ يَرُدُّونَكُم مِّن بَعْدِ إِيمَانِكُمْ كُfَّarًا حَسَدًا مِّنْ عِندِ أَنfُسِهِم مِّن بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ ۖ فَاعْfُوا وَاصْfَحُوا حَتَّىٰ يَأْتِيَ اللَّهُ بِأَمْرِهِ ۗ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ชาวคัมภีร์จำนวนมากปรารถนาที่จะทำให้พวกเจ้ากลับไปเป็นผู้ปฏิเสธหลังจากที่พวกเจ้าได้ศรัทธาแล้ว, ด้วยความอิจฉาจากตัวเอง, หลังจากที่ความจริงได้ปรากฏแก่พวกเขาแล้ว. ดังนั้นจงให้อภัยและอดทนจนกว่าอัลลอฮ์จะทรงมีพระบัญชา. แท้จริงอัลลอฮ์ทรงมีอำนาจเหนือทุกสิ่ง.", english: "Many of the People of the Scripture wish they could turn you back to disbelief after you have believed, out of envy from themselves [even] after the truth has become clear to them. So pardon and overlook until Allah brings His command. Indeed, Allah is over all things competent." },
  { number: "2:110", arabic: "وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ ۚ وَمَا تُقَدِّمُوا لِأَنfُسِكُم مِّنْ خَيْرٍ تَjِدُوهُ عِندَ اللَّهِ ۗ إِنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ", lao: "ແລະຈົ່ງດຳລົງການລະໝາດແລະຈົ່ງຈ່າຍຊາກາດ. ແລະຄວາມດີໃດໆที่พวกเจ้าส่งไปล่วงหน้าสำหรับตัวเอง, พวกเจ้าจะพบมันกับอัลลอฮ์. แท้จริงอัลลอฮ์ทรงเห็นในสิ่งที่พวกเจ้าทำ.", english: "And establish prayer and give zakah, and whatever good you put forward for yourselves - you will find it with Allah. Indeed, Allah of what you do, is Seeing." },
  { number: "2:111", arabic: "وَقَالُوا لَن يَدْخُلَ الْجَنَّةَ إِلَّا مَن كَانَ هُودًا أَوْ نَصَarَىٰ ۗ تِلْكَ أَمَانِيُّهُمْ ۗ قُلْ هَاتُوا بُرْهَانَكُمْ إِن كُنتُمْ صَادِقِينَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ไม่มีใครจะเข้าสวรรค์ได้นอกจากผู้ที่เป็นชาวยิวหรือชาวคริสต์.' นั่นคือความปรารถนาของพวกเขา. ຈົ່ງກ່າວວ່າ: 'จงนำหลักฐานของพวกเจ้ามา, ຖ້າພວກເຈົ້າเป็นผู้ที่สัตย์จริง.'", english: "And they say, 'None will enter Paradise except one who is a Jew or a Christian.' That is [merely] their wishful thinking. Say, 'Produce your proof, if you should be truthful.'" },
  { number: "2:112", arabic: "بَلَىٰ مَنْ أَسْلَمَ وَjْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ فَلَهُ أَjْرُهُ عِندَ رَبِّهِ وَلَا خَوْfٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", lao: "ບໍ່, ຜູ້ໃດที่ยอมจำนนต่ออัลลอฮ์ในขณะที่เขาเป็นผู้ทำความดี, เขาจะได้รับรางวัลจากพระเจ้าของเขา, และจะไม่มีความกลัวใดๆ ต่อพวกเขาและพวกเขาจะไม่โศกเศร้า.", english: "Yes [on the contrary], whoever submits his face in Islam to Allah while being a doer of good will have his reward with his Lord. And no fear will there be concerning them, nor will they grieve." },
  { number: "2:113", arabic: "وَقَالَتِ الْيَهُودُ لَيْسَتِ النَّصَarَىٰ عَلَىٰ شَيْءٍ وَقَالَتِ النَّصَarَىٰ لَيْسَتِ الْيَهُودُ عَلَىٰ شَيْءٍ وَهُمْ يَتْلُونَ الْكِتَابَ ۗ كَذَٰلِكَ قَالَ الَّذِينَ لَا يَعْلَمُونَ مِثْلَ قَوْلِهِمْ ۚ فَاللَّهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِfُونَ", lao: "ແລະຊາວຢິວກ່າວວ່າ: 'ຊາວຄຣິດບໍ່ມີຫຍັງເລີຍ.' ແລະຊາວຄຣິດກ່າວວ່າ: 'ຊາວຢິວບໍ່ມີຫຍັງເລີຍ.' ທັງທີ່ພວກເຂົາອ່ານຄຳພີ. ເຊັ່ນດຽວກັນ, ຜູ້ທີ່ບໍ່ຮູ້ກໍກ່າວເໝືອນກັບພວກເຂົາ. ດັ່ງນັ້ນອັລລໍຮ໌ຈະຊົງຕັດສິນລະຫວ່າງພວກເຂົາໃນວັນກິຍາມະໃນສິ່ງທີ່ພວກເຂົາເຄີຍຂັດແຍ່ງກັນ.", english: "The Jews say 'The Christians have nothing [true] to stand on,' and the Christians say, 'The Jews have nothing to stand on,' although they [both] recite the Scripture. Thus the polytheists speak the same as their words. But Allah will judge between them on the Day of Resurrection concerning that over which they used to differ." },
  { number: "2:114", arabic: "وَمَنْ أَظْلَمُ مِمَّن مَّنَعَ مَسَajِدَ اللَّهِ أَن يُذْكَرَ فِيهَا اسْمُهُ وَسَعَىٰ فِي خَرَابِهَا ۚ أُولَٰئِكَ مَا كَانَ لَهُمْ أَن يَدْخُلُوهَا إِلَّا خَائِfِينَ ۚ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الْآخِرَةِ عَذَابٌ عَظِيمٌ", lao: "ແລະໃຜຈະອະທຳຍິ່ງກວ່າຜູ້ທີ່ຫ້າມມັດສະຍິດຂອງອັລລໍຮ໌ບໍ່ໃຫ້มีการກ່າວເຖິງພຣະນາມຂອງພຣະອົງໃນນັ້ນແລະພະຍາຍາມທຳລາຍມັນ? ພວກເຂົາບໍ່ຄວນເຂົ້າໄປໃນນັ້ນເວັ້ນແຕ່ດ້ວຍຄວາມຢ້ານກົວ. ສຳລັບພວກເຂົາໃນໂລກນີ້ຄືຄວາມອັບອາຍ, ແລະສຳລັບພວກເຂົາໃນໂລກໜ້າຄືການລົງໂທດອັນຍິ່ງໃຫຍ່.", english: "And who are more unjust than those who prevent the name of Allah from being mentioned in His mosques and strive toward their ruin? It is not for them to enter them except in fear. For them in this world is disgrace, and they will have in the Hereafter a great punishment." },
  { number: "2:115", arabic: "وَلِلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ ۚ فَأَيْنَمَا تُوَلُّوا فَثَمَّ وَjْهُ اللَّهِ ۚ إِنَّ اللَّهَ وَاسِعٌ عَلِيمٌ", lao: "ແລະທິດຕາເວັນອອກແລະທິດຕາເວັນຕົກເປັນຂອງອັລລໍຮ໌. ດັ່ງນັ້ນບໍ່ວ່າພວກເຈົ້າຈະຫັນໄປທາງໃດ, ທີ່ນັ້ນຄືພຣະພັກຂອງອັລລໍຮ໌. ແທ້ຈິງອັລລໍຮ໌ຊົງກວ້າງໃຫຍ່, ຊົງຮອບຮູ້.", english: "And to Allah belongs the east and the west. So wherever you [might] turn, there is the Face of Allah. Indeed, Allah is all-Encompassing and Knowing." },
  { number: "2:116", arabic: "وَقَالُوا اتَّخَذَ اللَّهُ وَلَدًا ۗ سُbْحَانَهُ ۖ بَل لَّهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ كُلٌّ لَّهُ قَانِتُونَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ອັລລໍຮ໌ຊົງມີບຸດ.' ພຣະອົງຊົງບໍລິສຸດ! ບໍ່, ສິ່ງທີ່ຢູ່ໃນຊັ້ນຟ້າແລະແຜ່ນດິນເປັນຂອງພຣະອົງ. ທຸກสิ่งຍອມຈຳນົນຕໍ່ພຣະອົງ.", english: "They say, 'Allah has taken a son.' Exalted is He! Rather, to Him belongs whatever is in the heavens and the earth. All are devoutly obedient to Him," },
  { number: "2:117", arabic: "بَدِيعُ السَّمَاوَاتِ وَالْأَرْضِ ۖ وَإِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُن فَيَكُونُ", lao: "ผู้สร้างชั้นฟ้าและแผ่นดิน. และเมื่อพระองค์ทรงกำหนดสิ่งใด, พระองค์เพียงกล่าวแก่มันว่า: 'จงเป็น' และมันก็เป็น.", english: "Originator of the heavens and the earth. When He decrees a matter, He only says to it, 'Be,' and it is." },
  { number: "2:118", arabic: "وَقَالَ الَّذِينَ لَا يَعْلَمُونَ لَوْلَا يُكَلِّمُنَا اللَّهُ أَوْ تَأْتِينَا آيَةٌ ۗ كَذَٰلِكَ قَالَ الَّذِينَ مِن قَبْلِهِم مِّثْلَ قَوْلِهِمْ ۘ تَشَابَهَتْ قُلُوبُهُمْ ۗ قَدْ بَيَّنَّا الْآيَاتِ لِقَوْمٍ يُوقِنُونَ", lao: "ແລະຜູ້ທີ່ບໍ່ຮູ້ກ່າວວ່າ: 'ທຳໄມອັລລໍຮ໌ບໍ່ตรัสກັບเราຫຼືມີอายะມາໃຫ້เรา?' ເຊັ່ນດຽວກັນ, ຜູ້ທີ່ຢູ່ກ່ອນหน้าพวกเขาก็กล่าวเหมือนกับพวกเขา. ຫົວໃຈຂອງພວກເຂົາຄ້າຍກັນ. เราได้ชี้แจงอายะต่างๆ แก่ผู้ที่เชื่อมั่นแล้ว.", english: "Those who do not know say, 'Why does Allah not speak to us or there come to us a sign?' Thus spoke those before them like their words. Their hearts resemble each other. We have shown clearly the signs to a people who are certain [in faith]." },
  { number: "2:119", arabic: "إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا ۖ وَلَا تُسْأَلُ عَنْ أَصْحَابِ الْjَحِيمِ", lao: "แท้จริง, เราได้ส่งเจ้ามาด้วยความจริง, เป็นผู้แจ้งข่าวดีและผู้ตักเตือน. และเจ้าจะไม่ถูกถามเกี่ยวกับชาวนรก.", english: "Indeed, We have sent you, [O Muhammad], with the truth as a bringer of good tidings and a warner, and you will not be asked about the companions of Hellfire." },
  { number: "2:120", arabic: "وَلَن تَرْضَىٰ عَنكَ الْيَهُودُ وَلَا النَّصَarَىٰ حَتَّىٰ تَتَّbِعَ مِلَّتَهُمْ ۗ قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَىٰ ۗ وَلَئِنِ اتَّbَعْتَ أَهْوَاءَهُم بَعْدَ الَّذِي jَاءَكَ مِنَ الْعِلْمِ ۙ مَا لَكَ مِنَ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ", lao: "ແລະຊາວຢິວແລະຊາວຄຣິດຈະບໍ່ມີວັນພໍໃຈเจ้าຈົນກວ່າเจ้าจะปฏิบัติตามศาสนาของพวกเขา. ຈົ່ງກ່າວວ່າ: 'แท้จริง, ทางนำของอัลลอฮ์คือทางนำที่แท้จริง.' และถ้าเจ้าปฏิบัติตามความปรารถนาของพวกเขาหลังจากที่ความรู้ได้มาถึงเจ้าแล้ว, เจ้าจะไม่มีผู้พิทักษ์หรือผู้ช่วยเหลือใดๆ จากอัลลอฮ์.", english: "And never will the Jews or the Christians approve of you until you follow their religion. Say, 'Indeed, the guidance of Allah is the [only] guidance.' If you were to follow their desires after what has come to you of knowledge, you would have against Allah no protector or helper." },
  { number: "2:121", arabic: "الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَتْلُونَهُ حَقَّ تِلَاوَتِهِ أُولَٰئِكَ يُؤْمِنُونَ بِهِ ۗ وَمَن يَكْfُرْ بِهِ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ", lao: "ບັນດາຜູ້ທີ່ເຮົາໄດ້ໃຫ້ຄຳພີແກ່ພວກເຂົາ, ພວກເຂົາອ່ານມັນຢ່າງຖືກຕ້ອງ. ພວກເຂົາຄືຜູ້ທີ່ເຊື່ອໃນມັນ. ແລະຜູ້ໃດທີ່ປະຕິເສດມັນ, ພວກເຂົາຄືຜູ້ທີ່ຂາດທຶນ.", english: "Those to whom We have given the Book recite it with its true recital. They [are the ones who] believe in it. And whoever disbelieves in it - it is they who are the losers." },
  { number: "2:122", arabic: "يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ", lao: "ໂອ້ ລູກຫຼານຂອງອິດສະຣາເອວ, ຈົ່ງລະນຶກເຖິງຄວາມໂປດປານຂອງຂ້ອຍທີ່ຂ້ອຍໄດ້ໃຫ້ແກ່ພວກເຈົ້າ ແລະວ່າຂ້ອຍໄດ້ຍົກຍ້ອງພວກເຈົ້າເໜືອຄົນທັງຫຼາຍ.", english: "O Children of Israel, remember My favor which I have bestowed upon you and that I preferred you over the worlds." },
  { number: "2:123", arabic: "وَاتَّقُوا يَوْمًا لَّا تَjْزِي نَفْسٌ عَن نَّfْسٍ شَيْئًا وَلَا يُقْبَلُ مِنْهَا عَدْلٌ وَلَا تَنfَعُهَا شَفَاعَةٌ وَلَا هُمْ يُنصَرُونَ", lao: "ແລະຈົ່ງຢຳເກງວັນທີ່ບໍ່ມີໃຜສາມາດຊ່ວຍເຫຼືອໃຜໄດ້, ແລະບໍ່ມີຄ່າໄຖ່ໃດໆຈະຖືກຮັບຈາກລາວ, ແລະບໍ່ມີການໄກ່ເກ່ຍໃດໆຈະມີປະໂຫຍດແກ່ລາວ, ແລະພວກເຂົາຈະບໍ່ໄດ້ຮັບການຊ່ວຍເຫຼືອ.", english: "And fear a Day when no soul will suffice for another soul at all, and no compensation will be accepted from it, nor will any intercession benefit it, nor will they be aided." },
  { number: "2:124", arabic: "وَإِذِ ابْتَلَىٰ إِبْرَاهِيمَ رَبُّهُ بِكَلِمَاتٍ فَأَتَمَّهُنَّ ۖ قَالَ إِنِّي jَاعِلُكَ لِلنَّاسِ إِمَامًا ۖ قَالَ وَمِن ذُرِّيَّتِي ۖ قَالَ لَا يَنَالُ عَهْدِي الظَّالِمِينَ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອພຣະຜູ້ເປັນເຈົ້າຂອງອັບຣາຮາມໄດ້ທົດສອບລາວດ້ວຍຄຳສັ່ງຕ່າງໆ, ແລ້ວລາວໄດ້ປະຕິບັດມັນຢ່າງສົມບູນ. ພຣະອົງກ່າວວ່າ: 'ແທ້ຈິງ, ຂ້ອຍຈະເຮັດໃຫ້ເຈົ້າເປັນຜູ້ນຳຂອງມະນຸດ.' ລາວໄດ້ກ່າວວ່າ: 'ແລະຈາກລູກຫຼານຂອງຂ້ອຍເດ?' ພຣະອົງກ່າວວ່າ: 'ສັນຍາຂອງຂ້ອຍຈະບໍ່ລວມເຖິງຜູ້ທີ່ບໍ່ຍຸດຕິທຳ.'", english: "And [mention, O Muhammad], when Abraham was tried by his Lord with commands and he fulfilled them. [Allah] said, 'Indeed, I will make you a leader for the people.' [Abraham] said, 'And of my descendants?' [Allah] said, 'My covenant does not include the wrongdoers.'" },
  { number: "2:125", arabic: "وَإِذْ جَعَلْنَا الْbَيْتَ مَثَابَةً لِّلنَّاسِ وَأَمْنًا وَاتَّخِذُوا مِن مَّقَامِ إِبْرَاهِيمَ مُصَلًّى ۖ وَعَهِدْنَا إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ أَن طَهِّrَا بَيْتِيَ لِلطَّائِfِينَ وَالْعَاكِfِينَ وَالرُّكَّعِ السُّjُودِ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອເຮົາໄດ້ເຮັດໃຫ້ບ້ານ (ກາບະ) ເປັນບ່ອນຊຸມນຸມແລະບ່ອນປອດໄພສຳລັບມະນຸດ. ແລະຈົ່ງເອົາສະຖານທີ່ຂອງອັບຣາຮາມເປັນບ່ອນລະໝາດ. ແລະເຮົາໄດ້ສັນຍາກັບອັບຣາຮາມແລະອິສມາອີລວ່າ: 'ຈົ່ງຊຳລະລ້າງບ້ານຂອງຂ້ອຍໃຫ້ສະອາດສຳລັບຜູ້ທີ່ເດີນອ້ອມ, ຜູ້ທີ່ພັກຢູ່, ແລະຜູ້ທີ່ກົ້ມຂາບແລະຂາບໄຫວ້.'", english: "And [mention] when We made the House a place of return for the people and [a place of] security. And take, [O believers], from the standing place of Abraham a place of prayer. And We charged Abraham and Ishmael, [saying], 'Purify My House for those who perform Tawaf and those who are staying [there] for worship and those who bow and prostrate [in prayer].'" },
  { number: "2:126", arabic: "وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ اجْعَلْ هَٰذَا بَلَدًا آمِنًا وَarْزُقْ أَهْلَهُ مِنَ الثَّمَرَاتِ مَنْ آمَنَ مِنْهُم بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُ قَلِيلًا ثُمَّ أَضْطَرُّهُ إِلَىٰ عَذَابِ النَّarِ ۖ وَبِئْسَ الْمَصِيرُ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອອັບຣາຮາມໄດ້ກ່າວວ່າ: 'ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງຂ້ອຍ, ຂໍໃຫ້ເມືອງນີ້ເປັນເມືອງທີ່ປອດໄພ, ແລະຂໍໃຫ້ປະຊາຊົນຂອງມັນໄດ້ຮັບໝາກໄມ້, ຜູ້ທີ່ເຊື່ອໃນອັລລໍຮ໌ແລະວັນສຸດທ້າຍ.' ພຣະອົງກ່າວວ່າ: 'ແລະຜູ້ທີ່ປະຕິເສດ, ຂ້ອຍຈະໃຫ້ລາວມີຄວາມສຸກຊົ່ວຄາວ, ແລ້ວຂ້ອຍຈະບັງຄັບລາວໄປສູ່ການລົງໂທດຂອງໄຟນະລົກ, ແລະມັນເປັນຈຸດໝາຍປາຍທາງທີ່ຊົ່ວຊ້າ.'", english: "And [mention] when Abraham said, 'My Lord, make this a secure city and provide its people with fruits - whoever of them believes in Allah and the Last Day.' [Allah] said, 'And whoever disbelieves - I will grant him enjoyment for a little; then I will force him to the punishment of the Fire, and wretched is the destination.'" },
  { number: "2:127", arabic: "وَإِذْ يَرْfَعُ إِبْرَاهِيمُ الْقَوَاعِدَ مِنَ الْbَيْتِ وَإِسْمَاعِيلُ رَبَّنَا تَقَبَّلْ مِنَّا ۖ إِنَّكَ أَنتَ السَّمِيعُ الْعَلِيمُ", lao: "ແລະຈົ່ງລະນຶກເຖິງເມື່ອອັບຣາຮາມແລະອິສມາອີລໄດ້ຍົກຮາກຖານຂອງບ້ານຂຶ້ນ, [ໂດຍກ່າວວ່າ]: 'ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້ຮັບຈາກພວກເຮົາ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງໄດ້ຍິນ, ຜູ້ຊົງຮອບຮູ້.'", english: "And [mention] when Abraham was raising the foundations of the House and [with him] Ishmael, [saying], 'Our Lord, accept [this] from us. Indeed You are the Hearing, the Knowing.'" },
  { number: "2:128", arabic: "رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِن ذُرِّيَّتِنَا أُمَّةً مُّسْلِمَةً لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُbْ عَلَيْنَا ۖ إِنَّكَ أَنتَ التَّوَّابُ الرَّحِيمُ", lao: "ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້ພວກເຮົາເປັນຜູ້ທີ່ຍອມຈຳນົນຕໍ່ພຣະອົງ, ແລະຈາກລູກຫຼານຂອງພວກເຮົາ, ຂໍໃຫ້ມີປະຊາຊາດທີ່ຍອມຈຳນົນຕໍ່ພຣະອົງ. ແລະຂໍໃຫ້ສະແດງພິທີກຳຂອງພວກເຮົາໃຫ້ພວກເຮົາເຫັນ, ແລະຂໍໃຫ້ອະໄພໃຫ້ພວກເຮົາ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງອະໄພ, ຜູ້ຊົງເມດຕາ.", english: "Our Lord, and make us Muslims [in submission] to You and from our descendants a Muslim nation [in submission] to You. And show us our rites and accept our repentance. Indeed, You are the Accepting of repentance, the Merciful." },
  { number: "2:129", arabic: "رَبَّنَا وَابْعَثْ فِيهِمْ رَسُولًا مِّنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِكَ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُزَكِّيهِمْ ۚ إِنَّكَ أَنتَ الْعَزِيزُ الْحَكِيمُ", lao: "ໂອ້ ພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາ, ຂໍໃຫ້ສົ່ງສາດສະດາຈາກພວກເຂົາເອງມາໃນກຸ່ມຂອງພວກເຂົາ, ຜູ້ທີ່ຈະອ່ານອາຍະຕ່າງໆຂອງພຣະອົງໃຫ້ພວກເຂົາຟັງ, ແລະສອນຄຳພີແລະປັນຍາໃຫ້ພວກເຂົາ, ແລະຊຳລະລ້າງພວກເຂົາ. ແທ້ຈິງ, ພຣະອົງຄືຜູ້ຊົງອຳນາດ, ຜູ້ຊົງມີປັນຍາ.", english: "Our Lord, and send among them a messenger from themselves who will recite to them Your verses and teach them the Book and wisdom and purify them. Indeed, You are the Exalted in Might, the Wise." },
  { number: "2:130", arabic: "وَمَن يَرْغَبُ عَن مِّلَّةِ إِبْرَاهِيمَ إِلَّا مَن سَfِهَ نَفْسَهُ ۚ وَلَقَدِ اصْطَفَيْنَاهُ فِي الدُّنْيَا ۖ وَإِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ", lao: "ແລະໃຜຈະຫັນໜີຈາກສາສະໜາຂອງອັບຣາຮາມນອກຈາກຜູ້ທີ່ເຮັດໃຫ້ຕົວເອງໂງ່? ແລະແນ່ນອນ, ເຮົາໄດ້ເລືອກລາວໃນໂລກນີ້, ແລະແທ້ຈິງ, ໃນໂລກໜ້າ, ລາວຈະຢູ່ໃນກຸ່ມຜູ້ທີ່ຊອບທຳ.", english: "And who would be averse to the religion of Abraham except one who makes a fool of himself? And We had chosen him in this world, and indeed he, in the Hereafter, will be among the righteous." },
  { number: "2:131", arabic: "إِذْ قَالَ لَهُ رَبُّهُ أَسْلِمْ ۖ قَالَ أَسْلَمْتُ لِرَبِّ الْعَالَمِينَ", lao: "ເມື່ອພຣະຜູ້ເປັນເຈົ້າຂອງລາວໄດ້ກ່າວແກ່ລາວວ່າ: 'ຈົ່ງຍອມຈຳນົນ.' ລາວໄດ້ກ່າວວ່າ: 'ຂ້ອຍຍອມຈຳນົນຕໍ່ພຣະຜູ້ເປັນເຈົ້າແຫ່ງສາກົນໂລກ.'", english: "When his Lord said to him, 'Submit', he said, 'I have submitted to the Lord of the worlds.'" },
  { number: "2:132", arabic: "وَوَصَّىٰ بِهَا إِبْرَاهِيمُ بَنِيهِ وَيَعْقُوبُ يَا بَنِيَّ إِنَّ اللَّهَ اصْطَفَىٰ لَكُمُ الدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسْلِمُونَ", lao: "ແລະອັບຣາຮາມໄດ້ສັ່ງເສຍສິ່ງນີ້ແກ່ລູກໆຂອງລາວ, ແລະຢາໂຄບກໍເຊັ່ນກັນ, [โดยกล่าวว่า]: 'ໂອ້ ລູກຂອງຂ້ອຍ, ແທ້ຈິງອັລລໍຮ໌ໄດ້ເລືອກສາສະໜາໃຫ້ພວກເຈົ້າ, ດັ່ງນັ້ນຢ່າຕາຍເວັ້ນແຕ່ໃນສະພາບທີ່ເປັນມຸດສະລິມ.'", english: "And Abraham instructed his sons [to do the same] and [so did] Jacob, saying, 'O my sons, indeed Allah has chosen for you this religion, so do not die except while you are Muslims.'" },
  { number: "2:133", arabic: "أَمْ كُنتُمْ شُهَدَاءَ إِذْ حَضَرَ يَعْقُوبَ الْمَوْتُ إِذْ قَالَ لِبَنِيهِ مَا تَعْبُdُونَ مِن بَعْدِي قَالُوا نَعْبُdُ إِلَٰهَكَ وَإِلَٰهَ آبَائِكَ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ إِلَٰهًا وَاحِدًا وَنَحْنُ لَهُ مُسْلِمُونَ", lao: "ຫຼືພວກເຈົ້າເປັນພະຍານເມື່ອຄວາມຕາຍມາເຖິງຢາໂຄບບໍ? ເມື່ອລາວໄດ້ກ່າວແກ່ລູກໆຂອງລາວວ່າ: 'ພວກເຈົ້າຈະນະມັດສະການຫຍັງຫຼັງຈາກຂ້ອຍ?' ພວກເຂົາກ່າວວ່າ: 'ພວກເຮົາຈະນະມັດສະການພຣະເຈົ້າຂອງເຈົ້າແລະພຣະເຈົ້າຂອງບັນພະບຸລຸດຂອງເຈົ້າ, ອັບຣາຮາມ, ອິສມາອີລ, ແລະອີຊາກ - ພຣະເຈົ້າອົງດຽວ. ແລະພວກເຮົາຈະຍອມຈຳນົນຕໍ່ພຣະອົງ.'", english: "Or were you witnesses when death approached Jacob, when he said to his sons, 'What will you worship after me?' They said, 'We will worship your God and the God of your fathers, Abraham and Ishmael and Isaac - one God. And we are Muslims [in submission] to Him.'" },
  { number: "2:134", arabic: "تِلْكَ أُمَّةٌ قَدْ خَلَتْ ۖ لَهَا مَا كَسَبَتْ وَلَكُم مَّا كَسَبْتُمْ ۖ وَلَا تُsْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ", lao: "ນັ້ນຄືປະຊາຊາດທີ່ຜ່ານໄປແລ້ວ. ສຳລັບພວກເຂົາຄືສິ່ງທີ່ພວກເຂົາໄດ້ສະແຫວງຫາ, ແລະສຳລັບພວກເຈົ້າຄືສິ່ງທີ່ພວກເຈົ້າໄດ້ສະແຫວງຫາ. ແລະພວກເຈົ້າຈະບໍ່ຖືກຖາມກ່ຽວກັບສິ່ງທີ່ພວກເຂົາເຄີຍເຮັດ.", english: "That was a nation which has passed on. It will have [the consequence of] what it earned, and you will have what you have earned. And you will not be asked about what they used to do." },
  { number: "2:135", arabic: "وَقَالُوا كُونُوا هُودًا أَوْ نَصَarَىٰ تَهْتَدُوا ۗ قُلْ بَلْ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۖ وَمَا كَانَ مِنَ الْمُشْرِكِينَ", lao: "ແລະພວກເຂົາກ່າວວ່າ: 'ຈົ່ງເປັນຊາວຢິວຫຼືຊາວຄຣິດ, ພວກເຈົ້າຈະໄດ້ຮັບການຊີ້ແນະ.' ຈົ່ງກ່າວວ່າ: 'ບໍ່, [ພວກເຮົາຈະຕາມ] ສາສະໜາຂອງອັບຣາຮາມ, ຜູ້ທີ່ຊື່ກົງ, ແລະລາວບໍ່ໄດ້ຢູ່ໃນກຸ່ມผู้ที่ตั้งภาคี.'", english: "They say, 'Be Jews or Christians [so] you will be guided.' Say, 'Rather, [we follow] the religion of Abraham, inclining toward truth, and he was not of the polytheists.'" },
  { number: "2:136", arabic: "قُولُوا آمَنَّا بِاللَّهِ وَمَا أُNzِلَ إِلَيْنَا وَمَا أُNzِلَ إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَمَا أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَمَا أُوتِيَ النَّbِيُّونَ مِن رَّbِّهِمْ لَا نُfَرِّقُ بَيْنَ أَحَدٍ مِّنْهُمْ وَنَحْنُ لَهُ مُسْلِمُونَ", lao: "ຈົ່ງກ່າວວ່າ: 'ພວກເຮົາເຊື່ອໃນອັລລໍຮ໌, ແລະສິ່ງທີ່ຖືກເປີດເຜີຍລົງມາໃຫ້ພວກເຮົາ, ແລະສິ່ງທີ່ຖືກເປີດເຜີຍລົງມາໃຫ້ອັບຣາຮາມ, ອິສມາອີລ, ອີຊາກ, ຢາໂຄບ, ແລະບັນດາເຜົ່າ, ແລະສິ່ງທີ່ມູຊາແລະອີຊາໄດ້ຮັບ, ແລະສິ່ງທີ່ສາດສະດາທັງຫຼາຍໄດ້ຮັບຈາກພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຂົາ. ພວກເຮົາບໍ່ແຍກແຍະລະຫວ່າງໃຜໃນພວກເຂົາ, ແລະພວກເຮົາຈະຍອມຈຳນົນຕໍ່ພຣະອົງ.'", english: "Say, [O believers], 'We have believed in Allah and what has been revealed to us and what has been revealed to Abraham and Ishmael and Isaac and Jacob and the Descendants and what was given to Moses and Jesus and what was given to the prophets from their Lord. We make no distinction between any of them, and we are Muslims [in submission] to Him.'" },
  { number: "2:137", arabic: "فَإِنْ آمَنُوا بِمِثْلِ مَا آمَنتُم بِهِ فَقَدِ اهْتَدَوا ۖ وَّإِن تَوَلَّوْا فَإِنَّمَا هُمْ فِي شِقَاقٍ ۖ فَسَيَكْfِيكَهُمُ اللَّهُ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ", lao: "ຖ້າພວກເຂົາເຊື່ອໃນສິ່ງທີ່ພວກເຈົ້າເຊື່ອ, ພວກເຂົາກໍຈະໄດ້ຮັບການຊີ້ແນະ. ແຕ່ຖ້າພວກເຂົາຫັນໜີ, ພວກເຂົາກໍຢູ່ໃນຄວາມແຕກແຍກ. ແລະອັລລໍຮ໌ຈະພຽງພໍສຳລັບເຈົ້າໃນການຕໍ່ຕ້ານພວກເຂົາ. ແລະພຣະອົງຄືຜູ້ຊົງໄດ້ຍິນ, ຜູ້ຊົງຮອບຮູ້.", english: "So if they believe in the same as you believe in, then they have been [rightly] guided; but if they turn away, they are only in dissension, and Allah will be sufficient for you against them. And He is the Hearing, the Knowing." },
  { number: "2:138", arabic: "صِبْغَةَ اللَّهِ ۖ وَمَنْ أَحْسَنُ مِنَ اللَّهِ صِبْغَةً ۖ وَنَحْنُ لَهُ عَابِدُونَ", lao: "ສາສະໜາຂອງອັລລໍຮ໌. ແລະໃຜຈະດີກວ່າອັລລໍຮ໌ໃນການໃຫ້ສາສະໜາ? ແລະພວກເຮົາເປັນผู้นมัสการพระองค์.", english: "[And say, 'Ours is] the religion of Allah. And who is better than Allah in [ordaining] religion? And we are worshippers of Him.'" },
  { number: "2:139", arabic: "قُلْ أَتُحَajُّونَنَا فِي اللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمْ وَلَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ وَنَحْنُ لَهُ مُخْلِصُونَ", lao: "ຈົ່ງກ່າວວ່າ: 'ພວກເຈົ້າຈະຖຽງກັບພວກເຮົາກ່ຽວກັບອັລລໍຮ໌ບໍ, ທັງທີ່ພຣະອົງເປັນພຣະຜູ້ເປັນເຈົ້າຂອງພວກເຮົາและພຣະຜູ້ເປັນເຈົ້າของพวกเจ้า? สำหรับเราคือการกระทำของเรา, และสำหรับพวกเจ้าคือการกระทำของพวกเจ้า. และเราจะอุทิศตนเพื่อพระองค์.'", english: "Say, 'Do you argue with us about Allah while He is our Lord and your Lord? For us are our deeds, and for you are your deeds. And we are sincere [in deed and intention] to Him.'" },
  { number: "2:140", arabic: "أَمْ تَقُولُونَ إِنَّ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطَ كَانُوا هُودًا أَوْ نَصَarَىٰ ۗ قُلْ أَأَنتُمْ أَعْلَمُ أَمِ اللَّهُ ۗ وَمَنْ أَظْلَمُ مِمَّن كَتَمَ شَهَادَةً عِندَهُ مِنَ اللَّهِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", lao: "ຫຼືພວກເຈົ້າເວົ້າວ່າອັບຣາຮາມ, ອິສມາອີລ, ອີຊາກ, ຢາໂຄບ, ແລະບັນດາເຜົ່າເປັນຊາວຢິວຫຼືຊາວຄຣິດ? ຈົ່ງກ່າວວ່າ: 'พวกเจ้ามีความรู้มากกว่าหรืออัลลอฮ์? และใครจะอธรรมยิ่งกว่าผู้ที่ปกปิดคำพยานที่เขามีจากอัลลอฮ์? และอัลลอฮ์ไม่ทรงละเลยในสิ่งที่พวกเจ้าทำ.'", english: "Or do you say that Abraham and Ishmael and Isaac and Jacob and the Descendants were Jews or Christians? Say, 'Are you more knowing or is Allah?' And who is more unjust than one who conceals a testimony he has from Allah? And Allah is not unaware of what you do." },
  { number: "2:141", arabic: "تِلْكَ أُمَّةٌ قَدْ خَلَتْ ۖ لَهَا مَا كَسَبَتْ وَلَكُم مَّا كَسَبْتُمْ ۖ وَلَا تُsْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ", lao: "ນັ້ນຄືປະຊາຊາດທີ່ຜ່ານໄປແລ້ວ. ສຳລັບພວກເຂົາຄືສິ່ງທີ່ພວກເຂົາໄດ້ສະແຫວງຫາ, ແລະສຳລັບພວກເຈົ້າຄືສິ່ງທີ່ພວກເຈົ້າໄດ້ສະແຫວງຫາ. ແລະພວກເຈົ້າຈະບໍ່ຖືກຖາມກ່ຽວກັບສິ່ງທີ່ພວກເຂົາເຄີຍເຮັດ.", english: "That was a nation which has passed on. It will have what it earned, and you will have what you have earned. And you will not be asked about what they used to do." },
  { number: "2:142", arabic: "سَيَقُولُ السُّfَهَاءُ مِنَ النَّاسِ مَا وَلَّاهُمْ عَن قِبْلَتِهِمُ الَّتِي كَانُوا عَلَيْهَا ۚ قُل لِّلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ ۚ يَهْدِي مَن يَشَاءُ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ", lao: "ຄົນໂງ່ໃນບັນດາມະນຸດຈະເວົ້າວ່າ: 'ອັນໃດທີ່ເຮັດໃຫ້ພວກເຂົາຫັນໜີຈາກກິບລາຂອງພວກເຂົາທີ່ພວກເຂົາເຄີຍປະຕິບັດ?' ຈົ່ງກ່າວວ່າ: 'ทิศตะวันออกและทิศตะวันตกเป็นของอัลลอฮ์. พระองค์ทรงชี้นำผู้ที่พระองค์ทรงประสงค์ไปยังหนทางที่เที่ยงตรง.'", english: "The foolish among the people will say, 'What has turned them away from their qiblah, which they used to face?' Say, 'To Allah belongs the east and the west. He guides whom He wills to a straight path.'" },
  { number: "2:143", arabic: "وَكَذَٰلِكَ جَعَلْنَاكُمْ أُمَّةً وَسَطًا لِّتَكُونُوا شُهَدَاءَ عَلَى النَّاسِ وَيَكُونَ الرَّسُولُ عَلَيْكُمْ شَهِيدًا ۗ وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِي كُنتَ عَلَيْهَا إِلَّا لِنَعْلَمَ مَن يَتَّbِعُ الرَّسُولَ مِمَّن يَنقَلِبُ عَلَىٰ عَقِبَيْهِ ۚ وَإِن كَانَتْ لَكَبِيرَةً إِلَّا عَلَى الَّذِينَ هَدَى اللَّهُ ۗ وَمَا كَانَ اللَّهُ لِيُضِيعَ إِيمَانَكُمْ ۚ إِنَّ اللَّهَ بِالنَّاسِ لَرَءُوfٌ رَّحِيمٌ", lao: "ແລະເຊັ່ນນັ້ນແຫຼະ, ເຮົາໄດ້ເຮັດໃຫ້ພວກເຈົ້າເປັນປະຊາຊາດທີ່ເປັນກາງ, ເພື່ອພວກເຈົ້າຈະໄດ້ເປັນພະຍານຕໍ່ມະນຸດ, ແລະສາດສະດາຈະໄດ້ເປັນພະຍານຕໍ່ພວກເຈົ້າ. ແລະເຮົາບໍ່ໄດ້ກຳນົດກິບລາທີ່ເຈົ້າເຄີຍປະຕິບັດນອກຈາກເພື່ອທົດສອບຜູ້ທີ່ຕາມສາດສະດາຈາກຜູ້ທີ່ຫັນຫຼັງກັບ. ແລະແທ້ຈິງ, ມັນເປັນສິ່ງທີ່ຍາກລຳບากຍົກເວັ້ນສຳລັບผู้ທີ່อัลลอฮ์ทรงชี้นำ. ແລະอัลลอฮ์จะไม่ทรงทำให้ความศรัทธาของพวกเจ้าสูญเปล่า. แท้จริงอัลลอฮ์ทรงเมตตาปรานีต่อมนุษย์, ทรงเมตตาเสมอ.", english: "And thus we have made you a just community that you will be witnesses over the people and the Messenger will be a witness over you. And We did not make the qiblah which you used to face except that We might make evident who would follow the Messenger from who would turn back on his heels. And indeed, it is difficult except for those whom Allah has guided. And never would Allah have caused you to lose your faith. Indeed Allah is, to the people, Kind and Merciful." },
  { number: "2:144", arabic: "قَدْ نَرَىٰ تَقَلُّbَ وَjْهِكَ فِي السَّمَاءِ ۖ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضَاهَا ۖ فَوَلِّ وَjْهَكَ شَطْرَ الْمَسْjِدِ الْحَرَامِ ۚ وَحَيْثُ مَا كُنتُمْ فَوَلُّوا وُjُوهَكُمْ شَطْرَهُ ۗ وَإِنَّ الَّذِينَ أُوتُوا الْكِتَابَ لَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِن رَّbِّهِمْ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا يَعْمَلُونَ", lao: "ເຮົາເຫັນເຈົ້າຫັນໜ້າໄປມາເທິງທ້ອງຟ້າ. ດັ່ງນັ້ນ, ເຮົາຈະເຮັດໃຫ້ເຈົ້າຫັນໄປສູ່ກິບລາที่ເຈົ້າพอใจ. ດັ່ງນັ້ນ, ຈົ່ງຫັນໜ້າໄປທາງມັດສະຍິດອັນສັກສິດ. ແລະບໍ່ວ່າພວກເຈົ້າຈະຢູ່ໃສ, ຈົ່ງຫັນໜ້າໄປທາງນັ້ນ. ແລະແທ້ຈິງ, ຜູ້ທີ່ໄດ້ຮັບຄຳພີຮູ້ວ່າມັນເປັນความจริงจากพระเจ้าของพวกเขา. และอัลลอฮ์ไม่ทรงละเลยในสิ่งที่พวกเขาทำ.", english: "We have certainly seen the turning of your face, [O Muhammad], toward the heaven, and We will surely turn you to a qiblah with which you will be pleased. So turn your face toward al-Masjid al-Haram. And wherever you [believers] are, turn your faces toward it [in prayer]. Indeed, those who have been given the Scripture well know that it is the truth from their Lord. And Allah is not unaware of what they do." },
  { number: "2:145", arabic: "وَلَئِنْ أَتَيْتَ الَّذِينَ أُوتُوا الْكِتَابَ بِكُلِّ آيَةٍ مَّا تَبِعُوا قِبْلَتَكَ ۚ وَمَا أَنتَ بِتَابِعٍ قِبْلَتَهُمْ ۚ وَمَا بَعْضُهُم بِتَابِعٍ قِبْلَةَ بَعْضٍ ۚ وَلَئِنِ اتَّbَعْتَ أَهْوَاءَهُم مِّن بَعْدِ مَا jَاءَكَ مِنَ الْعِلْمِ ۙ إِنَّكَ إِذًا لَّمِنَ الظَّالِمِينَ", lao: "และถ้าเจ้าจะนำหลักฐานทุกอย่างมาให้ผู้ที่ได้รับคัมภีร์, พวกเขาก็จะไม่ตามกิบละของเจ้า. และเจ้าก็จะไม่ตามกิบละของพวกเขา. และบางคนในพวกเขาก็จะไม่ตามกิบละของคนอื่น. และถ้าเจ้าจะตามความปรารถนาของพวกเขาหลังจากที่ความรู้ได้มาถึงเจ้าแล้ว, แท้จริงเจ้าก็จะเป็นผู้ที่ไม่ยุติธรรม.", english: "And if you brought to those who were given the Scripture every sign, they would not follow your qiblah. Nor will you be a follower of their qiblah. Nor are some of them followers of the qiblah of others. So if you were to follow their desires after what has come to you of knowledge, indeed, you would then be among the wrongdoers." },
  { number: "2:146", arabic: "الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْرِfُونَهُ كَمَا يَعْرِfُونَ أَبْنَاءَهُمْ ۖ وَإِنَّ فَرِيقًا مِّنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَهُمْ يَعْلَمُونَ", lao: "ຜູ້ທີ່ເຮົາໄດ້ໃຫ້ຄຳພີແກ່ພວກເຂົາ, ພວກເຂົາຮູ້ຈັກລາວເໝືອນດັ່ງที่พวกเขารู้จักลูกชายของตัวเอง. และแท้จริง, กลุ่มหนึ่งในพวกเขากำลังปกปิดความจริงทั้งที่พวกเขารู้.", english: "Those to whom We gave the Scripture know him as they know their own sons. But indeed, a party of them conceal the truth while they know [it]." },
  { number: "2:147", arabic: "الْحَقُّ مِن رَّbِّكَ ۖ فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ", lao: "ความจริงมาจากพระเจ้าของเจ้า, ดังนั้นอย่าเป็นผู้ที่สงสัย.", english: "The truth is from your Lord, so never be among the doubters." },
  { number: "2:148", arabic: "وَلِكُلٍّ وِjْهَةٌ هُوَ مُوَلِّيهَا ۖ فَاسْتَبِقُوا الْخَيْرَاتِ ۚ أَيْنَ مَا تَكُونُوا يَأْتِ بِكُمُ اللَّهُ jَمِيعًا ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ", lao: "ແລະສຳລັບແຕ່ລະຄົນມີທິດທາງที่ລາວຫັນໄປ. ດັ່ງນັ້ນຈົ່ງແຂ່ງຂັນກັນໃນຄວາມດີ. ບໍ່ວ່າພວກເຈົ້າຈະຢູ່ໃສ, ອັລລໍຮ໌ຈະຊົງນຳພວກເຈົ້າທັງໝົດມາ. แท้จริงอัลลอฮ์ทรงมีอำนาจเหนือทุกสิ่ง.", english: "For each [religious following] is a direction toward which it faces. So race to [all that is] good. Wherever you may be, Allah will bring you forth [for judgement] all together. Indeed, Allah is over all things competent." },
  { number: "2:149", arabic: "وَمِنْ حَيْثُ خَرَjْتَ فَوَلِّ وَjْهَكَ شَطْرَ الْمَسْjِدِ الْحَرَامِ ۖ وَإِنَّهُ لَلْحَقُّ مِن رَّbِّكَ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", lao: "และจากที่ใดที่เจ้าออกมา, ຈົ່ງຫັນໜ້າໄປທາງມັດສະຍິດອັນສັກສິດ. และแท้จริง, มันเป็นความจริงจากพระเจ้าของเจ้า. และอัลลอฮ์ไม่ทรงละเลยในสิ่งที่พวกเจ้าทำ.", english: "And from wherever you go out [for prayer, O Muhammad], turn your face toward al-Masjid al-Haram. And indeed, it is the truth from your Lord. And Allah is not unaware of what you do." },
  { number: "2:150", arabic: "وَمِنْ حَيْثُ خَرَjْتَ فَوَلِّ وَjْهَكَ شَطْرَ الْمَسْjِدِ الْحَرَامِ ۚ وَحَيْثُ مَا كُنتُمْ فَوَلُّوا وُjُوهَكُمْ شَطْرَهُ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُjَّةٌ إِلَّا الَّذِينَ ظَلَمُوا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِي وَلِأُتِمَّ نِعْمَتِي عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُونَ", lao: "และจากที่ใดที่เจ้าออกมา, ຈົ່ງຫັນໜ້າໄປທາງມັດສະຍິດອັນສັກສິດ. และไม่ว่าพวกเจ้าจะอยู่ที่ไหน, ຈົ່ງຫันหน้าไปทางนั้น, ເພື່ອไม่ให้ผู้คนมีข้อโต้แย้งกับพวกเจ้า, ยกเว้นผู้ที่ไม่ยุติธรรมในหมู่พวกเขา. ดังนั้นอย่ากลัวพวกเขา, แต่จงกลัวข้า. และเพื่อให้ข้าได้ทำให้ความโปรดปรานของข้าสมบูรณ์แก่พวกเจ้า, และเพื่อให้พวกเจ้าได้รับการชี้นำ.", english: "And from wherever you go out [for prayer], turn your face toward al-Masjid al-Haram. And wherever you [believers] may be, turn your faces toward it in order that the people will not have any argument against you, except for those of them who are wrongdoers, so fear them not but fear Me. And [it is] so I may complete My favor upon you and that you may be guided." },
  { number: "2:151", arabic: "كَمَا أَرْسَلْنَا فِيكُمْ رَسُولًا مِّنكُمْ يَتْلُو عَلَيْكُمْ آيَاتِنَا وَيُزَكِّيكُمْ وَيُعَلِّمُكُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُعَلِّمُكُم مَّا لَمْ تَكُونُوا تَعْلَمُونَ", lao: "เหมือนกับที่เราได้ส่งศาสดาจากพวกเจ้าเองมาในหมู่พวกเจ้า, ผู้อ่านอายะต่างๆ ของเราให้พวกเจ้าฟัง, และชำระล้างพวกเจ้า, และสอนคัมภีร์และปัญญาให้พวกเจ้า, และสอนสิ่งที่พวกเจ้าไม่เคยรู้.", english: "Just as We have sent among you a Messenger from yourselves reciting to you Our verses and purifying you and teaching you the Book and wisdom and teaching you that which you did not know." },
  { number: "2:152", arabic: "فَاذْكُرُونِي أَذْكُرْكُمْ وَاشْكُرُوا لِي وَلَا تَكْfُرُونِ", lao: "ดังนั้นจงระลึกถึงข้า, ข้าจะระลึกถึงพวกเจ้า. และจงขอบคุณข้า, และอย่าปฏิเสธข้า.", english: "So remember Me; I will remember you. And be grateful to Me and do not deny Me." },
  { number: "2:153", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَعِينُوا بِالصَّbْرِ وَالصَّلَاةِ ۚ إِنَّ اللَّهَ مَعَ الصَّابِرِينَ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຈົ່ງຂໍຄວາມຊ່ວຍເຫຼືອດ້ວຍຄວາມອົດທົນແລະການລະໝາດ. แท้จริงอัลลอฮ์อยู่กับผู้ที่อดทน.", english: "O you who have believed, seek help through patience and prayer. Indeed, Allah is with the patient." },
  { number: "2:154", arabic: "وَلَا تَقُولُوا لِمَن يُقْتَلُ فِي سَبِيلِ اللَّهِ أَمْوَاتٌ ۚ بَلْ أَحْيَاءٌ وَلَٰكِن لَّا تَشْعُرُونَ", lao: "และอย่ากล่าวถึงผู้ที่ถูกฆ่าในหนทางของอัลลอฮ์ว่าเป็น 'คนตาย'. ไม่, พวกเขามีชีวิตอยู่, แต่พวกเจ้าไม่รู้สึก.", english: "And do not say about those who are killed in the way of Allah, 'They are dead.' Rather, they are alive, but you perceive [it] not." },
  { number: "2:155", arabic: "وَلَنَبْلُوَنَّكُم بِشَيْءٍ مِّنَ الْخَوْfِ وَالْjُوعِ وَنَقْصٍ مِّنَ الْأَمْوَالِ وَالْأَنfُسِ وَالثَّمَرَاتِ ۗ وَبَشِّرِ الصَّابِرِينَ", lao: "และแน่นอน, เราจะทดสอบพวกเจ้าด้วยความกลัว, ความหิว, การสูญเสียทรัพย์สิน, ชีวิต, และผลไม้. แต่จงแจ้งข่าวดีแก่ผู้ที่อดทน.", english: "And We will surely test you with something of fear and hunger and a loss of wealth and lives and fruits, but give good tidings to the patient," },
  { number: "2:156", arabic: "الَّذِينَ إِذَا أَصَابَتْهُم مُّصِيبَةٌ قَالُوا إِنَّا لِلَّهِ وَإِنَّا إِلَيْهِ رَajِعُونَ", lao: "ผู้ที่เมื่อประสบกับภัยพิบัติ, กล่าวว่า: 'แท้จริงเราเป็นของอัลลอฮ์, และแท้จริงเราจะกลับไปหาพระองค์.'", english: "Who, when disaster strikes them, say, 'Indeed we belong to Allah, and indeed to Him we will return.'" },
  { number: "2:157", arabic: "أُولَٰئِكَ عَلَيْهِمْ صَلَوَاتٌ مِّن رَّbِّهِمْ وَرَحْمَةٌ ۖ وَأُولَٰئِكَ هُمُ الْمُهْتَدُونَ", lao: "พวกเขาคือผู้ที่ได้รับพรจากพระเจ้าของพวกเขาและความเมตตา. และพวกเขาคือผู้ที่ได้รับการชี้นำ.", english: "Those are the ones upon whom are blessings from their Lord and mercy. And it is those who are the [rightly] guided." },
  { number: "2:158", arabic: "إِنَّ الصَّfَا وَالْمَرْوَةَ مِن شَعَائِرِ اللَّهِ ۖ فَمَنْ حَjَّ الْbَيْتَ أَوِ اعْتَمَرَ فَلَا jُنَاحَ عَلَيْهِ أَن يَطَّوَّfَ بِهِمَا ۚ وَمَن تَطَوَّعَ خَيْرًا فَإِنَّ اللَّهَ شَاكِرٌ عَلِيمٌ", lao: "แท้จริง, ซอฟาและมัรวาเป็นสัญลักษณ์ของอัลลอฮ์. ดังนั้นผู้ใดที่ทำฮัจญ์ที่บ้าน (กาบา) หรือทำอุมเราะฮ์, ก็ไม่มีบาปสำหรับเขาที่จะเดินระหว่างทั้งสอง. และผู้ใดที่อาสาทำความดี, แท้จริงอัลลอฮ์ทรงขอบคุณ, ทรงรอบรู้.", english: "Indeed, as-Safa and al-Marwah are among the symbols of Allah. So whoever makes Hajj to the House or performs 'umrah - there is no blame upon him for walking between them. And whoever volunteers good - then indeed, Allah is appreciative and Knowing." },
  { number: "2:159", arabic: "إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنزَلْنَا مِنَ الْbَيِّنَاتِ وَالْهُدَىٰ مِن بَعْدِ مَا بَيَّنَّاهُ لِلنَّاسِ فِي الْكِتَابِ ۙ أُولَٰئِكَ يَلْعَنُهُمُ اللَّهُ وَيَلْعَنُهُمُ اللَّاعِنُونَ", lao: "แท้จริง, ผู้ที่ปกปิดสิ่งที่เราได้เปิดเผยจากหลักฐานที่ชัดเจนและทางนำ, หลังจากที่เราได้ชี้แจงให้แก่ผู้คนในคัมภีร์แล้ว, พวกเขาจะถูกสาปแช่งโดยอัลลอฮ์และผู้ที่สาปแช่ง.", english: "Indeed, those who conceal what We sent down of clear proofs and guidance after We made it clear for the people in the Scripture - those are cursed by Allah and cursed by those who curse," },
  { number: "2:160", arabic: "إِلَّا الَّذِينَ تَابُوا وَأَصْلَحُوا وَبَيَّنُوا فَأُولَٰئِكَ أَتُوبُ عَلَيْهِمْ ۚ وَأَنَا التَّوَّابُ الرَّحِيمُ", lao: "ยกเว้นผู้ที่กลับใจ, แก้ไข, และชี้แจง. พวกเขาคือผู้ที่ข้าจะอภัยให้. และข้าคือผู้ทรงอภัย, ผู้ทรงเมตตา.", english: "Except for those who repent and correct themselves and make clear [what they concealed]. Those - I will accept their repentance, and I am the Accepting of repentance, the Merciful." },
  { number: "2:161", arabic: "إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُfَّarٌ أُولَٰئِكَ عَلَيْهِمْ لَعْنَةُ اللَّهِ وَالْمَلَائِكَةِ وَالنَّاسِ أَjْمَعِينَ", lao: "แท้จริง, ผู้ที่ปฏิเสธและตายในขณะที่ยังเป็นผู้ปฏิเสธ, พวกเขาจะได้รับคำสาปแช่งจากอัลลอฮ์, ทูตสวรรค์, และมนุษย์ทั้งหมด.", english: "Indeed, those who disbelieve and die while they are disbelievers - upon them will be the curse of Allah and of the angels and the people, all together," },
  { number: "2:162", arabic: "خَالِدِينَ فِيهَا ۖ لَا يُخَفَّfُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنظَرُونَ", lao: "พวกเขาจะอยู่ในนั้นตลอดไป. การลงโทษจะไม่ถูกลดหย่อนให้พวกเขา, และพวกเขาจะไม่ถูกรอคอย.", english: "Abiding eternally therein. The punishment will not be lightened for them, nor will they be reprieved." },
  { number: "2:163", arabic: "وَإِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۖ لَّا إِلَٰهَ إِلَّا هُوَ الرَّحْمَٰنُ الرَّحِيمُ", lao: "และพระเจ้าของพวกเจ้าคือพระเจ้าองค์เดียว. ไม่มีพระเจ้าอื่นใดนอกจากพระองค์, ผู้ทรงเมตตา, ผู้ทรงเมตตาเสมอ.", english: "And your god is one God. There is no deity [worthy of worship] except Him, the Entirely Merciful, the Especially Merciful." },
  { number: "2:164", arabic: "إِنَّ فِي خَلْقِ السَّمَاوَاتِ وَالْأَرْضِ وَاخْتِلَافِ اللَّيْلِ وَالنَّهَarِ وَالْfُلْكِ الَّتِي تَjْرِي فِي الْbَحْرِ بِمَا يَنfَعُ النَّاسَ وَمَا أَنزَلَ اللَّهُ مِنَ السَّمَاءِ مِن مَّاءٍ فَأَحْيَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيهَا مِن كُلِّ دَابَّةٍ وَتَصْرِيفِ الرِّيَاحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَاءِ وَالْأَرْضِ لَآيَاتٍ لِّقَوْمٍ يَعْقِلُونَ", lao: "แท้จริง, ในการสร้างชั้นฟ้าและแผ่นดิน, การสลับของกลางคืนและกลางวัน, เรือที่แล่นในทะเลด้วยสิ่งที่ให้ประโยชน์แก่ผู้คน, และสิ่งที่อัลลอฮ์ทรงส่งลงมาจากท้องฟ้าคือน้ำ, แล้วทรงทำให้แผ่นดินมีชีวิตขึ้นมาหลังจากที่มันตายแล้ว, และทรงกระจายสัตว์ทุกชนิดในนั้น, และการเปลี่ยนทิศทางของลมและเมฆที่อยู่ระหว่างท้องฟ้าและแผ่นดิน, ล้วนเป็นสัญญาณสำหรับผู้ที่มีปัญญา.", english: "Indeed, in the creation of the heavens and the earth, and the alternation of the night and the day, and the [great] ships which sail through the sea with that which benefits people, and what Allah has sent down from the heavens of rain, giving life thereby to the earth after its lifelessness and dispersing therein every [kind of] moving creature, and [His] directing of the winds and the clouds controlled between the heaven and the earth are signs for a people who use reason." },
  { number: "2:165", arabic: "وَمِنَ النَّاسِ مَن يَتَّخِذُ مِن دُونِ اللَّهِ أَندَادًا يُحِبُّونَهُمْ كَحُbِّ اللَّهِ ۖ وَالَّذِينَ آمَنُوا أَشَدُّ حُbًّا لِّلَّهِ ۗ وَلَوْ يَرَى الَّذِينَ ظَلَمُوا إِذْ يَرَوْنَ الْعَذَابَ أَنَّ الْقُوَّةَ لِلَّهِ جَمِيعًا وَأَنَّ اللَّهَ شَدِيدُ الْعَذَابِ", lao: "และในบรรดาผู้คน, มีผู้ที่เอาสิ่งอื่นนอกจากอัลลอฮ์มาเป็นภาคี, พวกเขารักสิ่งเหล่านั้นเหมือนกับที่รักอัลลอฮ์. แต่ผู้ที่ศรัทธามีความรักต่ออัลลอฮ์มากกว่า. และถ้าผู้ที่ไม่ยุติธรรมจะเห็นเมื่อพวกเขาเห็นการลงโทษว่าอำนาจทั้งหมดเป็นของอัลลอฮ์และอัลลอฮ์ทรงลงโทษอย่างรุนแรง.", english: "And [yet], among the people are those who take other than Allah as equals [to Him]. They love them as they [should] love Allah. But those who believe are stronger in love for Allah. And if only they who have wronged would consider [that] when they see the punishment, [they will be certain] that all power belongs to Allah and that Allah is severe in punishment." },
  { number: "2:166", arabic: "إِذْ تَبَرَّأَ الَّذِينَ اتُّbِعُوا مِنَ الَّذِينَ اتَّbَعُوا وَرَأَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْأَسْبَابُ", lao: "เมื่อผู้ที่ถูกตามได้ปฏิเสธผู้ที่ตาม, และพวกเขาได้เห็นการลงโทษ, และความสัมพันธ์ระหว่างพวกเขาก็ถูกตัดขาด.", english: "[And] when those who were followed disassociate themselves from those who followed [them], and they see the punishment, and cut off from them are the ties [of relationship]," },
  { number: "2:167", arabic: "وَقَالَ الَّذِينَ اتَّbَعُوا لَوْ أَنَّ لَنَا كَرَّةً فَنَتَبَرَّأَ مِنْهُمْ كَمَا تَبَرَّءُوا مِنَّا ۗ كَذَٰلِكَ يُرِيهِمُ اللَّهُ أَعْمَالَهُمْ حَسَرَاتٍ عَلَيْهِمْ ۖ وَمَا هُم بِخَarِjِينَ مِنَ النَّarِ", lao: "และผู้ที่ตามจะกล่าวว่า: 'ถ้าเรามีโอกาสกลับไปอีกครั้ง, เราจะปฏิเสธพวกเขาเหมือนกับที่พวกเขาปฏิเสธเรา.' เช่นนั้นแหละ, อัลลอฮ์จะทรงแสดงให้พวกเขาเห็นการกระทำของพวกเขาเป็นความเสียใจ. และพวกเขาจะไม่สามารถออกจากไฟนรกได้.", english: "And those who followed will say, 'If only we had another turn [at worldly life] so we could disassociate ourselves from them as they have disassociated themselves from us.' Thus will Allah show them their deeds as regrets upon them. And they are not to emerge from the Fire." },
  { number: "2:168", arabic: "يَا أَيُّهَا النَّاسُ كُلُوا مِمَّا فِي الْأَرْضِ حَلَالًا طَيِّbًا وَلَا تَتَّbِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ مُّbِينٌ", lao: "ໂອ້ ມະນຸດເອີຍ, ຈົ່ງກິນຈາກສິ່ງທີ່ຢູ່ໃນແຜ່ນດິນທີ່ເປັນຮາລານและดี, และอย่าตามรอยเท้าของชัยฏอน. แท้จริง, มันเป็นศัตรูที่เปิดเผยสำหรับพวกเจ้า.", english: "O mankind, eat from whatever is on earth [that is] lawful and good and do not follow the footsteps of Satan. Indeed, he is to you a clear enemy." },
  { number: "2:169", arabic: "إِنَّمَا يَأْمُرُكُم بِالسُّوءِ وَالْfَحْشَاءِ وَأَن تَقُولُوا عَلَى اللَّهِ مَا لَا تَعْلَمُونَ", lao: "แท้จริง, มันสั่งให้พวกเจ้าทำความชั่วและความลามก, และให้พวกเจ้ากล่าวถึงอัลลอฮ์ในสิ่งที่พวกเจ้าไม่รู้.", english: "He only orders you to evil and immorality and to say about Allah what you do not know." },
  { number: "2:170", arabic: "وَإِذَا قِيلَ لَهُمُ اتَّbِعُوا مَا أَنزَلَ اللَّهُ قَالُوا بَلْ نَتَّbِعُ مَا أَلْfَيْنَا عَلَيْهِ آبَاءَنَا ۗ أَوَلَوْ كَانَ آبَاؤُهُمْ لَا يَعْقِلُونَ شَيْئًا وَلَا يَهْتَدُونَ", lao: "และเมื่อถูกกล่าวแก่พวกเขาว่า: 'จงปฏิบัติตามสิ่งที่อัลลอฮ์ได้ทรงเปิดเผย', พวกเขากล่าวว่า: 'ไม่, เราจะปฏิบัติตามสิ่งที่เราพบบรรพบุรุษของเราทำ.' แม้ว่าบรรพบุรุษของพวกเขาจะไม่เข้าใจอะไรเลยและไม่ได้รับการชี้นำก็ตามหรือ?", english: "And when it is said to them, 'Follow what Allah has revealed,' they say, 'Rather, we will follow that upon which we found our fathers.' Even if their fathers understood nothing, nor were they guided?" },
  { number: "2:171", arabic: "وَمَثَلُ الَّذِينَ كَفَرُوا كَمَثَلِ الَّذِي يَنْعِقُ بِمَا لَا يَسْمَعُ إِلَّا دُعَاءً وَنِدَاءً ۚ صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُونَ", lao: "ແລະຕົວຢ່າງຂອງຜູ້ທີ່ປະຕິເສດຄືເໝືອນກັບผู้ທີ່ตะโกนใส่สิ่งที่ไม่ได้ยินอะไรเลยนอกจากเสียงเรียกและเสียงร้อง. ຫູໜວກ, ເປັນໃບ້, ຕາບອດ, ດັ່ງນັ້ນພວກເຂົາຈຶ່ງບໍ່ເຂົ້າໃຈ.", english: "The example of those who disbelieve is like that of one who shouts at what hears nothing but calls and cries cattle or sheep - deaf, dumb and blind, so they do not understand." },
  { number: "2:172", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا كُلُوا مِن طَيِّbَاتِ مَا رَزَقْنَاكُمْ وَاشْكُرُوا لِلَّهِ إِن كُنتُمْ إِيَّاهُ تَعْبُdُونَ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, ຈົ່ງກິນຈາກສິ່ງที่ดีที่เราได้ให้แก่พวกเจ้า, และจงขอบคุณอัลลอฮ์, หากพวกเจ้าบูชาพระองค์.", english: "O you who have believed, eat from the good things which We have provided for you and be grateful to Allah if it is [indeed] Him that you worship." },
  { number: "2:173", arabic: "إِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنزِيرِ وَمَا أُهِلَّ بِهِ لِغَيْرِ اللَّهِ ۖ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَلَا إِثْمَ عَلَيْهِ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ", lao: "แท้จริง, พระองค์ทรงห้ามพวกเจ้าเฉพาะสัตว์ที่ตายเอง, เลือด, เนื้อหมู, และสิ่งที่ถูกเชือดเพื่อสิ่งอื่นนอกจากอัลลอฮ์. แต่ผู้ใดที่ถูกบังคับโดยไม่มีเจตนาที่จะละเมิดหรือล่วงเกิน, ก็ไม่มีบาปสำหรับเขา. แท้จริงอัลลอฮ์ทรงอภัย, ทรงเมตตา.", english: "He has only forbidden to you dead animals, blood, the flesh of swine, and that which has been dedicated to other than Allah. But whoever is forced [by necessity], neither desiring [it] nor transgressing [its limit], there is no sin upon him. Indeed, Allah is Forgiving and Merciful." },
  { number: "2:174", arabic: "إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنزَلَ اللَّهُ مِنَ الْكِتَابِ وَيَشْتَرُونَ بِهِ ثَمَنًا قَلِيلًا ۙ أُولَٰئِكَ مَا يَأْكُلُونَ فِي بُطُونِهِمْ إِلَّا النَّarَ وَلَا يُكَلِّمُهُمُ اللَّهُ يَوْمَ الْقِيَامَةِ وَلَا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ", lao: "แท้จริง, ผู้ที่ปกปิดสิ่งที่อัลลอฮ์ได้ทรงเปิดเผยจากคัมภีร์และขายมันในราคาถูก, พวกเขาไม่ได้กินอะไรเข้าไปในท้องของพวกเขานอกจากไฟ. และอัลลอฮ์จะไม่ทรงตรัสกับพวกเขาในวันกิยามะ, และจะไม่ทรงชำระล้างพวกเขา, และพวกเขาจะได้รับการลงโทษที่เจ็บปวด.", english: "Indeed, they who conceal what Allah has sent down of the Book and exchange it for a small price - those consume not into their bellies except the Fire. And Allah will not speak to them on the Day of Resurrection, nor will He purify them. And they will have a painful punishment." },
  { number: "2:175", arabic: "أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدَىٰ وَالْعَذَابَ بِالْمَغْfِرَةِ ۚ فَمَا أَصْبَرَهُمْ عَلَى النَّarِ", lao: "พวกเขาคือผู้ที่ได้ซื้อความหลงทางด้วยทางนำและการลงโทษด้วยการอภัย. ช่างอดทนต่อไฟนรกเสียนี่กระไร!", english: "Those are the ones who have purchased error for guidance and punishment for forgiveness. So how patient are they in pursuit of the Fire!" },
  { number: "2:176", arabic: "ذَٰلِكَ بِأَنَّ اللَّهَ نَزَّلَ الْكِتَابَ بِالْحَقِّ ۗ وَإِنَّ الَّذِينَ اخْتَلَفُوا فِي الْكِتَابِ لَفِي شِقَاقٍ بَعِيدٍ", lao: "นั่นเป็นเพราะอัลลอฮ์ได้ทรงเปิดเผยคัมภีร์ด้วยความจริง. และแท้จริง, ผู้ที่ขัดแย้งเกี่ยวกับคัมภีร์นั้นอยู่ในความแตกแยกที่ห่างไกล.", english: "That is [deserved by them] because Allah has sent down the Book in truth. And indeed, those who differ over the Book are in extreme dissension." },
  { number: "2:177", arabic: "لَّيْسَ الْbِرَّ أَن تُوَلُّوا وُjُوهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلَٰكِنَّ الْbِرَّ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَالْمَلَائِكَةِ وَالْكِتَابِ وَالنَّbِيِّينَ وَآتَى الْمَالَ عَلَىٰ حُbِّهِ ذَوِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينَ وَابْنَ السَّbِيلِ وَالسَّائِلِينَ وَفِي الرِّقَابِ وَأَقَامَ الصَّلَاةَ وَآتَى الزَّكَاةَ وَالْمُوفُونَ بِعَهْدِهِمْ إِذَا عَاهَدُوا ۖ وَالصَّابِرِينَ فِي الْbَأْسَاءِ وَالضَّرَّاءِ وَحِينَ الْbَأْسِ ۗ أُولَٰئِكَ الَّذِينَ صَدَقُوا ۖ وَأُولَٰئِكَ هُمُ الْمُتَّقُونَ", lao: "ความดีไม่ได้อยู่ที่การหันหน้าไปทางทิศตะวันออกและทิศตะวันตก, แต่ความดีคือผู้ที่ศรัทธาในอัลลอฮ์, วันสุดท้าย, ทูตสวรรค์, คัมภีร์, และศาสดา, และให้ทรัพย์สินที่รักแก่ญาติ, เด็กกำพร้า, คนขัดสน, นักเดินทาง, ผู้ขอ, และเพื่อปลดปล่อยทาส, และดำรงการละหมาด, และจ่ายซะกาต, และผู้ที่ปฏิบัติตามสัญญาเมื่อได้ทำสัญญา, และผู้ที่อดทนในยามทุกข์ยาก, ความเจ็บป่วย, และในยามสงคราม. พวกเขาคือผู้ที่สัตย์จริง, และพวกเขาคือผู้ที่ยำเกรง.", english: "Righteousness is not that you turn your faces toward the east or the west, but [true] righteousness is [in] one who believes in Allah, the Last Day, the angels, the Book, and the prophets and gives wealth, in spite of love for it, to relatives, orphans, the needy, the traveler, those who ask [for help], and for freeing slaves; [and who] establishes prayer and gives zakah; [those who] fulfill their promise when they promise; and [those who] are patient in poverty and hardship and during battle. Those are the ones who have been true, and it is those who are the righteous." },
  { number: "2:178", arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِي الْقَتْلَى ۖ الْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْأُنثَىٰ بِالْأُنثَىٰ ۚ فَمَنْ عُfِيَ لَهُ مِنْ أَخِيهِ شَيْءٌ فَاتِّbَاعٌ بِالْمَعْرُوfِ وَأَدَاءٌ إِلَيْهِ بِإِحْسَانٍ ۗ ذَٰلِكَ تَخْfِيفٌ مِّن رَّbِّكُمْ وَرَحْمَةٌ ۗ فَمَنِ اعْتَدَىٰ بَعْدَ ذَٰلِكَ فَلَهُ عَذَابٌ أَلِيمٌ", lao: "ໂອ້ ຜູ້ທີ່ເຊື່ອ, การตอบแทนที่เท่าเทียมกันได้ถูกกำหนดไว้สำหรับพวกเจ้าในกรณีของการฆาตกรรม - คนอิสระสำหรับคนอิสระ, ทาสสำหรับทาส, และผู้หญิงสำหรับผู้หญิง. แต่ผู้ใดที่ได้รับการอภัยจากพี่น้องของเขา, ก็ให้ปฏิบัติตามอย่างเหมาะสมและชดใช้ให้เขาด้วยความดี. นั่นคือการผ่อนผันจากพระเจ้าของพวกเจ้าและความเมตตา. แต่ผู้ใดที่ล่วงเกินหลังจากนั้น, เขาจะได้รับการลงโทษที่เจ็บปวด.", english: "O you who have believed, prescribed for you is legal retribution for those murdered - the free for the free, the slave for the slave, and the female for the female. But whoever overlooks from his brother anything, then there should be a suitable follow-up and payment to him with good conduct. This is an alleviation from your Lord and a mercy. But whoever transgresses after that will have a painful punishment." },
  { number: "2:179", arabic: "وَلَكُمْ فِي الْقِصَاصِ حَيَاةٌ يَا أُولِي الْأَلْبَابِ لَعَلَّكُمْ تَتَّقُونَ", lao: "ແລະໃນການຕອບແທນທີ່ເທົ່າທຽມກັນມີຊີວິດສຳລັບພວກເຈົ້າ, ໂອ້ ຜູ້ที่มีปัญญา, ເພື່ອພວກເຈົ້າຈະໄດ້ຢຳເກງ.", english: "And there is for you in legal retribution [saving of] life, O you [people] of understanding, that you may become righteous." },
  { number: "2:180", arabic: "كُتِبَ عَلَيْكُمْ إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ إِن تَرَكَ خَيْرًا الْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْأَقْرَبِينَ بِالْمَعْرُوfِ ۖ حَقًّا عَلَى الْمُتَّقِينَ", lao: "ໄດ້ຖືກກຳນົດໄວ້ສຳລັບພວກເຈົ້າວ່າເມື່ອຄວາມຕາຍມາເຖິງຜູ້ໃດໃນພວກເຈົ້າ, ຖ້າລາວປະໄວ້ຊັບສິນ, ກໍໃຫ້ເຮັດພິນัยກຳໃຫ້ພໍ່ແມ່และญาติสนิทอย่างเหมาะสม. ນີ້ເປັນພັນທະສຳລັບผู้ที่ยำเกรง.", english: "Prescribed for you when death approaches one of you if he leaves wealth [is that he should make] a bequest for the parents and near relatives according to what is acceptable - a duty upon the righteous." },
];

export default function SurahAlBaqarahView({ goBack }: SurahAlBaqarahViewProps) {
  const [searchQuery, setSearchQuery] = useState('');

  const filteredVerses = verses.filter(verse =>
    verse.number.includes(searchQuery) ||
    verse.lao.toLowerCase().includes(searchQuery.toLowerCase()) ||
    verse.english.toLowerCase().includes(searchQuery.toLowerCase()) ||
    verse.arabic.includes(searchQuery)
  );
  
  return (
    <div className="flex flex-col">
      <ViewHeader title="ສູຣໍ ອັລບາກໍຣໍ" onBack={goBack} />
      <div className="p-4 sticky top-0 bg-background z-10">
        <Input
          type="text"
          placeholder="ຄົ້ນຫາອາຍັດ..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-full"
        />
      </div>
      <main className="p-4 pt-0">
        {filteredVerses.length > 0 ? (
          filteredVerses.map(v => <VerseCard key={v.number} {...v} />)
        ) : (
          <p className="text-center text-muted-foreground">ບໍ່ພົບອາຍັດທີ່ທ່ານຄົ້ນຫາ.</p>
        )}
        <SummaryCard title="ສະຫຼຸບ">
          ອາຍັດເຫຼົ່ານີ້ອະທິບາຍເຖິງກຸ່ມຄົນຕ່າງໆ: ຜູ້ສັດທາ, ຜູ້ປະຕິເສດ, ແລະ ຜູ້ໜ້າຊື່ໃຈຄົດ. ມັນໄດ້ທ້າທາຍຜູ້ທີ່ສົງໄສໃນຄຳພີກຸຣອານ, ໄດ້ສັນຍາລາງວັນແກ່ผู้ທີ່ເຊື່ອ, ແລະໄດ້ຢືນຢັນເຖິງອຳນາດຂອງອັນລໍຫ໌ໃນການສ້າງຈັກກະວານ ແລະ ມະນຸດ.
        </SummaryCard>
      </main>
    </div>
  );
}
